import{_ as e,a as o}from"./chunks/accelerometer_positions_px4.C_y67alf.js";import{_ as t,c as a,o as r,ab as l}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"Калібрування акселерометра","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/accelerometer.md","filePath":"uk/config/accelerometer.md"}'),c={name:"uk/config/accelerometer.md"},i=l('<h1 id="калібрування-акселерометра" tabindex="-1">Калібрування акселерометра <a class="header-anchor" href="#калібрування-акселерометра" aria-label="Permalink to &quot;Калібрування акселерометра&quot;">​</a></h1><p>Акселерометр повинен бути калібрований при першому використанні або якщо змінився орієнтація контролера польоту. Інакше йому не потрібно перекалібруватися (крім можливо взимку, якщо регулятор польоту не був <a href="./../advanced_config/sensor_thermal_calibration.html">термічно калібрований</a> на заводі).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Погана калібрування акселерометра, як правило, виявляється під час передпольотних перевірок та повідомлень про відмову в узброєнні (попередження QGC зазвичай стосуються &quot;високого зміщення акселерометра&quot; та &quot;помилок перевірки на відповідність&quot;).</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Це схоже на <a href="./../config/compass.html">калібрування компасу</a>, за винятком того, що ви тримаєте автомобіль нерухомим (замість обертання) в кожному положенні.</p></div><h2 id="виконання-калібрування" tabindex="-1">Виконання калібрування <a class="header-anchor" href="#виконання-калібрування" aria-label="Permalink to &quot;Виконання калібрування&quot;">​</a></h2><p><em>QGroundControl</em> буде керувати вами, щоб розмістити та утримувати ваш транспортний засіб в різних орієнтаціях (вас попередять, коли переміщуватися між позиціями).</p><p>Калібрування включає наступні кроки:</p><ol><li><p>Запустіть <em>QGroundControl</em> та підключіть транспортний засіб.</p></li><li><p>Виберіть <strong>іконку &quot;Q&quot; &gt; Налаштування транспортного засобу &gt; Датчики</strong> (бічна панель), щоб відкрити <em>Налаштування датчиків</em>.</p></li><li><p>Натисніть кнопку датчика <strong>Акселерометр</strong>.</p><p><img src="'+e+'" alt="Accelerometer calibration"></p><div class="info custom-block"><p class="custom-block-title">Ви вже повинні були встановити <a href="./../config/flight_controller_orientation.html">Орієнтацію автопілота</a>. Якщо ні, ви також можете встановити це тут.</p></div></li></ol><p>:::</p><ol><li><p>Клацніть <strong>OK</strong>, щоб розпочати калібрування.</p></li><li><p>Розмістіть автомобіль, як вказано на <em>зображеннях</em> на екрані. Після виклику (орієнтаційне зображення стає жовтим) тримайте транспортний засіб нерухомо. Після завершення калібрування для поточного орієнтації пов&#39;язане зображення на екрані стане зеленим.</p><div class="info custom-block"><p class="custom-block-title">INFO</p></div></li></ol><p>Калібрування використовує алгоритм найменших квадратів &#39;fit&#39;, який не вимагає від вас мати &quot;ідеальні&quot; орієнтації під кутом 90 градусів. За умови, що кожна вісь спрямована в основному вгору і вниз у певний момент у послідовності калібрування, а транспортний засіб утримується нерухомо, точна орієнтація не має значення. :::</p><p><img src="'+o+'" alt="Accelerometer calibration"></p><ol><li>Повторіть процес калібрування для всіх орієнтацій автомобіля.</li></ol><p>Після калібрування автомобіля у всіх положеннях <em>QGroundControl</em> відобразить <em>Калібрування завершено</em> (всі зображення орієнтації будуть відображені зеленим кольором, а панель прогресу буде заповнена повністю). Ви можете потім перейти до наступного сенсора.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/sensors_px4.html#accelerometer" target="_blank" rel="noreferrer"> QGroundControl Посібник користувача &gt; Датчики</a></li><li><a href="https://youtu.be/91VGmdSlbo4?t=1m46s" target="_blank" rel="noreferrer">PX4 Setup Video - @1m46s</a> (Youtube)</li></ul>',16),s=[i];function n(p,m,u,d,_,h){return r(),a("div",null,s)}const k=t(c,[["render",n]]);export{b as __pageData,k as default};
