import{_ as t,a as l,b as i,c as r,d as c,e as s,f as d,g as p,h as n,i as u,j as o,k as m,l as h,m as f,n as _,o as g,p as b,q,r as v,s as P,t as C}from"./chunks/reverse_dshot.DOJjvoGl.js";import{_ as k,E as T,c as A,J as M,m as e,a as S,ab as R,o as y}from"./chunks/framework.CUflZczI.js";const F=JSON.parse('{"title":"Конфігурація та тестування приводу","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/actuators.md","filePath":"uk/config/actuators.md"}'),x={name:"uk/config/actuators.md"},O=e("h1",{id:"конфігурація-та-тестування-приводу",tabindex:"-1"},[S("Конфігурація та тестування приводу "),e("a",{class:"header-anchor",href:"#конфігурація-та-тестування-приводу","aria-label":'Permalink to "Конфігурація та тестування приводу"'},"​")],-1),V=R('<p>Перегляд <em>Налаштування приводів</em> використовується для налаштування конкретної геометрії транспортного засобу, призначення приводів і двигунів виходам контролера польоту та перевірки реакції приводу та двигуна.</p><h2 id="загальнии-огляд" tabindex="-1">Загальний огляд <a class="header-anchor" href="#загальнии-огляд" aria-label="Permalink to &quot;Загальний огляд&quot;">​</a></h2><p>Відкрийте перегляд у <em>QGroundControl</em> тут: <strong>&quot;Q&quot; (меню програми) &gt; Налаштування автомобіля &gt; Актуатори</strong> (вкладка). Елементи, що відображаються, залежать від <a href="./../config/airframe.html">вибраного кадру</a>, а вихідні дані відображаються за замовчуванням, як показано в <a href="./../airframes/airframe_reference.html">Посиланні на планер</a>.</p><p>Вид має три розділи:</p><ul><li><a href="#geometry">Геометрія</a>: налаштуйте геометрію <a href="./../config/airframe.html">вибраного планера</a>. Це включає кількість, положення та властивості <a href="#motor-geometry">двигунів</a>, а також кількість і властивості <a href="#control-surfaces-geometry">керуючих поверхонь</a> і <a href="#motor-tilt-servo-geometry">сервоприводів нахилу двигуна</a>.</li><li><a href="#actuator-outputs">Виходи приводів</a>: призначте двигуни, поверхні керування та інші приводи певному виходу.</li><li><a href="#actuator-testing">Тестування приводу</a>: перевірте, чи двигуни та приводи рухаються в очікуваному напрямку/швидкості.</li></ul><p>Можливо, у квадрокоптера буде екран налаштувань, схожий на показаний нижче. Це визначає чотиримоторний коптер з геометрією X. Він відображає 4 двигуни на виходи AUX1 до AUX4 та вказує, що вони підключені до регуляторів швидкості DShot1200 ESC. Також він відображає виходи PWM400 AUX для керування парашутом та стійками посадки.</p><p><img src="'+t+'" alt="Actuators MC (QGC)"></p><div class="info custom-block"><p class="custom-block-title">За замовчуванням відображаються лише найпоширеніші налаштування. Установіть прапорець <strong>Додатково</strong> у верхньому правому куті, щоб відобразити всі налаштування.</p></div><h2 id="геометрія" tabindex="-1">Геометрія <a class="header-anchor" href="#геометрія" aria-label="Permalink to &quot;Геометрія&quot;">​</a></h2><p>Розділ геометрії використовується для встановлення будь-яких конфігурованих параметрів, пов’язаних із геометрією, для вибраного <a href="./../config/airframe.html">планера</a>. Це включає кількість і положення <a href="#motor-geometry">двигунів</a>, а також кількість, функції та властивості <a href="#control-surfaces-geometry">керуючих поверхонь</a>. Для конвертопланів VTOL також включатиме кількість і властивості <a href="#motor-tilt-servo-geometry">сервоприводів нахилу</a></p><div class="info custom-block"><p class="custom-block-title">Інтерфейс користувача налаштовано для вибраного планера:</p><ul><li>Відображаються лише <em>конфігуровані</em> поля для вибраного типу планера; поля, які не можна налаштувати для планера, приховані.</li><li>Діаграма положення двигуна наразі відображається лише для рам мультикоптера.</li></ul></div><h3 id="геометрія-двигунів" tabindex="-1">Геометрія двигунів <a class="header-anchor" href="#геометрія-двигунів" aria-label="Permalink to &quot;Геометрія двигунів&quot;">​</a></h3><p>Розділ геометрії двигунів дозволяє вам встановити кількість двигунів, відносне положення та інші властивості кожного двигуна.</p><p>Більшість властивостей двигуна застосовуються до всіх рамок. Деякі властивості застосовуються до певних рамок. Наприклад, <code>Tilted by</code> і <code>axis</code> актуальні лише для транспортних засобів <a href="#motor-geometry-vtol-tiltrotor">Tiltrotor VTOL</a> і <a href="#motor-geometry-standard-vtol">Standard VTOL</a> відповідно.</p><p>Конфігурація геометрії для повітряних рамок мультікоптерів надає діаграму, яка показує відносні позиції по вісі x та y для кожного з двигунів. Див. <a href="./../airframes/airframe_reference.html">Airframe Reference</a> для загального розуміння позицій двигунів для інших рамок.</p><p>Основні концепції геометрії та конфігурація для ряду різних фреймів наведені в наступних розділах.</p><h4 id="мотор-геометрія-мультикоптер" tabindex="-1">Мотор геометрія: Мультикоптер <a class="header-anchor" href="#мотор-геометрія-мультикоптер" aria-label="Permalink to &quot;Мотор геометрія: Мультикоптер&quot;">​</a></h4><p>На зображенні нижче показано налаштування геометрії для рамки квадрокоптера з і без розширеними налаштуваннями.</p><p><img src="'+l+'" alt="Geometry MC (QGC)"></p><p>Спочатку параметр випадаючого списку <strong>Двигуни</strong> дозволяє вибрати кількість двигунів (4 для вищезазначеного прикладу).</p><p>Для кожного двигуна ви можете встановити:</p><ul><li><code>Позиція X</code>: <a href="#motor-position-coordinate-system">Позиція X</a>, у метрах.</li><li><code>Позиція Y</code>: <a href="#motor-position-coordinate-system">Позиція Y</a>, у метрах.</li><li><code>Позиція Z</code>: <a href="#motor-position-coordinate-system">Z-position</a>, у метрах.</li><li>(Advanced) <code>Напрямок проти годинникової стрілки</code>: Прапорець, що вказує на те, що двигун обертається проти годинникової стрілки (зніміть прапорець для обертання за годинниковою стрілкою).</li><li>(Розширений) <code>Бідирекційний</code>: Прапорець, що вказує, що двигун є <a href="#bidirectional-motors">бідирекційним</a></li><li>(Розширено) <code>Коефіцієнт нахилу</code>: Див. розділ <a href="#control-surfaces-geometry">Геометрія керованих поверхонь</a> для отримання додаткової інформації</li></ul><div class="info custom-block"><p class="custom-block-title">Позиції <code>X</code>, <code>Y</code>, <code>Z</code> в <a href="#motor-position-coordinate-system">FRD координатній системі</a> відносно <em>центру ваги</em>. Зверніть увагу, що це може не збігатися з позицією контролера польоту!</p></div><h4 id="геометрія-двигуна-vtol-quadrotor-tailsitter" tabindex="-1">Геометрія двигуна: VTOL Quadrotor Tailsitter <a class="header-anchor" href="#геометрія-двигуна-vtol-quadrotor-tailsitter" aria-label="Permalink to &quot;Геометрія двигуна: VTOL Quadrotor Tailsitter&quot;">​</a></h4><p>Геометрія двигуна для <a href="./../airframes/airframe_reference.html#vtol-tailsitter">Вертикального зльоту Quad Tailsitter</a> показана нижче (підхід до налаштування інших вертикальних зльотних транспортних засобів буде подібним).</p><p>Двигуни мають ті ж поля конфігурації, що й для <a href="#motor-geometry-multicopter">геометрії мультикоптера</a>.</p><p><img src="'+i+'" alt="Geometry motor: tailsitter vtol"></p><h4 id="геометрія-двигуна-vtol-tiltrotor" tabindex="-1">Геометрія двигуна: VTOL Tiltrotor <a class="header-anchor" href="#геометрія-двигуна-vtol-tiltrotor" aria-label="Permalink to &quot;Геометрія двигуна: VTOL Tiltrotor&quot;">​</a></h4><p>Геометрія двигуна для <a href="./../airframes/airframe_reference.html#vtol_vtol_tiltrotor_generic_quadplane_vtol_tiltrotor">Загального квадроплана VTOL з креновими роторами</a> показана нижче (підхід до налаштування інших <a href="./../airframes/airframe_reference.html#vtol_vtol_tiltrotor_generic_quadplane_vtol_tiltrotor">VTOL кренових роторів</a> буде схожим).</p><p><img src="'+r+'" alt="Geometry motor: tiltrotor vtol"></p><ul><li><code>Нахилений на</code>: Супутній сервопривід, що використовується для нахилу двигуна. Властивості цього серводвигуна визначаються в <a href="#motor-tilt-servo-geometry">Геометрії серводвигуна нахилу двигуна</a>.</li></ul><h4 id="геометрія-двигуна-стандартнии-vtol" tabindex="-1">Геометрія двигуна: стандартний VTOL <a class="header-anchor" href="#геометрія-двигуна-стандартнии-vtol" aria-label="Permalink to &quot;Геометрія двигуна: стандартний VTOL&quot;">​</a></h4><p>Геометрія двигуна для <a href="./../airframes/airframe_reference.html#vtol_standard_vtol_generic_standard_vtol">Загального стандартного Вертикального зльоту</a> показана нижче (підхід до налаштування інших &quot;Стандартних Вертикальних зльотів&quot; буде подібним).</p><p><img src="'+c+'" alt="Geometry motor: standard vtol"></p><p>Двигуни мають більшість тих самих полів конфігурації, що й для <a href="#motor-geometry-multicopter">геометрії багтропа</a>. Є додаткове поле для позначення напрямку, в якому рухається двигун транспортного засобу (для стандартного ВТОЛ, гвинти для утримання у повітрі зазвичай встановлені &quot;вгору&quot;, а тяговий двигун встановлений &quot;вперед&quot;).</p><ul><li><code>Вісь</code>: Один з <code>Вгору</code>, <code>Вниз</code>, <code>Вперед</code>, <code>Назад</code>, <code>Ліворуч</code>, <code>Праворуч</code>, <code>Користувацький</code><ul><li>Якщо вибрано <code>Custom</code>, то інтерфейс користувача відображає три додаткові поля для встановлення орієнтації двигуна.</li></ul></li></ul><h4 id="геометрія-двигуна-інші-транспортні-засоби" tabindex="-1">Геометрія двигуна: Інші транспортні засоби <a class="header-anchor" href="#геометрія-двигуна-інші-транспортні-засоби" aria-label="Permalink to &quot;Геометрія двигуна: Інші транспортні засоби&quot;">​</a></h4><p>Інші типи транспортних засобів визначать відповідну геометрію двигуна для свого типу рами. Ще раз ці двигуни, як правило, матимуть ті ж самі властивості, що й показано вище.</p><p>Наприклад, у літального апарата з фіксованим крилом може бути лише один тяговий двигун, тоді як ровер з диференційним керуванням матиме двигун для керування швидкістю і для керування керуванням.</p><h4 id="система-координат-положення-двигуна" tabindex="-1">Система координат положення двигуна <a class="header-anchor" href="#система-координат-положення-двигуна" aria-label="Permalink to &quot;Система координат положення двигуна&quot;">​</a></h4><p>Система координат для позицій двигуна - FRD (у тілі), де вісь X вказує вперед, вісь Y - праворуч, а вісь Z - вниз.</p><p>Походження - **це центр тяжіння (COG) ** транспортного засобу. Можливо, це може <strong>НЕ БУТИ</strong> тим самим положенням, що й автопілот.</p><p><img src="'+s+'" alt="Actuators CG reference diagram"></p><h4 id="двосторонні-мотори" tabindex="-1">Двосторонні мотори <a class="header-anchor" href="#двосторонні-мотори" aria-label="Permalink to &quot;Двосторонні мотори&quot;">​</a></h4><p>Деякі транспортні засоби можуть використовувати двонаправлені двигуни (тобто двигуни, які підтримують обертання у обох напрямках). Наприклад, земляні транспортні засоби, які хочуть рухатися вперед і назад, або літальні апарати типу VTOL, які мають двигуни-тягунці, які можуть обертатися у будь-якому напрямку.</p><p>Якщо використовуються двонаправлені двигуни, переконайтеся, що для цих двигунів вибрано прапорець &quot;Реверсивний&quot; <strong>Reversible</strong> (цей прапорець відображається як &quot;розширена&quot; опція).</p><p><img src="'+d+'" alt="Reversible"></p><p>Зверніть увагу, що вам також потрібно переконатися, що регулятор швидкості (ESC), пов&#39;язаний з двунаправленими двигунами, налаштований відповідним чином (наприклад, увімкнення режиму 3D для регуляторів швидкості DShot, що можна здійснити за допомогою команд DShot <a href="./../peripherals/dshot.html#commands">DShot commands</a>).</p><h3 id="геометрія-поверхонь-керування" tabindex="-1">Геометрія поверхонь керування <a class="header-anchor" href="#геометрія-поверхонь-керування" aria-label="Permalink to &quot;Геометрія поверхонь керування&quot;">​</a></h3><p>Розділ поверхонь керування на панелі геометрії дозволяє встановити кількість і типи поверхонь керування, які присутні на транспортному засобі. У деяких випадках вам також може знадобитися встановити значення обрізки і швидкості переміщення. Для більш досвідчених користувачів також доступна можливість налаштувати масштаби кочення, рулювання та тангажу (зазвичай значення за замовчуванням є прийнятними і цього не потрібно). Нижче показаний &quot;приклад&quot; розділу поверхонь керування для транспортного засобу з двома елеронами. Зауважте, що елерони впливають тільки на кочення, тому поля тангажу та рулювання вимкнені.</p><p><img src="'+p+'" alt="Control Surface Setup Example"></p><div class="info custom-block"><p class="custom-block-title">За замовчуванням відображаються лише найпоширеніші налаштування. Виберіть прапорець <strong>Додаткові</strong> в правому верхньому куті вікна, щоб відобразити всі налаштування.</p></div><p>Кроки наступні:</p><ul><li><code>Керуючі поверхні</code>: Кількість керуючих поверхонь (встановіть це спочатку!)</li><li><code>Тип</code>: Тип кожної керуючої поверхні: <code>Лівий елерон</code>, <code>Правий елерон</code>, <code>Елеватор</code>, <code>Руль</code>, <code>Лівий елевон</code>, <code>Правий елевон</code>, <code>Ліва V-хвістка</code>, <code>Права V-хвістка</code>, <code>Лівий флап</code>, <code>Правий флап</code>, <code>Повітряні гальма</code>, <code>Користувацькі</code>.</li><li><code>Крутний момент по крену</code>: Ефективність приводу навколо осі крену (нормалізована: -1 до 1). <a href="#actuator-roll-pitch-and-yaw-scaling">Загалом ви повинні використовувати значення актуатора за замовчуванням</a>.</li><li><code>Крутний момент по тангажу</code>: Ефективність приводу навколо осі тангажу (нормалізована: -1 до 1). <a href="#actuator-roll-pitch-and-yaw-scaling">Загалом ви повинні використовувати значення актуатора за замовчуванням</a>.</li><li><code>Крутний момент по курсу</code>: Ефективність приводу навколо осі курсу (нормалізована: -1 до 1). <a href="#actuator-roll-pitch-and-yaw-scaling">Загалом ви повинні використовувати значення актуатора за замовчуванням</a>.</li><li><code>Обрізка</code>: Зміщення, додане до приводу, щоб він був в центрі без вводу. Це може бути визначено методом проб і помилок.</li><li>(Розширено) <code>Швидкість нахилу</code>: Мінімальний час, дозволений для сигналу двигуна/сервопривода для проходження повного діапазону виходу, в секундах. <ul><li>Параметр обмежує швидкість зміни приводу (якщо не вказано, то обмеження швидкості не застосовується). Призначено для приводів, які можуть пошкодитися, якщо вони рухаються занадто швидко — наприклад, приводи нахилу на повітряному судні VTOL з можливістю повороту.</li><li>Наприклад, значення 2.0 означає, що двигун/сервопривід не буде командуватися рухатися від 0 до 1 зі швидкістю, яка завершить операцію менш ніж за 2 секунди (у випадку реверсивних двигунів діапазон -1 до 1).</li></ul></li><li>(Розширено) <code>Масштаб закривання</code>: На скільки цей привод відхиляється в &quot;повній конфігурації закрил&quot; [0, 1] (див. <a href="#flap-scale-and-spoiler-scale-configuration">Конфігурацію масштабу закривання та масштабу спойлера</a> нижче). Може бути використаний для налаштування аеродинамічної поверхні як закрилок або для компенсації обертового моменту, що виникає через головні закрилки.</li><li>(Розширено) <code>Масштаб спойлера</code>: На скільки цей привод відхиляється в &quot;повній конфігурації спойлера&quot; [0, 1] (див. <a href="#flap-scale-and-spoiler-scale-configuration">Конфігурацію масштабу закривання та масштабу спойлера</a> нижче). Може бути використаний для налаштування аеродинамічної поверхні як закрилок або для компенсації обертового моменту, що виникає через головні закрилки.</li><li>(Тільки VTOL) <code>Заблокуйте керуючі поверхні у режимі гвинтокрилування</code>: <ul><li><code>Ввімкнено</code>: Більшість транспортних засобів не використовують керуючі поверхні у режимі утримання на місці. Використовуйте цей параметр, щоб заблокувати їх, щоб вони не впливали на динаміку транспортного засобу.</li><li><code>Вимкнено</code>: Встановіть це для транспортних засобів, які використовують керуючі поверхні у режимі утримання на місці, такі як двокрильний вертольот (який використовує елерони для кочення та розвороту). Також воно повинно бути встановлено для транспортних засобів, які використовують поверхні керування для забезпечення додаткової стабілізації в режимі утримання на місці при русі зі швидкістю або при сильному вітрі.</li></ul></li></ul><h4 id="конфігурація-масштабу-закрилка-та-споилера" tabindex="-1">Конфігурація масштабу закрилка та спойлера <a class="header-anchor" href="#конфігурація-масштабу-закрилка-та-споилера" aria-label="Permalink to &quot;Конфігурація масштабу закрилка та спойлера&quot;">​</a></h4><p>&quot;Керування флапами&quot; та &quot;керування спойлерами&quot; - це аеродинамічні конфігурації, які можуть керуватися вручну пілотом (наприклад, за допомогою RC) або встановлюватися автоматично контролером. Наприклад, пілот або система посадки можуть ввести &quot;керування спойлерами&quot;, щоб зменшити швидкість повітря перед посадкою.</p><p>Конфігурації є <em>абстрактним</em> способом, за допомогою якого контролер повідомляє розподілювачу, наскільки він має налаштувати аеродинамічні властивості крил відносно конфігурації «повні закрилки» або «повний спойлер» (між <code>[ 0,1]</code>, де &quot;1&quot; означає повний діапазон). Потім алокатор використовує будь-які доступні поверхні керування для досягнення вимаганої конфігурації: зазвичай флапи, елерони та кормові поверхні.</p><p>Налаштування масштабу флапів <code>flap scale</code> та спойлерів <code>spoiler scale</code> у користувальницькому інтерфейсі актуатора інформує алокатор, наскільки елерони, елеватори, флапи, спойлери та інші поверхні керування сприяють вимаганому значенню &quot;керування флапами&quot; і/або &quot;керування спойлерами&quot;. Зокрема, вони показують, на скільки кожна поверхня керування повинна бути відхиленою, коли контролер вимагає &quot;повних флапів&quot; або &quot;повного спойлера&quot;.</p><p>У наступному прикладі транспортний засіб має два елерона, один елеватор, один руль і дві флапи як поверхні керування:</p><p><img src="'+n+'" alt="Flaps and spoiler actuator configuration example"></p><ul><li>Флапи мають обидва масштаби флапів <code>Flap Scale</code>, встановлені на 1, що означає, що вони будуть повністю відхилені при керуванні флапами на рівні 1. Також вони мають швидкість переміщення 0,5/с, що означає, що їм знадобиться 2 секунди, щоб повністю відхилити їх (швидкість переміщення флапів, як правило, рекомендується, щоб зменшити перешкоди, які створює їх рух).</li><li>Елерони в основному призначені для забезпечення керованого крутного моменту. Вони також мають масштаби спойлерів <code>Spoiler Scale</code>, встановлені на 0,5, і додатково будуть відхилені вгору на 50%, якщо контролер вимагає повну конфігурацію спойлера. Таким чином, відхилення елеронів - це сума (асиметричного) відхилення для крутного моменту, плюс (симетричне) відхилення для вказаної точки спойлера.</li><li>Елеватор в основному призначений для забезпечення крутного моменту по тангажу. Він також має ненульні значення в полях масштабу флапів <code>Flap Scale</code> та спойлерів <code>Spoiler Scale</code>. Це відхилення елеватора, яке додається для компенсації моментів по тангажу, створених дією поверхні флапів та спойлерів. У цьому випадку елеватор буде відхилений на 0,3 вгору при повному розгортанні флапів для компенсації моменту, спрямованого вниз, який викликано дією флапів.</li></ul><h4 id="масштабування-крену-кута-та-повороту-приводу" tabindex="-1">Масштабування крену, кута та повороту приводу <a class="header-anchor" href="#масштабування-крену-кута-та-повороту-приводу" aria-label="Permalink to &quot;Масштабування крену, кута та повороту приводу&quot;">​</a></h4><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Для більшості конструкцій аеросуден вихідні значення для кожного типу керуючої поверхні не повинні змінюватися.</p></div><p>Значення <code>Масштабу кочення</code>, <code>Масштабу тангажу</code> та <code>Масштабу риштування</code> вказують на нормалізовану ефективність приводу навколо відповідної вісі.</p><p>Настройка значень є темою низького / рівня / продвинутої складності і, як правило, потрібна лише при налаштуванні зв&#39;язаних поверхонь керування (наприклад, елерон, який контролює як кочення, так і тангаж). У цьому випадку важливо знати наступне:</p><ul><li>Числа, які вводяться, безпосередньо вводяться в матрицю розподілу, яка потім інвертується для отримання сигналів керування від вибраних моментів (нормалізованих).</li><li>Збільшення масштабу зменшить <em>reduce</em> відхилення поверхонь керування (оскільки він інвертується).</li></ul><h4 id="конвенція-про-відхилення-поверхні-керування" tabindex="-1">Конвенція про відхилення поверхні керування <a class="header-anchor" href="#конвенція-про-відхилення-поверхні-керування" aria-label="Permalink to &quot;Конвенція про відхилення поверхні керування&quot;">​</a></h4><p>Діаграма нижче показує конвенцію для відхилень:</p><p><img src="'+u+'" alt="Control Surface Deflections"></p><p>Підсумовуючи:</p><ul><li><strong>Горизонтальні керуючі поверхні:</strong> Вгору рух означає позитивне відхилення. Містить елерони, тощо</li><li><strong>Вертикальні керуючі поверхні:</strong> Праворуч рух є позитивним відхиленням. Містить кермо тощо.</li><li><strong>Змішані керуючі поверхні:</strong> Вгору/праворуч рух є позитивним (як вище). Включає в себе V-Tail тощо.</li></ul><h3 id="геометрія-сервоприводу-нахилу-двигуна" tabindex="-1">Геометрія сервоприводу нахилу двигуна <a class="header-anchor" href="#геометрія-сервоприводу-нахилу-двигуна" aria-label="Permalink to &quot;Геометрія сервоприводу нахилу двигуна&quot;">​</a></h3><p><a href="./../frames_vtol/tiltrotor.html">Літальні апарати VTOL</a> з кільцевими роторами можуть нахиляти свої двигуни для переходу між вертикальним польотом і польотом вперед. Цей розділ визначає властивості сервоприводів з відхиленням. Ці значення відображаються на конкретні двигуни в геометрії двигуна для тільтротора.</p><p>Наведений нижче приклад показує налаштування серводвигуна для <a href="./../config/actuators.html#motor-geometry-vtol-tiltrotor">геометрії мотора тілтротора, показаної вище</a>.</p><p><img src="'+o+'" alt="Tilt Servo Geometry Setup Example"></p><p>Допустимі значення, які можна встановити:</p><ul><li><code>Сервоприводи нахилу</code>: Кількість сервоприводів (похилих моторів).</li><li><code>Кут при мінімальному нахилі</code>: <a href="#tilt-servo-coordinate-system">Максимальний кут нахилу</a> в градусах, відносно вісі z.</li><li><code>Кут у максимальному куті</code>: <a href="#tilt-servo-coordinate-system">Мінімальний кут нахилу</a> у градусах, відносно осі z-осі.</li><li><code>Напрямок нахилу</code>: `` (додатний х напрямок) або <code>до інструкцій справа від</code> (позитивний напрямок y).</li><li><code>Використовуйте для контролю</code>: <a href="#tilt-servos-for-yaw-pitch-control">Сервопривід нахилу використовується для крен/тангажу</a><ul><li><code>Жоден</code>: Контроль крутного моменту не використовується.</li><li><code>Крен</code>: Сервоприводи, що використовуються для керування креном.</li><li><code>Крен</code>: Сервоприводи, які використовуються для керування креном.</li><li><code>Як рульове і крен</code>: Сервоприводи нахилу використовуються для контролю як рульового, так і крену.</li></ul></li></ul><h4 id="система-координат-сервопривода-нахилу" tabindex="-1">Система координат сервопривода нахилу <a class="header-anchor" href="#система-координат-сервопривода-нахилу" aria-label="Permalink to &quot;Система координат сервопривода нахилу&quot;">​</a></h4><p>Система координат для кутів нахилу вітрокрила показана нижче. Орієнтація для кутів нахилу - пряме вгору (0 градусів). Кут нахилу вперед або праворуч від автомобіля є позитивним, а в напрямку назад або ліворуч - негативним.</p><p><img src="'+m+'" alt="Tilt Axis"></p><p><code>Кут при мінімальному нахилі</code> та <code>Кут при максимальному нахилі</code> вказують на діапазон руху серводвигуна нахилу. Мінімальний нахил - це менше <em>числове значення</em> (не абсолютне) двох кутів.</p><p>Якщо максимальні/мінімальні вектори нахилу <strong>P<sub>0</sub></strong> та <strong>P<sub>1</sub></strong>, як показано вище, обидва кути нахилу є позитивними, але <strong>θ<sub>0</sub></strong> менший:</p><ul><li><code>Кут при мінімальному нахилі</code> = <strong>θ<sub>0</sub></strong></li><li><code>Кут при максимальному нахилі</code> = <strong>θ<sub>1</sub></strong></li></ul><div class="info custom-block"><p class="custom-block-title">Якщо діаграма була відображена дзеркально, так що <strong>P<sub>0</sub></strong> та <strong>P<sub>1</sub></strong> нахилялися в -x, -y квадрант, тоді обидва кути нахилу були б від&#39;ємними. Тому що <strong>θ<sub>1</sub></strong> буде більш від&#39;ємним (меншим) ніж <strong>θ<sub>0</sub></strong>, він буде <code>Кут при мінімальному нахилі</code>.</p><p>Аналогічно, сервопривід, який рухається:</p><ul><li>між вертикальним і впереднім положеннями буде мати <code>min=0</code> і <code>max=90</code>.</li><li>симетрично на 45 градусів навколо вертикального положення матиме <code>min=-45</code> та <code>max=45</code></li><li>між вертикальним і назад положеннями буде мати <code>min=-90</code> і <code>max=0</code>.</li></ul></div><p><code>Напрямок нахилу</code> вказує, чи нахиляється сервопривід у площині до <code>Передньої</code> або <code>Правої</code> частини транспортного засобу. На діаграмі це буде представлено <strong>α</strong>, яке може приймати лише значення 0 (вперед) або 90 (праворуч).</p><h4 id="сервоприводи-для-крену-тангажу" tabindex="-1">Сервоприводи для крену/тангажу <a class="header-anchor" href="#сервоприводи-для-крену-тангажу" aria-label="Permalink to &quot;Сервоприводи для крену/тангажу&quot;">​</a></h4><p>Сервоприводи нахилу можуть надавати крутний момент на одній або декількох осях, які можуть бути використані для розвороту або крена транспортного засобу:</p><ul><li>Yaw зазвичай налаштовується таким чином, хоча на транспортних засобах з чотирма або більше двигунами часто використовуються двигуни.</li><li>Крен керується в основному за допомогою різниці тяги двигунів. Керування за допомогою сервоприводів нахилу корисне на повітряних конструкціях, які не можуть використовувати диференційний тягу, наприклад, <a href="https://www.youtube.com/watch?v=hfss7nCN40A" target="_blank" rel="noreferrer">Бікоптер</a>.</li></ul><p>Чи ця функція використовується, налаштовується в параметрі <code>Використовувати для управління</code>.</p><h2 id="виводи-приводу" tabindex="-1">Виводи приводу <a class="header-anchor" href="#виводи-приводу" aria-label="Permalink to &quot;Виводи приводу&quot;">​</a></h2><p>Розділ <em>Виводи приводу</em> використовується для призначення моторів, сервоприводів поверхні керування та інших приводів, які використовуються конкретною рамою, до фізичних виводів на контролері польоту та для встановлення параметрів для цих виводів.</p><p><img src="'+h+'" alt="Actuator Outputs - Multicopter diagram"></p><p>Для кожної підтримуваної вихідної шини, підключеної до керуючого контролера польоту, відображаються окремі вкладки: PWM MAIN (вихід дошки вводу/виводу), PWM AUX (вихід дошки FMU), UAVCAN.</p><p>Двигуни та приводи (які називаються &quot;<a href="#output-functions">функціями</a>&quot;) можуть бути призначені для будь-якого фізичного виводу на будь-якій з наявних шин.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>PWM AUX виходи вважаються бажаними для керування двигунами над PWM MAIN виходами (вони мають меншу затримку).</p></div><p>Вихідні PWM групуються на основі груп апаратних таймерів. Це означає, що всі виходи в одній групі повинні працювати за одним протоколом з однаковою швидкістю (наприклад, сигнал ШІМ на частоті 400 Гц для всіх виходів в одній групі). Тому неможливо відобразити Серводвигун та Двигун в одній вихідній групі, оскільки вони зазвичай працюють на різних швидкостях.</p><p>Вкладка PWM AUX має виходи CAP, які, як правило, використовуються як <a href="./../camera/fc_connected_camera.html#trigger-configuration">вхід знімка/спуску камери</a>. Однак ви можете відобразити виводи CAP на інші функції виводу, і інші AUX виводи можуть бути використані як вхід для захоплення / спрацювання камери.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Налаштування захоплення камери / Тригер вхід потребує перезавантаження для набуття чинності</p></div><p>Ви повинні призначити функції виходам, які відповідають вашій фізичній з&#39;єднаності моторів та сервоприводів, і використовувати розділ <a href="#actuator-testing">Тестування приводів</a>, описаний нижче, для визначення відповідних значень параметрів виходу. Ці кроки охоплені в <a href="#output-assignment-and-configuration">Вихідному призначенні та конфігурації</a>.</p><h3 id="функціі-виводу" tabindex="-1">Функції виводу <a class="header-anchor" href="#функціі-виводу" aria-label="Permalink to &quot;Функції виводу&quot;">​</a></h3><p>Функції виводу використовуються для відображення &quot;логічних функцій&quot; конструкції повітряного каркасу, таких як <code>Мотор 1</code> або <code>Шасі</code>, на фізичні виводи, наприклад, вивідний пін FMU 2. Це дозволяє легко використовувати певний вихідний контакт для майже будь-якої мети.</p><p>Деякі функції є актуальними лише для певних кадрів або типів виведення, і не будуть запропоновані на інших.</p><p>Функції включають:</p><ul><li><code>Відключено</code>: Виводу не призначено жодної функції.</li><li><code>Constant_Min</code>: Вивід постійного мінімального значення (-1).</li><li><code>Constant_Max</code>: Вивід встановлюється на постійне максимальне значення (+1).</li><li><code>Motor 1</code> до <code>Motor 12</code>: На виході вказано двигун. Відображаються тільки двигуни, дозволені для авіаконструкції.</li><li><code>Servo 1</code> - <code>Servo 8</code>: вихід сервоприводу. Ці об&#39;єкти отримують конкретне значення на основі конструкції, таке як &quot;серводвигун нахилу&quot;, &quot;лівий елерон&quot;.</li><li><code>Периферійний за допомогою набору приводів 1</code> до <code>Периферійний за допомогою набору приводів 6</code>: <a href="./../payloads/generic_actuator_control.html#generic-actuator-control-with-mavlink">Загальний контроль виконавчого пристрою з MAVLink</a>.</li><li><code>Шасі</code>: Вивід - це шасі.</li><li><code>Парашут</code>: Вихід - це парашут. Мінімальне значення надсилається у звичайному режимі, а максимальне - при спрацьовуванні захисту від збоїв.</li><li><code>RC Roll</code>: Вихід - це вихідний рол з RC (<a href="./../advanced_config/parameter_reference.html#RC_MAP_ROLL">RC_MAP_ROLL</a> відображає канал RC на цей вихід). Потік RC вимикача зіставляється з виходом за допомогою .</li><li><code>RC Pitch</code>: Вихід - це паспортний крок від RC (<a href="./../advanced_config/parameter_reference.html#RC_MAP_PITCH">RC_MAP_PITCH</a> відображає канал RC на цей вихід).</li><li><code>RC Throttle</code>: Output is passthrough throttle from RC (<a href="./../advanced_config/parameter_reference.html#RC_MAP_THROTTLE">RC_MAP_THROTTLE</a> maps an RC channel to this output).</li><li><code>RC Yaw</code>: Вихід - це розворот від RC (<a href="./../advanced_config/parameter_reference.html#RC_MAP_YAW">RC_MAP_YAW</a> відображає канал RC на цей вихід).</li><li><code>RC Flaps</code>: Вихід - це закрилки з RC (<a href="./../advanced_config/parameter_reference.html#RC_MAP_FLAPS">RC_MAP_FLAPS</a> відображає канал RC на цей вихід).</li><li><code>RC AUXn</code> до <code>RC AUX1</code>: Виводи, які використовуються для <a href="./../payloads/generic_actuator_control.html#generic-actuator-control-with-rc">довільних навантажень, спрацьованих через RC-передавання</a>.</li><li><code>Gimbal Roll</code>: Вихід контролює відвід Gimbal.</li><li><code>Gimbal Pitch</code>: Вихід контролює крок Gimbal.</li><li><code>Gimbal Yaw</code>: Output controls Gimbal yaw.</li></ul><p>Наведені функції можуть бути застосовані тільки до виходів FMU:</p><ul><li><code>Camera_Trigger</code>: Вихід для запуску камери. Увімкнено, коли <a href="./../advanced_config/parameter_reference.html#TRIG_MODE"><code>TRIG_MODE==0</code></a>. Налаштовано через параметри <code>TRIG_*</code>.</li><li><code>Camera_Capture</code>: Вхід для отримання сповіщення про захоплення зображення. Увімкнено, коли <a href="./../advanced_config/parameter_reference.html#CAM_CAP_FBACK">CAM_CAP_FBACK==0</a>. Налаштовано через параметри <code>CAM_CAP_*</code>.</li><li><code>PPS_Input</code>: Захоплення вводу імпульсів на секунду. Використовується для синхронізації GPS. Увімкнено, коли <a href="./../advanced_config/parameter_reference.html#PPS_CAP_ENABLE"><code>PPS_CAP_ENABLE==0</code></a></li></ul><div class="info custom-block"><p class="custom-block-title">Цей список є правильним у PX4 v1.13. Функції визначені у джерелі за посиланням на <a href="https://github.com/PX4/PX4-Autopilot/blob/release/1.15/src/lib/mixer_module/output_functions.yaml" target="_blank" rel="noreferrer">/src/lib/mixer_module/output_functions.yaml</a>.</p></div><h2 id="тестування-актуаторів" tabindex="-1">Тестування актуаторів <a class="header-anchor" href="#тестування-актуаторів" aria-label="Permalink to &quot;Тестування актуаторів&quot;">​</a></h2><p>Секція <em>Тестування приводів</em> у нижньому правому куті надає повзунки, які можна використовувати для тестування (та визначення) налаштувань приводу та двигуна. Для кожного виводу, визначеного в розділі <a href="#actuator-outputs">Виводи приводу</a>, надається повзунок. Наведений нижче приклад слайдера показує розділ для типової повітряної конструкції VTOL Tiltrotor.</p><p><img src="'+f+'" alt="Actuator Testing Slider"></p><p>Розділ має перемикач <strong>Увімкнути слайдери</strong>, який необхідно перемкнути перед використанням слайдерів. Повзунки можуть живити двигуни/сервоприводи в повному діапазоні їх руху, а також «прив’язувати» до вимкненого та мінімального положень.</p><div class="info custom-block"><p class="custom-block-title">Після перемикання перемикача <strong>Увімкнути слайдери</strong>, приводи/мотори не будуть робити нічого, доки відповідний слайдер не буде <em>переміщений</em>. Це функція безпеки, що запобігає раптовим рухам двигуна після увімкнення перемикача.</p></div><p>Повзунки можуть бути використані для перевірки наступного:</p><ol><li>Приводи (мотори, керуючі поверхні тощо) призначені для очікуваного виводу.</li><li>Двигуни не обертаються, коли досягнуто значення виводу ШШІ <code>роззброєно</code></li><li>Двигуни ледь, але надійно запускаються на <code>мінімальному</code> значенні вихідного сигналу ШІМ.</li><li>Двигуни дають <strong>позитивну тягу</strong> в очікуваному напрямку</li><li>Поверхні керування знаходяться в правильному положенні очікування для вихідного значення <code>знято з охорони</code></li><li>Поверхні керування знаходяться в правильному положенні очікування для вихідного значення <a href="#control-surface-deflection-convention">знято з охорони</a></li><li>Моторні сервоприводи належним чином знаходяться в позиції покою для вихідного значення <code>disarmed</code></li><li>Сервоприводи нахилу двигуна рухаються в напрямку, визначеному в <a href="#tilt-servo-coordinate-system">Конвенції про сервоприводи нахилу</a></li></ol><h2 id="призначення-та-конфігурація-виходу" tabindex="-1">Призначення та конфігурація виходу <a class="header-anchor" href="#призначення-та-конфігурація-виходу" aria-label="Permalink to &quot;Призначення та конфігурація виходу&quot;">​</a></h2><p>Виведення призначені для функцій та налаштовуються в розділі <a href="#actuator-outputs">Виведення приводу</a>, тоді як регулятори <a href="#actuator-testing">Тестування приводу</a> зазвичай використовуються для визначення відповідних значень конфігурації, які слід ввести:</p><ul><li>Транспортні засоби MC, які мають підключені двигуни до виходів ШШІ, можуть використовувати кнопку <a href="#multicopter-pwm-motor-assignment">Ідентифікувати &amp; Призначити Двигуни</a> для виконання призначення двигунів &quot;пів-автоматично&quot;.</li><li>Виведення призначення обох двигунів та приводів може бути виконано / перевірено за допомогою слайдерів (див. <a href="#output-assignment-manual">Призначення виведення (вручну)</a>).</li><li>Роззброєний, мінімальні та максимальні налаштування для всіх виходів також можуть бути визначені за допомогою слайдерів. Це показано як частина <a href="#motor-configuration">Конфігурація двигуна</a>, <a href="#control-surface-setup">Налаштування поверхні керування</a>, <a href="#tilt-servo-setup">Налаштування сервопривода нахилу</a></li></ul><h3 id="pwm-багатокоптера-призначення-двигуна" tabindex="-1">PWM багатокоптера: Призначення двигуна <a class="header-anchor" href="#pwm-багатокоптера-призначення-двигуна" aria-label="Permalink to &quot;PWM багатокоптера: Призначення двигуна&quot;">​</a></h3><p>Ви можете використовувати кнопку <strong>Ідентифікувати та Призначити Двигуни</strong> для призначення двигунів на виходи ШІМ з використанням напівавтоматизованого процесу.</p><div class="info custom-block"><p class="custom-block-title">Це найлегший спосіб призначення двигунів, але наразі підтримується лише для двигунів на <strong>багокоптерних транспортних засобах</strong>, які підключені до виходів ШШП (виходи UAVCAN та інші типи рамок не підтримують цю функцію). On other frames you can follow the instructions in <a href="#output-assignment-manual">Output Assignment (Manual)</a>.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Видаліть гвинти з моторів перед призначенням виходів або будь-якими випробуваннями.</p></div><p><img src="'+_+'" alt="Identify motor button"></p><p>Коли ви натискаєте кнопку, QGC відправляє команду до двигуна, що призводить до його обертання. Щоб призначити цей двигун виводу, просто виберіть відповідний двигун, який відображається на екрані. QGC потім автоматично обере наступний двигун для вас для призначення, і так далі.</p><p>Інструкції:</p><ol><li><p>Налаштуйте геометрію двигуна, щоб вона відповідала двигунам на вашій рамі.</p></li><li><p>Виберіть вкладку PWM, на якій ви хочете призначити двигуни.</p></li><li><p>Натисніть кнопку <strong>Визначити &amp; призначити двигуни</strong>.</p></li><li><p>Один двигун почне обертатися (натисніть <strong>Обертати двигун знову</strong>, якщо він зупиниться занадто швидко, щоб зауважити.)</p><p>Виберіть відповідний двигун в розділі геометрії.</p><p><img src="'+g+'" alt=""></p></li><li><p>Після призначення всіх двигунів інструмент встановить правильне відображення двигунів для виходів, а потім вийде.</p></li></ol><h3 id="вивід-призначення-ручнии" tabindex="-1">Вивід призначення (ручний) <a class="header-anchor" href="#вивід-призначення-ручнии" aria-label="Permalink to &quot;Вивід призначення (ручний)&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Видаліть гвинти з моторів перед призначенням виходів або будь-якими випробуваннями.</p></div><p>Виходи приводів для обох двигунів та сервоприводів можна <em>вручну</em> призначити за допомогою повзунків у розділі <a href="#actuator-testing">Тестування приводів</a>.</p><p>Для призначення приводу:</p><ol><li>Спочатку призначте функції виходам, які, на вашу думку, <em>ймовірно</em>, є правильними в розділі <em>Вихідні пристрої</em>.</li><li>Перемкніть перемикач <strong>Увімкнути слайдери</strong> у розділі <em>Тестування приводів</em>.</li><li>Перемістіть повзунок для приводу, який ви хочете перевірити: <ul><li>Двигуни повинні бути переведені в положення мінімального тяги.</li><li>Сервоприводи повинні бути переміщені близько до середнього положення.</li></ul></li><li>Перевірте, який привод рухається на транспортному засобі. Це повинно відповідати позиціям приводів для вашої геометрії (посилання на <a href="./../airframes/airframe_reference.html">довідник по конструкції повітряних суден</a> показує позиції двигунів для декількох стандартних конструкцій повітряних суден). <ul><li>Якщо правильний привод рухається, перейдіть до наступного кроку.</li><li>Якщо неправильний привод рухається, поміняйте призначення виводу.</li><li>Якщо нічого не рухається, то збільште регулятор наполовину діапазону, потім вище, якщо потрібно. Якщо після цього нічого не рухається, вихід може бути не підключений, можливо, двигун не живиться або вихід може бути неправильно налаштований. Вам потрібно буде вирішити проблему (можливо, спробуйте інші виходи виконавчих пристроїв, щоб побачити, чи &quot;щось&quot; рухається).</li></ul></li><li>Поверніть слайдер у положення &quot;роззброєно&quot; (донизу для двигунів, по центру для сервоприводів).</li><li>Повторити для всіх приводів</li></ol><h3 id="конфігурація-двигуна" tabindex="-1">Конфігурація двигуна <a class="header-anchor" href="#конфігурація-двигуна" aria-label="Permalink to &quot;Конфігурація двигуна&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">Якщо використовуєте PWM або OneShot ESC, спочатку слід виконати <a href="./../advanced_config/esc_calibration.html">калібрування ESC</a> (ця тема також охоплює конфігурацію PWM-специфічних двигунів).</p><p><a href="./../peripherals/dshot.html">DShot</a> ESCs не потребують налаштування меж команд, а лише напрямок обертання.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Видаліть пропелери!</p></div><p>Конфігурація двигуна встановлює вихідні значення так, що двигуни:</p><ul><li>не обертайтеся, коли роззброєні (при значенні виводу ШШІ <code>роззброєно</code>).</li><li>ледь, але надійно запускати на <code>мінімальному</code> значенні вихідного сигналу ШІМ.</li><li>мають <em>найнижче</em> <code>максимальне</code> вихідне значення ШІМ, яке обертає двигун із <em>найвищою</em> швидкістю.</li><li>дати <strong>позитивний тягу</strong> в очікуваному напрямку.</li></ul><p>Для кожного двигуна:</p><ol><li><p>Тягніть слайдер мотора вниз, щоб він защелкнувся унизу. У цій позиції двигун встановлений на значення виводів <code>disarmed</code>.</p><ul><li>Перевірте, що двигун не обертається в цьому положенні.</li><li>Якщо двигун крутиться, зменште відповідне значення ШІМ <code>disarmed</code> в розділі <a href="#actuator-outputs">Виводи виконавчих пристроїв</a> до рівня, при якому він все ще крутиться.</li></ul></li><li><p>Повільно переміщуйте повзунок вгору, доки він не защелкнеться в положенні <em>мінімум</em>. У цій позиції двигун встановлено на виходи значення <code>мінімальне</code>.</p><ul><li><p>Перевірте, чи двигун обертається дуже повільно в цьому положенні.</p></li><li><p>Якщо двигун не обертається або обертається занадто швидко, вам потрібно налаштувати відповідне мінімальне значення ШІМ <code>minimum</code> в <a href="#actuator-outputs">Виводах приводів</a> так, щоб двигуни ледве оберталися.</p><p><img src="'+b+'" alt="PWM Minimum Output"> :::info Для виходу DShot це не потрібно.</p></li></ul></li></ol><p>:::</p><ol start="3"><li><p>Збільште значення слайдера до рівня, на якому ви можете перевірити, що двигун обертається в правильному напрямку і що він надасть позитивний тяговий потік в очікуваному напрямку.</p><ul><li>Очікувана напрямок тяги може відрізнятися в залежності від типу транспортного засобу. Наприклад, у багатороторних літаках тяга завжди повинна вказувати вгору, тоді як у повітряному судні з фіксованим крилом тяга буде тягти судно вперед.</li><li>Для VTOL тяга повинна вказувати вгору, коли Сервопривід нахилу перебуває під кутом 0 градусів, як визначено у <a href="#tilt-servo-coordinate-system">Конвенції про Сервопривід нахилу</a>. Тестування <a href="#tilt-servo-setup">Крен-сервопривіду</a> також описано нижче.</li><li>Якщо тяга спрямована в неправильному напрямку, можливо, вам потрібно <a href="#reversing-motors">перевернути мотори</a>.</li></ul></li><li><p>Збільште значення слайдера до максимального значення, щоб двигун швидко обертався. Зменшіть значення <code>максимального</code> виходу ШІМ вище за типове значення. Прослухайте тон моторів, коли ви збільшуєте значення малими (25us) інкрементами. &quot;Оптимальне&quot; максимальне значення - це значення, при якому ви востаннє почули зміну тона.</p></li></ol><h3 id="налаштування-поверхонь-керування" tabindex="-1">Налаштування поверхонь керування <a class="header-anchor" href="#налаштування-поверхонь-керування" aria-label="Permalink to &quot;Налаштування поверхонь керування&quot;">​</a></h3><p>Спочатку встановіть <em>частоту кадрів</em> для сервоприводів, які використовуються в кожній групі виводів. Зазвичай це встановлюється на максимальне значення, підтримуване вашим серводвигуном. Нижче ми покажемо, як ви можете встановити його на PWM50 (найбільш поширене значення).</p><p><img src="'+q+'" alt="Control Surface Disarmed 1500 Setting"></p><div class="info custom-block"><p class="custom-block-title">Вам майже точно знадобиться змінити частоту пульса зі значенням за замовчуванням 400 Гц, оскільки підтримка рідко зустрічається (якщо не підтримується, сервопривід зазвичай видаватиме &quot;дивний&quot; шум). Якщо ви використовуєте сервоприводи PWM, PWM50 є набагато поширенішим. Якщо <em>дійсно</em> потрібен високовольтний сервопривід, DShot пропонує кращу вартість.</p></div><p>Для кожної з поверхонь керування:</p><ol><li><p>Встановіть значення <code>Disarmed</code>, щоб поверхні залишалися в нейтральному положенні під час роззброєння. Зазвичай це становить близько <code>1500</code> для PWM сервоприводів.</p></li><li><p>Перемістіть повзунок для поверхні вверх (позитивна команда) та переконайтеся, що він рухається в напрямку, визначеному в <a href="#control-surface-deflection-convention">Конвенції керуючої поверхнею</a>.</p><ul><li>Якщо поверхня керування рухається в протилежному напрямку, клацніть на прапорець <code>Rev Range</code>, щоб змінити діапазон.</li></ul></li><li><p>Перемістіть повзунок ще раз у середину та перевірте, чи вирівняні керуючі поверхні в нейтральному положенні крила</p><ul><li><p>Якщо воно не вирівняно, ви можете встановити значення <strong>Trim</strong> для поверхні керування. :::info Це робиться в налаштуванні <code>Trim</code> панелі Geometry, зазвичай методом &quot;спроба і помилка&quot;. <img src="'+v+'" alt="Control Surface Trimming"> :::</p></li><li><p>Після встановлення обрізки для поверхні керування перемістіть його ковзачок від центру, відпустіть, а потім поверніть у відключений (середній) положення. Підтвердіть, що поверхня знаходиться в нейтральному положенні.</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span></span></span></code></pre></div></li></ul></li></ol><div class="info custom-block"><p class="custom-block-title">Ще один спосіб протестувати без використання слайдерів - це встановити параметр <a href="./../advanced_config/parameter_reference.html#COM_PREARM_MODE"><code>COM_PREARM_MODE</code></a> на <code>Always</code>:</p><ul><li>Це дозволить керувати сервоприводами навіть коли транспортний засіб відброньований, і постійно застосовуватиме налаштування обрізки до керуючих поверхонь</li><li>Ви можете спробувати встановити різні значення для обрізки та перевірити вирівнювання, а потім вибрати значення, з яким ви будете задоволені.</li></ul></div><h3 id="налаштування-серводвигуна-нахилу" tabindex="-1">Налаштування серводвигуна нахилу <a class="header-anchor" href="#налаштування-серводвигуна-нахилу" aria-label="Permalink to &quot;Налаштування серводвигуна нахилу&quot;">​</a></h3><p>Спочатку встановіть <em>частоту кадрів</em> для сервоприводів, які використовуються в кожній групі виводів. Зазвичай це встановлюється на максимальне значення, підтримуване вашим серводвигуном. Нижче воно встановлено на PWM50 (найбільш поширене значення). Зверніть увагу, що ця частина налаштування є такою ж, як і для поверхонь керування вище.</p><p><img src="'+P+'" alt="Tilt Servo Setup"></p><p>Для кожного з сервоприводів нахилу:</p><ol><li><p>Встановіть значення <code>Disarmed</code> (наприклад, <code>1000</code> або <code>2000</code> для PWM Servos), щоб сервопривід був розташований в очікуваному напрямку при <em>роззброєнні</em>.</p></li><li><p>Розмістіть регулятор для сервоприводу в найнижчому положенні, і переконайтеся, що збільшення позитивного значення вказуватиме на <code>Кут при мінімальному нахилі</code> (визначено в розділі Геометрія).</p><p><img src="'+o+'" alt="Tilt Servo Geometry Setup"></p></li><li><p>Розмістіть регулятор для сервоприводу в найвищому положенні, і переконайтеся, що позитивне зусилля двигуна вказуватиме на <code>Кут при максимальному нахилі</code> (як визначено в розділі Геометрія).</p></li></ol><h3 id="інші-примітки" tabindex="-1">Інші примітки <a class="header-anchor" href="#інші-примітки" aria-label="Permalink to &quot;Інші примітки&quot;">​</a></h3><ul><li>Якщо використовується кнопка безпеки, її потрібно натиснути перед тим, як буде дозволено проводити випробування приводу.</li><li>Вимикач вимкнення все ще може бути використаний для негайного зупинення двигунів.</li><li>Сервоприводи фактично не рухаються, поки не буде змінено відповідний слайдер.</li><li>Параметр <a href="./../advanced_config/parameter_reference.html#COM_MOT_TEST_EN">COM_MOT_TEST_EN</a> може бути використаний для повного вимкнення тестування приводів.</li><li>На оболонці, <a href="./../modules/modules_command.html#actuator-test">actuator_test</a> також може бути використаний для тестування приводу.</li><li>VTOLs автоматично вимикатимуть двигуни, що спрямовані вгору, під час <strong>польоту з фіксованим крилом</strong>: <ul><li>Стандартний VTOL: Двигуни, визначені як багатокоптерні двигуни, будуть вимкнені</li><li>Tiltrotors : Двигуни, які не мають пов&#39;язаного сервоприводу нахилу, вимкнуться</li><li>Tailsitters не вимикають жодних двигунів у польоті з фіксованим крилом</li></ul></li></ul><h3 id="реверсивні-мотори" tabindex="-1">Реверсивні мотори <a class="header-anchor" href="#реверсивні-мотори" aria-label="Permalink to &quot;Реверсивні мотори&quot;">​</a></h3><p>Двигуни повинні обертатися в напрямку, визначеному в налаштованій геометрії (прапорці &quot;<strong>Напрям CCW</strong>&quot;). Якщо будь-які двигуни не обертаються в правильному напрямку, їх необхідно перевернути.</p><p>Є декілька варіантів:</p><ul><li><p>Якщо ESC налаштовані як <a href="./../peripherals/dshot.html">DShot</a>, ви можете назавжди змінити напрямок через інтерфейс користувача. Кнопки <strong>Встановити напрямок обертання</strong> відображаються нижче слайдерів приводів (якщо використовуються мотори DShot). Ці викидають діалогове вікно, в якому ви вибираєте мотор, для якого хочете застосувати напрямок.</p><p><img src="'+C+'" alt="Set spin direction buttons"></p><p>Зверніть увагу, що поточного напрямку не можна запитати, тому вам може знадобитися спробувати обидві опції.</p></li><li><p>Поміняйте місцями 2 з 3 кабелів двигуна (не має значення які саме).</p><div class="info custom-block"><p class="custom-block-title">INFO</p></div></li></ul><p>Якщо мотори не підключені за допомогою булет-конекторів, потрібно провести перепайку (це одна з причин, серед інших, чому варто віддавати перевагу DShot ESCs). :::</p>',158);function L(E,G,N,W,D,w){const a=T("Badge");return y(),A("div",null,[O,M(a,{type:"tip",text:"PX4 v1.14"}),V])}const U=k(x,[["render",L]]);export{F as __pageData,U as default};
