import{_ as a}from"./chunks/menu_setup.B5J5it7n.js";import{_ as e,c as r,o as l,ab as o}from"./chunks/framework.CUflZczI.js";const _=JSON.parse('{"title":"Стандартні налаштування","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/index.md","filePath":"uk/config/index.md"}'),i={name:"uk/config/index.md"},t=o('<h1 id="стандартні-налаштування" tabindex="-1">Стандартні налаштування <a class="header-anchor" href="#стандартні-налаштування" aria-label="Permalink to &quot;Стандартні налаштування&quot;">​</a></h1><p>У цьому розділі описано загальну конфігурацію програмного забезпечення та калібрування, необхідні для більшості пристроїв PX4.</p><p>Спочатку потрібно <a href="#firmware-vehicle-selection"> завантажити прошивку і вибрати каркас/тип вашого апарату</a>. Більшість інших кроків можна виконувати у довільному порядку, за винятком <a href="#tuning">налаштування</a>, яке має бути виконано в кінці.</p><h2 id="попередні-вимоги" tabindex="-1">Попередні вимоги <a class="header-anchor" href="#попередні-вимоги" aria-label="Permalink to &quot;Попередні вимоги&quot;">​</a></h2><p>Перед початком роботи вам слід <a href="http://qgroundcontrol.com/downloads/" target="_blank" rel="noreferrer">завантажити QGroundControl</a> і встановити його на ваш <strong>робочий</strong> комп&#39;ютер. Потім відкрийте меню програми QGC (піктограма &quot;Q&quot; у верхньому лівому куті) і виберіть <strong>Vehicle Setup</strong> у спливаючому вікні <em>Select Tool</em>:</p><p><img src="'+a+'" alt="Спливаюче вікно головного меню QGC: виділення пункту Налаштування апарату"></p><h2 id="етапи-конфігураціі" tabindex="-1">Етапи конфігурації <a class="header-anchor" href="#етапи-конфігураціі" aria-label="Permalink to &quot;Етапи конфігурації&quot;">​</a></h2><h3 id="вибір-прошивки-апарату" tabindex="-1">Вибір прошивки/апарату <a class="header-anchor" href="#вибір-прошивки-апарату" aria-label="Permalink to &quot;Вибір прошивки/апарату&quot;">​</a></h3><ul><li><a href="./../config/firmware.html">Завантаження прошивки</a></li><li><a href="./../config/airframe.html">Вибір апарату (каркасу)</a></li></ul><h3 id="налаштування-двигуна-приводу" tabindex="-1">Налаштування двигуна/приводу <a class="header-anchor" href="#налаштування-двигуна-приводу" aria-label="Permalink to &quot;Налаштування двигуна/приводу&quot;">​</a></h3><ul><li><a href="./../advanced_config/esc_calibration.html">Калібрування ESC (плати контролю двигунів)</a></li><li><a href="./../config/actuators.html">Конфігурація та тестування приводу</a></li></ul><h3 id="калібрування-датчика" tabindex="-1">Калібрування датчика <a class="header-anchor" href="#калібрування-датчика" aria-label="Permalink to &quot;Калібрування датчика&quot;">​</a></h3><ul><li><a href="./../config/flight_controller_orientation.html">Орієнтація сенсорів</a></li><li><a href="./../config/compass.html">Компас</a></li><li><a href="./../config/gyroscope.html">Гіроскоп</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/level_horizon_calibration.html">Рівень горизонту</a></li><li>Швидкість польоту (тільки для літаків / VTOL)</li></ul><div class="info custom-block"><p class="custom-block-title">Налаштування для цих та інших сенсорів знаходиться в <a href="./../sensor/">Апаратне забезпечення та налаштування сенсорів</a>.</p></div><h3 id="налаштування-ручного-керування" tabindex="-1">Налаштування ручного керування <a class="header-anchor" href="#налаштування-ручного-керування" aria-label="Permalink to &quot;Налаштування ручного керування&quot;">​</a></h3><p>Радіоуправління:</p><ul><li><a href="./../config/radio.html">Налаштування радіоконтролера (РК)</a></li><li><a href="./../config/flight_mode.html">Налаштування режиму польоту</a></li></ul><p>Джойстик/Ґеймпад</p><ul><li><a href="./../config/joystick.html">Налаштування джойстика</a></li></ul><h3 id="налаштування-безпеки" tabindex="-1">Налаштування безпеки <a class="header-anchor" href="#налаштування-безпеки" aria-label="Permalink to &quot;Налаштування безпеки&quot;">​</a></h3><ul><li><a href="./../config/battery.html">Battery Estimation Tuning</a> (requires <a href="./../power_module/">Power Module</a>)</li><li><a href="./../config/safety.html">Конфігурація безпеки (запобіжники)</a></li></ul><h3 id="тюнінг" tabindex="-1">Тюнінг <a class="header-anchor" href="#тюнінг" aria-label="Permalink to &quot;Тюнінг&quot;">​</a></h3><ul><li><a href="./../config/autotune.html">Autotune</a> (Рекомендовано для апаратів та платформ, які підтримують цю функцію)</li></ul><h2 id="відеоінструкція" tabindex="-1">Відеоінструкція <a class="header-anchor" href="#відеоінструкція" aria-label="Permalink to &quot;Відеоінструкція&quot;">​</a></h2><p><a href="https://youtu.be/91VGmdSlbo4" target="_blank" rel="noreferrer">youtube</a></p><p>@Якщо вам потрібна допомога з конфігурацією, ви можете звернутися за допомогою на форумі підтримки <a href="https://discuss.px4.io//c/qgroundcontrol/qgroundcontrol-usage" target="_blank" rel="noreferrer">QGroundControl</a>.</p><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Якщо вам потрібна допомога з конфігурацією, ви можете звернутися за допомогою на форумі підтримки <a href="https://discuss.px4.io//c/qgroundcontrol/qgroundcontrol-usage" target="_blank" rel="noreferrer">QGroundControl</a>.</p><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><p><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/setup_view.html" target="_blank" rel="noreferrer">QGroundControl &gt; Налаштування</a></p></li><li><p><a href="./../peripherals/README.html">Периферія контролера польоту</a> - налаштування конкретних датчиків, опціональних датчиків, актуаторів тощо.</p></li><li><p><a href="./../advanced_config/README.html">Розширена конфігурація</a> - заводське калібрування/OEM, налаштування додаткових функцій, менш поширені конфігурації.</p></li><li><p>Конфігурація/налаштування, що залежать від апарату:</p><ul><li><a href="./../config_mc/">Конфігурація/налаштування мультикоптера</a></li><li><a href="./../config_heli/">Конфігурація/налаштування гелікоптера</a></li><li><a href="./../config_fw/">Конфігурація/налаштування літака (з нерухомим крилом)</a></li><li><a href="./../config_vtol/">Конфігурація/налаштування VTOL</a></li></ul></li></ul>',30),n=[t];function h(c,s,f,u,d,m){return l(),r("div",null,n)}const b=e(i,[["render",h]]);export{_ as __pageData,b as default};
