import{_ as e}from"./chunks/micronav.BndX9JiN.js";import{_ as t,c as r,o,ab as a}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Налаштування джойстика","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/joystick.md","filePath":"uk/config/joystick.md"}'),i={name:"uk/config/joystick.md"},n=a('<h1 id="налаштування-джоистика" tabindex="-1">Налаштування джойстика <a class="header-anchor" href="#налаштування-джоистика" aria-label="Permalink to &quot;Налаштування джойстика&quot;">​</a></h1><p><a href="https://en.wikipedia.org/wiki/Joystick" target="_blank" rel="noreferrer">Комп’ютерний джойстик</a> або геймпад, підключений через <em>QGroundControl</em>, можна використовувати для ручного керування транспортним засобом (<em>замість</em> використання <a href="./../config/radio.html">RC Transmitter</a> &gt;).</p><p>Цей підхід може бути використаний керуючими пристроями з ручним управлінням, які мають інтегровану наземну станцію управління (наприклад, <em>UAVComponents</em> <a href="https://uxvtechnologies.com/ground-control-stations/micronav/" target="_blank" rel="noreferrer">MicroNav</a>, показана нижче). Джойстики також часто використовуються для того, щоб дозволити розробникам літати на транспортному засобі у симуляції.</p><p><img src="'+e+'" alt="Joystick MicroNav"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../config/radio.html">Налаштування радіо</a> не потрібно, якщо використовуєте лише джойстик (тому що джойстик не є пультом дистанційного керування)!</p></div><div class="info custom-block"><p class="custom-block-title"><em>QGroundControl</em> використовує багатоплатформенну бібліотеку <a href="http://www.libsdl.org/index.php" target="_blank" rel="noreferrer">SDL2</a> для перетворення рухів джойстика на повідомлення MAVLink <a href="https://mavlink.io/en/messages/common.html#MANUAL_CONTROL" target="_blank" rel="noreferrer">MANUAL_CONTROL</a>, які потім надсилаються на PX4 через канал телеметрії. В результаті система керування на основі джойстика потребує надійного телеметричного каналу високої пропускної здатності, щоб забезпечити реагування транспортного засобу на рухи джойстика.</p></div><h2 id="увімкнення-підтримки-джоистика-px4" tabindex="-1">Увімкнення підтримки джойстика PX4 <a class="header-anchor" href="#увімкнення-підтримки-джоистика-px4" aria-label="Permalink to &quot;Увімкнення підтримки джойстика PX4&quot;">​</a></h2><p>Інформація про те, як налаштувати джойстик, описана тут: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/joystick.html" target="_blank" rel="noreferrer">QGroundControl &gt; Налаштування джойстика</a>.</p><p>Підсумовуючи:</p><ul><li>Відкрити <em>QGroundControl</em></li><li>Встановіть параметр <a href="./../advanced_config/parameter_reference.html#COM_RC_IN_MODE">COM_RC_IN_MODE=1</a> - <code>Джойстик</code><ul><li>Дивіться <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/parameters.html" target="_blank" rel="noreferrer">Параметри</a> для отримання інформації про налаштування параметрів</li><li>Встановлення параметра на <code>2</code> або <code>3</code> також у деяких випадках активує джойстик.</li></ul></li><li>Підключіть джойстик</li><li>Налаштуйте підключений джойстик в: <strong>Vehicle Setup &gt; Джойстик</strong>.</li></ul>',10),l=[n];function c(s,m,_,d,p,h){return o(),r("div",null,l)}const k=t(i,[["render",c]]);export{g as __pageData,k as default};
