import{_ as o}from"./chunks/sensor_level_horizon.ZMhk5znq.js";import{_ as e,c as t,o as a,ab as l}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Калібрування рівня горизонту","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/level_horizon_calibration.md","filePath":"uk/config/level_horizon_calibration.md"}'),r={name:"uk/config/level_horizon_calibration.md"},i=l('<h1 id="калібрування-рівня-горизонту" tabindex="-1">Калібрування рівня горизонту <a class="header-anchor" href="#калібрування-рівня-горизонту" aria-label="Permalink to &quot;Калібрування рівня горизонту&quot;">​</a></h1><p>Ви можете використовувати <em>Калібрування горизонту рівня</em> для компенсації невеликих відхилень в орієнтації контролера та вирівнювання горизонту у виді польоту <em>QGroundControl</em> (синій зверху і зелений знизу).</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Виконання цього калібрування рекомендується лише у випадку, якщо орієнтація автопілота відкрито не відповідає вказаній орієнтації, або якщо є постійне відхилення під час польоту в режимах польоту, які не контролюють позицію.</p></div><h2 id="виконання-калібрування" tabindex="-1">Виконання калібрування <a class="header-anchor" href="#виконання-калібрування" aria-label="Permalink to &quot;Виконання калібрування&quot;">​</a></h2><p>Вирівняти горизонт:</p><ol><li><p>Запустіть <em>QGroundControl</em> та підключіть транспортний засіб.</p></li><li><p>Виберіть піктограму <strong>Шестерня</strong> (Налаштування транспортного засобу) на верхній панелі і потім <strong>Датчики</strong> у бічній панелі.</p></li><li><p>Натисніть кнопку <strong>Горизонт рівня</strong>. <img src="'+o+'" alt="Level Horizon calibration"> :::info Ви повинні вже встановити <a href="./../config/flight_controller_orientation.html">Орієнтацію автопілота</a>. Якщо ні, ви також можете встановити це тут. :::</p></li><li><p>Помістіть транспортний засіб на рівну відстань на рівній поверхні:</p><ul><li>Для літаків це положення під час рівнопланового польоту (літаки мають тенденцію трохи підняти свої крила!)</li><li>Для коптерів це позиція утримання.</li></ul></li><li><p>Натисніть <strong>OK</strong>, щоб розпочати процес калібрування.</p></li><li><p>Зачекайте, доки завершиться процес калібрування.</p></li></ol><h2 id="перевірка" tabindex="-1">Перевірка <a class="header-anchor" href="#перевірка" aria-label="Permalink to &quot;Перевірка&quot;">​</a></h2><p>Перевірте, що штучний горизонт, відображений у виді польоту, має показник посередині, коли транспортний засіб розміщений на рівній поверхні.</p><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="./../advanced_config/advanced_flight_controller_orientation_leveling.html">Розширена настройка орієнтації</a> (лише для досвідчених користувачів).</li><li><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/sensors_px4.html#level-horizon" target="_blank" rel="noreferrer">QGroundControl Посібник користувача &gt; Датчики</a></li><li><a href="https://youtu.be/91VGmdSlbo4?t=1m14s" target="_blank" rel="noreferrer">Відео налаштування PX4 &quot;Гіроскоп&quot; - @1m14s</a> (Youtube)</li></ul>',10),n=[i];function s(c,_,h,p,d,u){return a(),t("div",null,n)}const b=e(r,[["render",s]]);export{g as __pageData,b as default};
