import{_ as e}from"./chunks/jerk_limited_trajectory_1d.5k404LYe.js";import{_ as a,c as _,o as r,ab as c}from"./chunks/framework.CUflZczI.js";const p=JSON.parse('{"title":"Траєкторія Jerk-limited типу для Мультикоперів","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config_mc/mc_jerk_limited_type_trajectory.md","filePath":"uk/config_mc/mc_jerk_limited_type_trajectory.md"}'),i={name:"uk/config_mc/mc_jerk_limited_type_trajectory.md"},t=c('<h1 id="траєкторія-jerk-limited-типу-для-мультикоперів" tabindex="-1">Траєкторія Jerk-limited типу для Мультикоперів <a class="header-anchor" href="#траєкторія-jerk-limited-типу-для-мультикоперів" aria-label="Permalink to &quot;Траєкторія Jerk-limited типу для Мультикоперів&quot;">​</a></h1><p>Тип траєкторії з обмеженим рухом джерка забезпечує плавний рух у відповідь на введення палиці користувача або зміни місії (наприклад: для зйомки, картографування, вантажу). Він генерує симетричні плавні S-криві, де обмеження різкості та прискорення завжди гарантовані.</p><p>Цей тип траєкторії завжди увімкнений у режимі <a href="./../flight_modes_mc/mission.html">Місія</a>. Щоб увімкнути його в режимі <a href="./../flight_modes_mc/position.html">Режим позиціонування</a> встановіть параметр: <a href="./../advanced_config/parameter_reference.html#MPC_POS_MODE">MPC_POS_MODE=3</a>.</p><div class="info custom-block"><p class="custom-block-title">Тип з обмеженим jerk не використовується <em>за замовчуванням</em> у режимі позиціонування. Це може бути не підходить для транспортних засобів / випадків використання, які вимагають швидкої відповіді - наприклад, гонщицькі квадрокоптери.</p></div><h2 id="генератор-траєкторіі" tabindex="-1">Генератор траєкторії <a class="header-anchor" href="#генератор-траєкторіі" aria-label="Permalink to &quot;Генератор траєкторії&quot;">​</a></h2><p>Графік нижче показує типовий профіль обмеження ривка з наступними обмеженнями:</p><ul><li><code>jMax</code>: максимальне ривкості</li><li><code>a0</code>: початкове прискорення</li><li><code>aМакс</code>: максимальне прискорення</li><li><code>a3</code>: кінцеве прискорення (завжди 0)</li><li><code>v0</code>: початкова швидкість</li><li><code>vRef</code>: бажана швидкість</li></ul><p>Обмеження <code>jMax</code>, <code>aMax</code> можуть бути налаштовані користувачем через параметри і можуть відрізнятися в ручному керуванні позицією та автоматичному режимі.</p><p>Отриманий профіль швидкості часто називають &quot;S-кривою&quot;.</p><p><img src="'+e+'" alt="Jerk-limited trajectory"></p><h2 id="ручнии-режим" tabindex="-1">Ручний режим <a class="header-anchor" href="#ручнии-режим" aria-label="Permalink to &quot;Ручний режим&quot;">​</a></h2><p>У ручному режимі положення палиці відображаються на швидкість, де повне відхилення палиці XY відповідає <a href="./../advanced_config/parameter_reference.html#MPC_VEL_MANUAL">MPC_VEL_MANUAL</a>, а повне відхилення палиці Z відповідає <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_UP">MPC_Z_VEL_MAX_UP</a> (рух вгору) або <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_DN">MPC_Z_VEL_MAX_DN</a> (рух вниз).</p><h3 id="обмеження" tabindex="-1">Обмеження <a class="header-anchor" href="#обмеження" aria-label="Permalink to &quot;Обмеження&quot;">​</a></h3><p>XY-plane:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_MAX">MPC_JERK_MAX</a></li><li><code>aMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_ACC_HOR_MAX">MPC_ACC_HOR_MAX</a></li></ul><p>Z-axis:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_MAX">MPC_JERK_MAX</a></li><li><code>aMax</code> (рух вгору): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_UP_MAX">MPC_ACC_UP_MAX</a></li><li><code>aMax</code> (рух вниз): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_DOWN_MAX">MPC_ACC_DOWN_MAX</a></li></ul><h2 id="автоматичнии-режим" tabindex="-1">Автоматичний режим <a class="header-anchor" href="#автоматичнии-режим" aria-label="Permalink to &quot;Автоматичний режим&quot;">​</a></h2><p>У режимі автоматичного руху бажана швидкість є <a href="./../advanced_config/parameter_reference.html#MPC_XY_CRUISE">MPC_XY_CRUISE</a>, але це значення автоматично коригується в залежності від відстані до наступної точки маршруту, максимально можливої швидкості в точці маршруту та максимального бажаного прискорення та ривка. Вертикальна швидкість визначається за допомогою <a href="./../advanced_config/parameter_reference.html#MPC_Z_V_AUTO_UP">MPC_Z_V_AUTO_UP</a> (рух вгору) та <a href="./../advanced_config/parameter_reference.html#MPC_Z_V_AUTO_DN">MPC_Z_V_AUTO_DN</a> (рух вниз).</p><h3 id="обмеження-1" tabindex="-1">Обмеження <a class="header-anchor" href="#обмеження-1" aria-label="Permalink to &quot;Обмеження&quot;">​</a></h3><p>XY-plane:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_AUTO">MPC_JERK_AUTO</a></li><li><code>aMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_ACC_HOR">MPC_ACC_HOR</a></li></ul><p>Z-axis:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_AUTO">MPC_JERK_AUTO</a></li><li><code>aMax</code> (рух вгору): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_UP_MAX">MPC_ACC_UP_MAX</a></li><li><code>aMax</code> (рух донизу): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_DOWN_MAX">MPC_ACC_DOWN_MAX</a></li></ul><p>Відстань до отримання швидкості, коли близько до точки маршруту:</p><ul><li><a href="./../advanced_config/parameter_reference.html#MPC_XY_TRAJ_P">MPC_XY_TRAJ_P</a></li></ul><h3 id="зв-язані-параметри" tabindex="-1">Зв&#39;язані параметри <a class="header-anchor" href="#зв-язані-параметри" aria-label="Permalink to &quot;Зв&#39;язані параметри&quot;">​</a></h3><ul><li><a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_MAX">MPC_XY_VEL_MAX</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_UP">MPC_Z_VEL_MAX_UP</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_DN">MPC_Z_VEL_MAX_DN</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_TKO_SPEED">MPC_TKO_SPEED</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_SPEED">MPC_LAND_SPEED</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_ALT1">MPC_LAND_ALT1</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_ALT2">MPC_LAND_ALT2</a></li></ul>',28),l=[t];function d(o,n,f,m,h,M){return r(),_("div",null,l)}const A=a(i,[["render",d]]);export{p as __pageData,A as default};
