import{_ as e,c as a,o as _,ab as t}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"Налаштування зворотного переходу VTOL","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config_vtol/vtol_back_transition_tuning.md","filePath":"uk/config_vtol/vtol_back_transition_tuning.md"}'),r={name:"uk/config_vtol/vtol_back_transition_tuning.md"},n=t('<h1 id="налаштування-зворотного-переходу-vtol" tabindex="-1">Налаштування зворотного переходу VTOL <a class="header-anchor" href="#налаштування-зворотного-переходу-vtol" aria-label="Permalink to &quot;Налаштування зворотного переходу VTOL&quot;">​</a></h1><p>Коли VTOL виконує зворотний перехід (перехід з режиму фіксованих крил на багатороторний вертоліт), він повинен сповільнити рух перед тим, як багатороторний вертоліт може прийняти належний контроль. Для допомоги у гальмуванні контролер підійме ніс транспортного засобу, якщо поточне сповільнення менше, ніж те, що встановлено у очікуваному сповільненні (<a href="./../advanced_config/parameter_reference.html#VT_B_DEC_MSS">VT_B_DEC_MSS</a>). Відповідь цього контролера сповільнення може бути налаштована за допомогою параметра коефіцієнта <code>I</code>: <a href="./../advanced_config/parameter_reference.html#VT_B_DEC_I">VT_B_DEC_I</a>. Збільшення <code>I</code> призведе до більш агресивного піднімання носа для досягнення налаштованого значення сповільнення.</p><p>Транспортний засіб вважатиме зворотний перехід завершеним, коли горизонтальна швидкість досягне крейсерської швидкості мультикоптера (<a href="./../advanced_config/parameter_reference.html#MPC_XY_CRUISE">MPC_XY_CRUISE</a>) або коли мине тривалість зворотного переходу (<a href="./../advanced_config/parameter_reference.html#VT_B_TRANS_DUR">VT_B_TRANS_DUR</a>) (в залежності від того, яка подія наступить першою).</p><h2 id="встановлення-очікуваного-уповільнення" tabindex="-1">Встановлення очікуваного уповільнення <a class="header-anchor" href="#встановлення-очікуваного-уповільнення" aria-label="Permalink to &quot;Встановлення очікуваного уповільнення&quot;">​</a></h2><p>Під час виконання місій, які використовують місцеву точку приземлення <a href="https://mavlink.io/en/messages/common.html#MAV_CMD_NAV_VTOL_LAND" target="_blank" rel="noreferrer">VTOL_LAND</a>, автопілот спробує розрахувати належну відстань для початку зворотного переходу. Він робить це, розглядаючи поточну швидкість (порівнювальну зі швидкістю на землі) та очікуване сповільнення. Щоб транспортний засіб вийшов зі зворотного переходу дуже близько до своєї точки приземлення, ви можете налаштувати параметр очікуваного сповільнення (<a href="./../advanced_config/parameter_reference.html#VT_B_DEC_MSS">VT_B_DEC_MSS</a>). Переконайтеся, що ви встановили достатньо великий час зворотного переходу, щоб дозволити транспортному засобу досягнути своєї заданої позиції, перш ніж цей таймаут спрацює.</p><h2 id="тривалість-зворотного-переходу" tabindex="-1">Тривалість зворотного переходу <a class="header-anchor" href="#тривалість-зворотного-переходу" aria-label="Permalink to &quot;Тривалість зворотного переходу&quot;">​</a></h2><p>Встановлення великого часу зворотного переходу (<a href="./../advanced_config/parameter_reference.html#VT_B_TRANS_DUR">VT_B_TRANS_DUR</a>) надасть транспортному засобу більше часу для сповільнення. Протягом цього періоду VTOL вимкне свій двигун фіксованих крил і повільно підніме свої MC двигуни під час глайдування. Чим довше цей час, тим довше транспортний засіб буде глайдувати в спробі сповільнити рух. Недолік цього поведінки полягає в тому, що транспортний засіб буде контролювати лише висоту, а не позицію, під час цього періоду, тому може відбуватися деяке зміщення.</p>',7),o=[n];function c(i,l,d,h,s,f){return _(),a("div",null,o)}const p=e(r,[["render",c]]);export{T as __pageData,p as default};
