import{_ as e,c as t,o as a,ab as r}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"Інтеграція радіо/модему телеметрії","description":"","frontmatter":{},"headers":[],"relativePath":"uk/data_links/telemetry.md","filePath":"uk/data_links/telemetry.md"}'),o={name:"uk/data_links/telemetry.md"},i=r('<h1 id="інтеграція-радіо-модему-телеметріі" tabindex="-1">Інтеграція радіо/модему телеметрії <a class="header-anchor" href="#інтеграція-радіо-модему-телеметріі" aria-label="Permalink to &quot;Інтеграція радіо/модему телеметрії&quot;">​</a></h1><p>Телеметричні радіостанції можна (необов’язково) використовувати для забезпечення бездротового з’єднання MAVLink між наземною станцією керування, як-от <em>QGroundControl</em>, і транспортним засобом, на якому працює PX4. Цей розділ містить теми про розширене використання підтримуваних радіомодулів і інтеграцію нових телеметрічних систем в PX4.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../telemetry/README.html">Периферійне обладнання &gt; Telemetry Radios</a> містить інформацію про телеметричні радіосистеми, які вже підтримуються PX4. Сюди входять радіостанції, які використовують мікропрограму <em>SiK Radio</em> та <em>3DR Wi-Fi Telemetry Radios</em>.</p></div><h2 id="інтеграція-телеметричних-систем" tabindex="-1">Інтеграція телеметричних систем <a class="header-anchor" href="#інтеграція-телеметричних-систем" aria-label="Permalink to &quot;Інтеграція телеметричних систем&quot;">​</a></h2><p>PX4 дозволяє використовувати телеметрію на основі протоколу MAVLink через телеметрійний порт контролера польоту на основі Pixhawk. При умові, що телеметрійне радіо підтримує MAVLink та надає інтерфейс UART з сумісними рівнями напруги/роз&#39;ємом, подальша інтеграція не потрібна.</p><p>Телеметрійні системи, що використовують інший протокол, потребують більш глибокої інтеграції, можливо, як програмного (наприклад, драйвери пристрою), так і апаратного (роз&#39;єми тощо). Хоча це було зроблено для конкретних випадків (наприклад, телеметрія FrSky дозволяє надсилати статус транспортного засобу до радіокерувальника через приймач FrSky), надання загальних порад є складним завданням. Ми рекомендуємо спочатку <a href="./../contribute/support.html">обговорити це з командою розробників</a>.</p>',6),s=[i];function l(n,c,_,d,m,p){return a(),t("div",null,s)}const u=e(o,[["render",l]]);export{k as __pageData,u as default};
