import{_ as a}from"./chunks/avionics_anon_altimeter_uavcan_interface.BgsmXSK3.js";import{_ as t,c as e,o as r,ab as n}from"./chunks/framework.CUflZczI.js";const p=JSON.parse('{"title":"Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan","description":"","frontmatter":{},"headers":[],"relativePath":"uk/dronecan/avanon_laser_interface.md","filePath":"uk/dronecan/avanon_laser_interface.md"}'),o={name:"uk/dronecan/avanon_laser_interface.md"},i=n('<h1 id="інтерфеис-лазерного-альтиметра-avionics-anonymous-dronecan" tabindex="-1">Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan <a class="header-anchor" href="#інтерфеис-лазерного-альтиметра-avionics-anonymous-dronecan" aria-label="Permalink to &quot;Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">У 2022 році UAVCAN (v0) був форкнутий і підтримується як <code>DroneCAN</code>. Хоча цей продукт все ще згадує &quot;UAVCAN&quot;, він повністю сумісний з підтримкою DroneCAN в PX4.</p></div><p>Інтерфейс лазерного альтиметра Avionics Anonymous дозволяє підключати <a href="#supported_rangefinders">ряд звичайних далекомірів</a> за допомогою шини CAN (це надійніший інтерфейс, ніж I2C).</p><p><img src="'+a+'" alt="Avionics Anonymous Laser Altimeter DroneCAN Interface"></p><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><ul><li><a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">AvAnon Laser Interface</a></li></ul><p><a id="supported_rangefinders"></a></p><h2 id="підтримувані-далекоміри" tabindex="-1">Підтримувані далекоміри <a class="header-anchor" href="#підтримувані-далекоміри" aria-label="Permalink to &quot;Підтримувані далекоміри&quot;">​</a></h2><p>Повний список підтримуваних далекомірів можна знайти за посиланням вище.</p><p>Наступні далекоміри підтримуються на момент написання:</p><ul><li>Lightware SF30/D</li><li>Lightware SF10/a</li><li>Lightware SF10/b</li><li>Lightware SF10/c</li><li>Lightware SF11/c</li><li>Lightware SF/LW20/b</li><li>Lightware SF/LW20/c</li></ul><h2 id="встановлення-обладнання" tabindex="-1">Встановлення обладнання <a class="header-anchor" href="#встановлення-обладнання" aria-label="Permalink to &quot;Встановлення обладнання&quot;">​</a></h2><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><p>Далекомір (лазер) підключений до плати інтерфейсу AvAnon, яка підключена до одного з CAN-портів вашого автопілота. Проводка відбувається згідно з виведенням контактів вище, або необхідні кабелі можна придбати, щоб підключити їх безпосередньо до вашої системи. Ці продукти доступні за посиланнями <a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">тут</a>.</p><p>Інтерфейсна плата забезпечує фільтрований вихід живлення для лазера, але не забезпечує власного регулювання. Отже, лазер повинен бути сумісним з напругою, яка подається на плату.</p><h3 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h3><h3 id="конектор-шини-can" tabindex="-1">Конектор шини CAN <a class="header-anchor" href="#конектор-шини-can" aria-label="Permalink to &quot;Конектор шини CAN&quot;">​</a></h3><table><thead><tr><th>Pin</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>1</td><td>POWER_IN</td><td>Джерело живлення. Підтримується 4.0-5.5В, але також повинно бути сумісним з підключеним лазером.</td></tr><tr><td>2</td><td>TX/SCL</td><td>TX для послідовного режиму, Clock для режиму I2C.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX для послідовного режиму, Data для режиму I2C.</td></tr><tr><td>4</td><td>GND</td><td>Заземлення сигналу/напруги.</td></tr></tbody></table><h3 id="з-єднувач-лазеру" tabindex="-1">З&#39;єднувач лазеру <a class="header-anchor" href="#з-єднувач-лазеру" aria-label="Permalink to &quot;З&#39;єднувач лазеру&quot;">​</a></h3><table><thead><tr><th>Pin</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>1</td><td>POWER_OUT</td><td>Фільтрована потужність при напрузі живлення.</td></tr><tr><td>2</td><td>CAN+</td><td>TX для послідовного режиму, Clock для режиму I2C.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX для послідовного режиму, Data для режиму I2C.</td></tr><tr><td>4</td><td>GND</td><td>Заземлення сигналу/напруги.</td></tr></tbody></table><h2 id="налаштування-px4" tabindex="-1">Налаштування PX4 <a class="header-anchor" href="#налаштування-px4" aria-label="Permalink to &quot;Налаштування PX4&quot;">​</a></h2><p>Для увімкнення лазерного альтиметра вам необхідно <a href="./../advanced_config/parameters.html">встановити наступні параметри</a> (у QGroundControl):</p><ul><li>Увімкніть DroneCAN, встановивши <a href="./../advanced_config/parameter_reference.html#UAVCAN_ENABLE">UAVCAN_ENABLE</a> не нульовим.</li><li>Увімкніть підписку на лазерний далекомір DroneCAN, встановивши <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_RNG">UAVCAN_SUB_RNG</a></li><li>Встановіть мінімальний та максимальний діапазон далекоміра, використовуючи <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MIN">UAVCAN_RNG_MIN</a> та <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MAX">UAVCAN_RNG_MAX</a>.</li></ul>',23),d=[i];function l(c,h,s,_,A,u){return r(),e("div",null,d)}const N=t(o,[["render",l]]);export{p as __pageData,N as default};
