import{_ as t,a as r,b as a}from"./chunks/hb_dronecan_m8n_gps_dimension.BeBaHGML.js";import{_ as e,c as d,o,ab as n}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Holybro DroneCAN M8N GPS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/dronecan/holybro_m8n_gps.md","filePath":"uk/dronecan/holybro_m8n_gps.md"}'),i={name:"uk/dronecan/holybro_m8n_gps.md"},l=n('<h1 id="holybro-dronecan-m8n-gps" tabindex="-1">Holybro DroneCAN M8N GPS <a class="header-anchor" href="#holybro-dronecan-m8n-gps" aria-label="Permalink to &quot;Holybro DroneCAN M8N GPS&quot;">​</a></h1><p>Holybro DroneCAN GPS має модуль UBLOX M8N, компас BMM150, триколірний індикатор світлодіодів.</p><p>Модуль GPS використовує протокол <a href="./">DroneCAN</a> для зв&#39;язку. З&#39;єднання DroneCAN менше чутливі до електромагнітних перешкод порівняно з послідовним з&#39;єднанням, що робить його більш надійним. Крім того, використання DroneCAN означає, що GPS та компас не займають жодних портів послідовного керування польотом (різні/додаткові пристрої CAN можна підключити до того ж шини CAN за допомогою плати розгалужувача CAN).</p><img src="'+t+'" width="400px" title="Hero diagram for the GPS module"><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Замовте цей модуль з:</p><ul><li><a href="https://holybro.com/products/dronecan-m8n-gps" target="_blank" rel="noreferrer">Holybro</a></li></ul><h2 id="характеристики-обладнання" tabindex="-1">Характеристики обладнання <a class="header-anchor" href="#характеристики-обладнання" aria-label="Permalink to &quot;Характеристики обладнання&quot;">​</a></h2><table><thead><tr><th></th><th>DroneCAN M8N</th></tr></thead><tbody><tr><td>Приймач GNSS</td><td>Ublox NEO M8N</td></tr><tr><td>Кількість одночасних GNSS</td><td>2 (Стандартний GPS + GLONASS)</td></tr><tr><td>Процесор</td><td>STM32G4 (170MHz, 512K FLASH)</td></tr><tr><td>Компас</td><td>BMM150</td></tr><tr><td>Смуга частот</td><td><p>GPS: L1C/A<br>GLONASS: L10F<br>Beidou: B1I<br>Galileo: E1B/C</p></td></tr><tr><td>Система розширення GNSS</td><td>SBAS: WAAS, EGNOS, MSAS, QZSS</td></tr><tr><td>Оновлення навігації</td><td>5Гц За замовчуванням (10Гц МАКС)</td></tr><tr><td>Чутливість навігації</td><td>–167 dBm</td></tr><tr><td>Холодний запуск</td><td>~ 26s</td></tr><tr><td>Точність</td><td>2.5m</td></tr><tr><td>Точність швидкості</td><td>0.05 m/s</td></tr><tr><td>Макс. # супутників</td><td>22+</td></tr><tr><td>Швидкість передачі даних по CAN BUS за замовчуванням</td><td>1MHz</td></tr><tr><td>Протокол зв&#39;язку</td><td>DroneCAN @ 1 Mbit/s</td></tr><tr><td>Підтримує автопілот FW</td><td>PX4, Ardupilot</td></tr><tr><td>Тип порту</td><td>GHR-04V-S</td></tr><tr><td>Антена</td><td>25 x 25 x 4 мм керамічна патч-антена</td></tr><tr><td>Напруга</td><td>4.7-5.2V</td></tr><tr><td>Споживання електроенергії</td><td>Менше ніж 200мА @ 5В</td></tr><tr><td>Температура</td><td>-40~80C</td></tr><tr><td>Розмір</td><td><p>Діаметр: 54мм<br>Товщина: 14.5мм</p></td></tr><tr><td>Вага</td><td>36g</td></tr><tr><td>Довжина кабелю</td><td>26cm</td></tr><tr><td>Інше</td><td><ul><li>LNA MAX2659ELT+ RF підсилювач</li><li>Конденсатор Фараха, що перезаряджається</li><li>Малошумний стабілізатор 3,3 В</li><li>У комплект включено кабель завдовжки 26 см</li></ul></td></tr></tbody></table><h2 id="налаштування-обладнання" tabindex="-1">Налаштування обладнання <a class="header-anchor" href="#налаштування-обладнання" aria-label="Permalink to &quot;Налаштування обладнання&quot;">​</a></h2><h3 id="встановлення" tabindex="-1">Встановлення <a class="header-anchor" href="#встановлення" aria-label="Permalink to &quot;Встановлення&quot;">​</a></h3><p>Рекомендоване положення монтажу полягає в тому, щоб стрілка на GPS вказувала у напрямку <strong>передньої частини дрону</strong>.</p><p>Датчик може бути встановлений де завгодно на рамці, але ви повинні вказати його позицію відносно центру мас транспортного засобу під час <a href="#px4-configuration">налаштування PX4</a>.</p><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><p>The Holybro DroneCAN GPS підключений до шини CAN за допомогою стандартного кабелю Pixhawk 4 pin JST GH. Для отримання додаткової інформації, зверніться до інструкцій з <a href="./../can/#wiring">проводки CAN</a>.</p><h3 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h3><p><img src="'+r+'" alt="Diagram showing GPS pinouts"></p><h3 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h3><p><img src="'+a+'" alt="Diagram showing GPS dimensions"></p><h2 id="налаштування-px4" tabindex="-1">Налаштування PX4 <a class="header-anchor" href="#налаштування-px4" aria-label="Permalink to &quot;Налаштування PX4&quot;">​</a></h2><p>Вам потрібно встановити необхідні <a href="./">параметри DroneCAN</a> та визначити зсуви, якщо датчик не знаходиться у центрі транспортного засобу. Необхідні налаштування наведено нижче.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>GPS не завантажиться, якщо SD-карти немає у контролері польоту під час ввімкнення.</p></div><h3 id="увімкнення-dronecan" tabindex="-1">Увімкнення DroneCAN <a class="header-anchor" href="#увімкнення-dronecan" aria-label="Permalink to &quot;Увімкнення DroneCAN&quot;">​</a></h3><p>Для використання плати ARK GPS підключіть її до шини CAN Pixhawk та увімкніть драйвер DroneCAN, встановивши параметр <a href="./../advanced_config/parameter_reference.html#UAVCAN_ENABLE">UAVCAN_ENABLE</a> на <code>2</code> для динамічного призначення вузла (або <code>3</code>, якщо використовуєте <a href="./../dronecan/escs.html">DroneCAN ESCs</a>).</p><p>Кроки наступні:</p><ul><li>У <em>QGroundControl</em> встановіть параметр <a href="./../advanced_config/parameter_reference.html#UAVCAN_ENABLE">UAVCAN_ENABLE</a> на <code>2</code> або <code>3</code> та перезавантажте (див. <a href="./../advanced_config/parameters.html">Finding/Updating Parameters</a>).</li><li>Підключіть GPS CAN до шини CAN Pixhawk.</li></ul><p>Після активації модуль буде виявлено при завантаженні. Дані GPS повинні надходити з частотою 5 Гц.</p><p>Конфігурацію DroneCAN в PX4 пояснено більш детально в <a href="./../dronecan/#enabling-dronecan">DroneCAN &gt;Enabling DroneCAN</a>.</p><h3 id="конфігурація-позиціі-датчика" tabindex="-1">Конфігурація позиції датчика <a class="header-anchor" href="#конфігурація-позиціі-датчика" aria-label="Permalink to &quot;Конфігурація позиції датчика&quot;">​</a></h3><p>Якщо датчик не знаходиться у центрі пристрою, вам також потрібно буде визначити зміщення датчика:</p><ul><li>Увімкніть GPS синхронізацію по напрямку, встановивши біт 3 <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_CTRL">EKF2_GPS_CTRL</a> на true.</li><li>Увімкніть <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_GPS">UAVCAN_SUB_GPS</a>, <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_MAG">UAVCAN_SUB_MAG</a> та <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_BARO">UAVCAN_SUB_BARO</a>.</li><li>Встановіть <a href="./../advanced_config/parameter_reference.html#CANNODE_TERM">CANNODE_TERM</a> на <code>1</code> якщо це останній вузол на шині CAN.</li><li>Параметри <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_X">EKF2_GPS_POS_X</a>, <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_Y">EKF2_GPS_POS_Y</a> та <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_POS_Z">EKF2_GPS_POS_Z</a> можуть бути встановлені для врахування зміщення ARK GPS від центру мас транспортного засобу.</li></ul>',31),h=[l];function c(_,s,p,A,m,S){return o(),d("div",null,h)}const b=e(i,[["render",c]]);export{P as __pageData,b as default};
