import{_ as t,a,b as l}from"./chunks/nora-pinouts.D1ppdavo.js";import{_ as e}from"./chunks/cuav_v5_debug_cable.Bplh4dyQ.js";import{_ as r,c as o,o as i,ab as s}from"./chunks/framework.CUflZczI.js";const v=JSON.parse('{"title":"Диспетчер польотів CUAV Nora","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cuav_nora.md","filePath":"uk/flight_controller/cuav_nora.md"}'),c={name:"uk/flight_controller/cuav_nora.md"},n=s('<h1 id="диспетчер-польотів-cuav-nora" tabindex="-1">Диспетчер польотів CUAV Nora <a class="header-anchor" href="#диспетчер-польотів-cuav-nora" aria-label="Permalink to &quot;Диспетчер польотів CUAV Nora&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який) автопілот. Звертайтесь до <a href="https://www.cuav.net" target="_blank" rel="noreferrer">виробника</a> щодо питань апаратного забезпечення або питань відповідності.</p></div><p>Контролер польоту <a href="https://doc.cuav.net/flight-controller/x7/en/nora.html" target="_blank" rel="noreferrer">Nora</a><sup>®</sup> - це високопродуктивний автопілот. Це ідеальний вибір для промислових дронів і великомасштабних важких дронів. В основному постачається комерційним виробникам.</p><p><img src="'+t+'" alt="CUAV x7"></p><p>Нора - це варіант CUAV X7. Він використовує інтегровану материнську плату (м&#39;яку і тверду), що зменшує кількість внутрішніх роз&#39;ємів польотного контролера, підвищує надійність і розміщує всі інтерфейси збоку (роблячи проводку більш лаконічною).</p><div class="info custom-block"><p class="custom-block-title">Цей польотний контролер <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="особливості" tabindex="-1">Особливості <a class="header-anchor" href="#особливості" aria-label="Permalink to &quot;Особливості&quot;">​</a></h2><ul><li>Внутрішнє поглинання ударів</li><li>Інтегрований процес зменшує відмову, спричинену пошкодженням інтерфейсу.</li><li>Підтримка USB_HS, швидше завантаження журналів (PX4 ще не підтримується)</li><li>Підтримка більшої кількості виходів dshot</li><li>Підтримка нагріву IMU, покращення роботи датчика</li><li>Виділений порт для акумулятора CAN</li><li>3 комплекти датчиків IMU</li><li>Автомобільний компас RM3100</li><li>Високопродуктивний процесор</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Виробник <a href="https://doc.cuav.net/flight-controller/x7/en/nora.html" target="_blank" rel="noreferrer">CUAV Docs</a> є канонічним посиланням для Nora. Вони повинні використовуватися за перевагою, оскільки вони містять найбільш повну та актуальну інформацію.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li><p>Головний FMU процесор: STM32H743</p></li><li><p>Бортові сенсори:</p><ul><li>Акселерометр/Гіроскоп: ICM-20689</li><li>Прискорювач/гіроскоп: ICM-20649</li><li>Акселерометр/Гіроскоп: BMI088</li><li>Магнітометр: RM3100</li><li>Барометр: MS5611*2</li></ul></li><li><p>Інтерфейси:</p><ul><li>14 ШІМ-виходів （12 підтримує Dshot）</li><li>Підтримка декількох входів RC (SBU / CPPM / DSM)</li><li>Аналоговий / PWM вхід RSSI</li><li>2 GPS порти (GPS і UART4 порти)</li><li>4 шини i2c (два виділені порти i2c)</li><li>2 порти CAN шини</li><li>2 порти живлення (Power A - загальний інтерфейс адаптера, Power C - інтерфейс акумулятора DroneCAN)</li><li>2 входи АЦП</li><li>1 USB порт</li></ul></li><li><p>Система живлення:</p><ul><li>Живлення: 4.3~5.4В</li><li>Вхід USB: 4.75~5.25В</li><li>Вхід сервоприводу: 0~36V</li></ul></li><li><p>Вага та розміри:</p><ul><li>Вага: 101 g</li></ul></li><li><p>Інші характеристики:</p><ul><li>Робоча температура: -20 ~ 80°c (виміряне значення)</li><li>Три імуси</li><li>Підтримка компенсації температури</li><li>Внутрішнє поглинання ударів</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Коли він працює під управлінням прошивки PX4, працюють лише 8 ШІМ-виходів. Решта 6 ШІМ-портів все ще адаптуються (тому на момент написання статті вони не сумісні з VOLT).</p></div><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><ul><li><a href="https://store.cuav.net" target="_blank" rel="noreferrer">Магазин CUAV</a>&lt;\\br&gt;</li><li><a href="https://www.aliexpress.com/item/4001042501927.html?gps-id=8041884&amp;scm=1007.14677.110221.0&amp;scm_id=1007.14677.110221.0&amp;scm-url=1007.14677.110221.0&amp;pvid=3dc0a3ba-fa82-43d2-b0b3-6280e4329cef&amp;spm=a2g0o.store_home.promoteRecommendProducts_7913969.58" target="_blank" rel="noreferrer">CUAV Aliexpress</a></li></ul><h2 id="з-єднання-проводка" tabindex="-1">З&#39;єднання (Проводка) <a class="header-anchor" href="#з-єднання-проводка" aria-label="Permalink to &quot;З&#39;єднання (Проводка)&quot;">​</a></h2><p><a href="https://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-nora.html" target="_blank" rel="noreferrer">Короткий посібник з підключення CUAV nora</a></p><h2 id="розмір-та-роз-єми" tabindex="-1">Розмір та роз&#39;єми <a class="header-anchor" href="#розмір-та-роз-єми" aria-label="Permalink to &quot;Розмір та роз&#39;єми&quot;">​</a></h2><p><img src="'+a+'" alt="CUAV x7"></p><p><img src="'+l+'" alt="Роз&#39;єми X7"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Порт <code>RCIN</code> призначено лише для живлення rc-приймача і його не можна підключати до жодного джерела живлення/навантаження.</p></div><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p>Nora AutoPilot* може мати потрійне резервування джерела живлення, якщо до нього підключено три джерела живлення. Дві шини живлення: <strong>POWERA</strong>, <strong>POWERC</strong> і <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">Вихідні шини живлення <strong>PWM OUT</strong> (від 0В до 36В) не живлять плату контролера польоту (і не живляться від неї). Ви повинні подати живлення на один з виводів <strong>POWERA</strong>, <strong>POWERC</strong> або <strong>USB</strong>, інакше плата буде знеструмлена.</p></div><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWERA</strong> та <strong>POWERC</strong> входи (від 4,3 В до 5,4 В)</li><li><strong>USB</strong> вхід (4.75V до 5.25V)</li></ol><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make cuav_nora_default</span></span></code></pre></div><h2 id="захист-від-перенапруги" tabindex="-1">Захист від перенапруги <a class="header-anchor" href="#захист-від-перенапруги" aria-label="Permalink to &quot;Захист від перенапруги&quot;">​</a></h2><p><em>Nora</em> має захист від перенапруги на периферійних пристроях з напругою 5 Вольт і 5 Вольт високої потужності, який обмежує струм до 2,5 А. <em>Nora</em> має захист від короткого замикання.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>На роз&#39;єми, позначені як контакт 1, можна подавати до 2,5 А (хоча вони розраховані лише на 1 А).</p></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Консоль послідовного доступу до системи та інтерфейс SWD працюють через порт <strong>DSU7</strong>. Просто підключіть кабель FTDI до роз&#39;єму DSU7 (у списку продуктів є кабель CUAV FTDI).</p><p>Системна консоль <a href="./../debug/system_console.html">PX4</a> та інтерфейс <a href="./../debug/swd_debug.html">SWD</a> працюють через порт <strong>FMU Debug</strong> (<code>DSU7</code>).</p><p>Відладочний порт (<code>DSU7</code>) використовує роз&#39;єм <a href="https://www.digikey.com.au/product-detail/en/jst-sales-america-inc/BM06B-GHS-TBT-LF-SN-N/455-1582-1-ND/807850" target="_blank" rel="noreferrer">JST BM06B</a> і має наступну розводку:</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>5V+</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>DEBUG TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>DEBUG RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>FMU_SWDIO</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>FMU_SWCLK</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><p>CUAV надає спеціальний відладочний кабель, який можна підключити до порту <code>DSU7</code>. Він розділяє кабель FTDI для підключення <a href="./../debug/system_console.html">PX4 System Console</a> до USB-порту комп&#39;ютера і виводи SWD, які використовуються для налагодження SWD/JTAG. Відладочний кабель, що входить до комплекту, не підключається до контакту (1) порту SWD <code>Vref</code>.</p><p><img src="'+e+'" alt="Відладочний кабель CUAV"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Вивід Vref SWD (1) використовує 5В як Vref, але процесор працює при напрузі 3,3В!</p><p>Деякі JTAG-адаптери (SEGGER J-Link) використовують напругу Vref для встановлення напруги на лініях SWD. Для прямого підключення до <em>Segger Jlink</em> ми рекомендуємо використовувати 3,3 вольта з контакту 4 роз&#39;єму з позначкою <code>DSM</code>/<code>SBUS</code>/<code>RSSI</code> для забезпечення <code>Vtref</code> на JTAG (тобто для забезпечення 3,3 В і <em>NOT</em> 5 В).</p></div><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. Повний набір підтримуваних конфігурацій можна переглянути в <a href="./../airframes/airframe_reference.html">Довіднику авіаційних платформ</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-nora.html" target="_blank" rel="noreferrer">Швидкий старт</a></li><li><a href="http://doc.cuav.net" target="_blank" rel="noreferrer">Документація CUAV</a></li><li><a href="https://github.com/cuav/hardware/tree/master/X7_Autopilot" target="_blank" rel="noreferrer">схема nora</a></li></ul>',45),d=[n];function p(h,u,m,b,g,_){return i(),o("div",null,d)}const P=r(c,[["render",p]]);export{v as __pageData,P as default};
