import{_ as a}from"./chunks/v5__01.BYtbY4Vm.js";import{_ as t}from"./chunks/debug_port_dsu7.OydBcMxg.js";import{_ as e}from"./chunks/cuav_v5_debug_cable.Bplh4dyQ.js";import{_ as r,c as l,o as i,ab as o}from"./chunks/framework.CUflZczI.js";const v=JSON.parse('{"title":"Автопілот CUAV V5+","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cuav_v5_plus.md","filePath":"uk/flight_controller/cuav_v5_plus.md"}'),d={name:"uk/flight_controller/cuav_v5_plus.md"},s=o('<h1 id="автопілот-cuav-v5" tabindex="-1">Автопілот CUAV V5+ <a class="header-anchor" href="#автопілот-cuav-v5" aria-label="Permalink to &quot;Автопілот CUAV V5+&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який) автопілот. Зверніться до <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">виробника</a> щодо питань технічної підтримки або відповідності вимогам.</p></div><p><em>V5+</em><sup>®</sup> - вдосконалений автопілот виробництва CUAV<sup>®</sup>. Він був розроблений CUAV<sup>®</sup> у співпраці з командою PX4.</p><p>Автопілот рекомендується для комерційної системної інтеграції, але також підходить для академічних досліджень і будь-якого іншого використання.</p><p><img src="'+a+'" alt="V5+ AutoPilot - hero image"></p><p>Деякі з його основних функцій включають:</p><ul><li>Повна сумісність зі стандартом проєкту <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk</a> <strong>FMUv5</strong> і використовує <a href="https://pixhawk.org/pixhawk-connector-standard/" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> для всіх зовнішніх інтерфейсів.</li><li>Більш продуктивний процесор, оперативна пам&#39;ять та флеш-пам&#39;ять, ніж у FMU v3, разом із більш стабільними та надійними датчиками.</li><li>Прошивка сумісна з PX4.</li><li>Модульна конструкція дозволяє користувачам налаштовувати власну носійну плату.</li><li>Вбудована система поглинання вібрації з високоефективною системою поглинання ударів.</li><li>Множинні резервні датчики та системи живлення для покращення безпеки та стабільності польоту.</li></ul><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li><p>Головний FMU процесор: STM32F765</p><ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB пам&#39;ять, 512KB RAM</li></ul></li><li><p>IO процесор: STM32F100</p><ul><li>32 Bit Arm® Cortex®-M3, 24MHz, 8KB SRAM</li></ul></li><li><p>Сенсори на платі:</p><ul><li>Акселерометр/Гіроскоп: ICM-20689</li><li>Акселерометр/Гіроскоп: BMI055</li><li>Магнітометр: IST8310</li><li>Барометр: MS5611</li></ul></li><li><p>Інтерфейси:</p><ul><li>8-14 PWM виходів (6 з IO, 8 з FMU)</li><li>3 виділених PWM/Capture входи на FMU</li><li>Виділений R/C вхід для CPPM</li><li>Виділений R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM RSSI входом</li><li>аналоговий / PWM вхід RSSI</li><li>Вихід сервоприводу S.Bus</li><li>5 загальних послідовних портів</li><li>4x I2C порти</li><li>4 шини SPI</li><li>2 CANBuses з послідовними ESC</li><li>Аналогові входи для напруги / струму з 2 батарей</li></ul></li><li><p>Система живлення:</p><ul><li>Живлення: 4.3~5.4В</li><li>Вхід USB: 4.75~5.25В</li></ul></li><li><p>Вага та розміри:</p><ul><li>Вага: 90г</li><li>Розміри: 85.5*42*мм</li></ul></li><li><p>Інші характеристики:</p><ul><li>Робоча температура: -20 ~ 80°c (виміряне значення)</li></ul></li></ul><h2 id="де-купитиcuav-aliexpress-міжнародні-користувачі" tabindex="-1">Де купити<a href="https://www.aliexpress.com/item/32890380056.html?spm=a2g0o.detail.1000060.1.7a7233e7mLTlVl&amp;gps-id=pcDetailBottomMoreThisSeller&amp;scm=1007.13339.90158.0&amp;scm_id=1007.13339.90158.0&amp;scm-url=1007.13339.90158.0&amp;pvid=d899bfab-a7ca-46e1-adf2-72ad1d649822" target="_blank" rel="noreferrer">CUAV Aliexpress</a> (міжнародні користувачі) <a class="header-anchor" href="#де-купитиcuav-aliexpress-міжнародні-користувачі" aria-label="Permalink to &quot;Де купити&lt;!-- \\[CUAV Store\\](https://store.cuav.net/index.php?id_product=95&amp;id_product_attribute=0&amp;rewrite=cuav-new-pixhack-v5-autopilot-m8n-gps-for-fpv-rc-drone-quadcopter-helicopter-flight-simulator-free-shipping-whole-sale&amp;controller=product&amp;id_lang=1) --&gt;[CUAV Aliexpress](https://www.aliexpress.com/item/32890380056.html?spm=a2g0o.detail.1000060.1.7a7233e7mLTlVl&amp;gps-id=pcDetailBottomMoreThisSeller&amp;scm=1007.13339.90158.0&amp;scm_id=1007.13339.90158.0&amp;scm-url=1007.13339.90158.0&amp;pvid=d899bfab-a7ca-46e1-adf2-72ad1d649822) (міжнародні користувачі)&quot;">​</a></h2><p><a href="https://item.taobao.com/item.htm?spm=a1z10.5-c.w4002-21303114052.37.a28f697aeYzQx9&amp;id=594262853015" target="_blank" rel="noreferrer">CUAV Taobao</a> (користувачі з материкового Китаю)</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Автопілот можна придбати з модулем Neo GPS в комплекті</p></div><p><a id="connection"></a></p><h2 id="з-єднання-проводка" tabindex="-1">З&#39;єднання (Проводка) <a class="header-anchor" href="#з-єднання-проводка" aria-label="Permalink to &quot;З&#39;єднання (Проводка)&quot;">​</a></h2><p><a href="./../assembly/quick_start_cuav_v5_plus.html">Швидке підключення CUAV V5+</a></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p>Завантажте схему розводки <strong>V5+</strong> <a href="http://manual.cuav.net/V5-Plus.pdf" target="_blank" rel="noreferrer">звідси</a>.</p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Автопілот V5+</em> підтримує резервні джерела живлення - можна використовувати до трьох джерел: <code>Power1</code>, <code>Power2</code> та <code>USB</code>. Ви повинні подати живлення принаймні до одного з цих джерел, інакше контролер польоту буде знеструмлений.</p><div class="info custom-block"><p class="custom-block-title">У FMUv5 з модулем PX4IO (як у випадку з <em>V5+</em>) шина живлення сервоприводу контролюється тільки FMU. Вона не живиться від FMU і не забезпечує його живленням. Однак, контакти, позначені <strong>+</strong>, є загальними, і до будь-якого з наборів контактів серводвигуна може бути підключений BEC для живлення рейки живлення серводвигуна.</p></div><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li>Входи <code>Power1</code> та <code>Power2</code> (4,3 - 5,4 В)</li><li><code>USB</code> вхід (4.75В до 5.25В)</li></ol><h2 id="захист-від-перенапруги" tabindex="-1">Захист від перенапруги <a class="header-anchor" href="#захист-від-перенапруги" aria-label="Permalink to &quot;Захист від перенапруги&quot;">​</a></h2><p><em>V5+</em> має захист від перенапруги на периферійних пристроях з напругою 5 Вольт і 5 Вольт високої потужності, який обмежує струм до 2,5 А. <em>V5+</em> має захист від короткого замикання.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>На роз&#39;єми, позначені як контакт 1, можна подавати до 2,5 А (хоча вони розраховані лише на 1 А).</p></div><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана і автоматично встановлюється за допомогою <em>QGroundControl</em> при підключенні відповідного обладнання.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цього контролера:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v5_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Системна консоль <a href="./../debug/system_console.html">PX4</a> та інтерфейс <a href="./../debug/swd_debug.html">SWD</a> працюють через порт <strong>FMU Debug</strong> (<code>DSU7</code>). Плата не має інтерфейсу відладки вводу/виводу.</p><p><img src="'+t+'" alt="Debug port (DSU7)"></p><p>Відладочний порт (<code>DSU7</code>) використовує роз&#39;єм <a href="https://www.digikey.com.au/product-detail/en/jst-sales-america-inc/BM06B-GHS-TBT-LF-SN-N/455-1582-1-ND/807850" target="_blank" rel="noreferrer">JST BM06B</a> і має наступну розводку:</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>5V+</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>DEBUG TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>DEBUG RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>FMU_SWDIO</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>FMU_SWCLK</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><p>До комплекту поставки входить зручний відладочний кабель, який можна під&#39;єднати до порту <code>DSU7</code>. Він розділяє кабель FTDI для підключення <a href="./../debug/system_console.html">PX4 System Console</a> до USB-порту комп&#39;ютера і виводи SWD, які використовуються для налагодження SWD/JTAG. Відладочний кабель, що входить до комплекту, не підключається до контакту (1) порту SWD <code>Vref</code>.</p><p><img src="'+e+'" alt="Відладочний кабель CUAV"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Вивід Vref SWD (1) використовує 5В як Vref, але процесор працює при напрузі 3,3В!</p><p>Деякі JTAG-адаптери (SEGGER J-Link) використовують напругу Vref для встановлення напруги на лініях SWD. Для прямого підключення до <em>Segger Jlink</em> ми рекомендуємо використовувати 3,3 вольта з контакту 4 роз&#39;єму з позначкою <code>DSM</code>/<code>SBUS</code>/<code>RSSI</code> для забезпечення <code>Vtref</code> на JTAG (тобто для забезпечення 3,3 В і <em>NOT</em> 5 В).</p><p>За додатковою інформацією звертайтесь до розділу <a href="#using-jtag-for-hardware-debugging">Використання JTAG для апаратної налагодження</a>.</p></div><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>TELEM4</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>TX - RC-вхід з роз&#39;єму SBUS_RC</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>Debug Console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>PX4IO</td></tr></tbody></table><p><a id="optional-hardware"></a></p><h2 id="периферія" tabindex="-1">Периферія <a class="header-anchor" href="#периферія" aria-label="Permalink to &quot;Периферія&quot;">​</a></h2><ul><li><a href="https://item.taobao.com/item.htm?spm=a1z10.3-c-s.w4002-16371268452.37.6d9f48afsFgGZI&amp;id=9512463037" target="_blank" rel="noreferrer">Цифровий датчик швидкості польоту</a></li><li><a href="https://cuav.taobao.com/category-158480951.htm?spm=2013.1.w5002-16371268426.4.410b7a821qYbBq&amp;search=y&amp;catName=%CA%FD%B4%AB%B5%E7%CC%A8" target="_blank" rel="noreferrer">Телеметричні радіо модулі</a></li><li><a href="./../sensor/rangefinders.html">Далекоміри / Датчики відстані</a></li></ul><h2 id="підтримувані-платформи-airframes" tabindex="-1">Підтримувані платформи / Airframes <a class="header-anchor" href="#підтримувані-платформи-airframes" aria-label="Permalink to &quot;Підтримувані платформи / Airframes&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа чи човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. Повний перелік підтримуваних конфігурацій можна переглянути в розділі <a href="./../airframes/airframe_reference.html">Довідник планерів</a>.</p><h2 id="примітки" tabindex="-1">Примітки <a class="header-anchor" href="#примітки" aria-label="Permalink to &quot;Примітки&quot;">​</a></h2><h4 id="не-підключаите-цифровии-або-аналоговии-pm-до-роз-ємів-сконфігурованих-для-іншого-типу-pm" tabindex="-1">Не підключайте цифровий або аналоговий PM до роз&#39;ємів, сконфігурованих для іншого типу PM <a class="header-anchor" href="#не-підключаите-цифровии-або-аналоговии-pm-до-роз-ємів-сконфігурованих-для-іншого-типу-pm" aria-label="Permalink to &quot;Не підключайте цифровий або аналоговий PM до роз&#39;ємів, сконфігурованих для іншого типу PM&quot;">​</a></h4><p>Якщо ви під&#39;єднаєте аналоговий PM до цифрового роз&#39;єму PM, він зупинить всі пристрої I2C на цій шині. Зокрема, це призведе до зупинки компаса GPS через конфлікт, а також може пошкодити FMU (у довгостроковій перспективі).</p><p>Аналогічно, цифровий PM, підключений до аналогового роз&#39;єму, не працюватиме, а також може пошкодити/вивести з ладу модуль живлення (у довгостроковій перспективі).</p><h2 id="сумісність" tabindex="-1">Сумісність <a class="header-anchor" href="#сумісність" aria-label="Permalink to &quot;Сумісність&quot;">​</a></h2><p>CUAV використовує деякі відмінні дизайни і несумісний з деяким обладнанням, про що буде описано нижче.</p><p><a id="compatibility_gps"></a></p><h4 id="gps-несуміснии-з-іншими-пристроями" tabindex="-1">GPS несумісний з іншими пристроями <a class="header-anchor" href="#gps-несуміснии-з-іншими-пристроями" aria-label="Permalink to &quot;GPS несумісний з іншими пристроями&quot;">​</a></h4><p><em>Neo v2.0 GPS</em>, рекомендований для використання з <em>CUAV V5+</em> і <em>CUAV V5 nano</em>, не повністю сумісний з іншими контролерами польоту Pixhawk (зокрема, не сумісна частина сигнального зумера і можуть виникнути проблеми з перемикачем безпеки).</p><p>Пристрій прийому GNSS UAVCAN <a href="http://doc.cuav.net/gps/neo-series-gnss/en/neo-v2-pro.html" target="_blank" rel="noreferrer">NEO V2 PRO</a> також може бути використаний і сумісний з іншими контролерами польоту.</p><p><a id="compatibility_jtag"></a></p><h4 id="використання-jtag-для-апаратного-налагодження" tabindex="-1">Використання JTAG для апаратного налагодження <a class="header-anchor" href="#використання-jtag-для-апаратного-налагодження" aria-label="Permalink to &quot;Використання JTAG для апаратного налагодження&quot;">​</a></h4><p><code>DSU7</code> Штифт налагодження FMU 1 має напругу 5 вольтів - не 3,3 вольта від ЦП.</p><p>Деякі JTAG використовують цю напругу для встановлення рівнів вводу-виводу під час обміну даними з ціллю.</p><p>Для прямого підключення до <em>Segger Jlink</em> ми рекомендуємо використовувати 3,3 вольти з DSM/SBUS/RSSI пін 4 як Пін 1 на роз&#39;ємі для налагодження (<code>Vtref</code>).</p><h2 id="відомі-проблеми" tabindex="-1">Відомі проблеми <a class="header-anchor" href="#відомі-проблеми" aria-label="Permalink to &quot;Відомі проблеми&quot;">​</a></h2><p>Проблеми нижче посилаються на <em>номер партії</em>, в якій вони вперше з&#39;являються. Номер партії - це чотирицифрова дата виробництва за V01 та відображається на наклейці з боку контролера польоту. Наприклад, серійний номер партії V011904 ((V01 - це номер V5, 1904 - це дата виробництва, тобто номер партії).</p><p><a id="pin1_unfused"></a></p><h4 id="інтерфеис-sbus-dsm-rssi-pin1-не-захищении-від-перевантаження" tabindex="-1">Інтерфейс SBUS / DSM / RSSI Pin1 не захищений від перевантаження <a class="header-anchor" href="#інтерфеис-sbus-dsm-rssi-pin1-не-захищении-від-перевантаження" aria-label="Permalink to &quot;Інтерфейс SBUS / DSM / RSSI Pin1 не захищений від перевантаження&quot;">​</a></h4><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Це питання безпеки.</p></div><p>Будь ласка, не підключайте інше обладнання (крім RC приймача) до інтерфейсу SBUS / DSM / RSSI - це може призвести до пошкодження обладнання.</p><ul><li><em>Знайдено:</em> Партії V01190904xxxx</li><li><em>Виправлено:</em> Партії пізніше, ніж V01190904xxxx</li></ul><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="http://manual.cuav.net/V5-Plus.pdf" target="_blank" rel="noreferrer">Керівництво користувача CUAV V5+</a></li><li><a href="http://doc.cuav.net/flight-controller/v5-autopilot/en/v5+.html" target="_blank" rel="noreferrer">Документація CUAV V5+</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">Еталонна розводка FMUv5</a></li><li><a href="https://github.com/cuav" target="_blank" rel="noreferrer">CUAV Github</a></li><li><a href="https://github.com/cuav/hardware/tree/master/V5_Autopilot/V5%2B/V5%2BBASE" target="_blank" rel="noreferrer">Посилання на дизайн базової плати</a></li><li><a href="./../assembly/quick_start_cuav_v5_plus.html">Швидке підключення CUAV V5+</a></li><li><a href="./../frames_multicopter/dji_f450_cuav_5plus.html">Журнал збірки конструкції повітряного каркасу за допомогою CUAV v5+ на рамі DJI FlameWheel450</a></li></ul>',70),p=[s];function c(h,n,m,u,b,f){return i(),l("div",null,p)}const P=r(d,[["render",c]]);export{v as __pageData,P as default};
