import{_ as t,a as e,b as a}from"./chunks/x7-pinouts.BdNCz5F_.js";import{_ as l}from"./chunks/debug_port_dsu7.OydBcMxg.js";import{_ as r}from"./chunks/cuav_v5_debug_cable.Bplh4dyQ.js";import{_ as i,c as o,o as s,ab as c}from"./chunks/framework.CUflZczI.js";const U=JSON.parse('{"title":"Польотний контролер CUAV X7","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cuav_x7.md","filePath":"uk/flight_controller/cuav_x7.md"}'),d={name:"uk/flight_controller/cuav_x7.md"},p=c('<h1 id="польотнии-контролер-cuav-x7" tabindex="-1">Польотний контролер CUAV X7 <a class="header-anchor" href="#польотнии-контролер-cuav-x7" aria-label="Permalink to &quot;Польотний контролер CUAV X7&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який) автопілот. Звертайтесь до <a href="https://www.cuav.net" target="_blank" rel="noreferrer">виробника</a> щодо питань апаратного забезпечення або питань відповідності.</p></div><p>Контролер польоту <a href="http://doc.cuav.net/flight-controller/x7/en/x7.html" target="_blank" rel="noreferrer">X7</a><sup>®</sup> - це високоякісний автопілот. Це ідеальний вибір для промислових дронів і великомасштабних важких дронів. В основному постачається комерційним виробникам.</p><p><img src="'+t+'" alt="CUAV x7"></p><p>Контролер польоту використовує модульний дизайн і може бути поєднаний з різними базовими платами. Ви можете розробити спеціалізовану плату-носій для вашого БПЛА, щоб покращити інтеграцію комерційних систем, зменшити кількість проводів, підвищити надійність системи та підвищити конкурентоспроможність вашого БПЛА (наприклад, інтегруючи датчики швидкості повітря, телеметрію або навіть супутниковий комп&#39;ютер на платі-носії). CUAV також надає вам на вибір різноманітні несучі плати.</p><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="особливості" tabindex="-1">Особливості <a class="header-anchor" href="#особливості" aria-label="Permalink to &quot;Особливості&quot;">​</a></h2><ul><li>Внутрішнє поглинання ударів</li><li>Модульна конструкція, може бути саморобною несучою платою</li><li>Підтримка USB_HS, швидше завантаження журналів (PX4 ще не підтримується)</li><li>Підтримка більшої кількості виходів DShot</li><li>Підтримка нагріву IMU, покращення роботи датчика</li><li>Виділений порт для акумулятора CAN</li><li>3 комплекти датчиків IMU</li><li>Автомобільний компас RM3100</li><li>Високопродуктивний процесор</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Документація виробника <a href="https://doc.cuav.net/flight-controller/x7/en/" target="_blank" rel="noreferrer">CUAV Docs</a> є канонічним джерелом для X7. Вони повинні використовуватися за перевагою, оскільки вони містять найбільш повну та актуальну інформацію.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li><p>Головний FMU процесор: STM32H743</p></li><li><p>Бортові сенсори:</p><ul><li>Акселерометр/Гіроскоп: ICM-20689</li><li>Прискорювач/гіроскоп: ICM-20649</li><li>Акселерометр/Гіроскоп: BMI088</li><li>Магнітометр: RM3100</li><li>Барометр: MS5611*2</li></ul></li><li><p>Інтерфейси:</p><ul><li>14 ШІМ-виходів （12 підтримує Dshot）</li><li>Підтримка декількох входів RC (SBU / CPPM / DSM)</li><li>Аналоговий / PWM вхід RSSI</li><li>2 GPS порти (GPS і UART4 порти)</li><li>4 шини i2c (два виділені порти i2c)</li><li>2 порти CAN шини</li><li>2 порти живлення (Power A - загальний інтерфейс адаптера, Power C - інтерфейс акумулятора DroneCAN)</li><li>2 входи АЦП</li><li>1 USB порт</li></ul></li><li><p>Система живлення:</p><ul><li>Живлення: 4.3~5.4В</li><li>Вхід USB: 4.75~5.25В</li><li>Вхід сервоприводу: 0~36V</li></ul></li><li><p>Вага та розміри:</p><ul><li>Вага: 101 g</li></ul></li><li><p>Інші характеристики:</p><ul><li>Робоча температура: -20 ~ 80°c (виміряне значення)</li><li>Три імуси</li><li>Підтримка компенсації температури</li><li>Внутрішнє поглинання ударів</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Коли він працює на прошивці PX4, працює лише 8 pwm, решта 6 pwm все ще адаптуються, тому зараз він не сумісний з VOLT.</p></div><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><p><a href="https://store.cuav.net" target="_blank" rel="noreferrer">Магазин CUAV</a></p><p><a href="https://www.aliexpress.com/item/4001042683738.html?spm=a2g0o.detail.1000060.2.1ebb2a9d3WDryi&amp;gps-id=pcDetailBottomMoreThisSeller&amp;scm=1007.13339.169870.0&amp;scm_id=1007.13339.169870.0&amp;scm-url=1007.13339.169870.0&amp;pvid=f0df2481-1c0a-44eb-92a4-9c11c6cb3d06&amp;_t=gps-id:pcDetailBottomMoreThisSeller,scm-url:1007.13339.169870.0,pvid:f0df2481-1c0a-44eb-92a4-9c11c6cb3d06,tpp_buckets:668%230%23131923%2320_668%23808%234094%23518_668%23888%233325%2319_668%234328%2319934%23630_668%232846%238115%23807_668%232717%237566%23827_668%231000022185%231000066058%230_668%233468%2315607%2376" target="_blank" rel="noreferrer">CUAV Aliexpress</a></p><h2 id="з-єднання-проводка" tabindex="-1">З&#39;єднання (Проводка) <a class="header-anchor" href="#з-єднання-проводка" aria-label="Permalink to &quot;З&#39;єднання (Проводка)&quot;">​</a></h2><p><a href="http://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-x7.html" target="_blank" rel="noreferrer">Швидке підключення CUAV X7</a></p><h2 id="розмір-та-роз-єми" tabindex="-1">Розмір та роз&#39;єми <a class="header-anchor" href="#розмір-та-роз-єми" aria-label="Permalink to &quot;Розмір та роз&#39;єми&quot;">​</a></h2><p><img src="'+e+'" alt="CUAV x7"></p><p><img src="'+a+'" alt="Роз&#39;єми X7"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Порт <code>RCIN</code> призначено лише для живлення RC-приймача і його не можна підключати до жодного джерела живлення/навантаження.</p></div><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>X7 AutoPilot</em> може мати потрійне резервування джерела живлення, якщо до нього під&#39;єднано три джерела живлення. Шини живлення такі: <strong>POWERA</strong>, <strong>POWERC</strong> і <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">Вихідні шини живлення <strong>PWM OUT</strong> (від 0В до 36В) не живлять плату контролера польоту (і не живляться від неї). Ви повинні подати живлення на один з виводів <strong>POWERA</strong>, <strong>POWERC</strong> або <strong>USB</strong>, інакше плата буде знеструмлена.</p></div><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWERA</strong> та <strong>POWERC</strong> входи (від 4,3 В до 5,4 В)</li><li><strong>USB</strong> вхід (4.75В до 5.25В)</li></ol><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана і автоматично встановлюється за допомогою <em>QGroundControl</em> при підключенні відповідного обладнання.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цього контролера:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make cuav_x7pro_default</span></span></code></pre></div><h2 id="захист-від-перенапруги" tabindex="-1">Захист від перенапруги <a class="header-anchor" href="#захист-від-перенапруги" aria-label="Permalink to &quot;Захист від перенапруги&quot;">​</a></h2><p><em>X7</em> має захист від перенапруги на периферійних пристроях з напругою 5 Вольт і 5 Вольт високої потужності, який обмежує струм до 2,5 А. <em>X7</em> має захист від короткого замикання.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>На роз&#39;єми, позначені як контакт 1, можна подавати до 2,5 А (хоча вони розраховані лише на 1 А).</p></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Консоль послідовного доступу до системи та інтерфейс SWD працюють через порт <strong>DSU7</strong>. Просто підключіть кабель FTDI до роз&#39;єму DSU7 (у списку продуктів є кабель CUAV FTDI).</p><p><img src="'+l+'" alt="Debug port (DSU7)"></p><p>Системна консоль <a href="./../debug/system_console.html">PX4</a> та інтерфейс <a href="./../debug/swd_debug.html">SWD</a> працюють через порт <strong>FMU Debug</strong> (<code>DSU7</code>).</p><p>Відладочний порт (<code>DSU7</code>) використовує роз&#39;єм <a href="https://www.digikey.com.au/product-detail/en/jst-sales-america-inc/BM06B-GHS-TBT-LF-SN-N/455-1582-1-ND/807850" target="_blank" rel="noreferrer">JST BM06B</a> і має наступну розводку:</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>5V+</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>DEBUG TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>DEBUG RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>FMU_SWDIO</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>FMU_SWCLK</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><p>CUAV надає спеціальний відладочний кабель, який можна підключити до порту <code>DSU7</code>. Він розділяє кабель FTDI для підключення <a href="./../debug/system_console.html">PX4 System Console</a> до USB-порту комп&#39;ютера і виводи SWD, які використовуються для налагодження SWD/JTAG. Відладочний кабель, що входить до комплекту, не підключається до контакту (1) порту SWD <code>Vref</code>.</p><p><img src="'+r+'" alt="Відладочний кабель CUAV"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Вивід Vref SWD (1) використовує 5В як Vref, але процесор працює при напрузі 3,3В!</p><p>Деякі JTAG-адаптери (SEGGER J-Link) використовують напругу Vref для встановлення напруги на лініях SWD. Для прямого підключення до <em>Segger Jlink</em> ми рекомендуємо використовувати 3,3 вольта з контакту 4 роз&#39;єму з позначкою <code>DSM</code>/<code>SBUS</code>/<code>RSSI</code> для забезпечення <code>Vtref</code> на JTAG (тобто для забезпечення 3,3 В і <em>NOT</em> 5 В).</p></div><h2 id="підтримувані-платформи-airframes" tabindex="-1">Підтримувані платформи / Airframes <a class="header-anchor" href="#підтримувані-платформи-airframes" aria-label="Permalink to &quot;Підтримувані платформи / Airframes&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа чи човен, який може керуватися звичайними РК сервоприводами або сервоприводами Futaba S-Bus. Повний набір підтримуваних конфігурацій можна переглянути в <a href="./../airframes/airframe_reference.html">Довіднику авіаційних платформ</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-x7.html" target="_blank" rel="noreferrer">Швидкий старт</a></li><li><a href="http://doc.cuav.net" target="_blank" rel="noreferrer">Документація CUAV</a></li><li><a href="https://github.com/cuav/hardware/tree/master/X7_Autopilot" target="_blank" rel="noreferrer">схема x7</a></li></ul>',47),n=[p];function h(u,m,b,_,f,g){return s(),o("div",null,n)}const A=i(d,[["render",h]]);export{U as __pageData,A as default};
