import{_ as t}from"./chunks/cubepilot_cube_orangeplus_standard_set.DzZdU1T8.js";import{_ as d,a as r}from"./chunks/cube_ports_usb_sdcard.DjqeRsN_.js";import{_ as e,c as a,o as l,ab as i}from"./chunks/framework.CUflZczI.js";const _=JSON.parse('{"title":"Контролер польоту CubePilot Cube Orange+","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cubepilot_cube_orangeplus.md","filePath":"uk/flight_controller/cubepilot_cube_orangeplus.md"}'),o={name:"uk/flight_controller/cubepilot_cube_orangeplus.md"},h=i('<h1 id="контролер-польоту-cubepilot-cube-orange" tabindex="-1">Контролер польоту CubePilot Cube Orange+ <a class="header-anchor" href="#контролер-польоту-cubepilot-cube-orange" aria-label="Permalink to &quot;Контролер польоту CubePilot Cube Orange+&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не виробляє цей (або будь-який інший) автопілот. Зверніться до <a href="https://cubepilot.org/#/home" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p>Контролер польоту <a href="https://www.cubepilot.com/#/cube/features" target="_blank" rel="noreferrer">Cube Orange+</a> - це універсальний автопілот, призначений в першу чергу для виробників комерційних систем. Cube Orange+ схожий з Cube Orange, але має потужніший двоядерний процесор (STM32H757 та деякі інші сенсори.</p><p><img src="'+t+'" alt="Cube Orange"></p><p>Контролер призначений для використання зі специфічною для домену несучою платою, щоб зменшити кількість дротів, підвищити надійність і спростити збірку. Наприклад, несуча плата для комерційного інспекційного апарату може містити з&#39;єднання для комп&#39;ютера-компаньйона, тоді як несуча плата для пілота може містити ESC для рами транспортного засобу.</p><p>Несуча плата ADS-B включає налаштований приймач ADSB-In на 1090 МГц від uAvionix. Це забезпечує позиціонування і розташування комерційних пілотованих літаків в межах досяжності Cube. Ця функція автоматично налаштована і ввімкнена у прошивці PX4 за замовчуванням.</p><p>Cube має віброізоляцію на двох IMU, з третім фіксованим IMU в якості еталонного/резервного.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Документація виробника <a href="https://docs.cubepilot.org/user-guides/autopilot/the-cube-module-overview" target="_blank" rel="noreferrer">Cube Docs</a> містить детальну інформацію, включаючи огляд <a href="https://docs.cubepilot.org/user-guides/autopilot/the-cube-module-overview#differences-between-cube-colours" target="_blank" rel="noreferrer">Різниця між Cube Colours</a>.</p></div><h2 id="ключові-особливості" tabindex="-1">Ключові особливості <a class="header-anchor" href="#ключові-особливості" aria-label="Permalink to &quot;Ключові особливості&quot;">​</a></h2><ul><li>32-біт STM32H757ZI (32-біт <a href="https://en.wikipedia.org/wiki/ARM_Cortex-M#Cortex-M7" target="_blank" rel="noreferrer">ARM Cortex M7</a>, 400 МГц, флеш 2 МБ, оперативна пам&#39;ять 1 МБ).</li><li>32 bit STM32F103 відмовостійкий копроцесор</li><li>14 ШІМ / серво виходів (8 з відмовостійкими і ручним керуванням, 6 допоміжних, сумісних з великими потужностями)</li><li>Широкі можливості підключення додаткових периферійних пристроїв (UART, I2C, CAN)</li><li>Інтегрована система резервного копіювання для відновлення в польоті та ручного керування з виділеним процесором та автономним джерелом живлення (для літаків з фіксованим крилом)</li><li>Резервна система інтегрує систему мікшування, забезпечуючи узгоджені режими автопілота та ручного заміщення ( для літаків з фіксованим крилом)</li><li>Резервні входи живлення та автоматичне перемикання на резервне джерело</li><li>Зовнішній запобіжний вимикач</li><li>Головний візуальний індикатор - багатоколірний світлодіод</li><li>Потужний багатотональний п&#39;єзозвуковий індикатор</li><li>Карта microSD для високошвидкісної фіксації даних протягом тривалого періоду часу</li></ul><p><a id="stores"></a></p><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><ul><li><a href="https://www.cubepilot.com/#/reseller/list" target="_blank" rel="noreferrer">Список реселерів</a></li></ul><h2 id="збірка" tabindex="-1">Збірка <a class="header-anchor" href="#збірка" aria-label="Permalink to &quot;Збірка&quot;">​</a></h2><p><a href="./../assembly/quick_start_cube.html">Швидке підключення Cube</a></p><h2 id="специфікаціі" tabindex="-1">Специфікації <a class="header-anchor" href="#специфікаціі" aria-label="Permalink to &quot;Специфікації&quot;">​</a></h2><ul><li><strong>Процесор:</strong><ul><li>STM32H757 (32біт <a href="https://en.wikipedia.org/wiki/ARM_Cortex-M#Cortex-M7" target="_blank" rel="noreferrer">ARM Cortex M7</a>)</li><li>400 МГц</li><li>1 МБ RAM</li><li>2 МБ флеш-пам&#39;яті (повністю доступна)</li></ul></li><li><strong>Відмовостійкий співпроцесор:</strong><ul><li>STM32F103 (32біт <em>ARM Cortex-M3</em>)</li><li>24 МГц</li><li>8 KB SRAM</li></ul></li><li><strong>Датчики:</strong> (всі підключені через SPI) <ul><li><strong>Акселерометр:</strong> (3) ICM42688p, ICM20948, ICM20649</li><li><strong>Гіроскоп:</strong> (3) ICM42688p, ICM20948, ICM20649</li><li><strong>Компас:</strong> (1) ICM20948</li><li><strong>Барометричний датчик тиску:</strong> (2) MS5611</li></ul></li><li><strong>Умови експлуатації:</strong><ul><li><strong>Робоча температура:</strong> від -10C до 55C</li><li><strong>Степень захисту IP/Водонепроникність:</strong> Не водонепроникний</li><li><strong>Вхідна напруга серворейки:</strong> 3,3В / 5В</li><li><strong>Вхід USB-порту:</strong><ul><li>Напруга: 4В - 5.7В</li><li>Номінальний струм: 250 мА</li></ul></li><li><strong>POWER:</strong><ul><li>Вхідна напруга: 4.1В - 5.7В</li><li>Номінальний вхідний струм: 2,5 А</li><li>Номінальна вхідна/вихідна потужність: 14 Вт</li></ul></li></ul></li><li><strong>Розміри:</strong><ul><li><strong>Cube:</strong> 38.25 мм x 38.25 мм x 22.3 мм</li><li><strong>Carrier:</strong> 94,5 мм x 44,3 мм x 17,3 мм</li></ul></li><li><strong>Інтерфейси</strong><ul><li>Порти вводу-виводу: 14 ШІМ-виходів сервоприводів (8 від IO, 6 від FMU)</li><li>5x UART (послідовні порти), один високої потужності, 2x з контролем потоку ГВП</li><li>2x CAN (один з внутрішнім 3.3В трансивером, один на конекторі розширювача)</li><li><strong>Входи ПДУ (пульт дистанційного управління):</strong><ul><li>Spektrum DSM / DSM2 / DSM-X® Satellite сумісний вхід</li><li>Futaba S.BUS® сумісний вхід і вивід</li><li>Вхід сигналу PPM-SUM</li></ul></li><li>Вхід RSSI (ШІМ або напруга)</li><li>I2C</li><li>SPI</li><li>3.3В АЦП вхід</li><li>Внутрішній порт microUSB і розширення зовнішнього порту microUSB</li></ul></li></ul><h2 id="порти" tabindex="-1">Порти <a class="header-anchor" href="#порти" aria-label="Permalink to &quot;Порти&quot;">​</a></h2><h3 id="верхня-частина-gps-telem-тощо" tabindex="-1">Верхня частина (GPS, TELEM тощо) <a class="header-anchor" href="#верхня-частина-gps-telem-тощо" aria-label="Permalink to &quot;Верхня частина (GPS, TELEM тощо)&quot;">​</a></h3><p><img src="'+d+'" alt="Cube Ports - Top (GPS, TELEM etc) і Main/AUX"></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><h4 id="telem1-telem2-порти" tabindex="-1">TELEM1, TELEM2 порти <a class="header-anchor" href="#telem1-telem2-порти" aria-label="Permalink to &quot;TELEM1, TELEM2 порти&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>CTS (IN)</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>RTS (OUT)</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="gps1-порт" tabindex="-1">GPS1 порт <a class="header-anchor" href="#gps1-порт" aria-label="Permalink to &quot;GPS1 порт&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>SCL I2C2</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>SDA I2C2</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>Safety Button</td><td>GND</td></tr><tr><td>7 (blk)</td><td>Button LED</td><td>GND</td></tr><tr><td>8 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="gps2-порт" tabindex="-1">GPS2 порт <a class="header-anchor" href="#gps2-порт" aria-label="Permalink to &quot;GPS2 порт&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>SCL I2C1</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>SDA I2C1</td><td>+3.3В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="adc" tabindex="-1">ADC <a class="header-anchor" href="#adc" aria-label="Permalink to &quot;ADC&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>ADC IN</td><td>до +6.6В</td></tr><tr><td>3 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="i2c" tabindex="-1">I2C <a class="header-anchor" href="#i2c" aria-label="Permalink to &quot;I2C&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>SCL</td><td>+3.3 (pullups)</td></tr><tr><td>3 (blk)</td><td>SDA</td><td>+3.3 (pullups)</td></tr><tr><td>4 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="can1-can2" tabindex="-1">CAN1 &amp; CAN2 <a class="header-anchor" href="#can1-can2" aria-label="Permalink to &quot;CAN1 &amp; CAN2&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>CAN_H</td><td>+12В</td></tr><tr><td>3 (blk)</td><td>CAN_L</td><td>+12В</td></tr><tr><td>4 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="power1-power2" tabindex="-1">POWER1 &amp; POWER2 <a class="header-anchor" href="#power1-power2" aria-label="Permalink to &quot;POWER1 &amp; POWER2&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>3 (blk)</td><td>CURRENT sensing</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>VOLTAGE sensing</td><td>+3.3В</td></tr><tr><td>5 (blk)</td><td>GND</td><td>GND</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="usb" tabindex="-1">USB <a class="header-anchor" href="#usb" aria-label="Permalink to &quot;USB&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>OTG_DP1</td><td>+3.3В</td></tr><tr><td>3 (blk)</td><td>OTG_DM1</td><td>+3.3В</td></tr><tr><td>4 (blk)</td><td>GND</td><td>GND</td></tr><tr><td>5 (blk)</td><td>BUZZER</td><td>Напруга батареї</td></tr><tr><td>6 (blk)</td><td>FMU Error LED</td><td></td></tr></tbody></table><h4 id="spkt" tabindex="-1">SPKT <a class="header-anchor" href="#spkt" aria-label="Permalink to &quot;SPKT&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (blk)</td><td>IN</td><td></td></tr><tr><td>2 (blk)</td><td>GND</td><td>GND</td></tr><tr><td>3 (red)</td><td>OUT</td><td>+3.3В</td></tr></tbody></table><h4 id="telem1-telem2" tabindex="-1">TELEM1, TELEM2 <a class="header-anchor" href="#telem1-telem2" aria-label="Permalink to &quot;TELEM1, TELEM2&quot;">​</a></h4><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5В</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3В до 5В</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3В до 5В</td></tr><tr><td>4 (blk)</td><td>CTS (OUT)</td><td>+3.3В до 5В</td></tr><tr><td>5 (blk)</td><td>RTS (IN)</td><td>+3.3В до 5В</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART2</td><td>/dev/ttyS0</td><td>TELEM1 (контроль потоку)</td></tr><tr><td>USART3</td><td>/dev/ttyS1</td><td>TELEM2 (контроль потоку)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS3</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttyS4</td><td>CONSOLE/ADSB-IN</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>GPS2</td></tr></tbody></table><h3 id="порти-usb-sdcard" tabindex="-1">Порти USB/SDCard <a class="header-anchor" href="#порти-usb-sdcard" aria-label="Permalink to &quot;Порти USB/SDCard&quot;">​</a></h3><p><img src="'+r+'" alt="Cube USB/SDCard порти"></p><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Прошивка для Orange+ буде присутня у релізах, починаючи з PX4 v1.14.</p></div><p>Для <a href="./../dev_setup/building_px4.html">збірки PX4</a> для цієї цілі, відкрийте термінал та введіть:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make cubepilot_cubeorangeplus</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Прошивка для Cube Orange+ і Cube Orange не сумісні. У зв&#39;язку з функцією живлення STM32H757 потрібні <a href="https://github.com/PX4/NuttX/pull/214" target="_blank" rel="noreferrer">оновлення в NuttX</a> та, отже, нова конфігурація плати, завантажувач, ціль збірки тощо.</p></div><h2 id="схеми" tabindex="-1">Схеми <a class="header-anchor" href="#схеми" aria-label="Permalink to &quot;Схеми&quot;">​</a></h2><p>Схеми плат та іншу документацію можна знайти тут: <a href="https://github.com/proficnc/The-Cube" target="_blank" rel="noreferrer">Проект Cube</a>.</p><h2 id="додаткова-інформація-документація" tabindex="-1">Додаткова інформація/документація <a class="header-anchor" href="#додаткова-інформація-документація" aria-label="Permalink to &quot;Додаткова інформація/документація&quot;">​</a></h2><ul><li><a href="./../assembly/quick_start_cube.html">Швидке підключення Cube</a></li><li>Cube Docs (виробник): <ul><li><a href="https://docs.cubepilot.org/user-guides/autopilot/the-cube-module-overview" target="_blank" rel="noreferrer">Огляд модуля Cube</a></li><li><a href="https://docs.cubepilot.org/user-guides/autopilot/the-cube-user-manual" target="_blank" rel="noreferrer">Посібник користувача Cube</a></li><li><a href="https://docs.cubepilot.org/user-guides/carrier-boards/mini-carrier-board" target="_blank" rel="noreferrer">Mini Carrier Board</a></li></ul></li></ul>',54),s=[h];function b(n,u,c,p,g,k){return l(),a("div",null,s)}const f=e(o,[["render",b]]);export{_ as __pageData,f as default};
