import{_ as l,a as r,b as a,c as i}from"./chunks/FMU_Debug_Port_Horizontal.DIzdvN-v.js";import{_ as e,c as t,o,ab as s}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"Holybro Pix32 v5","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/holybro_pix32_v5.md","filePath":"uk/flight_controller/holybro_pix32_v5.md"}'),n={name:"uk/flight_controller/holybro_pix32_v5.md"},h=s('<h1 id="holybro-pix32-v5" tabindex="-1">Holybro Pix32 v5 <a class="header-anchor" href="#holybro-pix32-v5" aria-label="Permalink to &quot;Holybro Pix32 v5&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи відповідності вимогам.</p></div><p><a href="https://holybro.com/products/pix32-v5" target="_blank" rel="noreferrer">Pix32 v5</a><sup>®</sup> є передовим автопілотом керування польотом, розробленим і виготовленим компанією Holybro<sup>®</sup>. Він оптимізований для роботи з прошивкою PX4, яка призначена як для академічних, так і для комерційних розробників. Він базується на відкритому апаратному дизайні <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv5</strong> та запускає PX4 на операційній системі <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a>. Це можна вважати варіантом версії Pixhawk4.</p><p>Pix32 v5 розроблено для пілотів, які потребують потужності, гнучкої та настроюваної системи керування польотами. Він складається з окремого контролера польоту та базової плати, які з&#39;єднані 100-контактним роз&#39;ємом. Цей дизайн дозволяє користувачам вибирати базову плату від Holybro або налаштовувати свою власну.</p><p><img src="'+l+'" alt="Сімейство Pix32 v5"></p><div class="info custom-block"><p class="custom-block-title">Цей польотний контролер <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Головний FMU процесор: STM32F765 <ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB memory, 512KB RAM</li></ul></li><li>IO Processor: STM32F100 <ul><li>32 Bit Arm®️ Cortex®️-M3, 24MHz, 8KB SRAM</li></ul></li><li>Сенсори на платі: <ul><li>Accel/Gyro: ICM-20689</li><li>Accel/Gyro: BMI055 або ICM20602</li><li>Магнітометр: IST8310</li><li>Барометр: MS5611</li></ul></li><li>GPS: u-blox Neo-M8N GPS/ГЛОНАСС приймач; інтегрований магнетометр IST8310</li><li>Інтерфейси: <ul><li>8-16 PWM виходи (8 із IO, 8 із FMU)</li><li>3 спеціалізованих PWM/Capture входів на FMU</li><li>Спеціалізований R/C вхід для CPPM</li><li>Спеціалізований R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM входом RSSI</li><li>Спеціалізований S.BUS вивід сервоприводу</li><li>5 основних послідовних портів <ul><li>2 з повним контролем потоку</li><li>1 з відокремленням 1.5A поточної межі</li></ul></li><li>3x I2C порти</li><li>4 шини SPI <ul><li>1 внутрішня шина датчика високої швидкості SPI з 4 обраними чіпами і 6 DRDY</li><li>1 внутрішня SPI шина, виділена для</li><li>Барометр з 2-ма чіпами, не DRDY</li><li>1 внутрішня шина SPI виділена для FRAM</li><li>Підтримує спеціалізовану SPI калібрування EEPROM, розташовану на модулі сенсорів</li><li>1 зовнішня шина SPI</li></ul></li><li>До 2 CANBus для подвійного CAN з послідовним ESC <ul><li>Кожна CAN шина має індивідуальне керування шумом або ESC RX-MUX контроль</li><li>Аналогові входи для напруги / струму з 2 батарей</li><li>2 додаткових аналогових входи</li></ul></li></ul></li><li>Електрична система: <ul><li>Вивід енергомодуля 4.9~5.5 В</li><li>Максимальна вхідна напруга: 6 В</li><li>Максимальний струм у значенні: 120 A</li><li>Живлення USB входу: 4.75~5.25V</li><li>Вхід сервоприводу: 0~36 В</li></ul></li><li>Вага та розміри: <ul><li>Розміри: 45x45x13.5мм</li><li>Вага: 33.0гр</li></ul></li><li>Дані про навколишнє середовище, якість &amp; та надійність: <ul><li>Температура роботи: -40 ~ 85°c</li><li>Температура зберігання: -40 / 85℃</li><li>CE</li><li>FCC</li><li>Сумісний із RoHS (без свинцю)</li></ul></li></ul><p>Додаткову інформацію можна знайти в <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5_technical_data_sheet_v1.1.pdf" target="_blank" rel="noreferrer">Технічному описі Pix32 V5</a>.</p><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><p>Замовляйте на <a href="https://holybro.com/products/pix32-v5" target="_blank" rel="noreferrer">веб-сайті Holybro</a>.</p><h2 id="збірка-налаштування" tabindex="-1">Збірка / налаштування <a class="header-anchor" href="#збірка-налаштування" aria-label="Permalink to &quot;Збірка / налаштування&quot;">​</a></h2><p><a href="./../assembly/quick_start_holybro_pix32_v5.html">Швидкий старт з підключення Pix32 v5</a> надає інструкції щодо збирання необхідних/важливих периферійних пристроїв, таких як GPS, плата управління живленням тощо.</p><h2 id="макети-базовоі-плати" tabindex="-1">Макети базової плати <a class="header-anchor" href="#макети-базовоі-плати" aria-label="Permalink to &quot;Макети базової плати&quot;">​</a></h2><p><img src="'+r+'" alt="Зображення Pix32 v5"></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pix32-V5-Base-Mini-Pinouts.pdf" target="_blank" rel="noreferrer"><em>pix32 v5</em> та міні базова плата</a></p><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+a+'" alt="Зображення Pix32 v5"></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pix32 v5</em> може мати потрійну резервність у джерелі живлення, якщо подаються три джерела живлення. Три шини живлення: <strong>POWER1</strong>, <strong>POWER2</strong> і <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">Вихідні шини живлення <strong>FMU PWM OUT</strong> та <strong>I/O PWM OUT</strong> (від 0В до 36В) не живлять плату контролера польоту (і не живляться від неї). Ви повинні подавати живлення на один з <strong>POWER1</strong>, <strong>POWER2</strong> або <strong>USB</strong>, інакше плата буде знеструмлена.</p></div><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> входи (від 4,9 В до 5,5 В)</li><li><strong>USB</strong> вхід (4.75В до 5.25В)</li></ol><p><strong>Абсолютна максимальна напруга</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> і <strong>POWER2</strong> входи (робочий діапазон від 4,1 В до 5,7 В, від 0 В до 10 В без пошкоджень)</li><li><strong>USB</strong> вхід (операційний діапазон 4.1В до 5.7В, 0В до 6В без пошкоджень)</li><li>Серво-вхід: VDD_SERVO вивід <strong>FMU PWM OUT</strong> та <strong>I/O PWM OUT</strong> (від 0В до 42В без пошкоджень)</li></ol><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана і автоматично встановлюється за допомогою <em>QGroundControl</em> при підключенні відповідного обладнання.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html"> зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>зробіть holybro_durandal-v1_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Системна консоль <a href="./../debug/system_console.html">серійного порту</a> та SWD інтерфейс працюють на порту <strong>FMU Debug</strong></p><p><img src="'+i+'" alt="Діаграма порту налагодження FMU"></p><p>Роз’єм використовує стандартний <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a> інтерфейс, визначений у <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</p><h2 id="периферія" tabindex="-1">Периферія <a class="header-anchor" href="#периферія" aria-label="Permalink to &quot;Периферія&quot;">​</a></h2><ul><li><a href="./../sensor/airspeed.html">Цифровий датчик швидкості польоту</a></li><li><a href="./../telemetry/">Телеметричні радіо модулі</a></li><li><a href="./../sensor/rangefinders.html">Далекомір/Датчики відстані</a></li></ul><h2 id="підтримувані-платформи-планери" tabindex="-1">Підтримувані платформи / планери <a class="header-anchor" href="#підтримувані-платформи-планери" aria-label="Permalink to &quot;Підтримувані платформи / планери&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа чи човен, який може керуватися звичайними РК сервоприводами або сервоприводами Futaba S-Bus. Повний перелік підтримуваних конфігурацій можна переглянути в розділі <a href="./../airframes/airframe_reference.html">Довідник планерів</a>.</p><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5_technical_data_sheet_v1.1.pdf" target="_blank" rel="noreferrer">Технічна інформація по Pix32 v5</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pix32-V5-Base-Mini-Pinouts.pdf" target="_blank" rel="noreferrer">Розпіновка Pix32 v5</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5-BASE-Schematic_diagram.pdf" target="_blank" rel="noreferrer">Базова принципова схема Pix32 v5</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5-Base-Mini-Board_Schematic_diagram.pdf" target="_blank" rel="noreferrer">Базова принципова схема Pix32 v5 Mini Base Board</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">Схема роз&#39;ємів для посилання на FMUv5</a>.</li></ul>',42),p=[h];function c(d,u,_,f,b,g){return o(),t("div",null,p)}const k=e(n,[["render",c]]);export{x as __pageData,k as default};
