import{_ as a,a as r,b as l,c as d,d as o}from"./chunks/bottom.dvipQsek.js";import{_ as i,E as s,c as n,J as h,m as t,a as c,ab as f,o as g}from"./chunks/framework.CUflZczI.js";const S=JSON.parse('{"title":"ModalAI Flight Core v1","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/modalai_fc_v1.md","filePath":"uk/flight_controller/modalai_fc_v1.md"}'),m={name:"uk/flight_controller/modalai_fc_v1.md"},p=t("h1",{id:"modalai-flight-core-v1",tabindex:"-1"},[c("ModalAI Flight Core v1 "),t("a",{class:"header-anchor",href:"#modalai-flight-core-v1","aria-label":'Permalink to "ModalAI Flight Core v1"'},"​")],-1),b=f('<div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Звертайтесь до <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">виробника</a> щодо питань апаратного забезпечення або питань відповідності.</p></div><p>ModalAI <a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core v1</a> (<a href="https://docs.modalai.com/flight-core-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) є контролером польоту для PX4, виготовленим в США. Flight Core може бути поєднане з ModalAI <a href="https://modalai.com/voxl" target="_blank" rel="noreferrer">VOXL</a> (<a href="https://docs.modalai.com/voxl-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) для уникнення перешкод та навігації в умовах відсутності сигналу GPS, або використовуватися незалежно як автономний політ контролер.</p><p><img src="'+a+'" alt="FlightCoreV1"></p><p>Flight Core ідентичний з частиною контролера польоту PX4 <a href="https://www.modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>), яке інтегрує компаньйонний комп&#39;ютер VOXL та Flight Core в одну плату.</p><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Вага</td><td style="text-align:left;">6 г</td></tr><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Пам&#39;ять</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Прошивка</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Елемент захисту</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">Карта microSD</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Інформація про підтримувані картки</a></td></tr><tr><td style="text-align:left;">Вхідні дані</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Телеметрія</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN шина</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Виводи</td><td style="text-align:left;">6 світлодіодів (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 каналів PWM</td></tr><tr><td style="text-align:left;">Додаткові Інтерфейси</td><td style="text-align:left;">3 послідовні порти</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><p>::: інформація Докладнішу документацію щодо апаратного забезпечення можна знайти <a href="https://docs.modalai.com/flight-core-datasheet/" target="_blank" rel="noreferrer">тут</a>. :::</p><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+r+'" alt="FlightCoreV1Dimensions"></p><h2 id="сумісність-прошивки-px4" tabindex="-1">Сумісність прошивки PX4 <a class="header-anchor" href="#сумісність-прошивки-px4" aria-label="Permalink to &quot;Сумісність прошивки PX4&quot;">​</a></h2><p><em>Flight Core v1</em> повністю сумісний з офіційною прошивкою PX4 від PX4 v1.11.</p><p>ModalAI підтримує <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">гілку версії PX4</a> для PX4 v1.11. Це включає підтримку UART ESC та поліпшення в VIO та VOA, які планується включити в основний код.</p><p>Додаткову інформацію про прошивку можна знайти <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">тут</a>.</p><h2 id="qgroundcontrol-підтримка" tabindex="-1">QGroundControl Підтримка <a class="header-anchor" href="#qgroundcontrol-підтримка" aria-label="Permalink to &quot;QGroundControl Підтримка&quot;">​</a></h2><p>Ця плата підтримується QGroundControl 4.0 та пізнішими версіями.</p><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><ul><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Повний комплект Flight Core</a></li><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core інтегрований з супутнім комп&#39;ютером VOXL на одній платі</a></li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">Flight Core, інтегроване з комп&#39;ютером-компаньйоном VOXL та камерами уникання перешкод (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://shop.modalai.com/products/voxl-flight-deck-r1" target="_blank" rel="noreferrer">Flight Core, складений з VOXL та камерами</a></li></ul><h2 id="швидкии-старт" tabindex="-1">Швидкий Старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий Старт&quot;">​</a></h2><h3 id="орієнтація" tabindex="-1">Орієнтація <a class="header-anchor" href="#орієнтація" aria-label="Permalink to &quot;Орієнтація&quot;">​</a></h3><p>Діаграма нижче показує рекомендовану орієнтацію, яка відповідає <code>ROTATION_NONE</code> починаючи з PX4 v1.11.</p><p><img src="'+l+'" alt="FlightCoreV1Orientation"></p><h3 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h3><p>Детальну інформацію про роз&#39;єми можна знайти <a href="https://docs.modalai.com/flight-core-datasheet-connectors" target="_blank" rel="noreferrer">тут</a>.</p><p><img src="'+d+'" alt="FlightCoreV1Top"></p><table><thead><tr><th>З’єднання</th><th>Опис</th></tr></thead><tbody><tr><td>J1</td><td>Роз&#39;єм інтерфейсу зв&#39;язку VOXL (TELEM2)</td></tr><tr><td>J2</td><td>Програмний та відлагоджувальний роз&#39;єм</td></tr><tr><td>J3</td><td>USB конектор</td></tr><tr><td>J4</td><td>UART2, UART ESC (TELEM3)</td></tr><tr><td>J5</td><td>Конектор телеметрії (TELEM1)</td></tr><tr><td>J6</td><td>Введенням VOXL-Power Management / розширення</td></tr><tr><td>J7</td><td>Роз&#39;єм виводу з 8 каналами PWM</td></tr><tr><td>J8</td><td>Конектор шини CAN</td></tr><tr><td>J9</td><td>PPM RC In</td></tr><tr><td>J10</td><td>Зовнішній коннектор GPS &amp; магнітометра</td></tr><tr><td>J12</td><td>Вхід RC, Spektrum/SBus/UART конектор</td></tr><tr><td>J13</td><td>I2C Дисплей (роз&#39;єм запасного датчика) / Вхід кнопки безпеки</td></tr></tbody></table><p><img src="'+o+'" alt="FlightCoreV1Bottom"></p><h3 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h3><p>Повний посібник користувача доступний <a href="https://docs.modalai.com/flight-core-manual/" target="_blank" rel="noreferrer">тут</a>.</p><h3 id="як-зібрати" tabindex="-1">Як зібрати <a class="header-anchor" href="#як-зібрати" aria-label="Permalink to &quot;Як зібрати&quot;">​</a></h3><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цього контролера:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J10)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J4)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Консоль відлагодження (J2)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Розширення UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2, Основні зв&#39;язки VOXL (J1)</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J12)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J5)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Будь ласка, відвідайте <a href="https://forum.modalai.com/category/10/flight-core" target="_blank" rel="noreferrer">ModalAI Forum</a> для отримання додаткової інформації.</p>',36);function _(u,x,y,k,P,v){const e=s("Badge");return g(),n("div",null,[p,h(e,{type:"tip",text:"PX4 v1.11"}),b])}const M=i(m,[["render",_]]);export{S as __pageData,M as default};
