import{_ as a,a as r,b as l,c as d}from"./chunks/voxl-flight-bottom.Di9udeWe.js";import{_ as o,E as i,c as n,J as s,m as t,a as h,ab as f,o as c}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"ModalAI VOXL Flight","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/modalai_voxl_flight.md","filePath":"uk/flight_controller/modalai_voxl_flight.md"}'),g={name:"uk/flight_controller/modalai_voxl_flight.md"},p=t("h1",{id:"modalai-voxl-flight",tabindex:"-1"},[h("ModalAI VOXL Flight "),t("a",{class:"header-anchor",href:"#modalai-voxl-flight","aria-label":'Permalink to "ModalAI VOXL Flight"'},"​")],-1),x=f('<div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Звертайтесь до <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">виробника</a> щодо питань апаратного забезпечення або питань відповідності.</p></div><p>Voxl Flight від ModalAI <a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet" target="_blank" rel="noreferrer">Технічні характеристики</a>) є однією з перших обчислювальних платформ, яка поєднує потужність та вишуканість Snapdragon з гнучкістю та зручністю використання PX4 на STM32F7. Виготовлений у США, VOXL Flight підтримує уникнення перешкод та навігацію без сигналу GPS (в приміщенні), поєднуючи це з контролером польоту PX4 на одній платі.</p><p><img src="'+a+'" alt="VOXL-Flight"></p><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><h3 id="система" tabindex="-1">Система <a class="header-anchor" href="#система" aria-label="Permalink to &quot;Система&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Вага</td><td style="text-align:left;">26 г</td></tr></tbody></table><h3 id="супутні-комп-ютери" tabindex="-1">Супутні комп&#39;ютери <a class="header-anchor" href="#супутні-комп-ютери" aria-label="Permalink to &quot;Супутні комп&#39;ютери&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Базова операційна система</td><td style="text-align:left;">Linux Yocto Jethro з ядром 3.18. Додаткові операційні системи Linux можна використовувати, запускаючи Docker на VOXL, деталі <a href="https://docs.modalai.com/docker-on-voxl/" target="_blank" rel="noreferrer">тут</a></td></tr><tr><td style="text-align:left;">Обчислення</td><td style="text-align:left;">Qualcomm Snapdragon 821 з 4 ГБ LPDDR4 1866 МГц, Snapdragon 821 <a href="https://developer.qualcomm.com/download/sd820e/qualcomm-snapdragon-820e-processor-apq8096sge-device-specification.pdf" target="_blank" rel="noreferrer">Datasheet</a>, <a href="https://developer.qualcomm.com/hardware/apq-8096sg/tools" target="_blank" rel="noreferrer">Документи</a></td></tr><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">Чотирьохядерний процесор до 2,15 ГГц</td></tr><tr><td style="text-align:left;">GPU</td><td style="text-align:left;">Графічний процесор Adreno 530 на частоті 624МГц</td></tr><tr><td style="text-align:left;">Обчислення DSP</td><td style="text-align:left;">Шестизначний цифровий сигнальний процесор (cDSP) 825МГц</td></tr><tr><td style="text-align:left;">Сенсор DSP</td><td style="text-align:left;">Шестизначний сенсор DSP (sDSP) 700МГц</td></tr><tr><td style="text-align:left;">Відео</td><td style="text-align:left;">Захоплення відео 4k30 h.264/5 w/ 720p FPV</td></tr><tr><td style="text-align:left;">Інтерфейси камери</td><td style="text-align:left;">Підтримка MIPI-CSI2, USB UVC, HDMI</td></tr><tr><td style="text-align:left;">Wi-Fi</td><td style="text-align:left;">Ппопередньо сертифікований модуль Wi-Fi, <a href="https://fccid.io/PPD-QCNFA324" target="_blank" rel="noreferrer">QCNFA324 FCC ID:PPD-QCNFA324</a>, модем QCA6174A, 802.11ac 2x2 Dual-band, Bluetooth 4.2 (двохрежимний)</td></tr><tr><td style="text-align:left;">4G LTE</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-lte" target="_blank" rel="noreferrer">Додатковий модуль, що доступний за вибором</a></td></tr><tr><td style="text-align:left;">Microhard pDDL</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-microhard-modem-usb-hub" target="_blank" rel="noreferrer">Додатковий модуль, що доступний за вибором</a></td></tr><tr><td style="text-align:left;">GNSS</td><td style="text-align:left;">WGR7640 10Гц</td></tr><tr><td style="text-align:left;">I/O</td><td style="text-align:left;">1x USB3.0 OTG (порт ADB), 1x USB2.0 (порт розширення), 2x UART, 3x I2C, додаткові GPIO та SPI можуть бути налаштовані</td></tr><tr><td style="text-align:left;">Сховище</td><td style="text-align:left;">32ГБ (UFS 2.0), картка Micro SD</td></tr><tr><td style="text-align:left;">Програмне забезпечення</td><td style="text-align:left;">Docker, OpenCV 2.4.11, 3.4.6, 4.2, ROS Indigo, SDK Qualcomm Machine Vision SDK, подивіться на <a href="https://gitlab.com/voxl-public" target="_blank" rel="noreferrer">GitLab</a> безліч прикладів з відкритим кодом!</td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;">ICM-42688 (SPI10), ICM-20948 (SPI1)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;">BMP280</td></tr></tbody></table><h3 id="політнии-контролер" tabindex="-1">Політний контролер <a class="header-anchor" href="#політнии-контролер" aria-label="Permalink to &quot;Політний контролер&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216МГц, 32-бітний ARM М7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Пам&#39;ять</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Прошивка</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/release/1.15/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Елемент захисту</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">Карта microSD</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Інформація про підтримувані картки</a></td></tr><tr><td style="text-align:left;">Входи</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Телеметрія</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN шина</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Виходи</td><td style="text-align:left;">6 LEDs (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 PWM каналів</td></tr><tr><td style="text-align:left;">Додаткові інтерфейси</td><td style="text-align:left;">3 послідовні порти</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Докладнішу документацію щодо апаратного забезпечення можна знайти <a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">тут</a>.</p></div><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+r+'" alt="FlightCoreV1Dimensions"></p><p><a href="https://storage.googleapis.com/modalai_public/modal_drawings/M0019_VOXL-Flight.zip" target="_blank" rel="noreferrer">Файл 3D STEP</a></p><h2 id="сумісність-прошивки-px4" tabindex="-1">Сумісність прошивки PX4 <a class="header-anchor" href="#сумісність-прошивки-px4" aria-label="Permalink to &quot;Сумісність прошивки PX4&quot;">​</a></h2><p><em>Політ VOXL</em> повністю сумісний з офіційною прошивкою PX4 від PX4 v1.11.</p><p>ModalAI підтримує <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">гілка версії PX4</a> для PX4 v1.11. Це включає підтримку UART ESC та поліпшення в VIO та VOA, які планується включити в основний код.</p><p>Додаткову інформацію про прошивку можна знайти <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">тут</a>.</p><h2 id="підтримка-qgroundcontrol" tabindex="-1">Підтримка QGroundControl <a class="header-anchor" href="#підтримка-qgroundcontrol" aria-label="Permalink to &quot;Підтримка QGroundControl&quot;">​</a></h2><p>Ця плата підтримується в QGroundControl 4.0 та пізніше.</p><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><ul><li><a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">Комплект VOXL Flight Complete</a></li><li><a href="https://www.modalai.com/products/voxl-flight?variant=31707275362355" target="_blank" rel="noreferrer">Плата керування VOXL Flight</a> (тільки)</li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">VOXL Flight, інтегрований з камерами уникання перешкод (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://www.modalai.com/collections/development-drones/products/voxl-m500" target="_blank" rel="noreferrer">VOXL Flight в готовому до польоту Development Drone VOXL m500</a> (<a href="https://docs.modalai.com/voxl-m500-reference-drone-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li></ul><h2 id="швидкии-старт" tabindex="-1">Швидкий старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий старт&quot;">​</a></h2><p>Quickstarts від постачальника розташовані <a href="https://docs.modalai.com/voxl-flight-quickstart/" target="_blank" rel="noreferrer">тут</a>.</p><h3 id="voxl-vision-px4" tabindex="-1">voxl-vision-px4 <a class="header-anchor" href="#voxl-vision-px4" aria-label="Permalink to &quot;voxl-vision-px4&quot;">​</a></h3><p>VOXL Flight виконує <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">voxl-vision-px4</a> на обчислювальній частині апаратного забезпечення, що служить в якості виду посередника MAVLink. Для отримання детальної інформації вихідний код доступний <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">тут</a></p><h3 id="конектори" tabindex="-1">Конектори <a class="header-anchor" href="#конектори" aria-label="Permalink to &quot;Конектори&quot;">​</a></h3><p>Докладну інформацію про розпіновку можна знайти <a href="https://docs.modalai.com/voxl-flight-datasheet-connectors/" target="_blank" rel="noreferrer">тут</a>.</p><h4 id="top" tabindex="-1">Top <a class="header-anchor" href="#top" aria-label="Permalink to &quot;Top&quot;">​</a></h4><p><img src="'+l+'" alt="VOXLFlightTop"></p><p><em>Примітка: Роз&#39;єми серії 1000 доступні з STM32/PX4</em></p><table><thead><tr><th>З’єднання</th><th>Опис</th><th>Використовується</th></tr></thead><tbody><tr><td>J2</td><td>Датчик зображення 4k (CSI0) високої якості</td><td>Snapdragon - Linux</td></tr><tr><td>J3</td><td>Стерео Датчик Зображення (CSI1)</td><td>Snapdragon - Linux</td></tr><tr><td>J6</td><td>Конектор вентилятора охолодження</td><td>Snapdragon - Linux</td></tr><tr><td>J7</td><td>BLSP6 (GPIO) та BLSP9 (UART)</td><td>Snapdragon - Linux</td></tr><tr><td>J13</td><td>Expansion B2B</td><td>Snapdragon - Linux</td></tr><tr><td>J14</td><td>Підключення вбудованої антени GNSS</td><td>Snapdragon - Linux</td></tr><tr><td>J1001</td><td>Програмний та відлагоджувальний/UART3</td><td>STM32 - PX4</td></tr><tr><td>J1002</td><td>UART ESC, UART2/TELEM3</td><td>STM32 - PX4</td></tr><tr><td>J1003</td><td>PPM RC In</td><td>STM32 - PX4</td></tr><tr><td>J1004</td><td>Вхід RC, Spektrum/SBus/UART6</td><td>STM32 - PX4</td></tr><tr><td>J1006</td><td>Роз&#39;єм USB 2.0 (PX4/QGroundControl)</td><td>STM32 - PX4</td></tr><tr><td>J1007</td><td>8-Канальний вихід PWM/DShot</td><td>STM32 - PX4</td></tr><tr><td>J1008</td><td>CAN шина</td><td>STM32 - PX4</td></tr><tr><td>J1009</td><td>I2C3, UART4</td><td>STM32 - PX4</td></tr><tr><td>J1010</td><td>Телеметрія (TELEM1)</td><td>STM32 - PX4</td></tr><tr><td>J1011</td><td>I2C2, кнопка безпеки</td><td>STM32 - PX4</td></tr><tr><td>J1012</td><td>Зовнішній GPS &amp; Mag, UART1, I2C1</td><td>STM32 - PX4</td></tr><tr><td>J1013</td><td>Вхід живлення, I2C3</td><td>STM32 - PX4 (живить весь систему)</td></tr></tbody></table><h4 id="низ" tabindex="-1">Низ <a class="header-anchor" href="#низ" aria-label="Permalink to &quot;Низ&quot;">​</a></h4><p><img src="'+d+'" alt="VOXLFlightBottom"></p><p><em>Примітка: Роз&#39;єми серії 1000 доступні з STM32/PX4</em></p><table><thead><tr><th>З’єднання</th><th>Опис</th><th>Використовується</th></tr></thead><tbody><tr><td>J4</td><td>Датчик відстеження / оптичного потоку зображення (CSI2)</td><td>Snapdragon - Linux</td></tr><tr><td>J8</td><td>USB 3.0 OTG</td><td>Snapdragon - Linux, <strong>adb</strong></td></tr><tr><td>J10</td><td>BLSP7 UART та I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>J11</td><td>BLSP12 UART та I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>VOXL microSD</td><td></td><td>Snapdragon - Linux</td></tr><tr><td>PX4 microSD</td><td>32Gb Max</td><td>STM32 - PX4</td></tr><tr><td>Wi-Fi Antennas</td><td>У комплекті</td><td>Snapdragon - Linux</td></tr></tbody></table><h3 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h3><p>Повний посібник користувача доступний <a href="https://docs.modalai.com/voxl-flight-quickstart" target="_blank" rel="noreferrer">тут</a>.</p><h3 id="як-зібрати" tabindex="-1">Як зібрати <a class="header-anchor" href="#як-зібрати" aria-label="Permalink to &quot;Як зібрати&quot;">​</a></h3><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><p><em>Примітка: відображені відображення стосуються лише керованих інтерфейсів PX4</em></p><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J1012)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J1002)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Консоль відладки (J1001)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Розширення UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>UART між PX4 та Комп&#39;ютером-компаньйоном</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J1004)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J1010)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Будь ласка, відвідайте <a href="https://forum.modalai.com/category/8/voxl-flight" target="_blank" rel="noreferrer">Форум ModalAI</a> для отримання додаткової інформації.</p>',47);function m(b,u,y,S,P,_){const e=i("Badge");return c(),n("div",null,[p,s(e,{type:"tip",text:"PX4 v1.11"}),x])}const M=o(g,[["render",m]]);export{T as __pageData,M as default};
