import{_ as t,a as r,b as e,c as o}from"./chunks/mro_control_pinouts.lUp8_FjF.js";import{_ as a,c as l,o as i,ab as n}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Контролер польоту mRo Control Zero F7","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mro_control_zero_f7.md","filePath":"uk/flight_controller/mro_control_zero_f7.md"}'),s={name:"uk/flight_controller/mro_control_zero_f7.md"},d=n('<h1 id="контролер-польоту-mro-control-zero-f7" tabindex="-1">Контролер польоту mRo Control Zero F7 <a class="header-anchor" href="#контролер-польоту-mro-control-zero-f7" aria-label="Permalink to &quot;Контролер польоту mRo Control Zero F7&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Зверніться до <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки обладнання або питань сумісності.</p></div><p><em>mRo Control Zero F7<sup>®</sup></em> - це новий контролер польоту від mRo.</p><p><img src="'+t+'" alt="mRo Control Zero F7"></p><p>Це безкомпромісний трикомпонентний комерційний контролер польоту з трьома IMU. Це включає 8 виходів PWM (здатні до DShot), 3x IMU, 1x Магнітомер, 1x Датчик атмосферного тиску (Висотомір), 6x UART та SD-карту, все це зберігається на PCB розміром 32 мм x 20 мм. PWM-сигнали здійснюють двосторонню комунікацію, захищені від EMI і переносяться до рівнів логіки 5В. Усі елементи доступні за допомогою передніх та задніх роз&#39;ємів Molex PicoClasp з 30 контактами. Міцний пластиковий корпус, конформне покриття плати та опціональне калібрування температури включені.</p><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="ключові-особливості" tabindex="-1">Ключові особливості <a class="header-anchor" href="#ключові-особливості" aria-label="Permalink to &quot;Ключові особливості&quot;">​</a></h2><ul><li><p>Мікропроцесор:</p><ul><li>32-bit STM32F777 Cortex<sup>®</sup> M4 core with FPU rev. 3</li><li>216 MHz/512 KB RAM/2 MB Flash</li><li>F-RAM Cypress MF25V02-G 256-Кбіт неволатильна пам&#39;ять (Flash-пам&#39;ять, яка працює так же швидко, як RAM)</li></ul></li><li><p>Датчики:</p><ul><li><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">Bosch BMI088</a> 3 осевий прискорювач/гіроскоп (внутрішньо відновлено вібрацію)</li><li><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">Invensense ICM-20602</a> 3 осевий прискорювач/гіроскоп</li><li><a href="https://www.invensense.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">Invensense ICM-20948</a> 3 осевий прискорювач/гіроскоп/магнітометр</li><li><a href="https://www.infineon.com/cms/en/product/sensor/pressure-sensors/pressure-sensors-for-iot/dps310/" target="_blank" rel="noreferrer">Барометр Infineon DPS310</a> (Такий плавний і НЕ чутливий до світла)</li></ul></li><li><p>Інтерфейси:</p><ul><li>6x UART (загальна кількість послідовних портів), 3x з HW керуванням потоком, 1x FRSky Telemetry (типи D або Х), 1x Консоль та 1x GPS+I2C</li><li>8x Виходи PWM (всі здатні до DShot)</li><li>1x CAN</li><li>1x I2C</li><li>1x SPI</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible вхід та біндинг</li><li>Вхід сумісний із Futaba S.BUS® &amp; S.BUS2®</li><li>Вивід телеметрії порту FRSky</li><li>Graupner SUMD</li><li>Yuneec ST24</li><li>PPM сигнал входу суми</li><li>1x JTAG (Роз&#39;єм TC2030)</li><li>1x RSSI (PWM чи вольтаж) вхід</li><li>LED триколор</li></ul></li><li><p>Вага та розміри (без кейса):</p><ul><li>Вага: 5.3г (0.19oz)</li><li>Ширина: 20 мм (0.79&quot;)</li><li>Довжина: 32 мм (1,26&quot;)</li></ul></li><li><p>Система живлення:</p><ul><li>3x Низькозатратний лінійний регулятор напруги Ultra Low Noise LDO</li></ul></li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">mRo Control Zero</a></li></ul><h2 id="створення-прошивки" tabindex="-1">Створення прошивки <a class="header-anchor" href="#створення-прошивки" aria-label="Permalink to &quot;Створення прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно створювати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make mro_ctrl-zero-f7</span></span></code></pre></div><h2 id="відладочні-порти" tabindex="-1">Відладочні порти <a class="header-anchor" href="#відладочні-порти" aria-label="Permalink to &quot;Відладочні порти&quot;">​</a></h2><h3 id="порт-консолі" tabindex="-1">Порт Консолі <a class="header-anchor" href="#порт-консолі" aria-label="Permalink to &quot;Порт Консолі&quot;">​</a></h3><p><a href="./../debug/system_console.html">Консоль системи PX4</a> працює на <code>USART7</code>, використовуючи перераховані нижче контакти. Це стандартна послідовна розпіновка, призначена для підключення до кабелю <a href="https://www.digikey.com/en/products/detail/TTL-232R-3V3/768-1015-ND/1836393" target="_blank" rel="noreferrer">3,3 В FTDI</a> (толерантного на 5 В).</p><table><thead><tr><th>mRo control zero f7</th><th></th><th>FTDI</th></tr></thead><tbody><tr><td>17</td><td>USART7 Tx</td><td>5</td></tr><tr><td>19</td><td>USART7 Rx</td><td>4</td></tr><tr><td>6</td><td>USART21 GND</td><td>1</td></tr></tbody></table><h3 id="swd-port" tabindex="-1">SWD Port <a class="header-anchor" href="#swd-port" aria-label="Permalink to &quot;SWD Port&quot;">​</a></h3><p>Порт <a href="./../debug/swd_debug.html">SWD</a> (JTAG) для відлагодження FMU є роз&#39;ємом відлагодження TC2030, як показано нижче.</p><p><img src="'+r+'" alt="mro swd port"></p><p>Ви можете використовувати кабель <a href="https://www.tag-connect.com/" target="_blank" rel="noreferrer">Tag Connect</a> кабель <a href="https://www.tag-connect.com/product/tc2030-idc-nl" target="_blank" rel="noreferrer">TC2030 IDC NL</a> нижче (зі відповідним <a href="https://www.tag-connect.com/product/tc2030-clip-retaining-clip-board-for-tc2030-nl-cables" target="_blank" rel="noreferrer">кліпом утримування</a>) для підключення до зонда BlackMagic, або до відлагоджувача ST-LINK V2.</p><p><img src="'+e+'" alt="tc2030 idc nl cable"></p><p>Також існує <a href="https://www.tag-connect.com/product/arm20-ctx-20-pin-to-tc2030-idc-adapter-for-cortex" target="_blank" rel="noreferrer">адаптер ARM20-CTX 20-Pin до TC2030-IDC</a>, який може бути використаний з іншими відлагоджувальними зондами.</p><h2 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h2><p><img src="'+o+'" alt="mRo Control Zero F7"></p><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART2</td><td>/dev/ttyS0</td><td>TELEM1 (контроль потоку)</td></tr><tr><td>USART3</td><td>/dev/ttyS1</td><td>TELEM2 (контроль потоку)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS3</td><td>Flex порт (можна налаштувати як SPI або UART із Flow Control).</td></tr><tr><td>UART7</td><td>/dev/ttyS4</td><td>CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>Вільний послідовний порт (зазвичай для телеметрії FrSky)</td></tr></tbody></table><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="https://mrobotics.io/introducing-the-new-mro-control-zero-autopilot/" target="_blank" rel="noreferrer">Представлення нового mRo Control Zero Autopilot</a> (блог)</li><li><a href="https://mrobotics.io/mrocontrolzero/" target="_blank" rel="noreferrer">Короткий посібник для початку роботи</a></li></ul>',30),c=[d];function h(p,m,u,_,b,f){return i(),l("div",null,c)}const x=a(s,[["render",h]]);export{g as __pageData,x as default};
