import{_ as t,a as e,b as a}from"./chunks/mro_x21_wiring.D9V6_9jN.js";import{_ as r,c as l,o as i,ab as o}from"./chunks/framework.CUflZczI.js";const S=JSON.parse('{"title":"mRo-X2.1 Autopilot","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mro_x2.1.md","filePath":"uk/flight_controller/mro_x2.1.md"}'),s={name:"uk/flight_controller/mro_x2.1.md"},d=o('<h1 id="mro-x2-1-autopilot" tabindex="-1">mRo-X2.1 Autopilot <a class="header-anchor" href="#mro-x2-1-autopilot" aria-label="Permalink to &quot;mRo-X2.1 Autopilot&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Зверніться до <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки обладнання або питань сумісності.</p></div><p>Автопілот <a href="http://www.mRobotics.io/" target="_blank" rel="noreferrer">mRo-X2.1</a> базується на <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> відкритому апаратному забезпеченні. Він виконує PX4 на ОС <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a>.</p><p><img src="'+t+'" alt="mRo X2.1"></p><div class="info custom-block"><p class="custom-block-title">Цей контролер польоту <a href="./../flight_controller/autopilot_manufacturer_supported.html">підтримується виробником</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Основна System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: STM32F427VIT6 ARM<sup>®</sup> мікроконтроллер - Revision 3</li><li>ІО: мікроконтролер STM32F100C8T6 ARM<sup>®</sup></li></ul></li><li>Датчики: <ul><li>Invensense<sup>®</sup> MPU9250 9DOF</li><li>Invensense ICM-20602 6DOF</li><li>MEAS MS5611 барометр</li></ul></li><li>Розміри/Вага <ul><li>Розміри: 36мм x 50мм (Можна замовити з вертикальним, горизонтальним або без голівок)</li><li>Точки кріплення: 30,5 мм х 30,5 мм діаметр 3,2 мм</li><li>Вага: 10.9г</li></ul></li></ul><p>Діаграма нижче надає порівняльний аналіз з Pixhawk 1. mRo має практично ідентичне апаратне забезпечення й підключення, але має значно менший слід. Основні відмінності - це оновлені датчики та Rev 3 FMU.</p><p><img src="'+e+'" alt="Mro Pixhawk 1 vs X2.1 comparison"></p><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><ul><li>2.54 мм головки:</li><li>GPS (UART4) з I2C</li><li>CAN шина</li><li>Вхід RC</li><li>PPM вхід</li><li>Spektrum input</li><li>RSSI вхід</li><li>sBus вхід</li><li>sBus вихід</li><li>Вхід живлення</li><li>Buzzer output</li><li>Вихід світлодіода</li><li>8 x Виводи сервоприводів</li><li>6 x Aux виходів</li><li>Позабортовий конектор microUSB</li><li>Вимкнути вивід Pin <em>(Зараз не підтримується програмним забезпеченням)</em></li><li>AirSpeed Sensor</li><li>USART2 (Telem 1)</li><li>USART3 (Telem 2)</li><li>UART7 (Console)</li><li>UART8 (OSD)</li></ul><h2 id="проблема-px4-bootloader" tabindex="-1">Проблема PX4 BootLoader <a class="header-anchor" href="#проблема-px4-bootloader" aria-label="Permalink to &quot;Проблема PX4 BootLoader&quot;">​</a></h2><p>За замовчуванням mRo X2.1 може бути попередньо налаштований на ArduPilot<sup>®</sup>, а не на PX4. Це можна побачити під час оновлення прошивки, коли плата визнається як FMUv2 замість X2.1.</p><p>У цьому випадку вам потрібно оновити BootLoader, використовуючи <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>. Якщо це виправлення не буде зроблено, ваша пеленга буде відображена неправильно і надмірний інерціальний модуль не буде виявлено.</p><p>Основні кроки:</p><ol><li>Завантажте та розпакуйте <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>.</li><li>Знайдіть папку <em>BL_Update_X21</em>. Ця папка містить файл <strong>bin</strong> та підпапку з назвою <strong>/etc</strong>, що містить файл <strong>rc.txt</strong></li><li>Скопіюйте ці файли на кореневий каталог вашої micro SD-карти та вставте її в mRO x2.1</li><li>Увімкніть mRO x2.1. Зачекайте, доки він завантажиться, а потім перезавантажте 1 раз.</li></ol><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><p>Цей продукт можна замовити в <a href="https://store.mrobotics.io/mRo-X2-1-Rev-2-p/m10021a.htm" target="_blank" rel="noreferrer">магазині mRobotics<sup>®</sup></a>.</p><h2 id="посібник-з-написання" tabindex="-1">Посібник з написання <a class="header-anchor" href="#посібник-з-написання" aria-label="Permalink to &quot;Посібник з написання&quot;">​</a></h2><p><img src="'+a+'" alt="mRo_X2.1_Wiring"></p><h2 id="створення-прошивки" tabindex="-1">Створення прошивки <a class="header-anchor" href="#створення-прошивки" aria-label="Permalink to &quot;Створення прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно створювати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make mro_x21_default</span></span></code></pre></div><h2 id="схема" tabindex="-1">Схема <a class="header-anchor" href="#схема" aria-label="Permalink to &quot;Схема&quot;">​</a></h2><p>Плата задокументована на апаратному репозиторії mRo: <a href="https://github.com/mRoboticsIO/Hardware/blob/master/X2.1/Docs/x21_V2_schematic.pdf" target="_blank" rel="noreferrer">x21_V2_schematic.pdf</a>.</p><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Девайс</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>SERIAL1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS/I2C</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>SERIAL5 CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>SERIAL4</td></tr></tbody></table>',28),p=[d];function n(h,c,u,m,_,b){return i(),l("div",null,p)}const R=r(s,[["render",n]]);export{S as __pageData,R as default};
