import{_ as e,a,b as d,c as l,d as i,e as o,f as h}from"./chunks/arm_10pin_jtag_connector_pinout.BunWLrXM.js";import{_ as s,c as n,o as c,ab as r,m as t}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"3DR Pixhawk 1 Flight Controller (знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk.md","filePath":"uk/flight_controller/pixhawk.md"}'),p={name:"uk/flight_controller/pixhawk.md"},b=r('<h1 id="_3dr-pixhawk-1-flight-controller-знято-з-виробництва" tabindex="-1">3DR Pixhawk 1 Flight Controller (знято з виробництва) <a class="header-anchor" href="#_3dr-pixhawk-1-flight-controller-знято-з-виробництва" aria-label="Permalink to &quot;3DR Pixhawk 1 Flight Controller (знято з виробництва)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Цей політний контролер був <a href="./../flight_controller/autopilot_experimental.html">знятий з виробництва</a> і більше не продається комерційно. Ви можете використовувати <a href="./../flight_controller/mro_pixhawk.html">mRo Pixhawk</a> як заміну.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не виробляє цей (або будь-який) автопілот. Звертайтесь до виробника з питань щодо підтримки або відповідності.</p></div><p>Автопілот <em>3DR Pixhawk<sup>®</sup> 1</em> є популярним універсальним політним контролером на основі відкритого апаратного дизайну <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv2</strong> (він поєднує функціональність PX4FMU + PX4IO). Він виконує PX4 на ОС <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a>.</p><p><img src="'+e+'" alt="Зображення Pixhawk"></p><p>Інструкції зі збирання/налаштування для використання з PX4 наведено тут: <a href="./../assembly/quick_start_pixhawk.html">Швидкий старт з підключення Pixhawk</a></p><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li>Основна System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: 180 MHz ARM<sup>®</sup> Cortex<sup>®</sup> M4 з одинарною точністю FPU</li><li>RAM: 256 KB SRAM (L1)</li></ul></li><li>Failsafe System-on-Chip: STM32F100 <ul><li>CPU: 24 MHz ARM Cortex M3</li><li>RAM: 8 KB SRAM</li></ul></li><li>Wifi: ESP8266 зовнішній</li><li>GPS: u-blox<sup>®</sup> 7/8 (Hobbyking<sup>®</sup>) / u-blox 6 (3D Robotics)</li><li>Оптичний потік: <a href="./../sensor/px4flow.html">PX4 Flow unit</a></li><li>Резервні входи живлення та автоматичне перемикання на резервне джерело</li><li>Зовнішній запобіжний вимикач</li><li>Головний візуальний індикатор - мультиколірний світлодіод</li><li>Потужний мультитональний п&#39;єзозвуковий індикатор</li><li>microSD карта для високошвидкісного логування даних протягом тривалого періоду</li></ul><p>Підключення</p><ul><li>1x I2C</li><li>1x CAN (2x опційно)</li><li>1x ADC</li><li>4x UART (2x з контролем потоку)</li><li>1x Консоль</li><li>8x PWM з ручним перевизначенням</li><li>6x PWM / GPIO / PWM вхід</li><li>S.BUS / PPM / Spektrum вхід</li><li>S.BUS вивід</li></ul><h1 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h1><p>Ця плата, що оригінально вироблялася компанією 3DR®, була стандартною платформою мікроконтролера для PX4®. Так, як плата більше не виробляється 3DR, ви можете використовувати <a href="./../flight_controller/mro_pixhawk.html">mRo Pixhawk</a> як заміну.</p><p>Замовте mRo Pixhawk з:</p><ul><li><a href="https://store.mrobotics.io/Genuine-PixHawk-1-Barebones-p/mro-pixhawk1-bb-mr.htm" target="_blank" rel="noreferrer">Bare Bones</a> - просто плата (корисна як заміна 3DR Pixhawk)</li><li><a href="https://store.mrobotics.io/Genuine-PixHawk-Flight-Controller-p/mro-pixhawk1-minkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Essential Kit</a> - включає все, крім телеметрії радіо</li><li><a href="https://store.mrobotics.io/product-p/mro-pixhawk1-fullkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Cool Kit! (Limited edition)</a> - включає все необхідне, включаючи телеметрію радіо</li></ul><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><h3 id="процесор" tabindex="-1">Процесор <a class="header-anchor" href="#процесор" aria-label="Permalink to &quot;Процесор&quot;">​</a></h3><ul><li>32bit STM32F427 <a href="http://en.wikipedia.org/wiki/ARM_Cortex-M#Cortex-M4" target="_blank" rel="noreferrer">Cortex-M4F</a> ядро з FPU</li><li>168 MHz</li><li>256 KB RAM</li><li>2 MB Flash</li><li>32 bit STM32F103 відмовостійкий копроцесор</li></ul><h3 id="датчики" tabindex="-1">Датчики <a class="header-anchor" href="#датчики" aria-label="Permalink to &quot;Датчики&quot;">​</a></h3><ul><li>ST Micro L3GD20H 16 bit гіроскоп</li><li>ST Micro LSM303D 14 bit акселерометр / магнітометр</li><li>Invensense MPU 6000 3-вісний акселерометр/гіроскоп</li><li>MEAS MS5611 барометр</li></ul><h3 id="інтерфеиси" tabindex="-1">Інтерфейси <a class="header-anchor" href="#інтерфеиси" aria-label="Permalink to &quot;Інтерфейси&quot;">​</a></h3><ul><li>5x UART (послідовні порти), один високої потужності, 2x з HW контролем потоку</li><li>2x CAN (один з внутрішнім 3.3V трансивером, один на конекторі розширення)</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite сумісний вхід</li><li>Futaba S.BUS® сумісний вхід і вивід</li><li>PPM sum signal вхід</li><li>RSSI (PWM чи voltage) вхід</li><li>I2C</li><li>SPI</li><li>3.3 та 6.6V ADC входи</li><li>Внутрішній порт microUSB і розширення зовнішнього порту microUSB</li></ul>',21),u=t("p",null,[t("div",{class:"embed-responsive embed-responsive-16by9"},[t("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/gCCC5A-Bvv4",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),k=r('<h3 id="система-живлення-та-захист" tabindex="-1">Система живлення та захист <a class="header-anchor" href="#система-живлення-та-захист" aria-label="Permalink to &quot;Система живлення та захист&quot;">​</a></h3><ul><li>Ідеальний діодний контролер з автоматичним перемиканням</li><li>Сервопривід високої потужності (max. 10V) і сильного струму (10A+)</li><li>Усі периферійні виводи захищені від перевантаження по струму, усі входи захищені від електростатичного розряду</li></ul><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p>Pixhawk може мати потрійну резервність у джерелі живлення, якщо подаються три джерела живлення. Три шини: вхід модуля живлення, вхід сервоприводу, вхід USB.</p><h3 id="максимальна-напруга-нормальноі-роботи" tabindex="-1">Максимальна напруга нормальної роботи <a class="header-anchor" href="#максимальна-напруга-нормальноі-роботи" aria-label="Permalink to &quot;Максимальна напруга нормальної роботи&quot;">​</a></h3><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи</p><ul><li>Вхід модуля живлення (4.8V to 5.4V)</li><li>Вхід сервоприводу (4.8V to 5.4V) <strong>ДО 10V ДЛЯ РУЧНОГО ПЕРЕКЛЮЧЕННЯ, АЛЕ АВТОПІЛОТ БУДЕ ЗНЕЖИВЛЕНИЙ ВИЩЕ 5.7V, ЯКЩО ВХІД МОДУЛЯ ЖИВЛЕННЯ НЕ ПРИСУТНІЙ</strong></li><li>Вхід живлення USB (4.8V до 5.4V)</li></ul><h3 id="абсолютна-максимальна-напруга" tabindex="-1">Абсолютна максимальна напруга <a class="header-anchor" href="#абсолютна-максимальна-напруга" aria-label="Permalink to &quot;Абсолютна максимальна напруга&quot;">​</a></h3><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ul><li>Вхід модуля живлення (4.1V до 5.7V, 0V до 20V неушкоджений)</li><li>Вхід сервоприводу (4.1V до 5.7V, 0V до 20V)</li><li>Вхід живлення USB (4.1V до 5.7V, 0V до 6V)</li></ul><h2 id="схеми" tabindex="-1">Схеми <a class="header-anchor" href="#схеми" aria-label="Permalink to &quot;Схеми&quot;">​</a></h2><p><a href="https://raw.githubusercontent.com/PX4/Hardware/master/FMUv2/PX4FMUv2.4.5.pdf" target="_blank" rel="noreferrer">Схема FMUv2 + IOv2</a> -- Схема та макет</p><div class="info custom-block"><p class="custom-block-title">Як дизайн Open Hardware з ліцензією CC-BY-SA 3.0, всі схеми та файли дизайну доступні <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">тут</a>.</p></div><h2 id="з-єднання" tabindex="-1">З&#39;єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З&#39;єднання&quot;">​</a></h2><p>Порти Pixhawk показані нижче. Вони використовують роз’єми Hirose DF13 (передують роз’ємам JST-GH, визначеним у стандарті роз’ємів Pixhawk).</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Багато клонів 3DR Pixhawk використовують роз’єми Molex picoblade замість роз’ємів DF13. Вони мають прямокутні штифти замість квадратних, і їх не можна вважати сумісними.</p></div><p><img src="'+a+'" alt="Конектори Pixhawk"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Порт <code>RC IN</code> призначений лише для RC приймачів і забезпечує достатню потужність для цієї мети. <strong>НІКОЛИ</strong> не підключайте до нього або до підключеного приймача жодних сервоприводів, джерел живлення або батарей.</p></div><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><h4 id="telem1-telem2-порти" tabindex="-1">TELEM1, TELEM2 порти <a class="header-anchor" href="#telem1-telem2-порти" aria-label="Permalink to &quot;TELEM1, TELEM2 порти&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>CTS (IN)</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>RTS (OUT)</td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="gps-port" tabindex="-1">GPS port <a class="header-anchor" href="#gps-port" aria-label="Permalink to &quot;GPS port&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>CAN2 TX</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>CAN2 RX</td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="serial-4-5-port" tabindex="-1">SERIAL 4/5 port <a class="header-anchor" href="#serial-4-5-port" aria-label="Permalink to &quot;SERIAL 4/5 port&quot;">​</a></h4><p>У зв&#39;язку з обмеженням простору два порти знаходяться на одному роз&#39;ємі.</p><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>TX (#4)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>RX (#4)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>TX (#5)</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>RX (#5)</td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="adc-6-6v" tabindex="-1">ADC 6.6V <a class="header-anchor" href="#adc-6-6v" aria-label="Permalink to &quot;ADC 6.6V&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>ADC IN</td><td>up to +6.6V</td></tr><tr><td>3 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="adc-3-3v" tabindex="-1">ADC 3.3V <a class="header-anchor" href="#adc-3-3v" aria-label="Permalink to &quot;ADC 3.3V&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Signal</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>ADC IN</td><td>до +3.3V</td></tr><tr><td>3 (blk)</td><td>GND</td><td>GND</td></tr><tr><td>4 (blk)</td><td>ADC IN</td><td>до +3.3V</td></tr><tr><td>5 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="i2c" tabindex="-1">I2C <a class="header-anchor" href="#i2c" aria-label="Permalink to &quot;I2C&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>SCL</td><td>+3.3 (pullups)</td></tr><tr><td>3 (blk)</td><td>SDA</td><td>+3.3 (pullups)</td></tr><tr><td>4 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="can" tabindex="-1">CAN <a class="header-anchor" href="#can" aria-label="Permalink to &quot;CAN&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>CAN_H</td><td>+12V</td></tr><tr><td>3 (blk)</td><td>CAN_L</td><td>+12V</td></tr><tr><td>4 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="spi-питома-споживана-потужність" tabindex="-1">SPI (питома споживана потужність) <a class="header-anchor" href="#spi-питома-споживана-потужність" aria-label="Permalink to &quot;SPI (питома споживана потужність)&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>SPI_EXT_SCK</td><td>+3.3</td></tr><tr><td>3 (blk)</td><td>SPI_EXT_MISO</td><td>+3.3</td></tr><tr><td>4 (blk)</td><td>SPI_EXT_MOSI</td><td>+3.3</td></tr><tr><td>5 (blk)</td><td>!SPI_EXT_NSS</td><td>+3.3</td></tr><tr><td>6 (blk)</td><td>!GPIO_EXT</td><td>+3.3</td></tr><tr><td>7 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="power" tabindex="-1">POWER <a class="header-anchor" href="#power" aria-label="Permalink to &quot;POWER&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>VCC</td><td>+5V</td></tr><tr><td>3 (blk)</td><td>CURRENT</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>VOLTAGE</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>GND</td><td>GND</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="switch" tabindex="-1">SWITCH <a class="header-anchor" href="#switch" aria-label="Permalink to &quot;SWITCH&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC</td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>!IO_LED_SAFETY</td><td>GND</td></tr><tr><td>3 (blk)</td><td>SAFETY</td><td>GND</td></tr></tbody></table><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table><h2 id="відладочні-порти" tabindex="-1">Відладочні порти <a class="header-anchor" href="#відладочні-порти" aria-label="Permalink to &quot;Відладочні порти&quot;">​</a></h2><h3 id="порт-консолі" tabindex="-1">Порт Консолі <a class="header-anchor" href="#порт-консолі" aria-label="Permalink to &quot;Порт Консолі&quot;">​</a></h3><p><a href="./../debug/system_console.html">Консоль системи PX4</a> працює на порту, позначеному <a href="#serial-4-5-port">SERIAL4/5</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Зручний спосіб підключення до консолі - скористатися <a href="https://kb.zubax.com/display/MAINKB/Dronecode+Probe+documentation" target="_blank" rel="noreferrer">Dronecode probe</a>, оскільки він поставляється з роз&#39;ємами, які можна використовувати з кількома різними пристроями Pixhawk. Просто під&#39;єднайте кабель DF13 1:1 з 6-позиційним роз&#39;ємом з <a href="https://kb.zubax.com/display/MAINKB/Dronecode+Probe+documentation" target="_blank" rel="noreferrer">Dronecode probe</a> до порту Pixhawk <code>SERIAL4/5</code>.</p><p><img src="'+d+'" alt="Dronecode probe"></p></div><p>Розводка є стандартною послідовною розводкою, призначеною для підключення до кабелю <a href="https://www.digikey.com/en/products/detail/TTL-232R-3V3/768-1015-ND/1836393" target="_blank" rel="noreferrer">3.3V FTDI</a> (до 5V).</p><table><thead><tr><th>3DR Pixhawk 1</th><th></th><th>FTDI</th></tr></thead><tbody><tr><td>1</td><td>+5V (red)</td><td></td></tr><tr><td>2</td><td>S4 Tx</td><td></td></tr><tr><td>3</td><td>S4 Rx</td><td></td></tr><tr><td>4</td><td>S5 Tx</td><td>5</td></tr><tr><td>5</td><td>S5 Rx</td><td>4</td></tr><tr><td>6</td><td>GND</td><td>1</td></tr></tbody></table><p>Підключення кабелю FTDI до 6-контактного роз’єму DF13 1:1 показано на малюнку нижче.</p><p><img src="'+l+'" alt="Конектор консолі"></p><p>Повна проводка показана нижче.</p><p><img src="'+i+'" alt="Console Debug"></p><div class="info custom-block"><p class="custom-block-title">Для отримання інформації про те, як <em>використовувати</em> консоль, див: <a href="./../debug/system_console.html">Системна консоль</a>.</p></div><h3 id="swd-port" tabindex="-1">SWD Port <a class="header-anchor" href="#swd-port" aria-label="Permalink to &quot;SWD Port&quot;">​</a></h3><p>Порти <a href="./../debug/swd_debug.html">SWD</a> (JTAG) приховані під покриттям (яке треба зняти для налагодження апаратного забезпечення). Є окремі порти для FMU та IO, як показано нижче.</p><p><img src="'+o+'" alt="Pixhawk SWD"></p><p>Порти являють собою 10-контактні роз’єми JTAG ARM, які вам, ймовірно, доведеться паяти. Схема контактів для портів показана нижче (квадратні маркери в кутах вище вказують на контакт 1).</p><p><img src="'+h+'" alt="Розводка 10-контактного роз&#39;єму ARM"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Усі плати Pixhawk FMUv2 мають подібний порт SWD.</p></div><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html"> зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v2_default</span></span></code></pre></div><h2 id="частини-корпуси" tabindex="-1">Частини / Корпуси <a class="header-anchor" href="#частини-корпуси" aria-label="Permalink to &quot;Частини / Корпуси&quot;">​</a></h2><ul><li><strong>ARM MINI JTAG (J6)</strong>: 1.27 mm 10pos header (SHROUDED), for Black Magic Probe: FCI 20021521-00010D4LF (<a href="https://www.distrelec.ch/en/minitek-127-straight-male-pcb-header-surface-mount-rows-10-contacts-27mm-pitch-amphenol-fci-20021521-00010d4lf/p/14352308" target="_blank" rel="noreferrer">Distrelec</a>, <a href="https://www.digikey.com/en/products/detail/20021521-00010T1LF/609-4054-ND/2414951" target="_blank" rel="noreferrer">Digi-Key</a>) or Samtec FTSH-105-01-F-DV-K (untested) or Harwin M50-3600542 (<a href="https://www.digikey.com/en/products/detail/harwin-inc/M50-3600542/2264370" target="_blank" rel="noreferrer">Digikey</a> or <a href="http://ch.mouser.com/ProductDetail/Harwin/M50-3600542/?qs=%2fha2pyFadujTt%2fIEz8xdzrYzHAVUnbxh8Ki%252bwWYPNeEa09PYvTkIOQ%3d%3d" target="_blank" rel="noreferrer">Mouser</a>) <ul><li>JTAG Adapter Option #1: <a href="https://1bitsquared.com/products/black-magic-probe" target="_blank" rel="noreferrer">BlackMagic Probe</a>. Зверніть увагу, що може поставлятися без кабелів (перевірте у виробника). Якщо так, вам знадобиться кабель <strong>Samtec FFSD-05-D-06.00-01-N</strong> (<a href="https://www.samtec.com/products/ffsd-05-d-06.00-01-n" target="_blank" rel="noreferrer">Samtec sample service</a> або <a href="http://www.digikey.com/product-search/en?x=0&amp;y=0&amp;lang=en&amp;site=us&amp;KeyWords=FFSD-05-D-06.00-01-N" target="_blank" rel="noreferrer">Digi-Key Link: SAM8218-ND</a>) або <a href="http://www.tag-connect.com/CORTEXRIBBON10" target="_blank" rel="noreferrer">Tag Connect Ribbon</a> та кабель Mini-USB.</li><li>JTAG Adapter Option #2: <a href="https://www.digikey.com/product-detail/en/stmicroelectronics/ST-LINK-V2/497-10484-ND" target="_blank" rel="noreferrer">Digi-Key Link: ST-LINK/V2</a> / <a href="http://www.st.com/internet/com/TECHNICAL_RESOURCES/TECHNICAL_LITERATURE/USER_MANUAL/DM00026748.pdf" target="_blank" rel="noreferrer">ST USER MANUAL</a>, потрібен адаптер ARM Mini JTAG до 20pos: <a href="https://www.digikey.com/en/products/detail/texas-instruments/MDL-ADA2/1986451" target="_blank" rel="noreferrer">Digi-Key Link: 726-1193-ND</a></li><li>JTAG Adapter Option #3: <a href="http://www.sparkfun.com/products/8278" target="_blank" rel="noreferrer">SparkFun Link: Olimex ARM-TINY</a> або будь-який інший адаптер JTAG ARM Cortex, сумісний з OpenOCD, потребує адаптера ARM Mini JTAG на 20 контактів: <a href="https://www.digikey.com/en/products/detail/texas-instruments/MDL-ADA2/1986451" target="_blank" rel="noreferrer">Digi-Key Link: 726-1193-ND</a></li></ul></li><li><strong>USARTs</strong>: Hirose DF13 6 pos (<a href="https://www.digikey.com/products/en?keywords=H3371-ND" target="_blank" rel="noreferrer">Digi-Key Link: DF13A-6P-1.25H(20)</a>) <ul><li>Mates: Hirose DF13 6 pos housing (<a href="https://www.digikey.com/products/en?keywords=H2182-ND" target="_blank" rel="noreferrer">Digi-Key Link: Hirose DF13-6S-1.25C</a>)</li></ul></li><li><strong>I2C і CAN</strong>: Hirose DF13 4 pos (<a href="https://www.digikey.com/en/products/detail/hirose-electric-co-ltd/DF13A-4P-1-25H-20/530666" target="_blank" rel="noreferrer">Digi-Key Link: DF13A-4P-1.25H(20)</a> - знято з виробництва)</li></ul><h2 id="підтримувані-платформи-шасі" tabindex="-1">Підтримувані платформи / шасі <a class="header-anchor" href="#підтримувані-платформи-шасі" aria-label="Permalink to &quot;Підтримувані платформи / шасі&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа чи човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus.</p>',67),m=[b,u,k];function f(P,_,w,g,D,x){return c(),n("div",null,m)}const T=s(p,[["render",f]]);export{C as __pageData,T as default};
