import{_ as t,a as r}from"./chunks/pixhawk3_pro_debug_ports.ClVdUQ5N.js";import{_ as a,c as e,o,ab as d}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Pixhawk 3 Pro (Знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk3_pro.md","filePath":"uk/flight_controller/pixhawk3_pro.md"}'),i={name:"uk/flight_controller/pixhawk3_pro.md"},l=d('<h1 id="pixhawk-3-pro-знято-з-виробництва" tabindex="-1">Pixhawk 3 Pro (Знято з виробництва) <a class="header-anchor" href="#pixhawk-3-pro-знято-з-виробництва" aria-label="Permalink to &quot;Pixhawk 3 Pro (Знято з виробництва)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який інший) автопілот. Зверніться до <a href="https://store-drotek.com/" target="_blank" rel="noreferrer">виробника</a> щодо питань технічної підтримки або відповідності вимогам.</p></div><p>Pixhawk<sup>®</sup> 3 Pro базується на апаратному дизайні FMUv4 (Pixracer) з деякими оновленнями та додатковими функціями. Плата була спроєктована <a href="https://drotek.com" target="_blank" rel="noreferrer">Drotek<sup>®</sup></a> і PX4.</p><p><img src="'+t+'" alt="Pixhawk 3 Pro hero image"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Основна документація з апаратного забезпечення тут: <a href="https://drotek.gitbook.io/pixhawk-3-pro/hardware" target="_blank" rel="noreferrer">https://drotek.gitbook.io/pixhawk-3-pro/hardware</a></p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Цей автопілот <a href="./../flight_controller/autopilot_pixhawk_standard.html">підтримується</a> командами підтримки та тестування PX4.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Мікроконтролер: <strong>STM32F469</strong>; Flash size <strong>2MiB</strong>, RAM size <strong>384KiB</strong></li><li><strong>ICM-20608-G</strong> гіроскоп / акселерометр</li><li><strong>MPU-9250</strong> гіроскоп / акселерометр / магнітометр</li><li><strong>LIS3MDL</strong> компас</li><li>Датчики, підключені через дві шини SPI (одна високочастотна й одна малошумна шина)</li><li>Два шини I2C</li><li>Два CAN шини</li><li>Показники напруги / батареї з двох блоків живлення</li><li>FrSky<sup>®</sup> інвертор</li><li>8 Main + 6 AUX виводів PWM (окремий IO чіп, PX4IO)</li><li>microSD (логування)</li><li>S.BUS / Spektrum / SUMD / PPM вхід</li><li>JST GH роз&#39;єми: ті самі роз&#39;єми та розводка, що й у Pixracer</li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>В <a href="https://store.drotek.com/" target="_blank" rel="noreferrer">Drotek</a> (ЄС) :</p><ul><li><a href="https://store.drotek.com/autopilots/844-pixhawk-3-pro-pack.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro (Pack)</a></li><li><a href="https://store.drotek.com/autopilots/821-pixhawk-pro-autopilot-8944595120557.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><p>В <a href="https://www.readymaderc.com" target="_blank" rel="noreferrer">readymaderc</a> (США) :</p><ul><li><a href="https://www.readymaderc.com/products/details/pixhawk-3-pro-flight-controller" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html"> зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v4pro_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Плата має порти FMU та IO для відладки, як показано нижче.</p><p><img src="'+r+'" alt="Відладочні порти"></p><p>Розводка та роз’єм відповідають інтерфейсу <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a>, визначеному в <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> (роз’єм JST SM06B).</p><table><thead><tr><th>Pin</th><th>Signal</th><th>Volt</th></tr></thead><tbody><tr><td>1 (червоний)</td><td>VCC TARGET SHIFT</td><td>+3.3V</td></tr><tr><td>2 (чорний)</td><td>CONSOLE TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (чорний)</td><td>CONSOLE RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (чорний)</td><td>SWDIO</td><td>+3.3V</td></tr><tr><td>5 (чорний)</td><td>SWCLK</td><td>+3.3V</td></tr><tr><td>6 (чорний)</td><td>GND</td><td>GND</td></tr></tbody></table><p>Інформацію про підключення та використання цього порту див:</p><ul><li><a href="./../debug/swd_debug.html">Порт відладки SWD</a></li><li><a href="./../debug/system_console.html#pixhawk_debug_port">Системна консоль PX4</a> (Зауважте, що консоль FMU зіставляється з UART7).</li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>WiFi</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26),s=[l];function h(p,n,c,k,u,_){return o(),e("div",null,s)}const P=a(i,[["render",h]]);export{g as __pageData,P as default};
