import{_ as t}from"./chunks/pixhawk4mini_iso_1.BZjVrNct.js";import{_ as e,a,b as i}from"./chunks/pixhawk4mini_fmu_debug.DdTFiXJg.js";import{_ as l,c as r,o,ab as n}from"./chunks/framework.CUflZczI.js";const f=JSON.parse('{"title":"Holybro Pixhawk 4 Mini (Знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk4_mini.md","filePath":"uk/flight_controller/pixhawk4_mini.md"}'),s={name:"uk/flight_controller/pixhawk4_mini.md"},h=n('<h1 id="holybro-pixhawk-4-mini-знято-з-виробництва" tabindex="-1">Holybro Pixhawk 4 Mini (Знято з виробництва) <a class="header-anchor" href="#holybro-pixhawk-4-mini-знято-з-виробництва" aria-label="Permalink to &quot;Holybro Pixhawk 4 Mini (Знято з виробництва)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (або будь-який інший) автопілот. Зверніться до <a href="https://holybro.com/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення або відповідності вимогам.</p></div><p>Автопілот <em>Pixhawk<sup>®</sup> 4 Mini</em> розроблений для інженерів та любителів, які прагнуть використовувати потужність <em>Pixhawk 4</em>, але працюють з меншими дронами. <em>Pixhawk 4 Mini</em> використовує FMU процесор та ресурси пам&#39;яті <em>Pixhawk 4</em>, але виключає інтерфейси, які зазвичай не використовуються. Це дозволяє <em>Pixhawk 4 Mini</em> бути достатньо малим, щоб поміститися у 250mm гоночний дрон.</p><p><em>Pixhawk 4 Mini</em> був спроєктований та розроблений у співпраці між Holybro<sup>®</sup> та Auterion<sup>®</sup>. Він заснований на стандарті дизайну <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk</a> <strong>FMUv5</strong> та оптимізований для роботи на програмному забезпеченні управління польотом PX4.</p><p><img src="'+t+'" alt="Pixhawk4 mini"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Цей автопілот <a href="./../flight_controller/autopilot_pixhawk_standard.html">підтримується</a> командами підтримки та тестування PX4.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Основний FMU Processor: STM32F765 <ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB memory, 512KB RAM</li></ul></li><li>Бортові сенсори: <ul><li>Accel/Gyro: ICM-20689</li><li>Accel/Gyro: BMI055 або ICM20602</li><li>Magnetometer: IST8310</li><li>Barometer: MS5611</li></ul></li><li>GPS: u-blox Neo-M8N GPS/GLONASS приймач; інтегрований магнетометр IST8310</li><li>Інтерфейси: <ul><li>8 PWM виводів</li><li>4 виділених PWM/Capture входи на FMU</li><li>Виділений R/C вхід для CPPM</li><li>Виділений R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM RSSI входом</li><li>3 загальних послідовних портів</li><li>2 I2C порти</li><li>3 SPI шини</li><li>1 CAN шина для CAN ESC</li><li>Аналогові входи для напруги / струму з батареї</li><li>2 додаткових аналогових входи</li></ul></li><li>Система живлення: <ul><li>Вхід Power Brick: 4.75~5.5V</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід Servo Rail: 0~24V</li><li>Максимальне вимірювання струму: 120A</li></ul></li><li>Вага та розміри: <ul><li>Вага: 37.2g</li><li>Розміри: 38x55x15.5mm</li></ul></li><li>Інші характеристики: <ul><li>Робоча температура: -40 ~ 85°c</li></ul></li></ul><p>Додаткову інформацію можна знайти у <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4mini/pixhawk4mini_technical_data_sheet.pdf" target="_blank" rel="noreferrer"><em>Технічному описі</em> Pixhawk 4 Mini</a>.</p><h2 id="де-придбати" tabindex="-1">Де придбати <a class="header-anchor" href="#де-придбати" aria-label="Permalink to &quot;Де придбати&quot;">​</a></h2><p>Замовляйте на <a href="https://holybro.com/collections/autopilot-flight-controllers/products/pixhawk4-mini" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="інтерфеиси" tabindex="-1">Інтерфейси <a class="header-anchor" href="#інтерфеиси" aria-label="Permalink to &quot;Інтерфейси&quot;">​</a></h2><p><img src="'+e+'" alt="Інтерфейси Pixhawk 4 Mini"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Порти <strong>RC IN</strong> та <strong>PPM</strong> призначені лише для RC приймачів. Вони працюють на електроживленні! НІКОЛИ не підключайте до них жодних сервоприводів, джерел живлення або батарей (або до будь-якого підключеного приймача).</p></div><h2 id="розводка" tabindex="-1">Розводка <a class="header-anchor" href="#розводка" aria-label="Permalink to &quot;Розводка&quot;">​</a></h2><p>Завантажте схему розводки <em>Pixhawk 4 Mini</em> <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4mini/pixhawk4mini_pinouts.pdf" target="_blank" rel="noreferrer">звідси</a>.</p><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+a+'" alt="Розміри Pixhawk 4 Mini"></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 4 Mini</em> може мати резервне живлення — якщо надаються два джерела живлення. Шини живлення: <strong>POWER</strong> та <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">Вихідна потужність <strong>MAIN OUT</strong> не живить плату політного контролера (і не живиться нею). Ви повинні <a href="./../assembly/quick_start_pixhawk4_mini.html#power">подати живлення</a> або на <strong>POWER</strong> або на <strong>USB</strong>, інакше плата буде знеживлена.</p></div><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER</strong> (4.75V до 5.5V)</li><li><strong>USB</strong> вхід (4.75V до 5.25V)</li></ol><p><strong>Абсолютна максимальна напруга</strong></p><p>За таких умов система залишиться неушкодженою.</p><ol><li><strong>POWER</strong> вхід (0V до 6V без пошкоджень)</li><li><strong>USB</strong> вхід (0V до 6V без пошкоджень)</li><li>Вхід серводвигуна: контакт VDD_SERVO <strong>MAIN OUT</strong> (0V до 24V без пошкоджень)</li></ol><h2 id="збірка-налаштування" tabindex="-1">Збірка / налаштування <a class="header-anchor" href="#збірка-налаштування" aria-label="Permalink to &quot;Збірка / налаштування&quot;">​</a></h2><p><a href="./../assembly/quick_start_pixhawk4_mini.html"><em>Pixhawk 4 Mini</em> Швидкий старт з підключення</a> надає інструкції щодо збирання необхідних/важливих периферійних пристроїв, таких як GPS, плата управління живленням тощо.</p><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більшості користувачів не потрібно збирати цю прошивку! Вона попередньо зібрана й автоматично встановлюється <em>QGroundControl</em> при підключенні відповідного апаратного забезпечення.</p></div><p>Щоб <a href="./../dev_setup/building_px4.html">зібрати PX4</a> для цієї цілі:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v5_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p><a href="./../debug/system_console.html">Системна консоль PX4</a> та <a href="./../debug/swd_debug.html">SWD interface</a> працюють на порту <strong>FMU Debug</strong>. Для доступу до цих портів користувач має зняти корпус <em>Pixhawk 4 Mini</em>.</p><p><img src="'+i+'" alt="Pixhawk 4 Mini FMU Debug"></p><p>Порт має стандартну послідовну схему розводки й може бути підключений до стандартного кабелю FTDI (3.3V, до 5V) або до <a href="https://kb.zubax.com/display/MAINKB/Dronecode+Probe+documentation" target="_blank" rel="noreferrer">Dronecode probe</a>. Розводка використовує стандартну розводку <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk debug connector</a>. Будь ласка, зверніться до сторінки <a href="./../debug/system_console.html">підключення</a> щодо деталей того, як під&#39;єднатися до цього порту.</p><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th style="text-align:center;">UART</th><th style="text-align:center;">Device</th><th style="text-align:center;">Опис параметра QGC</th><th style="text-align:center;">Мітка порту на FC</th></tr></thead><tbody><tr><td style="text-align:center;">UART1</td><td style="text-align:center;">/dev/ttyS0</td><td style="text-align:center;">GPS1</td><td style="text-align:center;">GPS Module</td></tr><tr><td style="text-align:center;">USART2</td><td style="text-align:center;">/dev/ttyS1</td><td style="text-align:center;">TELEM1</td><td style="text-align:center;">TELEM1</td></tr><tr><td style="text-align:center;">USART3</td><td style="text-align:center;">/dev/ttyS2</td><td style="text-align:center;">TELEM2</td><td style="text-align:center;">N/A</td></tr><tr><td style="text-align:center;">UART4</td><td style="text-align:center;">/dev/ttyS3</td><td style="text-align:center;">TELEM/SERIAL4</td><td style="text-align:center;">UART/l2C B</td></tr><tr><td style="text-align:center;">USART6</td><td style="text-align:center;">/dev/ttyS4</td><td style="text-align:center;">N/A</td><td style="text-align:center;">RC IN</td></tr><tr><td style="text-align:center;">UART7</td><td style="text-align:center;">/dev/ttyS5</td><td style="text-align:center;">N/A</td><td style="text-align:center;">Debug</td></tr><tr><td style="text-align:center;">UART8</td><td style="text-align:center;">/dev/ttyS6</td><td style="text-align:center;">N/A</td><td style="text-align:center;">Not connected (no PX4IO)</td></tr></tbody></table><h2 id="периферія" tabindex="-1">Периферія <a class="header-anchor" href="#периферія" aria-label="Permalink to &quot;Периферія&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Цифровий датчик швидкості польоту</a></li><li><a href="./../telemetry/">Радіо модулі телеметрії</a></li><li><a href="./../sensor/rangefinders.html">Далекоміри / Датчики відстані</a></li></ul><h2 id="підтримувані-платформи" tabindex="-1">Підтримувані платформи <a class="header-anchor" href="#підтримувані-платформи" aria-label="Permalink to &quot;Підтримувані платформи&quot;">​</a></h2><p>Мотори та сервоприводи підключені до портів <strong>MAIN OUT</strong> в порядку, вказаному для вашого апарату в <a href="./../airframes/airframe_reference.html">Довіднику планерів</a>. Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><em>Pixhawk 4 Mini</em> не має AUX портів. Плата не може використовуватися з шасі, яким необхідно більше ніж 8 портів або які використовують AUX порти для моторів або керування. Вона може бути використана для планерів, які використовують AUX для другорядних периферійних пристроїв (наприклад, &quot;feed-through of RC AUX1 channel&quot;).</p></div><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4mini/pixhawk4mini_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Технічна інформація по <em>Pixhawk 4 Mini</em></a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">Довідник з дизайну розводки FMUv5</a>.</li></ul>',46),d=[h];function c(p,m,g,u,x,k){return o(),r("div",null,d)}const y=l(s,[["render",c]]);export{f as __pageData,y as default};
