import{_ as r,a as i,b as l}from"./chunks/pilotpi_sens_wiring.CWbVN67d.js";import{_ as a,E as e,c as o,J as h,m as t,a as s,ab as n,o as p}from"./chunks/framework.CUflZczI.js";const q=JSON.parse('{"title":"RPi PilotPi Shield","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/raspberry_pi_pilotpi.md","filePath":"uk/flight_controller/raspberry_pi_pilotpi.md"}'),c={name:"uk/flight_controller/raspberry_pi_pilotpi.md"},u=t("h1",{id:"rpi-pilotpi-shield",tabindex:"-1"},[s("RPi PilotPi Shield "),t("a",{class:"header-anchor",href:"#rpi-pilotpi-shield","aria-label":'Permalink to "RPi PilotPi Shield"'},"​")],-1),b=n('<div class="warning custom-block"><p class="custom-block-title">PX4 не виробляє цей (чи будь-який інший) автопілот. Зверніться до <a href="mailto:lhf2613@gmail.com" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи питань відповідності вимогам.</p></div><p><em>PilotPi</em> shield - це повнофункціональне рішення для запуску автопілота PX4 безпосередньо на Raspberry Pi. Він розроблений як низькобюджетна, але високомасштабована платформа з постійними оновленнями як зі сторони Linux, так і PX4. Не потрібні пропрієтарні драйвери, оскільки всі компоненти мають підтримку від спільноти RPi та PX4. PCB та схема також є відкритими.</p><p><img src="'+r+'" alt="PilotPi з RPi 4B"></p><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Підтримувані плати RPi: <ul><li>Raspberry Pi 2B/3B/3B+/4B</li></ul></li><li>Підтримувані ОС: <ul><li>Raspberry Pi OS</li><li>Ubuntu Server (armhf/arm64)</li></ul></li><li>Акселерометр / Гіроскоп: <ul><li>ICM42688P</li></ul></li><li>Магнітометр: <ul><li>IST8310</li></ul></li><li>Барометр: <ul><li>MS5611</li></ul></li><li>PWM: <ul><li>PCA9685</li></ul></li><li>ADC: <ul><li>ADS1115</li></ul></li><li>Живлення: <ul><li>Акумулятор 3~6S з вбудованим вимірюванням напруги.</li><li>Живлення Pi через кабель USB</li></ul></li><li>Доступність: <em>підготовка до відправлення</em></li></ul><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><p>Shield надає:</p><ul><li>16 вихідних каналів PWM</li><li>GPS конектор</li><li>Конектор телеметрії</li><li>Зовнішній конектор шини I2C (<strong>Примітка:</strong> конфліктує з камерою CSI)</li><li>Вхідний порт RC (SBUS)</li><li>3x канали ADC діапазоном 0~5V</li><li>2*8 2.54 мм не використовуваний конектор GPIO</li></ul><p>Прямий доступ з RPi:</p><ul><li>4x USB конектори</li><li>CSI конектор(<strong>Примітка:</strong> конфліктує з зовнішньою I2C шиною)</li><li>тощо.</li></ul><h2 id="рекомендована-проводка" tabindex="-1">Рекомендована проводка <a class="header-anchor" href="#рекомендована-проводка" aria-label="Permalink to &quot;Рекомендована проводка&quot;">​</a></h2><p><img src="'+i+'" alt="PilotPi PowerPart wiring"></p><p><img src="'+l+'" alt="PilotPi SensorPart wiring"></p><h2 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Все ще використовує старі конектори GH1.25. Проводка сумісна з Pixhawk 2.4.8</p></div><h3 id="конектори" tabindex="-1">Конектори <a class="header-anchor" href="#конектори" aria-label="Permalink to &quot;Конектори&quot;">​</a></h3><h4 id="gps-конектор" tabindex="-1">GPS конектор <a class="header-anchor" href="#gps-конектор" aria-label="Permalink to &quot;GPS конектор&quot;">​</a></h4><p>Зіставлено на <code>/dev/ttySC0</code></p><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1</td><td>VCC</td><td>+5V</td></tr><tr><td>2</td><td>TX</td><td>+3v3</td></tr><tr><td>3</td><td>RX</td><td>+3v3</td></tr><tr><td>4</td><td>NC</td><td>+3v3</td></tr><tr><td>5</td><td>NC</td><td>+3v3</td></tr><tr><td>6</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="конектор-телеметріі" tabindex="-1">Конектор телеметрії <a class="header-anchor" href="#конектор-телеметріі" aria-label="Permalink to &quot;Конектор телеметрії&quot;">​</a></h4><p>Зіставлено на <code>/dev/ttySC1</code></p><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1</td><td>VCC</td><td>+5V</td></tr><tr><td>2</td><td>TX</td><td>+3v3</td></tr><tr><td>3</td><td>RX</td><td>+3v3</td></tr><tr><td>4</td><td>CTS</td><td>+3v3</td></tr><tr><td>5</td><td>RTS</td><td>+3v3</td></tr><tr><td>6</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="зовнішніи-i2c-конектор" tabindex="-1">Зовнішній I2C конектор <a class="header-anchor" href="#зовнішніи-i2c-конектор" aria-label="Permalink to &quot;Зовнішній I2C конектор&quot;">​</a></h4><p>Зіставлено на <code>/dev/i2c-0</code></p><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1</td><td>VCC</td><td>+5V</td></tr><tr><td>2</td><td>SCL</td><td>+3v3(pullups)</td></tr><tr><td>3</td><td>SDA</td><td>+3v3(pullups)</td></tr><tr><td>4</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="rc-та-adc2-3-4" tabindex="-1">RC та ADC2/3/4 <a class="header-anchor" href="#rc-та-adc2-3-4" aria-label="Permalink to &quot;RC та ADC2/3/4&quot;">​</a></h4><p>RC мапиться на <code>/dev/ttyAMA0</code> з перемикачем інвертора сигналу на лінії RX.</p><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1</td><td>RC</td><td>+3V3~+5V</td></tr><tr><td>2</td><td>VCC</td><td>+5V</td></tr><tr><td>3</td><td>GND</td><td>GND</td></tr></tbody></table><ul><li>ADC1 внутрішньо підключений до розподільника напруги для моніторингу напруги батареї.</li><li>ADC2 залишається невикористаним.</li><li>ADC3 може бути підключений до аналогового датчика швидкості.</li><li>ADC4 має перемичку між ADC та VCC, щоб відстежувати рівень напруги системи.</li></ul><table><thead><tr><th>Пін</th><th>Сигнал</th><th>Вольт</th></tr></thead><tbody><tr><td>1</td><td>ADCx</td><td>0V~+5V</td></tr><tr><td>2</td><td>VCC</td><td>+5V</td></tr><tr><td>3</td><td>GND</td><td>GND</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>ADC3 та 4 мають альтернативне джерело VCC Коли перемикач &#39;Vref&#39; увімкнено, пін &#39;VCC&#39; керується REF5050.</p></div><h4 id="вільні-gpio-доступні-зверху-на-платі" tabindex="-1">Вільні GPIO доступні зверху на платі <a class="header-anchor" href="#вільні-gpio-доступні-зверху-на-платі" aria-label="Permalink to &quot;Вільні GPIO доступні зверху на платі&quot;">​</a></h4><table><thead><tr><th>Shield Pin</th><th>BCM</th><th>WiringPi</th><th>RPi Pin</th></tr></thead><tbody><tr><td>1</td><td>3V3</td><td>3v3</td><td>3V3</td></tr><tr><td>2</td><td>5V</td><td>5V</td><td>5V</td></tr><tr><td>3</td><td>4</td><td>7</td><td>7</td></tr><tr><td>4</td><td>14</td><td>15</td><td>8</td></tr><tr><td>5</td><td>17</td><td>0</td><td>11</td></tr><tr><td>6</td><td>27</td><td>2</td><td>13</td></tr><tr><td>7</td><td>22</td><td>3</td><td>15</td></tr><tr><td>8</td><td>23</td><td>4</td><td>16</td></tr><tr><td>9</td><td>7</td><td>11</td><td>26</td></tr><tr><td>10</td><td>5</td><td>21</td><td>29</td></tr><tr><td>11</td><td>6</td><td>22</td><td>31</td></tr><tr><td>12</td><td>12</td><td>26</td><td>32</td></tr><tr><td>13</td><td>13</td><td>23</td><td>33</td></tr><tr><td>14</td><td>16</td><td>27</td><td>36</td></tr><tr><td>15</td><td>26</td><td>25</td><td>37</td></tr><tr><td>16</td><td>GND</td><td>GND</td><td>GND</td></tr></tbody></table><h3 id="перемикачі" tabindex="-1">Перемикачі <a class="header-anchor" href="#перемикачі" aria-label="Permalink to &quot;Перемикачі&quot;">​</a></h3><h4 id="rc-інвертор" tabindex="-1">RC Інвертор <a class="header-anchor" href="#rc-інвертор" aria-label="Permalink to &quot;RC Інвертор&quot;">​</a></h4><p>Цей перемикач вирішуватиме полярність сигналу лінії RX: <code>UART_RX = SW xor RC_INPUT</code></p><ul><li>On: підходить з SBUS (сигнал інвертований)</li><li>Off: збережено</li></ul><h4 id="vref" tabindex="-1">Vref <a class="header-anchor" href="#vref" aria-label="Permalink to &quot;Vref&quot;">​</a></h4><p>ADC 3 та 4 матимуть VCC, що кероване:</p><ul><li>Вихід Vref від REF5050, якщо увімкнено</li><li>5V пін напряму з RPi, якщо вимкнено</li></ul><h4 id="режим-завантаження" tabindex="-1">Режим завантаження <a class="header-anchor" href="#режим-завантаження" aria-label="Permalink to &quot;Режим завантаження&quot;">​</a></h4><p>Цей перемикач підключений до Pin22(BCM25). Системний rc script перевірить його значення і вирішить, чи повинен PX4 запускатися разом із завантаженням системи чи ні.</p><ul><li>On: автоматичний запуск PX4</li><li>Off: не запускати PX4</li></ul><h2 id="швидкии-старт-для-розробника" tabindex="-1">Швидкий старт для розробника <a class="header-anchor" href="#швидкии-старт-для-розробника" aria-label="Permalink to &quot;Швидкий старт для розробника&quot;">​</a></h2><p>Зверніться до інструкцій для вашої операційної системи на RPi:</p><ul><li><a href="./raspberry_pi_pilotpi_rpios.html">Raspberry Pi OS Lite (armhf)</a></li><li><a href="./raspberry_pi_pilotpi_ubuntu_server.html">Ubuntu Server (arm64 &amp; armhf)</a></li></ul>',46);function P(m,_,C,f,S,V){const d=e("LinkedBadge");return p(),o("div",null,[u,h(d,{type:"warning",text:"Experimental",url:"../flight_controller/autopilot_experimental.html"}),b])}const v=a(c,[["render",P]]);export{q as __pageData,v as default};
