import{_ as t,a as e}from"./chunks/pinmap.pN8VcZTU.js";import{_ as l,c as a,o as r,ab as i}from"./chunks/framework.CUflZczI.js";const R=JSON.parse('{"title":"ThePeach FCC-R1","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/thepeach_r1.md","filePath":"uk/flight_controller/thepeach_r1.md"}'),o={name:"uk/flight_controller/thepeach_r1.md"},s=i('<h1 id="thepeach-fcc-r1" tabindex="-1">ThePeach FCC-R1 <a class="header-anchor" href="#thepeach-fcc-r1" aria-label="Permalink to &quot;ThePeach FCC-R1&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не виробляє цей (або будь-який інший) автопілот. Зверніться до <a href="https://thepeach.kr/" target="_blank" rel="noreferrer">виробника</a> щодо підтримки апаратного забезпечення чи питань відповідності вимогам.</p></div><p><strong>ThePeach FCC-R1</strong> - це сучасний автопілот, розроблений і виготовлений в <strong>ThePeach</strong>.</p><p>Вона базується на <strong>Pixhawk-проекті FMUv3</strong> з відкритим апаратним забезпеченням і працює під управлінням <strong>PX4</strong> на <strong>Nuttx OS</strong>.</p><p><img src="'+t+'" alt="ThePeach_R1"></p><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><ul><li><p>Основний процесор: STM32F427VIT6</p><ul><li>32-бітний ARM Cortex-M4, 168 МГц 256 КБ ОЗП 2 МБ флеш-пам&#39;яті</li></ul></li><li><p>IO процесор: STM32F100C8T6</p><ul><li>ARM Cortex-M3, 32 бітний ARM Cortex-M3, 24 МГц, 8КБ SRAM</li></ul></li><li><p>Бортові сенсори</p><ul><li>Акселератор/гіроскоп: ICM-20602</li><li>Акселератор/гіроскоп/магнітометр: MPU-9250</li><li>Барометр: MS5611</li></ul></li><li><p>Інтерфейси</p><ul><li>8+6 PWM виходів (8 з IO, 6 з FMU)</li><li>Spektrum DSM / DSM2 / DSM-X Satellite сумісний вхід</li><li>Futaba S.BUS сумісний вхід та вихід</li><li>Вхід сигналу PPM sum</li><li>Аналоговий / PWM вхід RSSI</li><li>Вихід сервоприводу S.Bus</li><li>Запобіжний вимикач / LED</li><li>4x UART: TELEM1, TELEM2(Raspberry Pi CM3+), GPS, SERIAL4</li><li>1x I2C порт</li><li>1x CAN шина</li><li>Аналогові входи для напруги / струму з 1 батареї</li></ul></li><li><p>Інтерфейси для Raspberry Pi CM3+</p><ul><li>VBUS</li><li>DDR2 Connector: Raspberry Pi CM3+</li><li>1x UART</li><li>2x USB</li><li>1x Raspberry Pi камера</li></ul></li><li><p>Деталі механічної частини</p><ul><li>Розміри: 49.2 x 101 x 18.2мм</li><li>Вага: 100g</li></ul></li></ul><h2 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h2><p><img src="'+e+'" alt="pinmap_top"></p><h2 id="зіставлення-послідовних-портів" tabindex="-1">Зіставлення послідовних портів <a class="header-anchor" href="#зіставлення-послідовних-портів" aria-label="Permalink to &quot;Зіставлення послідовних портів&quot;">​</a></h2><table><thead><tr><th>UART</th><th>Девайс</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>Відладка процесора вводу-виводу</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (Raspberry pi cm3+)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttys5</td><td>Debug console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>TELEM4</td></tr></tbody></table><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><strong>Peach FCC-R1</strong> може мати подвійне резервування живлення, якщо до нього під&#39;єднано два джерела живлення. Шини живлення: <strong>POWER</strong> та <strong>USB</strong>.</p><p>Примітка:</p><ol><li>Вихідні шини живлення <strong>FMU PWM OUT</strong> і <strong>I/O PWM OUT</strong> не живлять плату контролера польоту (і не живляться від неї). Ви повинні подати живлення на один з роз&#39;ємів <strong>POWER</strong> або <strong>USB</strong>, інакше плата буде знеструмлена.</li><li>USB не живить <strong>Raspberry Pi CM3+</strong>. Ви повинні подати живлення на <strong>POWER</strong>, інакше Raspberry Pi CM3+ буде знеструмлено.</li></ol><p><strong>Максимальна напруга нормальної роботи</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li>POWER вхід (5В до 5.5В)</li><li>USB вхід (4.75В до 5.25В)</li></ol><p><strong>Абсолютна максимальна напруга</strong></p><p>За таких умов всі джерела живлення спричиняють постійні пошкодження контролеру польоту.</p><ol><li><p>POWER вхід (більше 5.5В)</p></li><li><p>USB вхід (більше 5.5В)</p></li></ol><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><p>Щоб зібрати PX4 для цього контролера:</p><div class="language-jsx vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">jsx</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">make thepeach_r1_default</span></span></code></pre></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Замовляйте у <a href="http://thepeach.shop/" target="_blank" rel="noreferrer">ThePeach</a></p>',26),h=[s];function d(n,p,c,g,u,P){return r(),a("div",null,h)}const b=l(o,[["render",d]]);export{R as __pageData,b as default};
