import{_ as t}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as e,c as a,o as i,ab as l}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"Режим утримання (з нерухомим крилом)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/hold.md","filePath":"uk/flight_modes_fw/hold.md"}'),o={name:"uk/flight_modes_fw/hold.md"},r=l('<h1 id="режим-утримання-з-нерухомим-крилом" tabindex="-1">Режим утримання (з нерухомим крилом) <a class="header-anchor" href="#режим-утримання-з-нерухомим-крилом" aria-label="Permalink to &quot;Режим утримання (з нерухомим крилом)&quot;">​</a></h1><img src="'+t+'" title="Position fix required (e.g. GPS)" width="30px"><p>Режим <em>Hold</em> призводить до того, щоб транспорт затримувався (обходив) навколо свого поточного GPS-положення та зберігав свою поточну висоту.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><em>Режим утримання</em> може бути використаний для призупинення місії або для допомоги у відновленні контролю над транспортним засобом у випадку надзвичайної ситуації. Зазвичай він активується за допомогою наперед заданого перемикача.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим автоматичний - для керування транспортним засобом <em>не потрібно</em> втручання користувача.</li><li>Режим вимагає глобальної тривимірної оцінки позиції (з GPS або виведеної з <a href="./../ros/external_position_estimation.html#enabling-auto-modes-with-a-local-position">локальної позиції</a>). <ul><li>Літаючі транспортні засоби не можуть переключатися на цей режим без глобального положення.</li><li>Літаючі транспортні засоби перейдуть в режим аварійної безпеки, якщо втратять оцінку положення.</li><li>Роззброєні транспортні засоби можуть переключатися в режим без дійсної оцінки позиції, але не можуть озброюватися.</li></ul></li><li>Режим вимагає, щоб швидкість вітру та час польоту були в межах допустимих значень (вказано через параметри).</li><li>Перемикачі керування RC можуть використовуватися для зміни режимів польоту на будь-якому транспортному засобі.</li><li>Рух стіків радіокерування ігнорується.</li></ul></div><h2 id="технічнии-підсумок" tabindex="-1">Технічний підсумок <a class="header-anchor" href="#технічнии-підсумок" aria-label="Permalink to &quot;Технічний підсумок&quot;">​</a></h2><p>Літак кружляє навколо позиції утримання GPS на поточній висоті. Транспортний засіб спочатку підніметься до <a href="#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a>, якщо режим увімкнено нижче цієї висоти.</p><p>Рух стіків радіокерування ігнорується.</p><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Поведінку режиму утримання можна налаштувати за допомогою наведених нижче параметрів.</p><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#NAV_LOITER_RAD">NAV_LOITER_RAD</a></td><td>Радіус кола обертання.</td></tr><tr><td><a id="NAV_MIN_LTR_ALT"></a><a href="./../advanced_config/parameter_reference.html#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a></td><td>Мінімальна висота для режиму очікування (транспортний засіб підніметься на цю висоту, якщо режим увімкнуто на меншій висоті).</td></tr></tbody></table><h2 id="дивись-також" tabindex="-1">Дивись також <a class="header-anchor" href="#дивись-також" aria-label="Permalink to &quot;Дивись також&quot;">​</a></h2><p><a href="./../flight_modes_mc/hold.html">Hold Mode (MC)</a></p>',13),_=[r];function d(s,h,c,n,m,p){return i(),a("div",null,_)}const b=e(o,[["render",d]]);export{T as __pageData,b as default};
