import{_ as a}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as t,c as e,o as i,ab as l}from"./chunks/framework.CUflZczI.js";const A=JSON.parse('{"title":"Режим посадки (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/land.md","filePath":"uk/flight_modes_fw/land.md"}'),r={name:"uk/flight_modes_fw/land.md"},_=l('<h1 id="режим-посадки-фіксоване-крило" tabindex="-1">Режим посадки (фіксоване крило) <a class="header-anchor" href="#режим-посадки-фіксоване-крило" aria-label="Permalink to &quot;Режим посадки (фіксоване крило)&quot;">​</a></h1><img src="'+a+'" title="Position estimate required (e.g. GPS)" width="30px"><p>Режим польоту <em>Посадка</em> змушує транспортний засіб сідати на місці, де був увімкнений цей режим, слідуючи по круговій траєкторії до посадки. Після посадки транспортний засіб вимкнеться через короткий проміжок часу (за замовчуванням).</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Фіксованим крилом <em>режим приземлення</em> повинен використовуватися лише в <strong>надзвичайній ситуації</strong>! Транспортний засіб опуститься навколо поточного місцезнаходження незалежно від придатності підлягаючої території, і сяде на землю, слідуючи круговому шляху польоту.</p><p>Де можливо, замість цього використовуйте <a href="./../flight_modes_fw/return.html">Режим повернення</a> з попередньо визначеною <a href="./../flight_modes_fw/mission.html#mission-landing">місією посадки з фіксованим крилом</a>.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим автоматичний - для керування апаратом <em>не потрібно</em> втручання користувача.</li><li>Режим потребує принаймні дійсної локальної оцінки позиції (не потребує глобальної позиції). <ul><li>Літаючі транспортні засоби не можуть переключатися на цей режим без глобального положення.</li><li>Літаючі транспортні засоби перейдуть в режим аварійної безпеки, якщо втратять оцінку положення.</li></ul></li><li>Режим перешкоджає зброюванню (транспортний засіб повинен бути зброєний при переході на цей режим).</li><li>Перемикачі керування RC можуть бути використані для зміни режимів польоту на будь-якому транспортному засобі.</li><li>Рух стіків радіокерування ігнорується.</li><li>Режим може бути активовано за допомогою команди MAVLink <a href="https://mavlink.io/en/messages/common.html#MAV_CMD_NAV_LAND" target="_blank" rel="noreferrer">MAV_CMD_NAV_LAND</a>, або прямо переключившись в режим посадки.</li></ul></div><h2 id="технічнии-підсумок" tabindex="-1">Технічний підсумок <a class="header-anchor" href="#технічнии-підсумок" aria-label="Permalink to &quot;Технічний підсумок&quot;">​</a></h2><p>Режим посадки змушує літальний засіб прямувати по спускаючій круговій траєкторії (шнуру) до посадки.</p><p>Коли режим увімкнено, транспортний засіб починає кружляти навколо поточного положення транспортного засобу з радіусом блукання <a href="#NAV_LOITER_RAD">NAV_LOITER_RAD</a> та починає опускатися з постійною швидкістю опускання. Швидкість спуску розраховується за допомогою <a href="#FW_LND_ANG">FW_LND_ANG</a> і встановленої швидкості посадки <a href="#FW_LND_AIRSPD">FW_LND_AIRSPD</a>. Транспортний засіб спалахне, якщо налаштований на це (див. <a href="./../flight_modes_fw/mission.html#flaring-roll-out">Флерінг</a>), в іншому випадку буде обертатися з постійною швидкістю спуску до виявлення посадки.</p><p><a href="./../flight_modes_fw/mission.html#automatic-abort">Ручне перетворення</a> та <a href="./../flight_modes_fw/mission.html#nudging">автоматична відміна посадки</a> недоступні в режимі посадки.</p><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Поведінку режиму приземлення можна налаштувати за допомогою наведених нижче параметрів.</p><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="NAV_LOITER_RAD"></a><a href="./../advanced_config/parameter_reference.html#NAV_LOITER_RAD">NAV_LOITER_RAD</a></td><td>Радіус блукання, який контролер відстежує протягом усієї послідовності посадки.</td></tr><tr><td><a id="FW_LND_ANG"></a><a href="./../advanced_config/parameter_reference.html#FW_LND_ANG">FW_LND_ANG</a></td><td>Виставте кут шляху пункту налаштувань.</td></tr><tr><td><a id="FW_LND_AIRSPD"></a><a href="./../advanced_config/parameter_reference.html#FW_LND_AIRSPD">FW_LND_AIRSPD</a></td><td>Налаштування швидкості.</td></tr></tbody></table><h2 id="дивись-також" tabindex="-1">Дивись також <a class="header-anchor" href="#дивись-також" aria-label="Permalink to &quot;Дивись також&quot;">​</a></h2><ul><li><a href="./../flight_modes_mc/land.html">Land Mode (MC)</a></li><li><a href="./../flight_modes_vtol/land.html">Land Mode (VTOL)</a></li></ul>',14),o=[_];function d(s,n,h,m,f,c){return i(),e("div",null,o)}const N=t(r,[["render",d]]);export{A as __pageData,N as default};
