import{_ as t}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as a}from"./chunks/remote_control.ss1psCxn.js";import{_ as e,c as _,o as r,ab as o}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"Ручний режим (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/manual.md","filePath":"uk/flight_modes_fw/manual.md"}'),d={name:"uk/flight_modes_fw/manual.md"},i=o('<h1 id="ручнии-режим-фіксоване-крило" tabindex="-1">Ручний режим (фіксоване крило) <a class="header-anchor" href="#ручнии-режим-фіксоване-крило" aria-label="Permalink to &quot;Ручний режим (фіксоване крило)&quot;">​</a></h1><p><img src="'+t+'" title="Hard to fly" width="30px"> <img src="'+a+'" title="Необхідний ручний/дистанційний пульт" width="30px"> </p><p><em>Ручний режим</em> надсилає введення стіків безпосередньо до розподілу керування для повного ручного управління.</p><p>Це найважчий режим польоту, оскільки нічого не стабілізовано. На відміну від <a href="./../flight_modes_fw/acro.html">режиму Акро</a>, якщо стік руля буде в центрі, транспортний засіб не автоматично зупинить обертання навколо осі — пілот фактично повинен перемістити палицю для накладання сили в іншому напрямку.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Це єдиний режим, що перевизначає FMU (команди відправляються через копроцесор безпеки). Він забезпечує механізм безпечності, який дозволяє повний контроль газового засуву, елеватора, елеронів та руля через RC в разі виникнення несправності програмного забезпечення FMU.</p></div><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Ручний режим, де введення стіків надсилається безпосередньо до розподілу керування (для &quot;повного&quot; ручного керування).</p><p>Це єдиний режим, що перевизначає FMU (команди відправляються через копроцесор безпеки). Він забезпечує механізм безпеки, який дозволяє повний контроль газового засуву, елеватора, елеронів та руля через RC в разі виникнення несправності програмного забезпечення FMU.</p><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_SC">FW_MAN_P_SC</a></td><td>Масштаб ручної шкали. Множник масштабу, застосований до бажаної команди приводу висоти в режимі повного ручного керування. Цей параметр дозволяє налаштовувати кидання поверхонь керування. За замовчуванням: 1.0 norm.</td></tr><tr><td><a id="FW_MAN_R_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_SC">FW_MAN_R_SC</a></td><td>Масштаб ручної шкали. Множник масштабу, застосований до бажаної команди приводу висоти в режимі повного ручного керування. Цей параметр дозволяє налаштовувати кидання поверхонь керування. За замовчуванням: 1.0 norm.</td></tr><tr><td><a id="FW_MAN_Y_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_Y_SC">FW_MAN_Y_SC</a></td><td>Масштаб ручної шкали рискання. Множник масштабу, застосований до бажаної команди приводу розвороту в режимі повного ручного керування. Цей параметр дозволяє налаштовувати кидання поверхонь керування. За замовчуванням: 1.0 norm.</td></tr></tbody></table>',10),n=[i];function c(l,s,m,h,p,f){return r(),_("div",null,n)}const C=e(d,[["render",c]]);export{b as __pageData,C as default};
