import{_ as t}from"./chunks/difficulty_medium.CPaRe1i7.js";import{_ as e}from"./chunks/remote_control.ss1psCxn.js";import{_ as a}from"./chunks/stabilized_fw.xtP5tMjx.js";import{_ as r,c as _,o as i,ab as d}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Стабілізований режим (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/stabilized.md","filePath":"uk/flight_modes_fw/stabilized.md"}'),l={name:"uk/flight_modes_fw/stabilized.md"},o=d('<h1 id="стабілізовании-режим-фіксоване-крило" tabindex="-1">Стабілізований режим (фіксоване крило) <a class="header-anchor" href="#стабілізовании-режим-фіксоване-крило" aria-label="Permalink to &quot;Стабілізований режим (фіксоване крило)&quot;">​</a></h1><p><img src="'+t+'" title="Medium difficulty to fly" width="30px"> <img src="'+e+'" title="Необхідний ручний/дистанційний пульт" width="30px"></p><p><em>Стабілізований режим</em> - це режим, де центрування стіків вирівнює положення транспортного засобу (крен та тангаж) та забезпечує горизонтальну позицію.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>Режим стабілізації</em> схожий на <a href="./../flight_modes_fw/altitude.html">Режим висоти</a> в тому, що відпускання стіків вирівнює транспортний засіб, але, на відміну від режиму висоти, він не підтримує висоту або курс. Політ в режимі <a href="./../flight_modes_fw/manual.html">Мануальний режим</a> набагато простіше, оскільки ви не можете звертати або перекидати його, і за потреби можна легко вирівняти транспортний засіб (центруючи керуючі стіки).</p></div><p>Транспортний засіб піднімається / опускається на основі крена та керування газом та виконує <a href="https://en.wikipedia.org/wiki/Coordinated_flight" target="_blank" rel="noreferrer">координований поворот</a>, якщо стік крену не дорівнює нулю. Курс і крен контролюються кутом (ви не можете перекотитися чи зробити петлю).</p><p>Транспортний засіб буде летіти інертно, якщо рівень газу знижується до 0% (двигун зупиняється). Для виконання повороту команда повинна бути утримана протягом маневру, оскільки якщо випускати рульове управління, літак зупинить поворот і врівноважить себе (так само відносно тангажу).</p><p>Стік повороту може бути використана для збільшення/зменшення кута приводу автомобіля на поворотах. Якщо контролер фіксований у центрі, то він самостійно здійснює координацію повороту, що означає, що він застосовує необхідну швидкість розвороту для поточного кута крену, щоб виконати плавний поворот.</p><p>Діаграма нижче візуально показує поведінку режиму (для <a href="./../getting_started/rc_transmitter_receiver.html#transmitter_modes">передавача режиму 2</a>).</p><p><img src="'+a+'" alt="FW Manual Flight"></p><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Ручний режим, де центровані стіки крену/тангажурівняють положення транспортного засобу. Курс і висота транспортного засобу не підтримуються, і він може дрейфувати через вітер.</p><ul><li>Центровані стіки крену/тангажу/рискання (в межах мертвої зони) встановлюють повітряний апарат на прямолінійний та горизонтальний польот. Курс і висота транспортного засобу не підтримуються, і він може дрейфувати через вітер.</li><li>Стік керування контролює кут крена. Автопілот буде підтримувати <a href="https://en.wikipedia.org/wiki/Coordinated_flight">координований політ</a>.</li><li>Стік крена керує кутом крена навколо визначеної зміни <a href="./../advanced_config/parameter_reference.html#FW_PSP_OFF">FW_PSP_OFF</a></li><li>Ручка дроселя керує дроселем безпосередньо.</li><li>Стік рискання додає додатковий значення швидкості рискання (додається до розрахованого автопілотом для підтримки координованого польоту). Може бути використаний для ручної зміни кута рискання безпілотного засобу.</li><li>Потрібен ручний ввід управління (наприклад, за допомогою пульта дистанційного керування, джойстика).</li></ul><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><p>На режим впливають наступні параметри:</p><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_MAX">FW_MAN_P_MAX</a></td><td>Максимальний крен для керування вручну в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_MAN_R_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_MAX">FW_MAN_R_MAX</a></td><td>Максимальне значення крена для керування в ручному режимі в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_MAN_YR_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_YR_MAX">FW_MAN_YR_MAX</a></td><td>Максимальна вручну додана швидкість крену. За замовчуванням: 30 градусів на секунду.</td></tr><tr><td><a id="FW_PSP_OFF"></a><a href="./../advanced_config/parameter_reference.html#FW_PSP_OFF">FW_PSP_OFF</a></td><td>Зсув вихідної установки крена (крен на рівному польоті). За замовчуванням: 0 градусів.</td></tr></tbody></table>',15),h=[o];function s(n,m,c,f,p,F){return i(),_("div",null,h)}const b=r(l,[["render",s]]);export{P as __pageData,b as default};
