import{_ as o}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as t,a as r,b as i,c as d}from"./chunks/followme_altitude_modes.DLj-K9c9.js";import{_ as n,c as s,o as _,ab as a,m as e,a as l}from"./chunks/framework.CUflZczI.js";const W=JSON.parse('{"title":"Режим Follow-Me Слідуй за мною (мультикоптер)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_mc/follow_me.md","filePath":"uk/flight_modes_mc/follow_me.md"}'),c={name:"uk/flight_modes_mc/follow_me.md"},m=a('<h1 id="режим-follow-me-слідуи-за-мною-мультикоптер" tabindex="-1">Режим Follow-Me Слідуй за мною (мультикоптер) <a class="header-anchor" href="#режим-follow-me-слідуи-за-мною-мультикоптер" aria-label="Permalink to &quot;Режим Follow-Me Слідуй за мною (мультикоптер)&quot;">​</a></h1><img src="'+o+'" title="Position fix required (e.g. GPS)" width="30px"><p>Режим <em>Follow Me</em> дозволяє багтротору автономно утримувати позицію та висоту відносно іншої системи, яка транслює свою позицію (і, за бажанням, швидкість) за допомогою повідомлення MAVLink <a href="https://mavlink.io/en/messages/common.html#FOLLOW_TARGET" target="_blank" rel="noreferrer">FOLLOW_TARGET</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим є автоматичним - для керування транспортним засобом не потрібне <em>втручання</em> користувача.</li><li>Режим потребує принаймні дійсної локальної оцінки позиції (не потребує глобальної позиції). <ul><li>Літаючі апарати не можуть перемикатися в цей режим без валідної локальної позиції.</li><li>Літаючі транспортні засоби будуть аварійно переходити в безпечний режим, якщо втрачають оцінку позиції.</li></ul></li><li>Режим перешкоджає взброєнню (транспортний засіб повинен бути включеним при переході в цей режим).</li><li>Режим вимагає, щоб швидкість вітру та час польоту були в межах допустимих значень (вказано через параметри).</li><li>Цей режим в даний час підтримується лише на багатокоптерних (або VTOL у режимі MC).</li><li>Також обраний об&#39;єкт повинен мати можливість постачання інформації про позицію.</li><li>Режим слідування підтримується <em>QGroundControl</em> на пристроях Android з модулем GPS, та <a href="#follow-me-with-mavsdk">MAVSDK</a>.</li></ul></div><h2 id="загальнии-огляд" tabindex="-1">Загальний огляд <a class="header-anchor" href="#загальнии-огляд" aria-label="Permalink to &quot;Загальний огляд&quot;">​</a></h2><p><img src="'+t+'" alt="Follow-me Concept"></p><p>Транспортний засіб автоматично почне розвертатися, щоб вирівнятися з ціллю та слідувати за нею з вказаного <a href="#FLW_TGT_FA">відносного кута</a>, <a href="#FLW_TGT_DST">відстані</a> та <a href="#FLW_TGT_HT">висоти</a> та висоти, залежно від <a href="#FLW_TGT_ALT_M">режиму керування висотою</a>.</p><p>За замовчуванням він буде слідувати безпосередньо за цільовим об&#39;єктом на відстані 8 метрів, і на висоті 8 метрів вище домашньої (озброєної) позиції.</p><p>Користувачі можуть налаштувати кут слідування, висоту та відстань за допомогою пульта дистанційного керування, як показано вище:</p><ul><li><p><em>Керування Висотою</em> здійснюється за допомогою введення <code>up-down</code> (&quot;Ручка газу&quot;). Центруйте палицю, щоб тримати відстеження цілі на постійній висоті. Підніміть або опустіть палицю, щоб налаштувати висоту.</p></li><li><p><em>Відстань слідування</em> керується вхідним параметром <code>вперед-назад</code> (&quot;Крен&quot;). Тиснення палиці вперед збільшує відстань слідування, витягування назад зменшує відстань.</p></li><li><p><em>Слідкуйте за кутом</em> керується за допомогою введення <code>left-right</code> (&quot;Roll&quot;). Рух відбувається з точки зору користувача, тому якщо ви стоїте обличчям до дрона й рухаєте палицю ліворуч, він буде рухатися ліворуч. Зверху, якщо ви рухатимете палицю вліво, квадрокоптер буде рухатися проти годинникової стрілки.</p><p>Follow Angle визначається як збільшення в годинниковому напрямку відносно напрямку руху цілі (який дорівнює 0 градусів)</p><p><img src="'+r+'" alt="Follow-me Angle Diagram"></p></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Кут, висота та відстань, встановлені за допомогою пульта керування RC, відкидаються, коли ви виходите з режиму слідування за мною. Якщо ви вийдете з режиму &quot;Follow-Me&quot; й активуєте його знову, значення будуть скинуті до їх типових значень.</p></div><p>Відео-демонстрація:</p>',12),p=e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/csuMtU6seXI",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),h=a('<h3 id="заходи-безпеки" tabindex="-1">Заходи безпеки <a class="header-anchor" href="#заходи-безпеки" aria-label="Permalink to &quot;Заходи безпеки&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><strong>Режим слідування</strong> не реалізує жодного типу уникнення перешкод. Спеціальну увагу слід звертати при використанні цього режиму.</p></div><p>Наступні попередження про польот слід дотримуватися:</p><ul><li>Режим слідування за мною слід використовувати тільки в широких відкритих місцях, які не перешкоджають дерева, електропроводи, будинки тощо. <ul><li>Встановіть висоту <a href="#FLW_TGT_HT">слідуй за висотою</a> на значення, яке вище будь-яких оточуючих перешкод. За <em>замовчуванням</em> це є 8 метрів вище домашньої (озброювання) позиції.</li></ul></li><li>Це <em>безпечніше</em> вручну піднятися на безпечну висоту перед активацією режиму &#39;слідуй за мною&#39;, ніж активувати режим &#39;слідуй за мною&#39;, коли приземлилися (навіть якщо режим реалізує автоматичний зліт).</li><li>Надайте вашому транспортному засобу достатньо місця для зупинки, особливо коли він рухається швидко.</li><li>Будьте готові повернутися до режиму позиціонування, якщо щось піде не так, особливо коли ви використовуєте режим слідування за мною вперше.</li><li>Ви не можете вимкнути режим &#39;слідуй за мною&#39;, використовуючи рухи палицею RC (оскільки це налаштує властивості). Вам потрібно мати GroundStation, яка може відправляти сигнали перемикання режиму польоту або налаштований перемикач режиму польоту на вашому RC передавачі.</li></ul><h3 id="дотримуитесь-мене-з-qgroundcontrol" tabindex="-1">Дотримуйтесь мене з QGroundControl <a class="header-anchor" href="#дотримуитесь-мене-з-qgroundcontrol" aria-label="Permalink to &quot;Дотримуйтесь мене з QGroundControl&quot;">​</a></h3><p><img src="'+i+'" alt="Follow-me QGC Example"></p><p>Режим <em>Follow Me</em> підтримується за допомогою <em>QGroundControl</em> як цілі на апаратному засобі наземної станції, що має модуль GPS. Рекомендована конфігурація - це Android-пристрій з підтримкою USB OTG з двома телеметричними радіостанціями.</p><p>Для налаштування режиму <em>Дотримуйся мене</em>:</p><ul><li>Підключіть телеметричне радіо до пристрою земної станції та інше до транспортного засобу (це дозволяє передавати інформацію про позицію між двома радіостанціями).</li><li>Вимкніть режим сну на вашому пристрої Android: <ul><li>Цей параметр зазвичай можна знайти за адресою: <strong>Налаштування &gt; Відображення</strong>.</li><li>Важливо, щоб ви встановили свій пристрій на Android, щоб не спав, оскільки це може призвести до того, що сигнал GPS перестане випромінюватися через регулярні інтервали.</li></ul></li><li>Зліт на висоту щонайменше 2-3 метри (рекомендується навіть якщо підтримується автоматичний зліт). <ul><li>Поставте транспортний засіб на землю, натисніть безпечний перемикач і відійдіть принаймні на 10 метрів.</li><li>Збройте транспортний засіб і злітайте.</li></ul></li><li>Перемикайтеся в режим слідування за мною. <ul><li>Коптер спочатку підніметься на мінімальну безпечну висоту 1 метр над землею або додому, залежно від наявності датчика відстані.</li><li>Він підніметься, поки не буде на висоті 3 метри в межах <a href="#FLW_TGT_HT">висоти слідування</a>, щоб уникнути можливих зіткнень перед рухом у горизонтальному напрямку.</li><li>Коптер завжди буде коригувати свою орієнтацію, щоб бути спрямованим на ціль</li></ul></li></ul><p>На цьому етапі ви можете почати рухатися, і дрон буде слідувати за вами.</p><p>Режим було протестовано на наступних пристроях Android:</p><ul><li>Galaxy S10</li><li>Планшет Nexus 7</li></ul><h3 id="дотримуитесь-мене-з-mavsdk" tabindex="-1">Дотримуйтесь мене з MAVSDK <a class="header-anchor" href="#дотримуитесь-мене-з-mavsdk" aria-label="Permalink to &quot;Дотримуйтесь мене з MAVSDK&quot;">​</a></h3><p><a href="https://mavsdk.mavlink.io/develop/en/api_reference/classmavsdk_1_1_follow_me.html" target="_blank" rel="noreferrer">MAVSDK</a> підтримує <a href="https://mavsdk.mavlink.io/main/en/cpp/guide/follow_me.html" target="_blank" rel="noreferrer">Follow Me</a>, дозволяючи створювати додаток для дрона, який є ціллю Follow Me.</p><p>Для отримання додаткової інформації див. документацію класу <a href="https://mavsdk.mavlink.io/main/en/cpp/guide/follow_me.html" target="_blank" rel="noreferrer">Follow Me</a>, а також <a href="https://mavsdk.mavlink.io/main/en/cpp/examples/follow_me.html" target="_blank" rel="noreferrer">приклад Follow Me</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>MAVSDK наразі не рекомендується через помилку (<a href="https://github.com/mavlink/MAVSDK/issues/1756" target="_blank" rel="noreferrer">MAVSDK#1756</a>, коли той самий повідомлення іноді відправляється двічі. Це може заплутати оцінювач цільового положення та швидкості.</p></div><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><h3 id="режим-контролю-висоти" tabindex="-1">Режим контролю висоти <a class="header-anchor" href="#режим-контролю-висоти" aria-label="Permalink to &quot;Режим контролю висоти&quot;">​</a></h3><p><img src="'+d+'" alt="Follow Me Altitude Modes"></p><p>Режим керування висотою визначає, чи є висота транспортного засобу відносно домашньої позиції, висоти місцевості або висоти, звітованої ціллю слідування.</p><ul><li><p><code>2D відстеження</code> (режим <a href="#FLW_TGT_ALT_M">висоти за замовчуванням</a>) дозволяє дрону слідувати на висоті відносно фіксованої домашньої позиції (висота зльоту). Відносна відстань до дрона до цілі зміниться, коли ви підніметесь та опуститесь (використовуйте обережно в гірській місцевості).</p></li><li><p><code>2D + Terrain</code> робить дрона слідувати на фіксованій висоті відносно місцевості під ним, використовуючи інформацію від датчика відстані.</p><ul><li>Якщо у транспортному засобі немає датчика відстані, слідування буде ідентичним до <code>2D відстеження</code>.</li><li>Датчики відстані не завжди точні, і транспортні засоби можуть бути &quot;скачущими&quot; під час польоту в цьому режимі.</li><li>Зверніть увагу, що висота вимірюється відносно землі під транспортним засобом, а не цільового об&#39;єкта. Дрон може не слідувати за змінами висоти цілі!</li></ul></li><li><p>Режим відстеження <code>3D tracking</code> дозволяє дрону слідувати на висоті відносно цілі відстеження, як постачається його GPS-датчиком. Це адаптується до змін висоти цілі, наприклад, коли ви йдете вгору по пагорбу.</p></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Не встановлюйте режим <strong>Висоти (<a href="#FLW_TGT_ALT_M">FLW_TGT_ALT_M</a></strong>) на <code>3D відстеження</code> при використанні QGC для Android (або загалом, без перевірки, що <a href="https://mavlink.io/en/messages/common.html#FOLLOW_TARGET" target="_blank" rel="noreferrer">FOLLOW_TARGET.altitude</a> є значенням AMSL).</p><p>Визначення повідомлення MAVLink <a href="https://mavlink.io/en/messages/common.html#FOLLOW_TARGET" target="_blank" rel="noreferrer">FOLLOW_TARGET</a> передбачає висоту відносно середнього рівня моря (AMSL), тоді як QGC на Android надсилає висоту відносно геодезичного еліпсоїда GPS. Це може відрізнятися на 200 метрів!</p><p>Дрон, ймовірно, не впаде через вбудований мінімальний обмежений безпечний висотний ліміт (1 метр), але він може літати набагато вище, ніж очікувалося. Якщо висота дрона значно відрізняється від зазначеної, припускайте, що вихідна висота наземної станції неправильна, і використовуйте 2D відстеження.</p></div><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Стандартна поведінка може бути налаштована за допомогою параметрів:</p><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FLW_TGT_HT"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_HT">FLW_TGT_HT</a></td><td>Висота автомобіля, який супроводжує, у метрах. Зверніть увагу, що ця висота фіксована <em>відносно дому/положення озброєння</em> (а не цільового транспортного засобу). Стандартна та мінімальна висота - 8 метрів (близько 26 футів)</td></tr><tr><td><a id="FLW_TGT_DST"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_DST">FLW_TGT_DST</a></td><td>Розділення транспортного засобу/станції на землі у <em>горизонтальній</em> (x, y) площині, у метрах. Мінімально допустимий інтервал - 1 метр. Стандартна відстань - 8 метрів (близько 26 футів).</td></tr><tr><td><a id="FLW_TGT_FA"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_FA">FLW_TGT_FA</a></td><td>Слідкуйте за кутом відносно напрямку руху цілі, в градусах. Якщо введено значення поза межами діапазону [<code>-180.0</code>, <code>+180.0</code>], воно буде автоматично звернуто і застосовано (наприклад, <code>480.0</code> буде конвертовано в <code>120.0</code>)</td></tr><tr><td><a id="FLW_TGT_ALT_M"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_ALT_M">FLW_TGT_ALT_M</a></td><td>Режим керування висотою. <br>- <code>0</code> = 2D Відстеження (Висота фіксована) <br>- <code>1</code> = 2D Відстеження + Слідування за рельєфом <br>- <code>2</code> = 3D Відстеження висоти GPS цільової точки <strong>УВАГА: <a href="#altitude-control-mode">НЕ ВИКОРИСТОВУВАТИ З QGC для Android</a></strong>.</td></tr><tr><td><a id="FLW_TGT_MAX_VEL"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_MAX_VEL">FLW_TGT_MAX_VEL</a></td><td>Максимальна відносна швидкість для орбітального руху навколо цілі, у м/с.<br>- 10 м/с виявився оптимальним показником для агресивності проти плавності.<br>- Встановлення більш високого значення означає, що траєкторія орбіти навколо цілі буде рухатися швидше, але якщо дрон фізично не здатний досягти такої швидкості, це призводить до агресивної поведінки.</td></tr><tr><td><a id="FLW_TGT_RS"></a><a href="./../advanced_config/parameter_reference.html#FLW_TGT_RS">FLW_TGT_RS</a></td><td>Динамічний алгоритм фільтрації, який реагує на фільтрацію вхідної цільової локації.<br>- <code>0.0</code> = Дуже чутливий до рухів та шумних оцінок позиції, швидкості та прискорення.<br>- <code>1.0</code> = Дуже стабільний, але не реагує на фільтр</td></tr></tbody></table><h3 id="поради-та-хитрощі" tabindex="-1">Поради та хитрощі <a class="header-anchor" href="#поради-та-хитрощі" aria-label="Permalink to &quot;Поради та хитрощі&quot;">​</a></h3>',26),u=e("ol",null,[e("li",null,[e("p",null,[l("Встановіть відстань "),e("a",{href:"#FLW_TGT_DST"},"слідування"),l(' більше 12 метрів (8 метрів є "рекомендованим мінімумом").')]),e("p",null,"Існує вроджений вплив позиції (3 ~ 5 метрів) між цільовим об'єктом та GPS-датчиком дрона, що змушує дрон слідувати 'примарній цілі' десь поблизу фактичної цілі. Це стає більш очевидним, коли відстань слідування дуже мала. Ми рекомендуємо встановити достатньо велику відстань, щоб відхилення GPS не було значним.")]),e("li",null,[e("p",null,[l("Швидкість, з якою ви можете змінювати кут слідування, залежить від параметра "),e("a",{href:"#FLW_TGT_MAX_VEL"},"максимальної тангенціальної швидкості"),l(".")]),e("p",null,[l("Експеримент показує, що значення між "),e("code",null,"5 м/с"),l(" та "),e("code",null,"10 м/с"),l(" зазвичай є придатними.")])]),e("li",null,[e("p",null,"Використовуючи коригування RC для висоти, відстані та кута, ви можете отримати деякі креативні знімки камери."),e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/o3DhvCL_M1E",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})]),l("Це відео демонструє перспективу виду Google Earth, змінюючи висоту на приблизно 50 метрів (високо), відстань на 1 метр (близько). Що дозволяє перспективу, як знято з супутника.")])])],-1),T=e("h2",{id:"відомі-проблеми",tabindex:"-1"},[l("Відомі проблеми "),e("a",{class:"header-anchor",href:"#відомі-проблеми","aria-label":'Permalink to "Відомі проблеми"'},"​")],-1),f=e("ul",null,[e("li",null,[l("Телеметричне радіо SiK 915 МГц "),e("a",{href:"./../telemetry/sik_radio.html"},"відомо"),l(", що заважає сигналу GPS, який отримують деякі пристрої Android. Тримайте радіо та пристрій на базі Android якомога подалі один від одного при використанні режиму слідування цілі, щоб уникнути перешкод.")]),e("li",null,"QGC для Android повідомляє про неправильну висоту (висота над еліпсоїдом, а не над рівнем моря). Висота може бути відхиленою на до 200м!")],-1),b=[m,p,h,u,T,f];function G(L,F,k,A,g,w){return _(),s("div",null,b)}const q=n(c,[["render",G]]);export{W as __pageData,q as default};
