import{_ as t}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as a,c as e,o,ab as i}from"./chunks/framework.CUflZczI.js";const C=JSON.parse('{"title":"Режим Зависання або Hold (Мультикоптер)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_mc/hold.md","filePath":"uk/flight_modes_mc/hold.md"}'),_={name:"uk/flight_modes_mc/hold.md"},l=i('<h1 id="режим-зависання-або-hold-мультикоптер" tabindex="-1">Режим Зависання або Hold (Мультикоптер) <a class="header-anchor" href="#режим-зависання-або-hold-мультикоптер" aria-label="Permalink to &quot;Режим Зависання або Hold (Мультикоптер)&quot;">​</a></h1><img src="'+t+'" title="Position fix required (e.g. GPS)" width="30px"><p>Режим <em>Hold або Зависання</em> призводить до того, щоб транспорт зупинявся і зависав на своєму поточному GPS-положенні та висоті.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><em>Режим утримання</em> може бути використаний для призупинення місії або для допомоги у відновленні контролю над транспортним засобом у випадку надзвичайної ситуації. Зазвичай він активується за допомогою наперед заданого перемикача.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим є автоматичним - для керування транспортним засобом не потрібне <em>втручання</em> користувача.</li><li>Режим вимагає глобальної тривимірної оцінки позиції (з GPS або виведеної з <a href="./../ros/external_position_estimation.html#enabling-auto-modes-with-a-local-position">локальної позиції</a>). <ul><li>Літаючі апарати не можуть перемикатися в цей режим без глобальної позиції.</li><li>Літаючі транспортні засоби будуть аварійно переходити в безпечний режим, якщо втрачають оцінку позиції.</li><li>Роззброєні транспортні засоби можуть переключатися в режим без дійсної оцінки позиції, але не можуть озброюватися.</li></ul></li><li>Режим вимагає, щоб швидкість вітру та час польоту були в межах допустимих значень (вказано через параметри).</li><li>Перемикачі керування RC можна використовувати для зміни режимів польоту на будь-якому автомобілі.</li><li>Рух палиць дистанційного керування буде <a href="#COM_RC_OVERRIDE">за замовчуванням</a> змінювати транспортний засіб на <a href="./../flight_modes_mc/position.html">режим позиції</a>, якщо не виникне критична аварія батареї.</li></ul></div><h2 id="технічнии-підсумок" tabindex="-1">Технічний підсумок <a class="header-anchor" href="#технічнии-підсумок" aria-label="Permalink to &quot;Технічний підсумок&quot;">​</a></h2><p>Транспортний засіб ширяє у поточному положенні на поточній висоті. Транспортний засіб спочатку підніметься до <a href="#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a>, якщо режим увімкнено нижче цієї висоти.</p><p>Рух палиці RC змінить транспортний засіб на <a href="./../flight_modes_mc/position.html">Режим положення</a> (за <a href="#COM_RC_OVERRIDE">за замовчуванням</a>).</p><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Поведінку режиму утримання/зависання можна налаштувати за допомогою наведених нижче параметрів.</p><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="NAV_MIN_LTR_ALT"></a><a href="./../advanced_config/parameter_reference.html#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a></td><td>Це мінімальна висота над домашньою позицією, яку система завжди буде дотримуватися в режимі утримання, якщо перемикається в цей режим без вказівки висоти (наприклад, за допомогою перемикача на дистанційному керуванні).</td></tr><tr><td><a id="COM_RC_OVERRIDE"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a></td><td>Контролює, чи рух палиць на багтрековому літальному апараті (або VTOL у режимі MC) викликає зміну режиму на <a href="./../flight_modes_mc/position.html">Режим позиціонування</a>. Це можна окремо увімкнути для автоматичних режимів та для режиму поза бортом, і в автоматичних режимах воно включено за замовчуванням.</td></tr><tr><td><a id="COM_RC_STICK_OV"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_STICK_OV">COM_RC_STICK_OV</a></td><td>Кількість рухів стиків, яка викликає перехід у режим <a href="./../flight_modes_mc/position.html">Положення</a> (якщо <a href="#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a> увімкнено).</td></tr></tbody></table><h2 id="дивись-також" tabindex="-1">Дивись також <a class="header-anchor" href="#дивись-також" aria-label="Permalink to &quot;Дивись також&quot;">​</a></h2><p><a href="./../flight_modes_fw/hold.html">Режим зависання (FW)</a></p>',13),r=[l];function d(h,s,c,m,n,f){return o(),e("div",null,r)}const u=a(_,[["render",d]]);export{C as __pageData,u as default};
