import{_ as t}from"./chunks/difficulty_medium.CPaRe1i7.js";import{_ as a}from"./chunks/remote_control.ss1psCxn.js";import{_ as e}from"./chunks/stabilized_mc.5krRyVGN.js";import{_ as i,c as l,o as r,ab as o}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Ручний/стабілізований режим (Multicopter)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_mc/manual_stabilized.md","filePath":"uk/flight_modes_mc/manual_stabilized.md"}'),_={name:"uk/flight_modes_mc/manual_stabilized.md"},d=o('<h1 id="ручнии-стабілізовании-режим-multicopter" tabindex="-1">Ручний/стабілізований режим (Multicopter) <a class="header-anchor" href="#ручнии-стабілізовании-режим-multicopter" aria-label="Permalink to &quot;Ручний/стабілізований режим (Multicopter)&quot;">​</a></h1><p><img src="'+t+'" title="Medium difficulty to fly" width="30px"> <img src="'+a+'" title="Manual/Remote control required" width="30px"> </p><p>Режим <em>Ручний/Стабілізація</em> стабілізує мультикоптер, коли стіки дистанційного керування відцентровані. Щоб ручно перемістити/полетіти на транспортному засобі, ви пересуваєте палички за межі центру.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей режим мультикоптера активується, якщо ви встановили режими <em>Ручний</em> або <em>Стабілізований</em>.</p></div><p>Під час ручного керування рульові палиці керують <em>кутом</em> рухомого засобу (положенням) навколо відповідних осей, рульова палиця керує швидкістю обертання над горизонтальною площиною, а педаль газу керує висотою / швидкістю.</p><p>Як тільки ви відпустите ручки керування, вони повернуться до центральної мертвої зони. Багатороторник вирівняється і зупиниться, як тільки палиці кочення та тангажу будуть в центрі. Автомобіль потім буде зависати на місці / підтримувати висоту - за умови, що він належним чином збалансований, ручка газу налаштована належним чином (див. <a href="#params">нижче</a>), й не застосовуються зовнішні сили (наприклад, вітер). Літальний апарат буде дрейфувати в напрямку будь-якого вітру, і вам доведеться керувати реостатом, щоб утримати висоту.</p><p><img src="'+e+'" alt="MC Manual Flight"></p><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Режим RC, у якому центровані стіки вирівнюють транспортний засіб (тільки - позиція не стабілізується).</p><p>Команди пілота передаються як команди кутів крену та тангажу, а рискання як команда швидкості. Тяга масштабується (див. <a href="#params">нижче</a>) і передається напряму до розподілу керування. Автопілот контролює положення, це означає що він регулює кути крену та тангажу до нуля коли органи керування пульту РК центровані всередині мертвої зони контролера (як наслідок вирівнюючи положення). Автопілот не компенсує дрейф через вітер (або інші джерела).</p><ul><li>Центровані палиці (в межах дедбенду): <ul><li>Рівень ковзання/крена прикріплюється до транспортного засобу.</li></ul></li><li>Зовнішній центр: <ul><li>Палиці кочення/крену керують кутом нахилу у цих орієнтаціях, що призводить до відповідного руху ліворуч-праворуч та вперед-назад.</li><li>Ручка дроселя керує швидкістю вгору/вниз (та швидкістю руху в інших осях).</li><li>Палиця крену контролює швидкість кутової ротації вище горизонтальної площини.</li></ul></li><li>Потрібен ручний ввід управління (наприклад, за допомогою пульта дистанційного керування, джойстика). <ul><li>Крен, Тангаж: Допомога від автопілота для стабілізації поклику. Положення палиці RC відображає орієнтацію транспортного засобу.</li><li>Дросель: Ручне керування за допомогою палиць RC. RC ввід передається напряму до розподілу керування.</li><li>Курс: Допомога від автопілота для стабілізації швидкості польоту. Положення палиці RC відображає швидкість обертання транспортного засобу в цій орієнтації.</li></ul></li></ul><p><a id="params"></a></p><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><table><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="MPC_THR_HOVER"></a><a href="./../advanced_config/parameter_reference.html#MPC_THR_HOVER">MPC_THR_HOVER</a></td><td>Наведення дроселя, яке видається, коли важіль дроселя знаходиться в центрі, а <code>MPC_THR_CURVE</code> встановлено ​​в значення за замовчуванням.</td></tr><tr><td><a id="MPC_THR_CURVE"></a><a href="./../advanced_config/parameter_reference.html#MPC_THR_CURVE">MPC_THR_CURVE</a></td><td>Визначає масштаб ручки газу. За замовчуванням це встановлено на <strong>Масштабування до утримування тяги</strong>, що означає, що коли дросельна важіль у центрі, виводиться налаштована тяга утримування (<code>MPC_THR_HOVER</code>), а вхід важеля лінійно масштабується вище і нижче цього (дозволяючи плавний перехід між стабілізованим та регулюванням висоти/позиції контролю). <br>На потужних транспортних засобах керування порогу може бути дуже низьким (наприклад, нижче 20%), тому що масштабування спотворює вхід газу - тобто тут 80% тяги керується лише верхньою половиною входу палиці, а 20% - нижнім. Якщо потрібно <code>MPC_THR_CURVE</code> може бути встановлений ​​на <strong>No Rescale</strong>, щоб не було масштабування (вхід стіку до картографування польоту є незалежним від <code>MPC_THR_HOVER</code>).</td></tr></tbody></table>',14),c=[d];function s(m,p,n,h,u,f){return r(),l("div",null,c)}const T=i(_,[["render",s]]);export{P as __pageData,T as default};
