import{_ as a,c as e,o as t,ab as o}from"./chunks/framework.CUflZczI.js";const p=JSON.parse('{"title":"Режими польоту (Ровер)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_rover/index.md","filePath":"uk/flight_modes_rover/index.md"}'),r={name:"uk/flight_modes_rover/index.md"},i=o('<h1 id="режими-польоту-ровер" tabindex="-1">Режими польоту (Ровер) <a class="header-anchor" href="#режими-польоту-ровер" aria-label="Permalink to &quot;Режими польоту (Ровер)&quot;">​</a></h1><p>Режими польоту (або, точніше, &quot;режими керування&quot; для наземних транспортних засобів) забезпечують автопілоту підтримку, щоб полегшити ручне керування транспортним засобом, виконувати автономні місії або передавати керування зовнішній системі.</p><p>У наземних транспортних засобах PX4 підтримуються лише <a href="#manual-mode">ручний режим</a>, <a href="#mission-mode">режим місії</a> та <a href="#offboard-mode">режим зовнішнього керування</a> (інші режими можуть бути доступні в наземній станції, але всі вони працюють так само, як ручний режим).</p><h3 id="ручнии-режим" tabindex="-1">Ручний режим <a class="header-anchor" href="#ручнии-режим" aria-label="Permalink to &quot;Ручний режим&quot;">​</a></h3><p><em>Ручний режим</em> - це єдиний ручний режим для наземних транспортних засобів на PX4 і вимагає ручного контролера (RC контролера, геймпада, джойстика тощо).</p><p>У цьому режимі двигуни зупиняються, коли джойстики RC знаходяться в центрі. Щоб перемістити транспортний засіб, ви пересуваєте палички за межі центру.</p><p>Як тільки ви відпустите ручки керування, вони повернуться до центральної мертвої зони. Це вимкне мотори та центр коліс. Активного гальмування немає, тому транспортний засіб може продовжувати рух, доки його імпульс не розсіється.</p><h3 id="режим-місіі" tabindex="-1">Режим місії <a class="header-anchor" href="#режим-місіі" aria-label="Permalink to &quot;Режим місії&quot;">​</a></h3><p><a href="./../flight_modes/mission.html">Режим місії</a> - це автоматичний режим, який змушує транспортний засіб виконувати передбачений автономний <a href="./../flying/missions.html">план місії</a>, який був завантажений до керуючого пристрою польоту. Зазвичай місія створюється та завантажується за допомогою програми для керування наземною станцією (GCS), такої як <a href="https://docs.qgroundcontrol.com/master/en/" target="_blank" rel="noreferrer">QGroundControl</a>.</p><h3 id="зовнішнє-керування" tabindex="-1">Зовнішнє керування <a class="header-anchor" href="#зовнішнє-керування" aria-label="Permalink to &quot;Зовнішнє керування&quot;">​</a></h3><p><a href="./../flight_modes/offboard.html">Режим безпосереднього управління</a> призводить до того, що транспортний засіб слухається позиції, швидкості або орієнтації, які задаються через MAVLink. Не всі типи заданих точок мають сенс для наземного транспортного засобу або підтримуються.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей режим призначений для керування транспортним засобом з компаньйонних комп&#39;ютерів та наземних станцій!</p></div><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../config/flight_mode.html">Основна конфігурація &gt; Режими польоту</a> - Як зв&#39;язати перемикачі керування RC з конкретними режимами польоту</li><li><a href="./../flight_modes_mc/README.html">Режими польоту (Мультикоптер)</a></li><li><a href="./../flight_modes_fw/README.html">Режими польоту (Фіксовані крила)</a></li><li><a href="./../flight_modes_vtol/README.html">Режими польоту (VTOL)</a></li></ul>',14),l=[i];function h(s,n,d,m,_,f){return t(),e("div",null,l)}const u=a(r,[["render",h]]);export{p as __pageData,u as default};
