import{_ as e,a as t}from"./chunks/geofence_editor.DgVmtifv.js";import{_ as o,c as a,o as r,ab as l}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Геозона","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flying/geofence.md","filePath":"uk/flying/geofence.md"}'),n={name:"uk/flying/geofence.md"},i=l('<h1 id="геозона" tabindex="-1">Геозона <a class="header-anchor" href="#геозона" aria-label="Permalink to &quot;Геозона&quot;">​</a></h1><p>Геозона — це віртуальна межа, яка визначає, де може рухатися апарат. Геозони можна використовувати, щоб запобігти польоту апарату за межі зони дії пульта дистанційного керування або в небезпечний чи обмежений повітряний простір.</p><p>PX4 надає два незалежні механізми для визначення геозони:</p><ul><li>Базова &quot;безвідмовна&quot; геозона, яка визначається простим циліндром.</li><li>Більш складні геометрії можуть бути визначені за допомогою плану геозони (<em>QGroundControl</em>).</li></ul><p>:::note Геозони застосовуються у всіх режимах, включаючи як місії, так і ручний політ. :::</p><h2 id="безвідмовна-геозона" tabindex="-1">Безвідмовна геозона <a class="header-anchor" href="#безвідмовна-геозона" aria-label="Permalink to &quot;Безвідмовна геозона&quot;">​</a></h2><p><a href="./../config/safety.html#geofence-failsafe">Безвідмовна геозона</a> визначає циліндр, центрований відносно домашньої позиції, з вказаним максимальним радіусом і висотою.</p><p>Налаштування також включають &quot;аварійну дію&quot; у випадку порушення геозони. Це може бути просто попереджувальне сповіщення, але частіше апарат негайно <a href="./../flight_modes/return.html">повертається</a> у безпечну локацію.</p><p>Для отримання додаткової інформації дивіться: <a href="./../config/safety.html#geofence-failsafe"> Безпека &gt; Безвідмовна геозона</a>.</p><h2 id="план-геозони" tabindex="-1">План геозони <a class="header-anchor" href="#план-геозони" aria-label="Permalink to &quot;План геозони&quot;">​</a></h2><p>PX4 підтримує комплексні межі геозони, які складаються з кількох кругових та багатокутних областей, які можуть бути визначені як зони включення (літати всередині) або виключення (літати зовні).</p><p>Геозона планується в <em>QGroundControl</em> разом з місією та точками збору.</p><p><img src="'+e+'" alt="План геозони"></p><p>Планування геозони детально описане в <a href="https://docs.qgroundcontrol.com/master/en/PlanView/PlanGeoFence.html" target="_blank" rel="noreferrer">Перегляд плану &gt; Геозона</a> (Керівництво користувача QGroundControl).</p><p>Підсумовуючи:</p><ol><li>Відкрийте <em>QGroundControl &gt; Перегляд плану</em>.</li><li>Оберіть <em>Тип плану</em>: <strong>Fence</strong>. Це відобразить <em>Редактор геозони</em>. <img src="'+t+'" alt="План геозони"></li><li>Оберіть <strong>Polygon Fence</strong> або <strong>Circular Fence</strong>, щоб додати на карту <em>базову</em> зону бажаного типу. Це також додає запис про тип зони в редактор.</li><li>Використовуйте карту для налаштування форми та положення зони. <ul><li>Маркер центру зони може бути використаний для переміщення зони у правильне положення.</li><li>Маркер на межі кругової зони може бути використаний для зміни радіуса.</li><li>Маркери на кутах (вершинах) можуть бути використані для зміни геометрії полігону. Додаткові вершини створюються шляхом натискання на середину ліній між наявними маркерами.</li></ul></li><li>Використовуйте <em>Редактор геозони</em> для встановлення зони як включення або виключення, а також для вибору зони для редагування (кнопка <strong>Edit</strong>) або видалення (кнопка <strong>Del</strong>).</li><li>Додайте стільки зон, скільки забажаєте.</li><li>Після завершення натисніть кнопку <strong>Upload</strong> (у верхньому правому куті), щоб відправити зону (разом із точками збору та місією) до апарату.</li><li>Встановіть дію при порушенні в <a href="./../config/safety.html#geofence-failsafe">Geofence Failsafe</a>.</li></ol><p>:::note Будь-яка геозона, яка не включає домашню позицію, буде відхилена політним контролером і не буде завантажена. Якщо апарат знаходиться в польоті, будь-яка геозона, яка одразу ж буде порушена після завантаження, також відхиляється. :::</p><p>:::note PX4 реалізує <a href="https://mavlink.io/en/services/mission.html" target="_blank" rel="noreferrer">мікросервіс місій</a> MAVLink, який включає підтримку геозон. :::</p>',18),s=[i];function c(f,m,p,g,h,_){return r(),a("div",null,s)}const k=o(n,[["render",c]]);export{P as __pageData,k as default};
