import{_ as e}from"./chunks/rally_points.B1TIZB5_.js";import{_ as t,c as a,o,ab as r}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Точки безпеки (точки збору)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flying/plan_safety_points.md","filePath":"uk/flying/plan_safety_points.md"}'),l={name:"uk/flying/plan_safety_points.md"},n=r('<h1 id="точки-безпеки-точки-збору" tabindex="-1">Точки безпеки (точки збору) <a class="header-anchor" href="#точки-безпеки-точки-збору" aria-label="Permalink to &quot;Точки безпеки (точки збору)&quot;">​</a></h1><p>Точки безпеки — це альтернативні пункти призначення/посадки в <a href="./../flight_modes/return.html">режимі повернення</a>. Коли активовано, апарат обере <em>найближчий пункт повернення</em> з: домашньої локації, місця посадки за місією або <em>точки безпеки</em>.</p><p><img src="'+e+'" alt="Точки безпеки"></p><h2 id="створення-визначення-точок-безпеки" tabindex="-1">Створення / визначення точок безпеки <a class="header-anchor" href="#створення-визначення-точок-безпеки" aria-label="Permalink to &quot;Створення / визначення точок безпеки&quot;">​</a></h2><p>Точки безпеки створюються в <em>QGroundControl</em> (який називає їх &quot;точками збору&quot;).</p><p>На загальному рівні:</p><ol><li>Відкрийте <strong>QGroundControl &gt; Plan View</strong></li><li>Виберіть вкладку/кнопку <strong>Rally</strong> в <em>Plan Editor</em> (праворуч на екрані).</li><li>Виберіть <strong>Rally Point</strong> на панелі інструментів (ліворуч на екрані).</li><li>Натисніть будь-де на карті, щоб додати точку збору/безпеки. <ul><li><em>Plan Editor</em> відображає та дозволяє редагувати поточну точку збору (позначену зеленим <strong>R</strong> на карті).</li><li>Ви можете вибрати іншу точку збору (позначену на карті більш помаранчевим/жовтим <strong>R</strong>), щоб змінити її.</li></ul></li><li>Виберіть <strong>Upload Required</strong>, щоб завантажити точки збору (разом із будь-якою <a href="./../flying/missions.html">місією</a> та <a href="./../flying/geofence.html">геозоною</a>) на апарат.</li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Більш повну документацію можна знайти в <em>Посібнику користувача QGroundControl</em>: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/plan_view/plan_rally_points.html" target="_blank" rel="noreferrer">Екран плану - Точки збору</a>.</p></div><h2 id="використання-точок-безпеки" tabindex="-1">Використання точок безпеки <a class="header-anchor" href="#використання-точок-безпеки" aria-label="Permalink to &quot;Використання точок безпеки&quot;">​</a></h2><p>Точки безпеки не активовано за замовчанням (існує кілька різних <a href="./../flight_modes/return.html#return_types">типів режиму повернення</a>).</p><p>Щоб активувати точки безпеки:</p><ol><li><a href="./../advanced_config/parameters.html">Використовуйте редактор параметрів QGroundControl</a>, щоб встановити параметр: <a href="./../advanced_config/parameter_reference.html#RTL_TYPE">RTL_TYPE=3</a>.</li></ol>',12),i=[n];function s(_,p,m,c,d,h){return o(),a("div",null,i)}const P=t(l,[["render",s]]);export{g as __pageData,P as default};
