import{_ as r,E as t,c as i,J as o,m as e,a as l,ab as n,o as s}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"Автогиро Каркаси","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_autogyro/index.md","filePath":"uk/frames_autogyro/index.md"}'),d={name:"uk/frames_autogyro/index.md"},h=e("h1",{id:"автогиро-каркаси",tabindex:"-1"},[l("Автогиро Каркаси "),e("a",{class:"header-anchor",href:"#автогиро-каркаси","aria-label":'Permalink to "Автогиро Каркаси"'},"​")],-1),c=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Підтримка для корпуса автогиро є <a href="./../airframes/#experimental-vehicles">експериментальною</a>. Волонтери та контрибютори, <a href="./../contribute/">внесення</a> нових функцій, нових конфігурацій каркасів або інших вдосконалень дуже вітається! :::</p><p><a href="https://en.wikipedia.org/wiki/Autogyro" target="_blank" rel="noreferrer">Автогиро</a> - це тип <a href="https://en.wikipedia.org/wiki/Rotorcraft" target="_blank" rel="noreferrer">вертольота</a>. Порівняно з іншими конструкціями він має наступні переваги:</p><ul><li>Можливість злітати та сідати, використовуючи лише дуже коротку злітну смугу (порівняно з фіксованим крилом).</li><li>Висока стійкість до погодних умов, особливо поривів вітру.</li><li>Володіння неприводним ротором, що дозволяє йому працювати в режимі авторотації (один із аварійних режимів гелікоптера). Отже, йому не потрібно активно змінювати режим польоту у разі відмови (йому не потрібен парашут або інші активно працюючі пристрої). Таким чином, політ за своєю суттю є стабільним у будь-який час.</li><li>Відсутність <a href="https://en.wikipedia.org/wiki/Downwash" target="_blank" rel="noreferrer">зниження</a> під час злітання або посадки, що створює небажане кружляння пилу.</li><li><a href="https://en.wikipedia.org/wiki/Lift-to-drag_ratio" target="_blank" rel="noreferrer">Низький коефіцієнт ліфт-опір</a>, який може бути налаштований за допомогою параметрів конструкції. Ця здатність може бути корисною, оскільки безпілотний автогир не може летіти дуже далеко у разі відмови (як у випадку звичайного літака), проте польот все ще безпечний, і літак не падає (як у випадку багтороторного або гелікоптера).</li></ul><h2 id="підтримувані-конструкціі" tabindex="-1">Підтримувані конструкції <a class="header-anchor" href="#підтримувані-конструкціі" aria-label="Permalink to &quot;Підтримувані конструкції&quot;">​</a></h2><p>PX4 підтримує кілька автогирних конструкцій. Повний набір підтримуваних конфігурацій можна переглянути в розділі <a href="./../airframes/airframe_reference.html#autogyro">Довідник про планери &gt; Autogyro(Автожир)</a>.</p><h3 id="diy-конструкціі" tabindex="-1">DIY Конструкції <a class="header-anchor" href="#diy-конструкціі" aria-label="Permalink to &quot;DIY Конструкції&quot;">​</a></h3><p>У цьому розділі містяться журнали збірки/інструкції щодо складання та налаштування ряду Autogyro конструкцій.</p><ul><li><a href="./../frames_autogyro/thunderfly_auto_g2.html">ThunderFly Auto-G2 (Holybro pix32)</a></li></ul><h3 id="повні-конструкціі-з-попередніми-налаштуваннями-px4" tabindex="-1">Повні конструкції з попередніми налаштуваннями PX4 <a class="header-anchor" href="#повні-конструкціі-з-попередніми-налаштуваннями-px4" aria-label="Permalink to &quot;Повні конструкції з попередніми налаштуваннями PX4&quot;">​</a></h3><p>У цьому розділі перелічені транспортні засоби, які продаються повністю зібраними та готові до польоту (RTF), з встановленим PX4.</p><ul><li><a href="https://www.thunderfly.cz/tf-g2.html" target="_blank" rel="noreferrer">ThunderFly TF-G2</a></li></ul></div>',1);function _(p,f,u,m,g,k){const a=t("LinkedBadge");return s(),i("div",null,[h,o(a,{type:"warning",text:"Experimental",url:"../airframes/#experimental-vehicles"}),c])}const y=r(d,[["render",_]]);export{b as __pageData,y as default};
