import{_ as a,a as t,b as l,c as o,d as i,e as s,f as h,g as n}from"./chunks/autog2_4.DxPgbVig.js";import{_ as u,c as p,o as b,ab as r,m as e}from"./chunks/framework.CUflZczI.js";const D=JSON.parse('{"title":"ThunderFly Auto-G2 Autogyro","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_autogyro/thunderfly_auto_g2.md","filePath":"uk/frames_autogyro/thunderfly_auto_g2.md"}'),c={name:"uk/frames_autogyro/thunderfly_auto_g2.md"},d=r('<h1 id="thunderfly-auto-g2-autogyro" tabindex="-1">ThunderFly Auto-G2 Autogyro <a class="header-anchor" href="#thunderfly-auto-g2-autogyro" aria-label="Permalink to &quot;ThunderFly Auto-G2 Autogyro&quot;">​</a></h1><p><em>ThunderFly Auto-G2</em> - це автопілот-керований автогир, заснований на <a href="https://hobbyking.com/en_us/duraflytm-auto-g2-gyrocopter-w-auto-start-system-821mm-pnf.html" target="_blank" rel="noreferrer">RC-моделі Durafly™ Auto-G2 Gyrocopter</a>, замінивши кілька частин оригінальної моделі на друковані в 3D.</p><p><img src="'+a+'" alt="Auto-G2"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Автогир Auto-G2 розробляється та утримується авіакомпанією ThuderFly s.r.o.</p></div><p>Усі додані частини доступні на <a href="https://github.com/ThunderFly-aerospace/TF-G2/" target="_blank" rel="noreferrer">GitHub</a> як проект з відкритим вихідним кодом. Надруковані деталі розроблені в <a href="https://www.openscad.org/" target="_blank" rel="noreferrer">OpenSCAD</a>.</p><h2 id="модифікаціі" tabindex="-1">Модифікації <a class="header-anchor" href="#модифікаціі" aria-label="Permalink to &quot;Модифікації&quot;">​</a></h2><p>Durafly Auto-G2, у своєму оригінальному дизайні, має трьохлопастний ротор з лопатями завдовжки 400 мм з профілем CLARK-Y. Головка ротора дозволяє нахил у вісі ROLL лише. Автогиро керується рулем та ліфтом. Durafly Auto-G2 коробка автожира містить полістирольне тіло автожира, регулятор швидкості, двигун (мабуть 800 кВт), 4 сервопривода, хвостові аеродинамічні профілі, 3 лопаті з частинами центру ротора, дротову шасі та попередньовідкручувач.</p><p>Модифікація моделі Durafly виглядає наступним чином:</p><ul><li>Додавання автопілота</li><li>Головка ротора з двома осями вільності (крен, кривошип)</li><li>Двохлопатевий ротор з безпечною роторною пластиною, яку можна безпечно зламати</li><li>Більші посадочні шасі</li></ul><h3 id="автопілот" tabindex="-1">Автопілот <a class="header-anchor" href="#автопілот" aria-label="Permalink to &quot;Автопілот&quot;">​</a></h3><p>Літак з усіма модифікаціями вже досить важкий. Отже, рекомендується низькомасовий контролер польоту (наприклад, <a href="./../flight_controller/holybro_pix32.html">Holybro pix32</a> або <a href="./../flight_controller/cuav_v5_nano.html">CUAV nano</a>).</p><p>Автопілот повинен бути встановлений на нижній стороні автожира на демпфуючій подушці, надрукованій у 3D. Ми використали платформу згасання, знайдену на <a href="https://www.thingiverse.com/thing:160655" target="_blank" rel="noreferrer">thingiverse</a></p><h3 id="голова-ротора" tabindex="-1">Голова ротора <a class="header-anchor" href="#голова-ротора" aria-label="Permalink to &quot;Голова ротора&quot;">​</a></h3><p>Головка ротора (у порівнянні з оригінальним автожиром) модифікована так, що дозволяє рух у обох віслях кочення та тангажу. Завдяки цьому ротор може контролювати обертання, а також підйом автогира. Напрямне керування автожира за допомогою ротора можливе навіть у випадку низької швидкості повітря порівняно з оригінальним керуванням рулем і елеватором.</p><p>Напечатана головка ротора складається з трьох частин. Нижня частина закручується за допомогою винта М2,5 до оригінального пілона з фанери. Гвинт M3x35, знайдений між першою та другою частиною, створює вільність по осі кроку, а з&#39;єднання між другою та третьою частиною - вільність по осі кочення. Останній вісь складається з гвинта M3x30 з самоблокуючою гайкою. З боку ротора головка шурупа має велику площу шайби.</p><p>Вісь ротора, виготовлена з високої міцності гвинта M3x50, проходить через третю частину. Використовуються підшипники 623 2Z C3 SKF. В кінці цієї частини є кульові вали, прикріплені за допомогою гвинтів M2.5 до сервоприводів, розташованих у нижній частині пілона. Краще обміняти ці оригінальні сервоприводи на якісніші, оскільки вони слабкі, і в оригінальній конструкції вони допомагають один одному.</p><p><img src="'+t+'" alt="Rotorhead"></p><h3 id="двоклиновии-ротор" tabindex="-1">Двоклиновий ротор <a class="header-anchor" href="#двоклиновии-ротор" aria-label="Permalink to &quot;Двоклиновий ротор&quot;">​</a></h3><p>Оригінальний автожир Durafly Auto-G2 має трьохлопастний ротор, який був модифікований у цій моделі для використання двохлопастного ротора. Причини - це зменшення вібрації та спрощення конструкції. Надруковані центральні частини призначені для використання як з китайськими лопатями Durafly, так і з надрукованими на 3D принтері лопатями.</p><p>Центральна частина ротора складається з кількох компонентів, які виконують наступні ролі:</p><ul><li>Вони дозволяють лопатці вільно коливатися.</li><li>Вони мають зони деформації, які ламаються при ударі з землею. Завдяки цьому ротор зазвичай можна швидко відремонтувати, замінивши лише один компонент.</li><li>Просте налаштування кута атаки лопаток.</li></ul><h4 id="лопатки-ротора-hobbyking" tabindex="-1">Лопатки ротора HobbyKing <a class="header-anchor" href="#лопатки-ротора-hobbyking" aria-label="Permalink to &quot;Лопатки ротора HobbyKing&quot;">​</a></h4><p>Можна використовувати надруковану центральну частину ротора з оригінальними лопатями. Ці леза можна придбати на <a href="https://hobbyking.com/en_us/duraflytm-auto-g-gyrocopter-821mm-replacement-main-blade-1pcs-bag.html" target="_blank" rel="noreferrer">HobbyKing</a>. Леза від Hobbyking відрізняються положенням центру ваги, тому необхідно правильно їх збалансувати.</p><h4 id="роторні-лопаті-надруковані-у-3d" tabindex="-1">Роторні лопаті, надруковані у 3D <a class="header-anchor" href="#роторні-лопаті-надруковані-у-3d" aria-label="Permalink to &quot;Роторні лопаті, надруковані у 3D&quot;">​</a></h4><p>Також можливо надрукувати лопаті ротора.</p><p>Надруковані лопаті ротора все ще знаходяться в стадії розробки, але попередні тести показують, що вони мають кращу якість в основному завдяки своїй точній формі та відсутності поздовжніх жолобів. Проте деякі з процесів виробництва все ще потребують налаштування.</p><p><img src="'+l+'" alt="Blades assembly"></p><h4 id="балансування" tabindex="-1">Балансування <a class="header-anchor" href="#балансування" aria-label="Permalink to &quot;Балансування&quot;">​</a></h4><p>Правильний баланс лез дуже важливий для мінімізації вібрацій. Леза повинні бути збалансовані таким чином, щоб центр ваги знаходився в середині осі ротора.</p><p>Надруковані леза збалансовані в процесі виробництва і не потребують додаткового балансування.</p><h3 id="випуск-пристрою" tabindex="-1">Випуск пристрою <a class="header-anchor" href="#випуск-пристрою" aria-label="Permalink to &quot;Випуск пристрою&quot;">​</a></h3><p>Якщо ви хочете запустити автожира за допомогою лебідки або якщо ви хочете запустити його шляхом буксирування, вам потрібно надрукувати пристрій випуску. Це невелика коробка, обладнана сервоприводом, який витягує шпильку та відпускає мотузку.</p><p>Вся частина склеєна за допомогою гарячого клейового розчину під двигуном на нижній частині корпусу автогиро. Якщо автогир буксирується за допомогою каната, його двигун не повинен бути увімкнений. Це може бути вирішено, наприклад, нулюючи вихід двигуна в передавачі, якщо вимикач пристрою вивільнення закритий.</p><p><img src="'+o+'" alt="Release device"></p><h2 id="список-деталеи" tabindex="-1">Список деталей <a class="header-anchor" href="#список-деталеи" aria-label="Permalink to &quot;Список деталей&quot;">​</a></h2><h3 id="електроніка" tabindex="-1">Електроніка <a class="header-anchor" href="#електроніка" aria-label="Permalink to &quot;Електроніка&quot;">​</a></h3><ul><li>Автопілот (<a href="./../flight_controller/holybro_pix32.html">Holybro pix32</a>, <a href="./../flight_controller/cuav_v5_nano.html">CUAV nano</a>)</li><li>GPS (Модуль GPS NEO-6M, з патч-антеною)</li><li>Датчик швидкості повітря ( <a href="https://www.sensirion.com/en/flow-sensors/differential-pressure-sensors/worlds-smallest-differential-pressure-sensor/" target="_blank" rel="noreferrer">SDP3x</a>)</li><li>Сильніші сервоприводи як заміна оригінальних (необов&#39;язково), (<a href="https://www.blue-bird-model.com/products_detail/411.htm" target="_blank" rel="noreferrer">BlueBird BMS-125WV</a>)</li><li>Додатковий сервопривід для пристрою відпускання (необов&#39;язково)</li></ul><h3 id="механічні-деталі" tabindex="-1">Механічні деталі <a class="header-anchor" href="#механічні-деталі" aria-label="Permalink to &quot;Механічні деталі&quot;">​</a></h3><ul><li>Підшипник роторної головки (623 2Z C3)</li><li>Пропелер (<a href="https://www.apcprop.com/product/10x7e/" target="_blank" rel="noreferrer">APC 10x7</a>)</li><li><a href="https://mpjet.com/shop/gb/prop-adapters/184-collet-prop-adapter-19-mm-4-mm-shaft-m629-standard.html" target="_blank" rel="noreferrer">Адаптер пропелера</a></li></ul><h3 id="друковані-частини" tabindex="-1">Друковані частини <a class="header-anchor" href="#друковані-частини" aria-label="Permalink to &quot;Друковані частини&quot;">​</a></h3><ul><li><p>Голова ротора:</p><ul><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1001.stl" target="_blank" rel="noreferrer">Кінцева опора</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1002.stl" target="_blank" rel="noreferrer">Частина кроку</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1003.stl" target="_blank" rel="noreferrer">Частина рулону</a></li></ul></li><li><p>Ротор:</p><ul><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1008.stl" target="_blank" rel="noreferrer">верхня частина пральної машини</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1004.stl" target="_blank" rel="noreferrer">нижня частина пральної машини центральної частини</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/888_1001.stl" target="_blank" rel="noreferrer">центральна пластина з деформаційними зонами</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/111_1005.stl" target="_blank" rel="noreferrer">шайби для встановлення AoA лопаток</a></li><li><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/888_1002.stl" target="_blank" rel="noreferrer">Гайка ротора</a></li></ul></li><li><p>Лопаті ротора (необов&#39;язково)</p></li><li><p>Держатель автопілота</p></li><li><p><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/888_1010.stl" target="_blank" rel="noreferrer">Випуск пристрою</a></p></li><li><p><a href="https://github.com/ThunderFly-aerospace/Auto-G2/blob/master/CAD/stl/888_1011.stl" target="_blank" rel="noreferrer">Передні колеса</a></p></li></ul><h3 id="рекомендовані-запасні-частини" tabindex="-1">Рекомендовані запасні частини <a class="header-anchor" href="#рекомендовані-запасні-частини" aria-label="Permalink to &quot;Рекомендовані запасні частини&quot;">​</a></h3><ul><li>Сервоприводи з покращеною якістю (рекомендовано <a href="https://www.blue-bird-model.com/products_detail/411.htm" target="_blank" rel="noreferrer">BlueBird BMS-125WV</a>, оригінальні сервоприводи не дуже довговічні))</li><li>Пропелер (<a href="https://www.apcprop.com/product/10x7e/" target="_blank" rel="noreferrer">APC 10x7</a>)</li><li>Центральна пластина ротора з деформаційними зонами (надрукована в 3D)</li><li>Лопатки ротора (<a href="https://hobbyking.com/en_us/duraflytm-auto-g-gyrocopter-821mm-replacement-main-blade-1pcs-bag.html" target="_blank" rel="noreferrer">HobbyKing</a> або надруковані на 3D принтері)</li></ul><h2 id="відео" tabindex="-1">Відео <a class="header-anchor" href="#відео" aria-label="Permalink to &quot;Відео&quot;">​</a></h2>',44),m=e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/YhXXSWz5wWs",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),_=r('<h2 id="фотогалерея-змін" tabindex="-1">Фотогалерея змін <a class="header-anchor" href="#фотогалерея-змін" aria-label="Permalink to &quot;Фотогалерея змін&quot;">​</a></h2><p><img src="'+i+'" alt="Auto-G2 1"> <img src="'+s+'" alt="Auto-G2 2"> <img src="'+h+'" alt="Auto-G2 3"> <img src="'+n+'" alt="Auto-G2 4"></p>',2),f=[d,m,_];function g(y,k,A,w,q,x){return b(),p("div",null,f)}const P=u(c,[["render",g]]);export{D as __pageData,P as default};
