import{_ as t,E as r,c as i,J as l,m as e,a as o,ab as n,o as s}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"Гелікоптери","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_helicopter/index.md","filePath":"uk/frames_helicopter/index.md"}'),h={name:"uk/frames_helicopter/index.md"},c=e("h1",{id:"гелікоптери",tabindex:"-1"},[o("Гелікоптери "),e("a",{class:"header-anchor",href:"#гелікоптери","aria-label":'Permalink to "Гелікоптери"'},"​")],-1),d=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Підтримка гелікоптерів є <a href="./../airframes/README.html#experimental-vehicles">експериментальною</a>. Волонтери та контрибютори, <a href="./../contribute/README.html">внесення</a> нових функцій, нових конфігурацій каркасів або інших вдосконалень дуже вітається!</p><p>Проблеми включають:</p><ul><li>Обмежена підтримка різних типів гелікоптерів. Наприклад, PX4 не підтримує гелікоптери з коаксіальним або подвійним ротором, а також функції, такі як автоматичне регулювання обертів і авторотація.</li></ul></div><h2 id="типи-гелікоптерів" tabindex="-1">Типи гелікоптерів <a class="header-anchor" href="#типи-гелікоптерів" aria-label="Permalink to &quot;Типи гелікоптерів&quot;">​</a></h2><p>PX4 підтримує гелікоптери з одним головним ротором з рингом рушія, керованим рухомим майданчиком за допомогою до 4 рушійних сервоприводів рухомого майданчика, і:</p><ul><li>механічно розділене хвостове вістря, приводиме в дію електронним регулятором, або</li><li>механічно з&#39;єднане хвостове вістря, кероване сервоприводом на хвостовому двигуні.</li></ul><p>Дозволені операції польоту та <a href="./../flight_modes_mc/README.html">режими польоту</a> такі ж, як і для мультикоптерів. Однак слід зауважити, що (на момент написання) 3D-польот з використанням негативного тяги не підтримується в автономних/керованих режимах.</p><h2 id="збирання" tabindex="-1">Збирання <a class="header-anchor" href="#збирання" aria-label="Permalink to &quot;Збирання&quot;">​</a></h2><p>Збирання основних компонентів автопілота схоже для всіх каркасів. Це описано в <a href="./../assembly/README.html">Базовому збиранні</a>.</p><p>Специфічне для гелікоптерів збирання в основному полягає в підключенні і живленні двигунів та сервоприводів рухомого майданчика.</p><p>:::note</p><p>Зауважте, що контролер польоту не може живити двигуни та сервоприводи (лише модуль GPS, приймач RC і модулі телеметрії малої потужності можуть живитися від контролерів польоту Pixhawk). Як правило, для живлення двигунів використовується плата розподілу електроенергії (PDB), а для живлення кожного з сервоприводів використовується окрема (або інтегрована) схема відключення батарей (BEC). :::</p><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Конфігурацію та налаштування гелікооптера описано в:</p><ul><li><a href="./../config_heli/README.html">Конфігурація гелікоптера</a>: вибір рами транспортного засобу, конфігурація та тестування приводу та налаштування.</li><li><a href="./../config/README.html">Стандартна конфігурація</a>: стандартна конфігурація та кроки калібрування для більшості рам. Це включає конфігурацію/калібрування основних компонентів, таких як компас і гіроскоп, налаштування режимів польоту на пульті дистанційного керування та налаштування безпеки.</li></ul><h2 id="каркасні-конструкціі" tabindex="-1">Каркасні конструкції <a class="header-anchor" href="#каркасні-конструкціі" aria-label="Permalink to &quot;Каркасні конструкції&quot;">​</a></h2><p>Недоступно.</p>',15);function p(m,_,f,u,x,E){const a=r("LinkedBadge");return s(),i("div",null,[c,l(a,{type:"warning",text:"Experimental",url:"../airframes/#experimental-vehicles"}),d])}const P=t(h,[["render",p]]);export{k as __pageData,P as default};
