import{_ as t,a as i,b as a,c as r,d as l,e as o,f as e,g as s,h as p,i as d,j as n,k as m,l as h,m as c,n as _,o as u,p as f,q as b,r as g,s as k,t as P,u as V,v as A,w as x,x as q,y as w,z as y,A as Q,B as v,C,D as S}from"./chunks/qgc_airframe_holybro_qav250.COq5eL-K.js";import{_ as M,c as T,o as H,ab as F}from"./chunks/framework.CUflZczI.js";const $=JSON.parse('{"title":"Holybro QAV250 + Pixhawk 4 Mini Build (Припинено)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_multicopter/holybro_qav250_pixhawk4_mini.md","filePath":"uk/frames_multicopter/holybro_qav250_pixhawk4_mini.md"}'),B={name:"uk/frames_multicopter/holybro_qav250_pixhawk4_mini.md"},E=F('<h1 id="holybro-qav250-pixhawk-4-mini-build-припинено" tabindex="-1">Holybro QAV250 + Pixhawk 4 Mini Build (Припинено) <a class="header-anchor" href="#holybro-qav250-pixhawk-4-mini-build-припинено" aria-label="Permalink to &quot;Holybro QAV250 + Pixhawk 4 Mini Build (Припинено)&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title"><em>Комплект Holybro Pixhawk 4 Mini QAV250</em> більше не доступний.</p><p>Інструкції залишено тут, оскільки дуже схожі комплекти на основі Pix32 v6 доступні <a href="https://holybro.com/products/qav250-kit" target="_blank" rel="noreferrer">тут</a>. Ці інструкції все ще можна виконувати (і можуть бути оновлені до Pix32 v6).</p></div><p>Повні комплекти включають карбонову раму для гонок QAV250, контролер польоту та майже всі інші необхідні компоненти (окрім акумулятора та приймача). Є варіанти набору як з підтримкою FPV, так і без неї. Ця тема надає повні інструкції для збирання комплекту та налаштування PX4 з використанням <em>QGroundControl</em>.</p><p>Основна Інформація</p><ul><li><strong>Каркас:</strong> Holybro QAV250</li><li><strong>Контролер польоту:</strong> <a href="./../flight_controller/pixhawk4_mini.html">Pixhawk 4 Mini</a></li><li><strong>Час зборки (приблизно):</strong> 3.5 hours (2 for frame, 1.5 autopilot installation/configuration)</li></ul><p><img src="'+t+'" alt="Assembled Holybro QAV250 with Pixhawk4 Mini"></p><h2 id="швидкии-старт-керівництво" tabindex="-1">Швидкий старт керівництво <a class="header-anchor" href="#швидкии-старт-керівництво" aria-label="Permalink to &quot;Швидкий старт керівництво&quot;">​</a></h2><p><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/flight_controller/pixhawk4mini/pixhawk4mini_qav250kit_quickstart_web.pdf" target="_blank" rel="noreferrer">Керівництво початку роботи з комплектом Pixhawk 4 Mini QAV250</a></p><h2 id="специфікація-матеріалів" tabindex="-1">Специфікація матеріалів <a class="header-anchor" href="#специфікація-матеріалів" aria-label="Permalink to &quot;Специфікація матеріалів&quot;">​</a></h2><p>Набір Holybro <a href="https://holybro.com/products/qav250-kit" target="_blank" rel="noreferrer">QAV250 Kit</a> включає майже всі необхідні компоненти:</p><ul><li><a href="./../telemetry/holybro_sik_radio.html">Holybro Transceiver Telemetry Radio V3</a></li><li>Модуль живлення holybro</li><li>Повністю зібрана плата керування живленням з ESC</li><li>Двигуни - DR2205 KV2300</li><li>5-дюймові пластикові стійки</li><li>Вуглепластиковий планер 250 з обладнанням</li><li>Камера Foxer</li><li>Vtx 5.8ghz</li></ul><p>Додатково вам знадобиться акумулятор та приймач (+сумісний передавач). Ця збірка використовує:</p><ul><li>Отримувач: <a href="https://www.frsky-rc.com/product/d4r-ii/" target="_blank" rel="noreferrer">FrSSKY D4R-II</a></li><li>Батарея: <a href="http://www.getfpv.com/lumenier-1300mah-4s-60c-lipo-battery-xt60.html" target="_blank" rel="noreferrer">4S 1300 мАг</a></li></ul><h2 id="обладнання" tabindex="-1">Обладнання <a class="header-anchor" href="#обладнання" aria-label="Permalink to &quot;Обладнання&quot;">​</a></h2><p>У цьому розділі перераховано все обладнання для встановлення рами та автопілота.</p><h3 id="frame-qav250" tabindex="-1">Frame QAV250 <a class="header-anchor" href="#frame-qav250" aria-label="Permalink to &quot;Frame QAV250&quot;">​</a></h3><table><thead><tr><th>Опис</th><th>Кількість</th></tr></thead><tbody><tr><td>Каркасна пластина Unibody</td><td>1</td></tr><tr><td>Кришка контролера польоту</td><td>1</td></tr><tr><td>PDB</td><td>1</td></tr><tr><td>Пластина камери</td><td>1</td></tr><tr><td>35-міліметрові стійки</td><td>6</td></tr><tr><td>Вінілові гвинти та гайки</td><td>4</td></tr><tr><td>Cталеві шурупи 15 мм</td><td>8</td></tr><tr><td>Сталеві гайки</td><td>8</td></tr><tr><td>Cталеві шурупи 7 мм</td><td>12</td></tr><tr><td>Акумуляторний ремінь на липучці</td><td>1</td></tr><tr><td>Піна для акумуляторів</td><td>1</td></tr><tr><td>Посадкові майданчики</td><td>4</td></tr></tbody></table><p><img src="'+i+'" alt="QAV250 components for frame"></p><h3 id="електроніка" tabindex="-1">Електроніка <a class="header-anchor" href="#електроніка" aria-label="Permalink to &quot;Електроніка&quot;">​</a></h3><table><thead><tr><th>Опис</th><th>Кількість</th></tr></thead><tbody><tr><td>Двигуни - DR2205 KV2300</td><td>4</td></tr><tr><td>Повністю зібрана плата керування живленням з ESC</td><td>4</td></tr><tr><td>Модуль живлення Holybro</td><td>1</td></tr><tr><td>Приймач Fr-sky D4R-II</td><td>1</td></tr><tr><td>Pixhawk 4 mini</td><td>1</td></tr><tr><td>Holybro GPS Neo-M8N</td><td>1</td></tr><tr><td><a href="./../telemetry/holybro_sik_radio.html">Holybro Transceiver Telemetry Radio V3</a></td><td>1</td></tr><tr><td>Акумуляторна батарея люмінесцентної лампи 1300 mAh 4S 14.8V</td><td>1</td></tr><tr><td>Vtx 5.8gHz</td><td>1</td></tr><tr><td>FPV-камера (тільки для повного комплекту)</td><td>1</td></tr></tbody></table><p>На наведеному нижче зображенні показані як рама, так і електронні компоненти.</p><p><img src="'+a+'" alt="QAV250 Frame/Pixhawk 4 Mini Electronics before assembly"></p><h2 id="монтаж" tabindex="-1">Монтаж <a class="header-anchor" href="#монтаж" aria-label="Permalink to &quot;Монтаж&quot;">​</a></h2><p>Очікуваний час для складання каркасу - 2 години, а для встановлення автопілоту та налаштування конструкції повітряного каркасу в <em>QGroundControl</em> - 1,5 години.</p><h3 id="необхідні-інструменти" tabindex="-1">Необхідні інструменти <a class="header-anchor" href="#необхідні-інструменти" aria-label="Permalink to &quot;Необхідні інструменти&quot;">​</a></h3><p>Наступні інструменти використовуються у цій збірці:</p><ul><li>Викрутка з шестигранником 2,0 мм</li><li>Хрестоподібна викрутка 3 мм</li><li>Кусачки</li><li>Прецизійний пінцет</li></ul><p><img src="'+r+'" alt="Tools required for assembling QAV250"></p><h3 id="збірка-рами" tabindex="-1">Збірка рами <a class="header-anchor" href="#збірка-рами" aria-label="Permalink to &quot;Збірка рами&quot;">​</a></h3><ol><li><p>Прикріпіть руки до плати кнопки за допомогою гвинтів довжиною 15 мм, як показано:</p><p><img src="'+l+'" alt="QAV250 Add arms to button plate"></p></li><li><p>Покладіть коротку пластину над руками</p><p><img src="'+o+'" alt="QAV250 Add short plate over arms"></p></li><li><p>Покладіть гайки на відносно 15 мм гвинти (показано в наступному кроці)</p></li><li><p>Вставте пластикові гвинти в показані отвори (зверніть увагу, що ця частина рами звернена донизу, коли транспортний засіб буде готовий). <img src="'+e+'" alt="QAV250 Add nuts to 15mm screws and put  plastic nuts in holes"></p></li><li><p>Додайте пластикові гайки на гвинти (переверніть, як показано) <img src="'+s+'" alt="QAV250 Plastic nuts onto screws"></p></li><li><p>Опустіть модуль живлення над пластиковими гвинтами, а потім додайте пластикові опори <img src="'+p+'" alt="QAV250 Add power module and standoffs"></p></li><li><p>Встановіть плату керування польотом на стойки (над модулем живлення) <img src="'+d+'" alt="QAV250 Add flight controller plate"></p></li><li><p>Підключіть двигуни. Двигуни мають стрілку, що показує напрямок обертання. <img src="'+n+'" alt="QAV250 Add motors"></p></li><li><p>Використовуйте двосторонній скотч з комплекту для кріплення <em>Pixhawk 4 Mini</em> до пластини керування польотом. <img src="'+m+'" alt="QAV250 Add doublesided tape"></p></li><li><p>Підключіть кабель живлення модуля живлення до <em>Pixhawk 4 mini</em>. <img src="'+h+'" alt="QAV250 Power Pixhawk"></p></li><li><p>Прикріпіть алюмінієві опори до платформи кнопок <img src="'+c+'" alt="QAV250 Aluminium standoffs"></p></li><li><p>Підключіть ESC з моторами та утримуйте. На цьому зображенні показаний порядок розташування двигунів та напрямок обертання. <img src="'+_+'" alt="QAV250 Connect ESCs"></p><p>Підключіть двигуни на ESC, переконайтеся, що двигуни обертаються у правильному напрямку, якщо двигун обертається у протилежний бік, змініть кабель A на плату C та C на плату A ESC.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p></div></li></ol><p>Перевірте напрямки руху моторів без гвинтів. :::</p><p><img src="'+u+'" alt="QAV250 Connect ESCs to Power"></p><ol><li><p>Підключіть кабелі сигналу ESC до виходів PWM Pixhawk у правильному порядку (див. попереднє зображення)</p><p><img src="'+f+'" alt="QAV250 Connect ESCs to Pixhawk PWM"></p></li><li><p>Підключіть приймач.</p><ul><li><p>Якщо використовуєте приймач PPM, підключіть його до порту PPM.</p><p><img src="'+b+'" alt="QAV250 Connect Receiver PPM"></p></li><li><p>Якщо використовується приймач SBUS, підключіть його до порту RC IN</p><p><img src="'+g+'" alt="QAV250 Connect Receiver SBUS"></p></li></ul></li><li><p>Підключіть модуль телеметрії. Вставте модуль за допомогою двостворчастої стрічки та підключіть його до порту телеметрії.</p><p><img src="'+k+'" alt="QAV250 Telemetry module"></p></li><li><p>Підключіть модуль GPS</p><p><img src="'+P+'" alt="QAV250 Connect GPS"></p><p>Прикріпіть модуль на верхню плату (використовуючи наданий стрічку 3M, або пастою). Потім покладіть верхню плиту на стойки, як показано</p><p><img src="'+V+'" alt="QAV250 Connect GPS"></p></li><li><p>Останнім &quot;обов&#39;язковим&quot; кроком зборки є додавання липучки для утримання батареї</p><p><img src="'+A+'" alt="QAV250 Velcro battery strap"></p></li></ol><p>&quot;Базова&quot; конструкція рами тепер завершена (хоча, якщо вам потрібна, ви можете знайти більше інформації про підключення компонентів у <a href="./../assembly/quick_start_pixhawk4.html">Швидкі початкові вказівки з підключення Pixhawk 4</a>).</p><p>Якщо у вас є &quot;базова&quot; версія комплекту, ви можете перейти безпосередньо до інструкцій з того, як <a href="#px4-configuration">встановити/налаштувати PX4</a>.</p><h3 id="збирання-fpv" tabindex="-1">Збирання FPV <a class="header-anchor" href="#збирання-fpv" aria-label="Permalink to &quot;Збирання FPV&quot;">​</a></h3><p>&quot;Complete&quot; версія комплекту додатково поставляється з системою FPV, яка встановлена на передній частині транспортного засобу, як показано.</p><p><img src="'+x+'" alt="QAV250 FPV Attach"></p><p>Кроки для встановлення комплекту:</p><ol><li>Встановіть кріплення камери на раму <img src="'+q+'" alt="Camera Connection"></li><li>Встановіть камеру на кріплення <img src="'+w+'" alt="Camera on Bracket"></li><li>Модуль живлення на комплекті має проводку, готову для підключення відеопередавача та камери: <img src="'+y+'" alt="Connecting FPV"><ul><li>Прикріпіть роз&#39;єм камери <img src="'+Q+'" alt="Camera Connection"> Проводи: синій=вольтажний датчик, жовтий=відеовихід, чорний=земля, червоний=+вольтаж.</li><li>Підключіть роз&#39;єм відеопередавача (VTX) <img src="'+v+'" alt="Video Transmitter Connection"> Жили: жовтий = відео вихід, чорний = земля, червоний = напруга.</li></ul></li><li>Закріпіть відеопередавач і плату OSD на рамку за допомогою стрічки.</li></ol><div class="info custom-block"><p class="custom-block-title">Якщо вам потрібно підключити систему самостійно, на діаграмі нижче показані всі з&#39;єднання між камерою, VTX та модулем живлення: <img src="'+C+'" alt="QAV250 FPV Wiring"></p></div><h2 id="налаштування-px4" tabindex="-1">Налаштування PX4 <a class="header-anchor" href="#налаштування-px4" aria-label="Permalink to &quot;Налаштування PX4&quot;">​</a></h2><p><em>QGroundControl</em> використовується для встановлення автопілота PX4 та його налаштування / налаштування для рами QAV250. <a href="http://qgroundcontrol.com/downloads/" target="_blank" rel="noreferrer">Завантажте та встановіть</a> <em>QGroundControl</em> для вашої платформи.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Повні інструкції щодо встановлення та налаштування PX4 можна знайти в <a href="./../config/">Основна конфігурація</a>.</p></div><p>Спочатку оновіть прошивку, конструкцію та відображення приводів:</p><ul><li><p><a href="./../config/firmware.html">Прошивки</a></p></li><li><p><a href="./../config/airframe.html">Планер</a></p><div class="info custom-block"><p class="custom-block-title">Вам потрібно буде вибрати шасі <em>HolyBro QAV250</em> (<strong>Квадрокоптер x &gt; HolyBro QAV250</strong>).</p><p><img src="'+S+'" alt="QGC - Select HolyBro QAV250 airframe"></p></div></li></ul><p>:::</p><ul><li><a href="./../config/actuators.html">Приводи</a><ul><li>Вам не потрібно оновлювати геометрію транспортного засобу (оскільки це попередньо налаштована конструкція повітряного каркасу).</li><li>Призначте функції приводу до виходів, щоб відповідати вашому підключенню. <ul><li>Для Pixhawk 4 Mini та інших контролерів, які не мають <a href="./../hardware/reference_design.html#main-io-function-breakdown">плати вводу/виводу (I/O board)</a>, вам потрібно призначити приводи до виходів на вкладці <code>PWM AUX</code> на екрані конфігурації.</li><li>Pix32 v6 має плату введення/виведення, щоб ви могли призначити його до AUX або MAIN.</li></ul></li><li>Перевірте конфігурацію, використовуючи слайдери.</li></ul></li></ul><p>Потім виконайте обов&#39;язкове налаштування / калібрування:</p><ul><li><a href="./../config/flight_controller_orientation.html">Орієнтація сенсорів</a></li><li><a href="./../config/compass.html">Компас</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/level_horizon_calibration.html">Калібрування рівня горизонту</a></li><li><a href="./../config/radio.html">Налаштування радіо</a></li><li><a href="./../config/flight_mode.html">Режими польоту</a></li></ul><p>В ідеалі ви також повинні зробити:</p><ul><li><a href="./../advanced_config/esc_calibration.html">Калібрування ESC</a></li><li><a href="./../config/battery.html">Налаштування оцінки заряду батареї</a></li><li><a href="./../config/safety.html">Загальна безпека</a></li></ul><h2 id="вдосконалення" tabindex="-1">Вдосконалення <a class="header-anchor" href="#вдосконалення" aria-label="Permalink to &quot;Вдосконалення&quot;">​</a></h2><p>Набори вибору конструкції встановлюють параметри автопілота <em>за замовчуванням</em> для рами. Ці можуть бути достатньо хороші для польоту, але вам слід налаштувати кожну конструкцію рами.</p><p>Для інструкцій щодо того, як розпочати, почніть з <a href="./../config/autotune.html">Автоналаштування</a>.</p><h2 id="подяки" tabindex="-1">Подяки <a class="header-anchor" href="#подяки" aria-label="Permalink to &quot;Подяки&quot;">​</a></h2><p>Цей журнал збірки був наданий Командою тестового польоту PX4.</p>',57),X=[E];function I(R,G,D,N,U,W){return H(),T("div",null,X)}const O=M(B,[["render",I]]);export{$ as __pageData,O as default};
