import{_ as t,a as r,b as a,c as l,d as e,e as i,f as o,g as p,h,i as m,j as s,k as d,l as n,m as c,n as _,o as f}from"./chunks/finalized_x500v2_kit.BnqUvkWg.js";import{_ as b}from"./chunks/gps2.BFpjtYVM.js";import{_ as u}from"./chunks/x500v2_airframe_qgc.DnZb3Qf9.js";import{_ as g,c as x,o as k,ab as P}from"./chunks/framework.CUflZczI.js";const B=JSON.parse('{"title":"Holybro X500 V2 (Побудова Pixhawk 5X)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_multicopter/holybro_x500V2_pixhawk5x.md","filePath":"uk/frames_multicopter/holybro_x500V2_pixhawk5x.md"}'),y={name:"uk/frames_multicopter/holybro_x500V2_pixhawk5x.md"},w=P('<h1 id="holybro-x500-v2-побудова-pixhawk-5x" tabindex="-1">Holybro X500 V2 (Побудова Pixhawk 5X) <a class="header-anchor" href="#holybro-x500-v2-побудова-pixhawk-5x" aria-label="Permalink to &quot;Holybro X500 V2 (Побудова Pixhawk 5X)&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Holybro спочатку постачав цей комплект з <a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a>, але на момент написання це було оновлено до <a href="./../flight_controller/pixhawk6c.html">Holybro Pixhawk 6C</a>. Цей журнал збірки все ще актуальний, оскільки збірка комплекту практично однакова і, ймовірно, залишиться такою ж, оскільки контролер польоту оновлюється.</p></div><p>Ця тема надає повні інструкції для збирання <a href="https://holybro.com/collections/x500-kits" target="_blank" rel="noreferrer">Комплекту Holybro X500 V2 ARF</a> та налаштування PX4 з використанням <em>QGroundControl</em>.</p><p>Набір ARF (&quot;Майже готовий до польоту&quot;) забезпечує найкоротший і простий досвід збирання для тих, хто хоче зайнятися розробкою дронів і не витрачати багато часу на налаштування апаратного забезпечення. Воно включає в себе раму, двигуни, ESC, пропелери та плату розподілу потужності.</p><p>Крім комплекту вам знадобиться платформа керування польотом, радіопередавачі, GPS та пульт дистанційного керування. Набір ARF може бути використаний з більшістю контролерів польоту, які підтримуються PX4.</p><h2 id="основна-інформація" tabindex="-1">Основна Інформація <a class="header-anchor" href="#основна-інформація" aria-label="Permalink to &quot;Основна Інформація&quot;">​</a></h2><ul><li><strong>Комплект:</strong> <a href="https://holybro.com/collections/x500-kits" target="_blank" rel="noreferrer">Holybro X500 V2 ARF Kit</a></li><li><strong>Польотний контролер:</strong> <a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a></li><li><strong>Час збірки (приблизно):</strong> 55 хвилин (25 хвилин на раму, 30 хвилин на встановлення/налаштування автопілота)</li></ul><p><img src="'+t+'" alt="Full X500 V2 Kit"></p><h2 id="специфікація-матеріалів" tabindex="-1">Специфікація матеріалів <a class="header-anchor" href="#специфікація-матеріалів" aria-label="Permalink to &quot;Специфікація матеріалів&quot;">​</a></h2><p>Набір Holybro <a href="https://holybro.com/collections/x500-kits" target="_blank" rel="noreferrer">X500 V2 Kit</a> включає майже всі необхідні компоненти:</p><ul><li>X500V2 Frame Kit <ul><li>Корпус - верхня &amp; нижня плити з повним вуглецевим волокном (144 х 144 мм, товщиною 2 мм)</li><li>Кронштейн - Високоміцні &amp; надлегкі 16-міліметрові вуглецеві труби</li><li>Стільники - трубки з вуглецевого волокна діаметром 16 мм &amp; 10 мм</li><li>Платформа - З отворами для кріплення GPS &amp; популярного компаньйон-комп&#39;ютера</li><li>Система кріплення з подвійними пружинними валиками 10 мм Ø та довжиною 250 мм</li><li>Кріплення батареї з двома стропами для батареї</li><li>Ручні інструменти для встановлення</li></ul></li><li>Двигуни Holybro - 2216 KV880 x6 (зупинено - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запасних частин</a> для поточної версії).</li><li>ESC Holybro BLHeli S 20A x4 (зупинено - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запасних частин</a> для поточної версії).</li><li>Пропелери - 1045 x4 (зупинено - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запчастин</a> для поточної версії).</li><li>Розподільча плата живлення – роз&#39;єм XT60 для акумулятора &amp; роз&#39;єм XT30 для ESC &amp; периферійних пристроїв</li><li>Кріплення для камери (необов&#39;язкове, а 3D-файл можна завантажити <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_X500_V2_3D_Print.rar?v=1665561017" target="_blank" rel="noreferrer">тут</a>)</li></ul><p>Інші частини у цій збірці(<strong>Не включені в комплект ARF</strong>):</p><ul><li><a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X автопілот</a></li><li><a href="https://holybro.com/products/m8n-gps" target="_blank" rel="noreferrer">M8N GPS</a></li><li><a href="./../power_module/holybro_pm02d.html">Модуль живлення - PM02D</a></li><li><a href="./../telemetry/holybro_sik_radio.html">433/915 MHz Радіо телеметрія</a></li></ul><p>Додатково вам знадобиться акумулятор (Holybro рекомендує 4S 5000mAh) і приймач (<a href="./../getting_started/rc_transmitter_receiver.html">сумісна радіосистема</a>), якщо ви хочете керувати дроном вручну.</p><h2 id="комплект-апаратного-забезпечення" tabindex="-1">Комплект апаратного забезпечення <a class="header-anchor" href="#комплект-апаратного-забезпечення" aria-label="Permalink to &quot;Комплект апаратного забезпечення&quot;">​</a></h2><p>У цьому розділі перераховано все обладнання для встановлення рами та автопілота.</p><table><thead><tr><th>Елемент</th><th>Опис</th><th>Кількість</th></tr></thead><tbody><tr><td>Нижня пластина</td><td>Вуглецеве волокно (товщиною 2 мм)</td><td>1</td></tr><tr><td>Верхня пластина</td><td>Вуглецеве волокно (товщиною 1.5мм)</td><td>1</td></tr><tr><td>Кронштейн</td><td>Вуглецева труба (складена з монтованими двигунами)</td><td>4</td></tr><tr><td>Шасі - Вертикальна стійка</td><td>Вуглепластикова трубка + інженерний пластик</td><td>2</td></tr><tr><td>Шасі - Поперечна балка</td><td>Вуглепластикова трубка + інженерний пластик + пінопласт</td><td>2</td></tr><tr><td>Монтажна рейка</td><td>Діаметр: 10мм довжина: 250мм</td><td>2</td></tr><tr><td>Плата кріплення акумулятора</td><td>Товщина: 2мм</td><td>1</td></tr><tr><td>Підставка для батареї</td><td>Чорний лист силікону товщиною 3 мм</td><td>1</td></tr><tr><td>Плата платформи</td><td>Товщина: 2мм</td><td>1</td></tr><tr><td>Кріплення &amp; гумове кільцеве ущільнення</td><td>Діаметр внутрішнього отвору: 10 мм чорний</td><td>8</td></tr></tbody></table><p><img src="'+r+'" alt="X500V2 ARF Kit Full Package Contents"></p><p><em>Рисунок 1</em>: Набір X500 V2 ARF - що всередині</p><h3 id="електроніка" tabindex="-1">Електроніка <a class="header-anchor" href="#електроніка" aria-label="Permalink to &quot;Електроніка&quot;">​</a></h3><table><thead><tr><th>Опис предмету</th><th>Кількість</th></tr></thead><tbody><tr><td>Pixhawk5x &amp; Assorted Cables</td><td>1</td></tr><tr><td>Модуль GPS M8N</td><td>1</td></tr><tr><td>Модуль живлення PM02D (з попередньо припаяними кабелями живлення ESC)</td><td>1</td></tr><tr><td>Двигуни 2216 KV880 (оновлення V2)</td><td>4</td></tr><tr><td>Holybro BLHeli S ESC 20A x4</td><td>1</td></tr><tr><td>Holybro BLHeli S ESC 20A x4</td><td>1</td></tr><tr><td>433 МГц / 915 МГц <a href="./../telemetry/holybro_sik_radio.html">Телеметричне радіо Holybro</a></td><td>1</td></tr></tbody></table><h3 id="необхідні-інструменти" tabindex="-1">Необхідні інструменти <a class="header-anchor" href="#необхідні-інструменти" aria-label="Permalink to &quot;Необхідні інструменти&quot;">​</a></h3><p>У комплект включені інструменти для зборки, проте вам може знадобитися:</p><ul><li>Кусачки</li><li>Прецизійний пінцет</li></ul><h2 id="збірка" tabindex="-1">Збірка <a class="header-anchor" href="#збірка" aria-label="Permalink to &quot;Збірка&quot;">​</a></h2><p>Орієнтовний час збірки - 55 хвилин (25 хвилин на раму, 30 хвилин на встановлення/налаштування автопілота)</p><ol><li><p>Почніть зі складання корисного навантаження &amp; тримача акумулятора. Втисніть гумки в захоплювачі (не використовуйте гострі предмети, щоб їх втиснути в них!). Далі пропустіть тримачі через планки тримача з основами тримача батарей, як показано на рисунку 3.</p><p><img src="'+a+'" alt="Landing Figure 1: Components"></p><p><em>Рисунок 2</em>: Компоненти тримача вантажу</p><p><img src="'+l+'" alt="Landing Figure 2: Assembled"></p><p><em>Рисунок 3</em>: Встановлювач навантаження складений</p></li><li><p>Наступним кроком буде прикріплення нижньої пластини до тримача вантажу.</p><p>Вам знадобляться деталі, як показано на рисунку 4. Потім встановіть основу для розподільної плати живлення, використовуючи нейлонові гайки, як зображено на Рис. 5. Нарешті, використовуючи 8 шестигранних гвинтів, ви можете приєднати нижню пластину до тримача навантаження (Рисунок 7)</p><p><img src="'+e+'" alt="Materials to attach bottom plate"></p><p><em>Рисунок 4</em>: Необхідні матеріали</p><p><img src="'+i+'" alt="PDB mountbase"></p><p><em>Рисунок 5</em>: База кріплення PDB</p><p><img src="'+o+'" alt="PDB attachment"></p><p><em>Рисунок 6</em>: Змонтований pdb з поліамідними гайками</p><p><img src="'+p+'" alt="Bottom plate Final"></p><p><em>Рисунок 7</em>: Встановлена плата на тримач для навантаження</p></li><li><p>Давайте зберемо речі, необхідні для монтажу посадкового шасі, як на рисунку 8. Використовуйте гвинти, щоб приєднати посадкові шасі до нижньої пластини. Також потрібно відкрити три шестигранних гвинти на кожній з ніжок, щоб ви могли вставити їх у вуглецеві труби. Не забудьте знову їх затягнути.</p><p><img src="'+h+'" alt="Attach Landing Gear Stuff"></p><p><em>Рисунок 8</em>: Обов&#39;язкові частини для кріплення посадочного шасі</p><p><img src="'+m+'" alt="Lanfing great to bottom plate"></p><p><em>Рисунок 9</em>: Кріплення посадочно шасі до корпусу</p></li><li><p>Зараз ми зберемо все оснащення, щоб встановити верхню пластину. Прошу звернути увагу, що номери моторів на кронштейнах відповідають тим, що згадані на верхній платі. На щастя, мотори встановлені, а ESCs були з&#39;єднані заздалегідь. Почніть, проходячи через всі гвинти, так як ви зафіксували кронштейни на їхніх власних місцях (Вони мають направляючий елемент, як показано на рисунку 11, щоб переконатися, що вони на місці), і трохи підтягніть всі нейлонові гайки. Потім ви зможете підключити роз&#39;єми живлення XT30 до плати живлення. Пам&#39;ятайте, що дроти сигналу повинні бути проведені через верхню пластину так, що ми зможемо пізніше їх підключити до Pixhawk.</p><img src="'+s+'" width="700" title="Arms and top plate materials"><p><em>Рисунок 10</em>: Потрібні матеріали для підключення кронштейнів.</p><img src="'+d+'" width="700" title="Guide for the arms mount"><p><em>Рисунок 11</em>: Керівництво для установки кронштенів</p></li><li><p>Для затягування всіх 16 гвинтів і гайок використовуйте як шестигранний ключ, так і гайковий ключ.</p><p><img src="'+n+'" alt="Top plae mounted"></p><p><em>Рисунок 12</em>: Монтуємо верхню панель</p></li><li><p>Наступним кроком ви можете закріпити свій pixhawk на верхній плиті, використовуючи наклейки. Рекомендується мати напрямок стрілки вашого Pixhawk таким же, як зазначено на верхній плиті.</p><p><img src="'+c+'" alt="Flight controller mounting stickers"></p><p><em>Рисунок 13</em>: Наклейки на Pixhawk</p></li><li><p>Якщо ви хочете встановити GPS на плату компаньйона-комп&#39;ютера, тепер ви можете закріпити кріплення GPS на ній за допомогою 4 гвинтів і гайок.</p><img src="'+_+'" width="400" title="Secure GPS mount onto companion plate"><p><em>Зображення 14</em>: Кріплення GPS на додатковій панелі</p></li><li><p>За допомогою скотча приклейте GPS до верхньої частини GPS-щогли і встановіть її на щоглу. Переконайтеся, що стрілка на gps вказує вперед (зображення 15).</p><img src="'+b+'" width="400" title="Figure 16: GPS and mast"><p><em>Рисунок 15</em>: GPS та щогла</p></li><li><p>Наразі ви можете підключити інтерфейси Pixhawk, такі як телеметрійне радіо до &#39;TELEM1&#39; та відповідно кабелі сигналів для моторів.</p></li></ol><p>Будь ласка, зверніться до <a href="./../assembly/quick_start_pixhawk5x.html">Pixhawk 5X Посібник зі швидкого старту</a> для отримання додаткової інформації.</p><p>Ось і все. Повністю зібраний комплект показаний нижче (глибинна камера не входить до комплекту):</p><p><img src="'+f+'" alt="Assembled Kit"></p><h2 id="конфігурація-px4" tabindex="-1">Конфігурація PX4 <a class="header-anchor" href="#конфігурація-px4" aria-label="Permalink to &quot;Конфігурація PX4&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Повні інструкції щодо встановлення та налаштування PX4 можна знайти в <a href="./../config/">Основна конфігурація</a>.</p></div><p><em>QGroundControl</em> використовується для встановлення автопілота PX4 та його налаштування / налаштування для рами X500. <a href="http://qgroundcontrol.com/downloads/" target="_blank" rel="noreferrer">Завантажте та встановіть</a> <em>QGroundControl</em> для вашої платформи.</p><p>Спочатку оновіть прошивку, конструкцію та відображення приводів:</p><ul><li><p><a href="./../config/firmware.html">Прошивка</a></p></li><li><p><a href="./../config/airframe.html">Планер</a></p><p>Вам потрібно буде вибрати шасі <em>Holybro X500 V2</em> (<strong>Квадрокоптер x &gt; Holybro 500 V2</strong>)</p><p><img src="'+u+'" alt="QGroundControl - Select HolyBro 500 airframe"></p></li><li><p><a href="./../config/actuators.html">Приводи</a></p><ul><li>Вам не потрібно оновлювати геометрію транспортного засобу (оскільки це попередньо налаштована конструкція повітряного каркасу).</li><li>Призначте функції приводу до виходів, щоб відповідати вашому підключенню.</li><li>Перевірте конфігурацію, використовуючи слайдери.</li></ul></li></ul><p>Потім виконайте обов&#39;язкове налаштування / калібрування:</p><ul><li><a href="./../config/flight_controller_orientation.html">Орієнтація сенсора</a></li><li><a href="./../config/compass.html">Компас</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/level_horizon_calibration.html">Калібрування рівня горизонту</a></li><li><a href="./../config/radio.html">Налаштування радіо</a></li><li><a href="./../config/flight_mode.html">Режими польоту</a></li></ul><p>В ідеалі ви також повинні зробити:</p><ul><li><a href="./../advanced_config/esc_calibration.html">Калібрування ESC</a></li><li><a href="./../config/battery.html">Налаштування оцінки заряду батареї</a></li><li><a href="./../config/safety.html">Безпека</a></li></ul><h2 id="вдосконалення" tabindex="-1">Вдосконалення <a class="header-anchor" href="#вдосконалення" aria-label="Permalink to &quot;Вдосконалення&quot;">​</a></h2><p>Набори вибору конструкції встановлюють параметри автопілота <em>за замовчуванням</em> для каркасу. Ці вистачають для польоту, але це добра ідея налаштувати параметри для конкретної конструкції рами.</p><p>Для інструкцій щодо того, як розпочати, почніть з <a href="./../config/autotune.html">Автоналаштування</a>.</p><h2 id="подяки" tabindex="-1">Подяки <a class="header-anchor" href="#подяки" aria-label="Permalink to &quot;Подяки&quot;">​</a></h2><p>Цей журнал збірки був наданий Командою PX4.</p>',44),S=[w];function X(q,V,H,A,C,G){return k(),x("div",null,S)}const D=g(y,[["render",X]]);export{B as __pageData,D as default};
