import{_ as t,a as r,b as a,c as l,d as e,e as i,f as o,g as p,h as d,i as s,j as m,k as h,l as n,m as c,n as _,o as u,p as f,q as b,r as g,s as P,t as k,u as x}from"./chunks/X500_assembled_frame.zzVp9C6Z.js";import{_ as y}from"./chunks/gps2.BFpjtYVM.js";import{_ as w}from"./chunks/qgc_airframe_holybro_s500.BlCtcDQn.js";import{_ as S,c as q,o as C,ab as M}from"./chunks/framework.CUflZczI.js";const O=JSON.parse('{"title":"Holybro X500 + Pixhawk4 Build","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_multicopter/holybro_x500_pixhawk4.md","filePath":"uk/frames_multicopter/holybro_x500_pixhawk4.md"}'),H={name:"uk/frames_multicopter/holybro_x500_pixhawk4.md"},E=M('<h1 id="holybro-x500-pixhawk4-build" tabindex="-1">Holybro X500 + Pixhawk4 Build <a class="header-anchor" href="#holybro-x500-pixhawk4-build" aria-label="Permalink to &quot;Holybro X500 + Pixhawk4 Build&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">Holybro спочатку постачав цей комплект з <a href="./../flight_controller/pixhawk4.html">Holybro Pixhawk 4</a>), але на момент написання це було оновлено до <a href="./../flight_controller/pixhawk6c.html">Holybro Pixhawk 6C</a>. Цей журнал збірки все ще актуальний, оскільки збірка комплекту практично однакова і, ймовірно, залишиться такою ж, оскільки контролер польоту оновлюється.</p></div><p>Ця тема надає повні інструкції для збирання комплекту та налаштування PX4 з використанням <em>QGroundControl</em>.</p><h2 id="основна-інформація" tabindex="-1">Основна Інформація <a class="header-anchor" href="#основна-інформація" aria-label="Permalink to &quot;Основна Інформація&quot;">​</a></h2><ul><li><strong>Повний комплект:</strong> <a href="https://holybro.com/products/px4-development-kit-x500-v2" target="_blank" rel="noreferrer">Комплект Holybro X500</a></li><li><strong>Контролер польоту:</strong> <a href="./../flight_controller/pixhawk4.html">Pixhawk 4</a></li><li><strong>Час зборки (приблизно):</strong> 3.75 години (180 хвилин на раму, 45 хвилин на встановлення/налаштування автопілота)</li></ul><p><img src="'+t+'" alt="Full X500 Kit"></p><h2 id="специфікація-матеріалів" tabindex="-1">Специфікація матеріалів <a class="header-anchor" href="#специфікація-матеріалів" aria-label="Permalink to &quot;Специфікація матеріалів&quot;">​</a></h2><p>Набір Holybro <a href="https://holybro.com/products/px4-development-kit-x500-v2" target="_blank" rel="noreferrer">X500 Kit</a> включає майже всі необхідні компоненти:</p><ul><li><a href="./../flight_controller/pixhawk4.html">Pixhawk 4 автопілот</a></li><li><a href="https://holybro.com/collections/gps/products/m8n-gps" target="_blank" rel="noreferrer">Holybro M9N GPS</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">Керування живленням - PM07</a></li><li>Двигуни Holybro - 2216 KV880 x4 (скасовано - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запасних частин</a> для поточної версії).</li><li>ESC Holybro BLHeli S 20A x4 (скасовано - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запасних частин</a> для поточної версії).</li><li>Пропелери - 1045 x4 (скасовано - перевірте <a href="https://holybro.com/products/spare-parts-x500-v2-kit" target="_blank" rel="noreferrer">список запчастин</a> для поточної версії).</li><li>Ремінь для акумулятора</li><li>Живлення та радіокабелі</li><li>Колісна база - 500 мм</li><li>Розміри - 410x410x300 мм</li><li>433 МГц / 915 МГц <a href="./../telemetry/holybro_sik_radio.html">Телеметричне радіо Holybro</a></li></ul><p>Додатково вам знадобиться акумулятор і приймач (<a href="./../getting_started/rc_transmitter_receiver.html">сумісна радіосистема</a>), якщо ви хочете керувати дроном вручну.</p><h2 id="головне-апаратне-забезпечення" tabindex="-1">Головне апаратне забезпечення <a class="header-anchor" href="#головне-апаратне-забезпечення" aria-label="Permalink to &quot;Головне апаратне забезпечення&quot;">​</a></h2><p>У цьому розділі перераховано все обладнання для встановлення рами та автопілота.</p><table><thead><tr><th>Елемент</th><th>Опис</th><th>Кількість</th></tr></thead><tbody><tr><td>Нижня пластина</td><td>Вуглецеве волокно (товщиною 2 мм)</td><td>1</td></tr><tr><td>Верхня пластина</td><td>Вуглецеве волокно (товщиною 1.5мм)</td><td>1</td></tr><tr><td>Кронштейн</td><td>Вуглепластикова трубка (діаметр: 16 мм, довжина: 200 мм)</td><td>4</td></tr><tr><td>Шасі - Вертикальна стійка</td><td>Вуглепластикова трубка + інженерний пластик</td><td>2</td></tr><tr><td>Шасі - Поперечна балка</td><td>Вуглепластикова трубка + інженерний пластик + пінопласт</td><td>2</td></tr><tr><td>Моторна база</td><td>Складається з 6 частин і 4 гвинтів 4 гайок</td><td>4</td></tr><tr><td>Бокова панель</td><td>Діаметр: 10мм довжина: 250мм</td><td>2</td></tr><tr><td>Плата кріплення акумулятора</td><td>Товщина: 2мм</td><td>1</td></tr><tr><td>Підставка для батареї</td><td>Чорний лист силікону товщиною 3 мм</td><td>1</td></tr><tr><td>Дошка платформи</td><td>Товщина: 2мм</td><td>1</td></tr><tr><td>Кріплення та гумове кільцеве ущільнення</td><td>Діаметр внутрішнього отвору: 10 мм чорний</td><td>8</td></tr></tbody></table><p><img src="'+r+'" alt="X500 Full Package Contents"></p><h3 id="електроніка" tabindex="-1">Електроніка <a class="header-anchor" href="#електроніка" aria-label="Permalink to &quot;Електроніка&quot;">​</a></h3><table><thead><tr><th>Опис предмету</th><th>Кількість</th></tr></thead><tbody><tr><td>Pixhawk4 &amp; Assorted Cables</td><td>1</td></tr><tr><td>Pixhawk4 GPS Module</td><td>1</td></tr><tr><td>Управління живленням PM07 (з попередньо припаяними кабелями живлення ESC)</td><td>1</td></tr><tr><td>Двигуни 2216 KV880 (оновлення V2)</td><td>4</td></tr><tr><td>Holybro BLHeli S ESC 20A x4</td><td>1</td></tr><tr><td>433 МГц / 915 МГц <a href="./../telemetry/holybro_sik_radio.html">Телеметричне радіо Holybro</a></td><td>1</td></tr></tbody></table><h3 id="необхідні-інструменти" tabindex="-1">Необхідні інструменти <a class="header-anchor" href="#необхідні-інструменти" aria-label="Permalink to &quot;Необхідні інструменти&quot;">​</a></h3><p>У цій збірці використовуються наступні інструменти:</p><ul><li>Викрутка з шестигранником 1,5 мм</li><li>Викрутка з шестигранником 2,0 мм</li><li>Викрутка з шестигранником 2,5 мм</li><li>Хрестоподібна викрутка 3 мм</li><li>Торцевий ключ 5,5 мм або невелика викрутка</li><li>Кусачки</li><li>Прецизійний пінцет</li></ul><h2 id="збірка" tabindex="-1">Збірка <a class="header-anchor" href="#збірка" aria-label="Permalink to &quot;Збірка&quot;">​</a></h2><p>Час збірки (приблизно): 3.75 години (180 хвилин на раму, 45 хвилин на встановлення/налаштування автопілота)</p><ol><li><p>Почніть зі збирання шасі. Відкрутіть гвинти шасі і вставте вертикальну стійку (зобр. 1 і 2).</p><p><img src="'+a+'" alt="Landing Figure 1: Components"></p><p><em>Зображення 1</em>: Компоненти шасі</p><p><img src="'+l+'" alt="Landing Figure 2: Assembled"></p><p><em>Зображення 2</em>: Шасі в зібраному вигляді</p></li><li><p>Потім просуньте 4 кронштейни через 4 основи двигуна, як показано на малюнку 3. Переконайтеся, що штанги злегка виступають з основи і є однаковими на всіх 4-х плечах, а також переконайтеся, що дроти електродвигуна спрямовані назовні.</p><p><img src="'+e+'" alt="Attach arms to motor bases"></p><p><em>Зображення 3</em>: Приєднання кронштейнів до основи двигуна</p></li><li><p>Вставте 4 нейлонові гвинти та нейлонові стійки і прикріпіть модуль живлення PM07 до нижньої панелі за допомогою 4 нейлонових гайок, як показано на зображенні 4.</p><p><img src="'+i+'" alt="Attach power module"></p><p><em>Зображення 4</em>: Приєднання модуля живлення</p></li><li><p>Протягніть 4 двигуни ESC через кожне з кронштейнів і підключіть трижильні дроти до двигунів, як показано на зображенні 5.</p><img src="'+o+'" width="250" title="Connect motors"><p><em>Зображення 5</em>: Підключення двигунів</p></li><li><p>Підключіть дроти живлення ESC до модуля живлення PM07, чорний - до чорного, червоний - до червоного, дроти сигналу ШІМ ESC йдуть на &quot;FMU-PWM-Out&quot;. Переконайтеся, що ви підключили дроти ШІМ ESC двигуна в правильному порядку. Номер двигуна повітряного корпусу дивіться на зображенні 7 і підключіть його до відповідного номера на платі PM07.</p><p><img src="'+p+'" alt="ESC power module and signal wiring"> <em>Зображення 7</em>: Модуль живлення ESC та сигнальна проводка</p><p>Колір на верхній частині двигуна вказує на напрямок обертання (зображення 7-1), чорний кінчик - за годинниковою стрілкою, а білий - проти годинникової стрілки. Переконайтеся, що при виборі напрямку двигуна ви дотримуєтесь орієнтира px4 quadrotor x airframe (зображення 7-2).</p><img src="'+d+'" width="240"><p><em>Зображення 7</em>: Діаграма порядку/напряму руху двигуна</p><img src="'+s+'" width="400"><p><em>Зображення 7-1</em>: Напрямок руху двигуна</p></li><li><p>Підключіть 10-контактні кабелі до FMU-PWM-in, а 6-контактні - до PWR1 на модулі живлення PM07.</p><p><img src="'+m+'" alt="Flight controller/Power module PWM and Power connections"></p><p><em>Зображення 8</em>: Модуль живлення ШІМ і силова проводка</p></li><li><p>Якщо ви хочете встановити GPS на верхній панелі, то тепер ви можете закріпити кріплення GPS на верхній панелі за допомогою 4 гвинтів і гайок.</p><img src="'+h+'" width="400" title="Secure GPS mount onto top plate"><p><em>Зображення 9</em>: Кріплення GPS на верхній панелі</p></li><li><p>Протягніть кабелі PM07 через верхню пластину. З&#39;єднайте верхню і нижню пластини за допомогою 4 U-подібних нейлонових ременів, гвинтів і гайок з кожного боку, переконайтеся, що кабелі ESC двигуна знаходяться всередині U-подібних нейлонових ременів, як показано на зображенні 10, гайки не затягуйте.</p><img src="'+n+'" width="300"><p><em>Зображення 10-1</em>: Кабелі силового модуля прокладені через верхню панель</p><img src="'+c+'" width="355" title="Connecting top and bottom plate"><p><em>Зображення 10-2</em>: З&#39;єднання верхньої та нижньої пластин</p></li><li><p>Трохи всуньте трубки кронштейнів у раму і переконайтеся, що величина виступу (червоний квадрат на зображенні 11) є однаковою на всіх 4-х кронштейнах. Переконайтеся, що всі двигуни спрямовані прямо вгору, а потім затягніть усі гайки та гвинти.</p><p><img src="'+_+'" alt="Arms 3"></p></li><li><p>Вставте прокладки для підвісів у 4 підвіси та закріпіть їх на нижній пластині за допомогою 8 шестигранних гвинтів (Зображення 11). Отвори для гвинтів позначені білою стрілкою на зображенні 12. Ми рекомендуємо нахилити дрон убік, щоб полегшити встановлення.</p><img src="'+u+'" width="400" title="Hanger gaskets"><p><em>Зображення 11</em>: Ущільнювачі для кріпленнь</p><p><img src="'+f+'" alt="Battery Mount 4"></p><p><em>Зображення 12</em>: Отвори для гвинтів</p></li><li><p>Вставте направляючі планки на кільця кріплення (зображення 13). Зберіть кріплення для батареї та плату платформи і встановіть їх на направляючі, як показано на зображенні 14.</p><p><img src="'+b+'" alt="Battery Mount 2: Slide bars"></p><p><em>Зображення 13</em>: Повзунки</p><img src="'+g+'" width="400" title="Battery mount on slide bars"><p><em>Зображення 14</em>: Кріплення акумулятора на висувних планках</p></li><li><p>Встановіть шасі на нижню пластину. Ми рекомендуємо нахилити дрон убік, щоб полегшити встановлення.</p><p><img src="'+P+'" alt="Landing Gear"></p><p><em>Зображення 15</em>: Компоненти шасі</p></li><li><p>За допомогою скотча приклейте GPS до верхньої частини GPS-щогли і встановіть її на щоглу. Переконайтеся, що стрілка на gps вказує вперед (зображення 16).</p><img src="'+y+'" width="400" title="Figure 16: GPS and mast"><p><em>Зображення 16</em>: GPS і щогла</p></li><li><p>Встановіть телеметричну радіостанцію на верхню пластину. Підключіть телеметричний кабель до порту <code>TELEM1</code>, а модуль GPS до порту <code>GPS MODULE</code> на польотному контролері. Підключіть кабель від PM07 <code>FMU-PWM-in</code> до <code>I/O-PWM-out</code> на FC і PM07 <code>PWR1</code> до <code>POWER1</code> на FC, як показано на зображенні 17.</p><p><img src="'+k+'" alt="Pixhawk 4 wiring 1"></p><p><em>Зображення 17</em>: Встановлення телеметричного радіоприймача/підключення ШІМ та кабелів живлення до контролера польоту.</p></li></ol><p>Будь ласка, зверніться до <a href="./../assembly/quick_start_pixhawk4.html">Pixhawk 4 Посібник зі швидкого запуску</a> для отримання додаткової інформації.</p><p>Ось і все. Повністю зібраний комплект показаний нижче:</p><p><img src="'+x+'" alt="Assembled Kit"></p><h2 id="конфігурація-px4" tabindex="-1">Конфігурація PX4 <a class="header-anchor" href="#конфігурація-px4" aria-label="Permalink to &quot;Конфігурація PX4&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Повні інструкції щодо встановлення та налаштування PX4 можна знайти в <a href="./../config/">Основна конфігурація</a>.</p></div><p><em>QGroundControl</em> використовується для встановлення автопілота PX4 та його налаштування / налаштування для рами X500. <a href="http://qgroundcontrol.com/downloads/" target="_blank" rel="noreferrer">Завантажте та встановіть</a> <em>QGroundControl</em> для вашої платформи.</p><p>Спочатку оновіть прошивку, конструкцію та відображення приводів:</p><ul><li><p><a href="./../config/firmware.html">Прошивка</a></p></li><li><p><a href="./../config/airframe.html">Рама або планер</a></p><p>Вам потрібно буде вибрати шасі <em>Holybro S500</em> (<strong>Квадрокоптер x &gt; Holybro S500</strong>).</p><p><img src="'+w+'" alt="QGroundControl - Select HolyBro X500 airframe"></p></li><li><p><a href="./../config/actuators.html">Приводи</a></p><ul><li>Вам не потрібно оновлювати геометрію транспортного засобу (оскільки це попередньо налаштована конструкція повітряного каркасу).</li><li>Призначте функції приводу до виходів, щоб відповідати вашому підключенню.</li><li>Перевірте конфігурацію, використовуючи слайдери.</li></ul></li></ul><p>Потім виконайте обов&#39;язкове налаштування / калібрування:</p><ul><li><a href="./../config/flight_controller_orientation.html">Орієнтація сенсора</a></li><li><a href="./../config/compass.html">Компас</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/level_horizon_calibration.html">Калібрування рівня горизонту</a></li><li><a href="./../config/radio.html">Налаштування радіо</a></li><li><a href="./../config/flight_mode.html">Режими польоту</a></li></ul><p>В ідеалі ви також повинні зробити:</p><ul><li><a href="./../advanced_config/esc_calibration.html">Калібрування ESC</a></li><li><a href="./../config/battery.html">Налаштування оцінки заряду батареї</a></li><li><a href="./../config/safety.html">Загальна безпека</a></li></ul><h2 id="вдосконалення" tabindex="-1">Вдосконалення <a class="header-anchor" href="#вдосконалення" aria-label="Permalink to &quot;Вдосконалення&quot;">​</a></h2><p>Набори вибору конструкції встановлюють параметри автопілота <em>за замовчуванням</em> для рами. Ці вистачають для польоту, але це добра ідея налаштувати параметри для конкретної конструкції рами.</p><p>Для інструкцій щодо того, як розпочати, почніть з <a href="./../config/autotune.html">Автоналаштування</a>.</p><h2 id="подяки" tabindex="-1">Подяки <a class="header-anchor" href="#подяки" aria-label="Permalink to &quot;Подяки&quot;">​</a></h2><p>Цей журнал збірки був наданий Командою тестового польоту Dronecode.</p>',39),G=[E];function v(X,A,B,F,T,W){return C(),q("div",null,G)}const D=S(H,[["render",v]]);export{O as __pageData,D as default};
