import{_ as r}from"./chunks/final_side.DW-ghjQN.js";import{_ as t,a as i}from"./chunks/airframe_rover_aion.Cztchofw.js";import{_ as o,E as s,c as l,J as n,m as e,a as m,ab as c,o as h}from"./chunks/framework.CUflZczI.js";const A=JSON.parse('{"title":"Марсоходи (UGVs)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_rover/index.md","filePath":"uk/frames_rover/index.md"}'),p={name:"uk/frames_rover/index.md"},d=e("h1",{id:"марсоходи-ugvs",tabindex:"-1"},[m("Марсоходи (UGVs) "),e("a",{class:"header-anchor",href:"#марсоходи-ugvs","aria-label":'Permalink to "Марсоходи (UGVs)"'},"​")],-1),f=c('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Підтримка для ровера є <a href="./../airframes/#experimental-vehicles">експериментальною</a>. Волонтери та контрибютори, <a href="./../contribute/">внесення</a> нових функцій, нових конфігурацій каркасів або інших вдосконалень дуже вітається!</p></div><p>PX4 підтримує ровери (безпілотні наземні транспортні засоби - UGVs) з керуванням <a href="#rover-types">акермана та диференційним</a>.</p><p>У цьому розділі містяться журнали збірки/інструкції щодо складання та налаштування ряду UGV фреймів.</p><p><img src="'+r+'" alt="Traxxas Rover Picture"></p><h2 id="типи-марсоходів" tabindex="-1">Типи Марсоходів <a class="header-anchor" href="#типи-марсоходів" aria-label="Permalink to &quot;Типи Марсоходів&quot;">​</a></h2><p>PX4 підтримує рухомі з використанням:</p><ul><li><strong>Диференційне керування</strong>: напрямок контролюється шляхом руху лівих і правих коліс з різною швидкістю. Цей вид керування часто використовується на бульдозерах, танках та інших гусеничних транспортних засобах.</li><li><strong>Кермування Акермана</strong>: напрямок керування контролюється спрямуванням коліс у напрямку руху (<a href="https://en.wikipedia.org/wiki/Ackermann_steering_geometry" target="_blank" rel="noreferrer">геометрія кермування Акермана</a> компенсує той факт, що колеса на внутрішньому та зовнішньому повороті рухаються з різними швидкостями). Цей вид керування використовується на більшості комерційних транспортних засобів, включаючи автомобілі, вантажівки тощо.</li></ul><p>Підтримувані каркаси можна переглянути в <a href="./../airframes/airframe_reference.html#rover">Довіднику про планери &gt; Rover(Рухавець)</a>.</p><h2 id="як-налаштувати-rover" tabindex="-1">Як налаштувати Rover <a class="header-anchor" href="#як-налаштувати-rover" aria-label="Permalink to &quot;Як налаштувати Rover&quot;">​</a></h2><h3 id="конфігурація-керування-акермана" tabindex="-1">Конфігурація керування Акермана <a class="header-anchor" href="#конфігурація-керування-акермана" aria-label="Permalink to &quot;Конфігурація керування Акермана&quot;">​</a></h3><p>Налаштування ровера з керуванням Аккермана просте:</p><ol><li><p>У конфігурації <a href="./../config/airframe.html">Airframe</a> виберіть <em>Загальний наземний транспортний засіб</em>.</p><p><img src="'+t+'" alt="Select Ackermann steered airframe"></p><p>Виберіть кнопку <strong>Застосувати та перезапустити</strong>.</p></li><li><p>Відкрийте <a href="./../config/actuators.html">Конфігурацію та &amp; тестування приводів</a> для відображення функцій керування та регулювання на виходи контролера польоту.</p></li></ol><h3 id="конфігурація-диференціиного-керування" tabindex="-1">Конфігурація диференційного керування <a class="header-anchor" href="#конфігурація-диференціиного-керування" aria-label="Permalink to &quot;Конфігурація диференційного керування&quot;">​</a></h3><ol><li><p>У конфігурації <a href="./../config/airframe.html">Airframe</a> виберіть або <em>Aion Robotics R1 UGV</em>, або <em>NXP Cup car: DF Robot GPX</em></p><p><img src="'+i+'" alt="Select Differential steered airframe"></p></li></ol><p>Виберіть кнопку <strong>Застосувати та перезапустити</strong>.</p><ol><li>Відкрийте <a href="./../config/actuators.html">Конфігурацію та &amp; тестування приводів</a> та відобразіть функції лівого та правого двигуна на виходи контролера польоту.</li></ol><h2 id="симуляція" tabindex="-1">Симуляція <a class="header-anchor" href="#симуляція" aria-label="Permalink to &quot;Симуляція&quot;">​</a></h2><p><a href="./../sim_gazebo_classic/">Класичний Газебо</a> надає симуляції для обох типів керування:</p><ul><li>Ackermann: <a href="./../sim_gazebo_classic/vehicles.html#ackermann-ugv">акерманський ровер</a></li><li>Диференціал: <a href="./../sim_gazebo_classic/vehicles.html#differential-ugv">роувер r1</a></li></ul><h2 id="відео" tabindex="-1">Відео <a class="header-anchor" href="#відео" aria-label="Permalink to &quot;Відео&quot;">​</a></h2><p>Це відео показує <a href="./../frames_rover/traxxas_stampede.html">Traxxas Stampede Rover</a> (автомобіль Акермана).</p>',21),_=e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/N3HvSKS3nCw",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1);function u(b,g,v,x,k,P){const a=s("LinkedBadge");return h(),l("div",null,[d,n(a,{type:"warning",text:"Experimental",url:"../airframes/#experimental-vehicles"}),f,_])}const N=o(p,[["render",u]]);export{A as __pageData,N as default};
