import{_ as a,a as r,b as l,c as e,d as o,e as p,f as s,g as d,h,i as c,j as n,k as _,l as m,m as f,n as u,o as b,p as g,q as x,r as v,s as k,t as w,u as P,v as q,w as S,x as T,y as V,z as C,A as y,B as A}from"./chunks/falcon_vertigo_55_level_motors.BCEXgB5v.js";import{_ as R}from"./chunks/px4_frame_generic_standard_vtol.ArpkwYLu.js";import{_ as N,c as O,o as E,ab as F,m as t,a as i}from"./chunks/framework.CUflZczI.js";const K=JSON.parse('{"title":"Припинено: Falcon Vertigo Hybrid VTOL RTF (Dropix)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_vtol/vtol_quadplane_falcon_vertigo_hybrid_rtf_dropix.md","filePath":"uk/frames_vtol/vtol_quadplane_falcon_vertigo_hybrid_rtf_dropix.md"}'),I={name:"uk/frames_vtol/vtol_quadplane_falcon_vertigo_hybrid_rtf_dropix.md"},D=F('<h1 id="припинено-falcon-vertigo-hybrid-vtol-rtf-dropix" tabindex="-1">Припинено: Falcon Vertigo Hybrid VTOL RTF (Dropix) <a class="header-anchor" href="#припинено-falcon-vertigo-hybrid-vtol-rtf-dropix" aria-label="Permalink to &quot;Припинено: Falcon Vertigo Hybrid VTOL RTF (Dropix)&quot;">​</a></h1><p>:::попередження Виробляється Каркас для польотів з переглядом на місцевості Falcon Venturi FPV Wing, на якому базується ця транспортний засіб, більше недоступний. :::</p><p><em>Falcon Vertigo Hybrid VTOL</em> - це квадропланний літальний апарат VTOL, що був розроблений для роботи з PX4 та керування польотом Dropix (сумісне з Pixhawk). Він може нести невелику камеру GoPro.</p><p>Набір RTF містить все необхідне для повної системи, за винятком приймача RC та телеметричного модуля. Компоненти також можуть бути куплені окремо.</p><p>Основна Інформація:</p><ul><li><strong>Каркас:</strong> Falcon Vertigo Hybrid VTOL</li><li><strong>Контролер польоту:</strong> Dropix</li><li><strong>Розмах крил:</strong> 1.3м</li></ul><p><img src="'+a+'" alt="Falcon Vertigo Hybrid VTOL RTF"></p><h2 id="специфікація-матеріалів" tabindex="-1">Специфікація матеріалів <a class="header-anchor" href="#специфікація-матеріалів" aria-label="Permalink to &quot;Специфікація матеріалів&quot;">​</a></h2><p>Майже все необхідне надається в комплекті RTF (посилання поруч з компонентами нижче надаються у випадку, якщо ви бажаєте придбати будь-який компонент окремо):</p><ul><li>Попередньо ламіновані крила з EPP</li><li>Кінчики крил і повне обладнання</li><li>Контролер польоту Dropix (знято з виробництва) з <ul><li>GPS u-blox M8N</li><li>Датник живлення</li><li><a href="https://store-drotek.com/793-digital-differential-airspeed-sensor-kit-.html" target="_blank" rel="noreferrer">Датчик швидкості</a></li></ul></li><li>Чотирьохмоторний набір потужності <a href="https://www.getfpv.com/tiger-motor-mt-2216-11-900kv-v2.html" target="_blank" rel="noreferrer">Tiger Motor MT-2216-11 900kv V2</a> (виробництва припинено)</li><li>4 x пропелер 10”x 5” (квадро-мотори)</li><li>4 x <a href="http://www.getfpv.com/tiger-motor-flame-25a-esc.html" target="_blank" rel="noreferrer">ESC 25A</a></li><li>1 x пропелер 10” x 5” (двигун-штовхач)</li><li>1 x ESC 30A</li><li>Система потужності двигуна-штовхача</li><li>Вуглецеві труби та кріплення</li><li>Кронштейни для мотора G10</li><li>1 x <a href="https://www.overlander.co.uk/batteries/lipo-batteries/power-packs/3700mah-4s-14-8v-25c-lipo-battery-overlander-sport.html" target="_blank" rel="noreferrer">3700mah 4S 30C Lipo акумулятор</a></li><li>Плата розподілу живлення Dropix та кабель</li></ul><p>Набір не постачається з радіоприймачем або (опціональними) модулями телеметрії. Для цієї конфігурації ми використали наступні компоненти:</p><ul><li>Отримувач: <a href="https://www.frsky-rc.com/product/d4r-ii/" target="_blank" rel="noreferrer">FrSSKY D4R-II</a></li><li>Телеметрія: <a href="https://www.getfpv.com/holybro-100mw-fpv-transceiver-telemetry-radio-set-915mhz.html" target="_blank" rel="noreferrer">Модулі Holybro 100 мВт 915МГц</a> (Припинено)</li></ul><h2 id="необхідні-інструменти" tabindex="-1">Необхідні інструменти <a class="header-anchor" href="#необхідні-інструменти" aria-label="Permalink to &quot;Необхідні інструменти&quot;">​</a></h2><p>Наступні інструменти використовувалися для збирання корпусу повітряного судна:</p><ul><li>Шуруповерт Phillips</li><li>5.5 мм шестигранник гайковерт</li><li>Кусачки</li><li>1x паяльник та припій</li><li>Hobby пінцет з нержавіючої сталі</li><li>Клей Gorilla</li><li>Скловолоконна армована стрічка</li></ul><p><img src="'+r+'" alt="Інструменти для створення ПЗ"></p><h2 id="кроки-збірки" tabindex="-1">Кроки збірки <a class="header-anchor" href="#кроки-збірки" aria-label="Permalink to &quot;Кроки збірки&quot;">​</a></h2><p>Набір RTF потребує наступного монтажу.</p><h3 id="крок-1-прикріпіть-кріплення-двигунів" tabindex="-1">Крок 1: Прикріпіть кріплення двигунів <a class="header-anchor" href="#крок-1-прикріпіть-кріплення-двигунів" aria-label="Permalink to &quot;Крок 1: Прикріпіть кріплення двигунів&quot;">​</a></h3><ol><li><p>Нанесіть клей Gorilla всередину кронштейнів крила, як показано.</p><p><img src="'+l+'" alt="Додайте клей на кріплення крила"></p></li><li><p>Вкріпіть карбонову трубку в держаки. Для вирівнювання піддона та трубки слід використовувати білу позначку (як показано на зображенні).</p><div class="info custom-block"><p class="custom-block-title">INFO</p></div></li></ol><p>Це дуже важливо, оскільки біла позначка вказує на центр мас. :::</p><img src="'+e+'" title="Вуглецева труба в дужках" width="300px"><ol><li><p>Наступні зображення показують вирівнювання стержнів з інших точок зору:</p><p><img src="'+o+'" alt="вирівнювання стержня рами квадрокоптера знизу"> <img src="'+p+'" alt="схема вирівнювання стержня рами квадрокоптера"></p></li></ol><h3 id="крок-2-прикріпіть-крила" tabindex="-1">Крок 2: Прикріпіть крила <a class="header-anchor" href="#крок-2-прикріпіть-крила" aria-label="Permalink to &quot;Крок 2: Прикріпіть крила&quot;">​</a></h3><ol><li><p>Вставте обидві вуглецеві труби в фюзеляж.</p><img src="'+s+'" width="500px" title="Вуглецеві труби фюзеляжу"></li><li><p>Нанесіть клей gorilla між двома білими позначками на кожну трубку (вказано червоними стрілками). Білий знак по центру (синя стрілка) буде розміщений в центрі фюзеляжу, а інші знаки - по боках.</p><img src="'+d+'" width="500px" title="Нанести клей на важіль"></li><li><p>Після того, як вуглецеві трубки знаходяться всередині фюзеляжу, розподіліть клей gorilla на решту трубки та прикріпіть крила.</p></li><li><p>Фюзеляж має два отвори для кабелів двигуна та сервоприводів. Пропустіть кабелі через отвори, а потім приєднайте крила до фюзеляжу.</p><img src="'+h+'" width="500px" title="Отвори фюзеляжу для кабелів"></li><li><p>Усередині фюзеляжу під&#39;єднайте сигнальні кабелі, які ви щойно прокинули з крил до регулятора ESC, використовуючи надані роз&#39;єми. Регулятори швидкості ESC вже підключені до двигунів і налаштовані на обертання в правильному порядку (вам потрібно буде підключити ESC PDB до модуля живлення на пізнішому етапі).</p><img src="'+c+'" width="500px" title="Підключіть кабелі живлення та сигнальні кабелі ESC"></li><li><p>Так само, як і з ESC, сервопристосування вже встановлені. Підключіть сигнальний кабель з крила (проходить через фюзеляж) до контролера польоту.</p><img src="'+n+'" width="500px" title="Підключіть кабелі серводвигунів"></li><li><p>Повторіть ці кроки для іньшого крила.</p></li></ol><h3 id="крок-3-підключіть-електроніку" tabindex="-1">Крок 3: Підключіть електроніку <a class="header-anchor" href="#крок-3-підключіть-електроніку" aria-label="Permalink to &quot;Крок 3: Підключіть електроніку&quot;">​</a></h3><p>Цей комплект включає контролер польоту Dropix з вже підключеною більшістю необхідної електроніки (якщо ви використовуєте інший контролер польоту, сумісний з Pixhawk, підключення схожі).</p><img src="'+_+'" width="500px" title="Електроніка Falcon Vertigo"><div class="info custom-block"><p class="custom-block-title">Загальну інформацію про підключення Dropix можна знайти в <a href="./../flight_controller/dropix.html">Контролері польоту Dropix</a>. :::</p><h4 id="підключіть-роз-єм-живлення-esc-та-прокладіть-кабелі-сигналів-до-контролера-польоту" tabindex="-1">Підключіть роз&#39;єм живлення ESC та прокладіть кабелі сигналів до контролера польоту <a class="header-anchor" href="#підключіть-роз-єм-живлення-esc-та-прокладіть-кабелі-сигналів-до-контролера-польоту" aria-label="Permalink to &quot;Підключіть роз&#39;єм живлення ESC та прокладіть кабелі сигналів до контролера польоту&quot;">​</a></h4><ol><li><p>Підключіть ЕСС до модуля живлення за допомогою роз&#39;єму XT60</p><img src="'+m+'" width="500px" title=""></li><li><p>Передайте кабелі сигналів до контролера польоту</p><img src="'+f+'" width="500px" title="GPS, ESC, Підключення серводвигунів"></li></ol><h4 id="підключення-двигуна" tabindex="-1">Підключення двигуна <a class="header-anchor" href="#підключення-двигуна" aria-label="Permalink to &quot;Підключення двигуна&quot;">​</a></h4><p>Проводка двигуна та сервоприводу практично взагалі залежить від вас, але повинна відповідати конфігурації <a href="./../airframes/airframe_reference.html#vtol_standard_vtol_generic_standard_vtol">Загального стандартного VTOL</a>, як показано в посиланні на конструкцію корпусу. Геометрія та призначення виводу можуть бути налаштовані в <a href="./../config/actuators.html#actuator-outputs">Конфігурації приводів</a></p><p>Наприклад, ви можете з’єднати його так, як у цьому прикладі (орієнтація як у &quot;сидячи в літаку&quot;):</p><table><thead><tr><th>Порт</th><th>Підключення</th></tr></thead><tbody><tr><td>MAIN 1</td><td>Передній правий мотор, CCW</td></tr><tr><td>MAIN 2</td><td>Задній лівий мотор, CCW</td></tr><tr><td>MAIN 3</td><td>Передній лівий мотор, CW</td></tr><tr><td>MAIN 4</td><td>Задній правий мотор, CW</td></tr><tr><td>AUX 1</td><td>Лівий елерон</td></tr><tr><td>AUX 2</td><td>Правий елерон</td></tr><tr><td>AUX 3</td><td>Elevator</td></tr><tr><td>AUX 4</td><td>Rudder</td></tr><tr><td>AUX 5</td><td>Тяга</td></tr></tbody></table><p><a id="dropix_back"></a></p><h4 id="підключення-контролера-польоту-мотори-сервомеханізми-приимач-rc-датчик-струму" tabindex="-1">Підключення контролера польоту: Мотори, Сервомеханізми, Приймач RC, датчик струму <a class="header-anchor" href="#підключення-контролера-польоту-мотори-сервомеханізми-приимач-rc-датчик-струму" aria-label="Permalink to &quot;Підключення контролера польоту: Мотори, Сервомеханізми, Приймач RC, датчик струму&quot;">​</a></h4><p>Нижче показане зображення задньої плати керування польотом dropix, підкреслюючи вихідні контакти для підключення кабелів квадрокоптерних моторів, кабелів сигналу елерону, мотору, дросельного мотору, а також контактів поточного сенсора та введення радіоприймача (RC IN).</p><img id="dropix_outputs" src="'+u+'" width="500px" title="Вихідні мотори/серводвигуни Dropix"><ol><li><p>Підключіть сигнальні кабелі квадро моторів.</p></li><li><p>Підключіть кабелі елеронів та мотора керування ручкою газу в допоміжні виходи.</p></li><li><p>Підключіть кабель сигналу двигуна дроселя від ESC до відповідного допоміжного порту контролера польоту. Підключіть ESC до регулятора газу.</p><img src="'+b+'" width="500px" title="Підключити дросельний мотор"></li><li><p>Підключіть приймач (RC IN).</p></li></ol><p><a id="dropix_front"></a></p><h4 id="підключення-контролера-польоту-телеметрія-датчик-швидкості-повітря-gps-сигналізація-та-перемикач-безпеки" tabindex="-1">Підключення контролера польоту: телеметрія, датчик швидкості повітря, GPS, сигналізація та перемикач безпеки <a class="header-anchor" href="#підключення-контролера-польоту-телеметрія-датчик-швидкості-повітря-gps-сигналізація-та-перемикач-безпеки" aria-label="Permalink to &quot;Підключення контролера польоту: телеметрія, датчик швидкості повітря, GPS, сигналізація та перемикач безпеки&quot;">​</a></h4><p>Датчикові входи, телеметрія, сигналізація та безпечний вимикач розташовані з переднього боку керування польотом, як показано на схемі підключення нижче.</p><img src="'+g+'" width="500px" alt="Роз&#39;єми Dropix спереду" title="Роз&#39;єми Dropix спереду"><ol><li><p>Підключіть телеметрію, датчик швидкості, GPS, гудок та безпечний перемикач, як показано.</p><img src="'+x+'" width="500px" title="Підключення датчиків"></li></ol><h4 id="контролер-польоту-підключіть-модуль-живлення-та-зовнішніи-usb" tabindex="-1">Контролер польоту: Підключіть модуль живлення та зовнішній USB <a class="header-anchor" href="#контролер-польоту-підключіть-модуль-живлення-та-зовнішніи-usb" aria-label="Permalink to &quot;Контролер польоту: Підключіть модуль живлення та зовнішній USB&quot;">​</a></h4><p>Входи для порту USB, модуля живлення та зовнішнього USB розташовані на правому боці контролера польоту.</p><ol><li><p>Підключіть живлення та USB, як показано</p><img src="'+v+'" width="500px" title="Підключення модулів живлення та USB"></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Зовнішній USB є необов&#39;язковим. Він повинен бути використано, якщо доступ до порту USB ускладнений після закріплення контролера польоту.</p></div></div><h4 id="встановіть-пітотрубку-датчик-швидкості" tabindex="-1">Встановіть пітотрубку (датчик швидкості) <a class="header-anchor" href="#встановіть-пітотрубку-датчик-швидкості" aria-label="Permalink to &quot;Встановіть пітотрубку (датчик швидкості)&quot;">​</a></h4><p>Труба пітота встановлена спереду літака й підключена до датчика швидкості через трубу.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Важливо, щоб ніщо не заважало протіканню повітря до труби Піто. Це критично для фіксованих крил та для переходу від квадрокоптера до літака.</p></div><ol><li><p>Встановіть трубку Піто у передній частині літака</p><img src="'+k+'" width="500px" title="Монтаж датчика швидкості"></li><li><p>Зафіксуйте з&#39;єднуючі трубки та переконайтеся, що вони не зігнуті / пом&#39;яті.</p><img src="'+w+'" width="500px" title="Монтаж датчика швидкості"></li><li><p>Підключіть трубки до датчика швидкості.</p><img src="'+P+'" width="500px" title="Підключіть датчик швидкості та трубопровід"></li></ol><h4 id="встановлення-підключення-приимача-та-модуля-телеметріі" tabindex="-1">Встановлення/підключення приймача та модуля телеметрії <a class="header-anchor" href="#встановлення-підключення-приимача-та-модуля-телеметріі" aria-label="Permalink to &quot;Встановлення/підключення приймача та модуля телеметрії&quot;">​</a></h4><ol><li><p>Вставте приймач та телеметричний модуль на зовнішню сторону рами транспортного засобу.</p><img src="'+q+'" width="500px" title="Приймач супутникового зв&#39;язку"></li><li><p>Підключіть приймач до роз&#39;єму RC IN <em>ззаду</em> плати dropix, як показано вище (дивіться також <a href="#dropix_back">інструкції з пілотажного контролера</a>).</p></li><li><p>Підключіть модуль телеметрії до <em>передньої</em> частини регулятора польоту, як показано нижче (див. інструкції регулятора польоту за посиланням <a href="#dropix_front">Літачний контролер інструкції</a> для більш детальної інформації про контакти).</p><img src="'+S+'" width="500px" title="Вставте модуль телеметрії"></li></ol><p><a id="compass_gps"></a></p><h4 id="модуль-gps-компас" tabindex="-1">Модуль GPS/Компас <a class="header-anchor" href="#модуль-gps-компас" aria-label="Permalink to &quot;Модуль GPS/Компас&quot;">​</a></h4><p>Модуль GPS/Компас вже встановлено на крило в типовому положенні. Вам не потрібно робити щось додаткове для цього!</p><img src="'+T+'" width="500px" title="GPS/компас"><p><a id="flight_controller_orientation"></a></p><h4 id="монтаж-та-орієнтація-політного-контролера" tabindex="-1">Монтаж та орієнтація політного контролера <a class="header-anchor" href="#монтаж-та-орієнтація-політного-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація політного контролера&quot;">​</a></h4><ol><li><p>Встановіть орієнтацію вашого політ контролеру на 270 градусів.</p><img src="'+V+'" width="500px" title="Орієнтація політного контролера"></li><li><p>Закріпіть контролер на місці за допомогою піни для поглинання вібрації.</p></li></ol><h3 id="крок-4-перевірка-остаточноі-збірки" tabindex="-1">Крок 4: Перевірка остаточної збірки <a class="header-anchor" href="#крок-4-перевірка-остаточноі-збірки" aria-label="Permalink to &quot;Крок 4: Перевірка остаточної збірки&quot;">​</a></h3><p>Останнім етапом збирання є перевірка стійкості дрона та правильності налаштування двигунів.</p><ol><li><p>Перевірте, що двигуни обертаються у правильних напрямках (як у діаграмі QuadX нижче).</p><img src="'+C+'" width="200px" title="Порядок/напрямки руху чотирьох моторів"><div class="info custom-block"><p class="custom-block-title">За необхідності може бути виправлено напрямок серводіви за допомогою прапорця <code>Rev Range (для серводів)</code>, пов&#39;язаного із кожним вихідним серводвигуном в конфігурації QGroundControl <a href="./../config/actuators.html#actuator-outputs">Актуаторний вихід</a> (тільки для серводвигунів) (це встановлює параметр <a href="./../advanced_config/parameter_reference.html#PWM_AUX_REV">PWM_AUX_REV</a> або <a href="./../advanced_config/parameter_reference.html#PWM_MAIN_REV">PWM_AUX_MAIN</a>). :::</p></div></li><li><p>Перевірте, чи транспортний засіб збалансований навколо очікуваного центру мас</p><ul><li><p>Утримуйте транспортний засіб пальцями у центрі ваги та переконайтеся, що транспортний засіб залишається стабільним.</p><p><img src="'+y+'" alt="Центр мас рівня"></p></li><li><p>Якщо транспортний засіб нахиляється вперед або назад, перемістіть двигуни, щоб утримати рівновагу.</p><p><img src="'+A+'" alt="Рівень моторів"></p></li></ul></li></ol><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Виконайте звичайну <a href="./../config/">основну настройку</a>.</p><p>Примітки:</p><ol><li><p>Для <a href="./../config/airframe.html">Airframe</a> виберіть групу/тип транспортного засобу як <em>Standard VTOL</em> та конкретний транспортний засіб як <a href="./../airframes/airframe_reference.html#vtol_standard_vtol_generic_standard_vtol">Generic Standard VTOL</a>, як показано нижче.</p><p><img src="'+R+'" alt="QCG - Вибір Загального Стандарту VTOL"></p></li><li><p>Встановіть <a href="./../config/flight_controller_orientation.html">Орієнтацію автопілота</a> на <code>ROTATION_YAW_270</code>, оскільки автопілот встановлений <a href="#flight_controller_orientation">боком</a> відносно передньої частини транспортного засобу. Компас орієнтований вперед, тому ви можете залишити це на типовому значенні (<code>ROTATION_NONE</code>).</p></li><li><p>Налаштуйте виводи та геометрію, дотримуючись інструкцій у <a href="./../config/actuators.html">Налаштування приводів</a></p></li><li><p>За замовчуванням параметри часто достатні для стабільного польоту. Для отримання докладнішої інформації з настройки дивіться <a href="./../config_vtol/vtol_quad_configuration.html">Стандартна Проводка та Налаштування VTOL</a>.</p></li></ol><p>Після завершення калібрування, VTOL готовий до польоту.</p><h2 id="відео" tabindex="-1">Відео <a class="header-anchor" href="#відео" aria-label="Permalink to &quot;Відео&quot;">​</a></h2>',51),G=t("p",null,[t("div",{class:"embed-responsive embed-responsive-16by9"},[t("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/h7OHTigtU0s",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),U=t("h2",{id:"підтримка",tabindex:"-1"},[i("Підтримка "),t("a",{class:"header-anchor",href:"#підтримка","aria-label":'Permalink to "Підтримка"'},"​")],-1),L=t("p",null,[i("Якщо у вас виникли запитання щодо перетворення або конфігурації VTOL, зайдіть на "),t("a",{href:"https://discuss.px4.io/c/px4/vtol",target:"_blank",rel:"noreferrer"},"https://discuss.px4.io/c/px4/vtol"),i(".")],-1),B=[D,G,U,L];function M(W,X,H,$,z,Q){return E(),O("div",null,B)}const Z=N(I,[["render",M]]);export{K as __pageData,Z as default};
