import{_ as a,a as l,b as e,c as i,d as r,e as t,f as o,g as s,h,i as n,j as p,k as c,l as d,m,n as f,o as _,p as u,q as g,r as k}from"./chunks/18-12s-power-module.DUUbUR_Q.js";import{_ as b}from"./chunks/servo_trim.5g82AaIR.js";import{_ as x,c as w,o as S,ab as P}from"./chunks/framework.CUflZczI.js";const I=JSON.parse('{"title":"Foxtech Loong 2160 VTOL","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_vtol/vtol_quadplane_foxtech_loong_2160.md","filePath":"uk/frames_vtol/vtol_quadplane_foxtech_loong_2160.md"}'),q={name:"uk/frames_vtol/vtol_quadplane_foxtech_loong_2160.md"},v=P('<h1 id="foxtech-loong-2160-vtol" tabindex="-1">Foxtech Loong 2160 VTOL <a class="header-anchor" href="#foxtech-loong-2160-vtol" aria-label="Permalink to &quot;Foxtech Loong 2160 VTOL&quot;">​</a></h1><p>Foxtech Loong 2160 VTOL - це легкий у монтажі майже готовий до польоту (ARF) квадрокоптерний ВТОЛ дрон з розмахом крила 2160 мм. Цей посібник зі збірки показує, як додати систему керування польотом, використовуючи набір оцінки <a href="./../companion_computer/auterion_skynode.html">Auterion Skynode evaluation kit</a> або <a href="./../flight_controller/pixhawk6c.html">Pixhawk 6C</a>, і налаштувати PX4.</p><p><img src="'+a+'" alt="Фото Loong VTOL у польоті"></p><h2 id="загальнии-огляд" tabindex="-1">Загальний огляд <a class="header-anchor" href="#загальнии-огляд" aria-label="Permalink to &quot;Загальний огляд&quot;">​</a></h2><p>Специфікації:</p><ul><li>Розмах крила: 2160 мм</li><li>Довжина фюзеляжу: 1200 мм</li><li>Вага при зльоті: Приблизно 7 кг (без вантажу)</li><li>Максимальний час польоту: До 1 години і 30 хвилин</li><li>Крейсерська швидкість: Приблизно 17 м/с</li><li>Максимальна вага вантажу: Близько 1,5 кг</li><li>Розміри чохла: 125 см х 34 см х 34 см</li></ul><p>Основні характеристики:</p><ul><li>Легка збірка: Швидке і просте налаштування</li><li>Переносимість: Компактний дизайн для зручного транспортування у включеному чохлі</li><li>Готовий до польоту: Всі приводи вже встановлені та підключені, що мінімізує час налаштування</li><li>Подовжений час польоту: До 1 години і 30 хвилин в залежності від погодних умов та ваги при зльоті</li><li>Універсальна вантажопідйомність: Просторий фюзеляж вміщує різноманітні вантажі, включаючи варіанти, такі як камера Sony A7R для картографічних застосувань.</li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://www.foxtechfpv.com/foxtech-loong-2160-vtol.html" target="_blank" rel="noreferrer">Foxtech FPV (ARF Combo)</a> - рекомендується</li><li><a href="https://www.alibaba.com/product-detail/Loong-2160-Long-Endurance-VTOL-Mapping_1600280686653.html" target="_blank" rel="noreferrer">Alibaba</a></li></ul><h2 id="політнии-контролер" tabindex="-1">Політний контролер <a class="header-anchor" href="#політнии-контролер" aria-label="Permalink to &quot;Політний контролер&quot;">​</a></h2><p>Наступна конфігурація протестована:</p><ul><li><a href="./../companion_computer/auterion_skynode.html">Auterion Skynode evaluation kit</a></li><li><a href="./../flight_controller/pixhawk6c.html">Pixhawk 6C</a></li></ul><h2 id="додаткові-ресурси" tabindex="-1">Додаткові ресурси <a class="header-anchor" href="#додаткові-ресурси" aria-label="Permalink to &quot;Додаткові ресурси&quot;">​</a></h2><ul><li>Auterion 12S Power Module</li><li><a href="https://holybro.com/collections/power-modules-pdbs/products/pm08d-digital-power-module-14s-200a" target="_blank" rel="noreferrer">Модуль живлення Holybro PM08D (альтернатива Auterion PM)</a></li><li><a href="./../gps_compass/rtk_gps_holybro_h-rtk-f9p.html">GPS F9P (включено в Skynode оціночний. комплект)</a></li><li><a href="./../gps_compass/rtk_gps_holybro_h-rtk-m8p.html">GPS M9N (дешевша альтернатива F9P)</a></li><li><a href="https://www.dualrc.com/parts/airspeed-sensor-sdp33" target="_blank" rel="noreferrer">Датчик швидкості (включено в Skynode eval. kit)</a> — рекомендований для покращення безпеки та продуктивності</li><li><a href="https://holybro.com/products/digital-air-speed-sensor?pr_prod_strat=use_description&amp;pr_rec_id=236dfda00&amp;pr_rec_pid=7150470561981&amp;pr_ref_pid=7150472462525&amp;pr_seq=uniform" target="_blank" rel="noreferrer">Датчик швидкості (дешевший альтернативний варіант)</a></li><li><a href="./../sensor/sfxx_lidar.html">Lidar Lightware lw20-c (включено в Skynode eval. kit)</a> (Необов&#39;язково)</li><li><a href="https://www.seeedstudio.com/PSK-CM8JL65-CC5-Infrared-Distance-Measuring-Sensor-p-4028.html" target="_blank" rel="noreferrer">Інфрачервоний сенсор вимірювання відстані Seeed Studio PSK-CM8JL65-CC5 (дешевший аналог)</a> (Опціонально)</li><li><a href="./../getting_started/rc_transmitter_receiver.html">Радіо (RC) система</a> на ваш вибір</li><li><a href="https://holybro.com/collections/rc-radio-transmitter-receiver/products/skydroid-h12?variant=42940989931709" target="_blank" rel="noreferrer">Наземна станція та радіо зв&#39;язок</a></li><li><a href="https://www.digitec.ch/en/s1/product/powerguard-usb-c-usb-c-025-m-usb-cables-22529949?dbq=1&amp;gclid=Cj0KCQjw2cWgBhDYARIsALggUhrh-z-7DSU0wKfLBVa8filkXLQaxUpi7pC0ffQyRzLng8Ph01h2R1gaAp0mEALw_wcB&amp;gclsrc=aw.ds" target="_blank" rel="noreferrer">Розширення кабеля USB-C</a></li><li><a href="https://www.3dxr.co.uk/autopilots-c2/the-cube-aka-pixhawk-2-1-c9/cube-cables-accessories-sensors-c15/cubepilot-i2c-can-splitter-jst-gh-4pin-p2840" target="_blank" rel="noreferrer">Розгалужувач I2C</a></li><li><a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/airframes/vtol/foxtech_loong_2160/loong-3d-prints.zip" target="_blank" rel="noreferrer">Монтажі, виготовлені на 3D-принтері</a><ul><li>1x Базова плита</li><li>1x Stack-fixture</li><li>1x Кріплення вентилятора</li><li>1x Монтаж радіо</li><li>1x Верхня плита</li><li>1x Адаптер радіоантени</li><li>1x USB-C Holder 1</li><li>1x USB-C Holder 2</li></ul></li><li><a href="https://cnckitchen.store/products/gewindeeinsatz-threaded-insert-set-standard-200-stk-pcs" target="_blank" rel="noreferrer">Монтаж вставних гвинтових вкладень</a></li><li><a href="https://www.amazon.com/Connectors-Female-Pieces-Shrink-Battery/dp/B0875MBLNH/ref=sr_1_1?keywords=xt30+connector&amp;qid=1700643604&amp;sr=8-1" target="_blank" rel="noreferrer">XT30 роз&#39;єми</a></li><li><a href="https://de.aliexpress.com/item/1005005999729125.html?spm=a2g0o.productlist.main.1.7fe0c7fcvInMsM&amp;algo_pvid=2e5373e9-747f-4a28-9739-cd59d05d64f1&amp;aem_p4p_detail=202311220106396068090130108300006423842&amp;algo_exp_id=2e5373e9-747f-4a28-9739-cd59d05d64f1-0&amp;pdp_npi=4%40dis%21CHF%2114.42%213.72%21%21%2116.01%21%21%402101f04d17006439995917563eeeb0%2112000035246480339%21sea%21CH%210%21AB&amp;curPageLogUid=24AixvgVOlG3&amp;search_p4p_id=202311220106396068090130108300006423842_1" target="_blank" rel="noreferrer">Div. Screws</a></li><li><a href="https://www.amazon.com/Superun-Cable-Tie-Kit-Assorted/dp/B07TMKJP5S/ref=sr_1_2?crid=968Z3XJK9N3J&amp;keywords=zip%2Bties%2Bset&amp;qid=1700644053&amp;sprefix=zip%2Bties%2Bset%2Caps%2C155&amp;sr=8-2&amp;th=1" target="_blank" rel="noreferrer">Кабельні стяжки-затискачі</a></li><li><a href="https://www.digikey.ch/de/products/detail/amphenol-rf/095-902-536-012/13246174" target="_blank" rel="noreferrer">Кабель продления антенны - соответствует вашей радиосистеме</a></li><li><a href="https://genstattu.com/tattu-22-2v-30c-6s-22000mah-lipo-battery-with-xt90-s-plug-for-uav.html" target="_blank" rel="noreferrer">Рекомендована батарея (12S 22Аг)</a></li></ul><h2 id="інструменти" tabindex="-1">Інструменти <a class="header-anchor" href="#інструменти" aria-label="Permalink to &quot;Інструменти&quot;">​</a></h2><p>Наступні інструменти використовувалися для цієї збірки.</p><ul><li>Набір гофроксена</li><li>Набір гаєчних ключів</li><li><a href="https://www.amazon.com/UY-CHAN-Programmable-Pocket-size-Soldering/dp/B07G71CKC4/ref=sr_1_7?crid=2S2XK6363XRDF&amp;keywords=ts+80+soldering+iron&amp;qid=1700644208&amp;sprefix=ts+80%2Caps%2C151&amp;sr=8-7" target="_blank" rel="noreferrer">Станція для паяння</a></li><li>Клей: гарячий клей, 5 хв епоксидка</li><li>Ізострічка</li><li>Двостороння стрічка 3M (<a href="https://www.amazon.in/3M-VHB-Tape-4910-Length/dp/B00GTABM3Y" target="_blank" rel="noreferrer">стрічка 3M VHB</a>)</li><li>3D-принтер</li><li><a href="https://www.amazon.com/Loctite-Heavy-Duty-Threadlocker-Single/dp/B000I1RSNS?th=1" target="_blank" rel="noreferrer">Синій Loctite</a></li></ul><h2 id="інтеграція-з-обладнанням" tabindex="-1">Інтеграція з обладнанням <a class="header-anchor" href="#інтеграція-з-обладнанням" aria-label="Permalink to &quot;Інтеграція з обладнанням&quot;">​</a></h2><p>У цій документації описано інтеграцію Auterion Skynode. Встановлення Pixhawk можна виконати аналогічно.</p><h3 id="підготовка" tabindex="-1">Підготовка <a class="header-anchor" href="#підготовка" aria-label="Permalink to &quot;Підготовка&quot;">​</a></h3><h3 id="авіонічнии-блок" tabindex="-1">Авіонічний блок <a class="header-anchor" href="#авіонічнии-блок" aria-label="Permalink to &quot;Авіонічний блок&quot;">​</a></h3><p><img src="'+l+'" alt="Повна збірка стеку"></p><h4 id="підготуите-деталі-надруковані-на-3d-принтері" tabindex="-1">Підготуйте деталі, надруковані на 3D-принтері <a class="header-anchor" href="#підготуите-деталі-надруковані-на-3d-принтері" aria-label="Permalink to &quot;Підготуйте деталі, надруковані на 3D-принтері&quot;">​</a></h4><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Використовуйте паяльник, щоб натиснути різьбові вкладки в надруковані деталі 3D.</p></div><ol><li><p>Вставте 10x різьбових вкладок M3 в піддон, як показано на малюнку:</p><p><img src="'+e+'" alt="Основна плита з різбленими вставками"></p></li><li><p>Вставте 2x різьбові вставки M3 в пристрій для накладання, як показано на зображенні нижче:</p><p><img src="'+i+'" alt="Фіксатор стекла з нарізними вкладками"></p></li><li><p>Вставте 2x різьбові вкладки M4 в кріплення вентилятора та кріплення радіо, як показано на малюнку нижче.</p><p><img src="'+r+'" alt="Кріплення радіо"></p><p>Якщо ви хочете додати 40-мм вентилятор з напругою 5 В на кріплення вентилятора, вставте 4x вставки M3.</p><p><img src="'+t+'" alt="Fan-mount"></p></li><li><p>Змініть кабельний роз&#39;єм на роз&#39;єм для сервоприводу, щоб його можна було вставити в шину сервоприводу для живлення.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Можливо знадобиться вентилятор, якщо використовується потужне радіо.</p></div></li></ol><p>:::</p><p><img src="'+o+'" alt="Кріплення вентилятора"></p><ol start="5"><li><p>Вилучіть оригінальну кронштейнну пластину з автомобіля. Приклейте кабелі до зовнішньої частини фюзеляжу.</p><p><img src="'+s+'" alt="Порожнє фюзеляж"></p></li><li><p>Перемістіть підставку в транспортний засіб.</p></li><li><p>Закрутіть кріплення стопора до основної плити та позначте місце розташування кріплення стопора стрічкою або ручкою.</p></li><li><p>Вилучіть частини з фюзеляжу та склейте кріплення стопки на місце за допомогою гарячого клею.</p></li></ol><p><img src="'+h+'" alt="Mounting stack fixture"></p><h3 id="модуль-потужністю-40a" tabindex="-1">Модуль потужністю 40A <a class="header-anchor" href="#модуль-потужністю-40a" aria-label="Permalink to &quot;Модуль потужністю 40A&quot;">​</a></h3><p>Модуль живлення 40A забезпечує живлення для авіоніки при використанні Skynode (і поставляється із комплектом оцінки Skynode):</p><ol><li><p>Вилучіть корпус з 40A PM.</p></li><li><p>Зафіксуйте ПМ з 2x M2x6mm до нижньої пластини.</p></li><li><p>Створіть кабель для подовження роз&#39;єму XT60 до XT30, який закріплений на базовій платі. З цим, живлення від акумулятора 6S може бути підключено безпосередньо до роз&#39;єму XT30 за допомогою попередньо налаштованого кабелю, що поставляється з транспортним засобом.</p><p><img src="'+n+'" alt="Встановлення модуля живлення 40A"></p></li></ol><p>Якщо потрібно, вихід 10V з радіопорту на PM також може бути викладений через XT30, який може бути встановлений поруч зі входом батареї 6S XT60.</p><h3 id="датчики-сенсори" tabindex="-1">Датчики (сенсори) <a class="header-anchor" href="#датчики-сенсори" aria-label="Permalink to &quot;Датчики (сенсори)&quot;">​</a></h3><h4 id="трубка-піто" tabindex="-1">Трубка Піто <a class="header-anchor" href="#трубка-піто" aria-label="Permalink to &quot;Трубка Піто&quot;">​</a></h4><ol><li><p>Датчик може бути встановлений за допомогою 2x винтів M3x16мм в передньому правому куті підставки. Піклуйтесь, щоб конектор був звернутий у бік центру фюзеляжу.</p><p><img src="'+p+'" alt="Встановлений датчик швидкості повітря"></p><p>Лише передню трубу (не так, як показано на картинці) використовується; іншу трубу можна видалити, оскільки наш досвід показав, що тиск всередині фюзеляжу достатній як статичний тиск.</p></li><li><p>Коли стек встановлено всередині фюзеляжу, труба, що йде з крила, та труба, що йде з датчика швидкості повітря, повинні бути з&#39;єднані разом. Використовуйте трохи слини (це найлегший спосіб) щоб з&#39;єднати їх разом, а потім використовуйте термоусадочну трубку, щоб посилити з&#39;єднання.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Використовуйте джерело тепла обережно, оскільки піна починаєтся танути при високих температурах.</p></div></li></ol><p>:::</p><h4 id="лідар" tabindex="-1">Лідар <a class="header-anchor" href="#лідар" aria-label="Permalink to &quot;Лідар&quot;">​</a></h4><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Лідар є рекомендованим! Якщо лідар не встановлено, ви повинні вимкнути використання дії фіксованого крила в режимі утримання для прискорення вперед (встановіть <a href="./../advanced_config/parameter_reference.html#VT_FWD_THRUST_EN">VT_FWD_THRUST_EN</a> на <code>0</code> замість <code>1</code>).</p></div><ol><li><p>Позначте місце для встановлення лідару за допомогою скотчу або ручки. Виріжте отвір всередині оболонки з ПВХ та піни, щоб лідар вміщувався на місце.</p><p><img src="'+c+'" alt="Підготоване отвір лідару"></p></li><li><p>Закріпіть лідар з гарячим клеєм.</p><p><img src="'+d+'" alt="Встановлений лідар"></p></li></ol><h4 id="gps-компас" tabindex="-1">GPS/компас <a class="header-anchor" href="#gps-компас" aria-label="Permalink to &quot;GPS/компас&quot;">​</a></h4><ol><li><p>Використовуйте двосторонній скотч для кріплення GPS на задній частині транспортного засобу під задньою засувкою.</p><p><img src="'+m+'" alt="Встановлений GPS"></p><p>Стрілка на GPS для орієнтації може бути проігнорована. Орієнтацію буде визначено під час калібрування автопілота.</p></li></ol><h3 id="політнии-контролер-1" tabindex="-1">Політний контролер <a class="header-anchor" href="#політнии-контролер-1" aria-label="Permalink to &quot;Політний контролер&quot;">​</a></h3><p>Встановіть на базову платформу або Pixhawk, або Skynode.</p><h4 id="pixhawk-6c-6c-mini" tabindex="-1">Pixhawk 6c/6c mini <a class="header-anchor" href="#pixhawk-6c-6c-mini" aria-label="Permalink to &quot;Pixhawk 6c/6c mini&quot;">​</a></h4><ol><li>Використовуйте двосторонню стрічку для кріплення контролера польоту до базової платформи.</li></ol><h4 id="skynode" tabindex="-1">Skynode <a class="header-anchor" href="#skynode" aria-label="Permalink to &quot;Skynode&quot;">​</a></h4><ol><li>Використовуйте 4x гвинти M3x8 для кріплення Skynode до підстави. Переконайтеся, що верхня частина &quot;A&quot; спрямована вперед транспортного засобу.</li><li>Вставте 40-амперний модуль живлення в верхній з двох роз&#39;ємів живлення.</li><li>Вставте один (або якщо потрібно, два) USB адаптери в 4-контактні роз&#39;єми JST-GH на задній частині Skynode та прокладіть їх до передньої панелі. Виправте кабелі за допомогою хомутів-ґудзиків на місці.</li><li>Приклейте розгалужувач I2C до правої передньої сторони підставки (Розгалужувач може бути використаний для підключення пристроїв ETH, таких як радіозв&#39;язок.)</li><li>Підключіть розгалужувач I2C до порту ETH на задній панелі Skynode.</li><li>Вставте два 40-контактних кабелі у передню частину Skynode.</li><li>Підключіть USB-C кабель подовження та згинайте його впереду. Згин повинен бути дуже тугим, щоб пластина влізла в дрон.</li></ol><p><img src="'+f+'" alt="Встановлений Skynode"></p><h4 id="плати-адаптерів" tabindex="-1">Плати адаптерів <a class="header-anchor" href="#плати-адаптерів" aria-label="Permalink to &quot;Плати адаптерів&quot;">​</a></h4><ol><li>Закрутіть до верхньої пластини плати адаптерів Pixhawk.</li></ol><h3 id="антени-та-rc-приимач" tabindex="-1">Антени та RC приймач <a class="header-anchor" href="#антени-та-rc-приимач" aria-label="Permalink to &quot;Антени та RC приймач&quot;">​</a></h3><ol><li><p>Приклейте антени Skynode LTE до боку фюзеляжу, як показано на зображенні:</p><p><img src="'+_+'" alt="Антени LTE"></p></li><li><p>Якщо ви використовуєте модуль радіотелеметрії, ви можете встановити антени на верх фюзеляжу. Зверху ви можете прямо встановити кабель подовження антени.</p><p><img src="'+u+'" alt="WIFI-Антени-Фронтальні"></p><p>На задній частині ви можете використовувати адаптер антени з використанням технології 3D-друку. Адаптер можна склеювати на місці гарячим клеєм.</p><p><img src="'+g+'" alt="Задня WIFI антена"></p></li></ol><h3 id="модуль-потужності-12s" tabindex="-1">Модуль потужності 12S <a class="header-anchor" href="#модуль-потужності-12s" aria-label="Permalink to &quot;Модуль потужності 12S&quot;">​</a></h3><p>Цей модуль живлення 12S є основним модулем живлення для двигунів. Цей блок може витримувати більше струму, ніж блок живлення потужністю 40A, що використовується для живлення авіоніки, і потрібен, оскільки Loong використовує до 120A у фазі зависання.</p><p>Модуль живлення 12S буде встановлено на верхній частині батареї. Підключіть XT90, який встановлений всередині транспортного засобу, до PM. Потрібно подовжити кабель живлення для підключення до Skynode. Це необхідно для отримання показників батареї з блоку живлення.</p><p>Модуль живлення може бути використаний як резервна для Skynode на 5В.</p><p><img src="'+k+'" alt="12S-Power-Module"></p><h3 id="збірка" tabindex="-1">Збірка <a class="header-anchor" href="#збірка" aria-label="Permalink to &quot;Збірка&quot;">​</a></h3><p>Кроки зборки:</p><ol><li>Перемістіть підставку в транспортний засіб.</li><li>Вставте LTE-антени в Skynode.</li><li>Закрутіть кріплення вентилятора та радіо на базову пластину.</li><li>Зсуньте основну пластину назад і закрутіть її на фіксатор стопки.</li><li>Поставте верхню плиту на верх стека та прокладіть 40-контактні кабелі від Skynode через дві отвори перед платами адаптерів Pixhawk.</li><li>Впевніться, що підключите верхній з&#39;єднувач до плати адаптера, на якій є вхід &#39;GPS1&#39;.</li></ol><p>Вставте приводи в адаптерну плату Pixhawk у такій послідовності:</p><p>MAIN:</p><ol><li>Мотор</li><li>Порожній, або вентилятор, якщо встановлено</li><li>Правий ельєрон</li><li>Лівий ейлерон</li><li>Правий руль висоти</li><li>Лівий руль висоти</li><li>Кермо</li></ol><p>AUX:</p><ol><li>Мотор правий передній</li><li>Мотор лівий задній</li><li>Мотор лівий передній</li><li>Мотор правий задній</li></ol><p>Якщо ви бажаєте підключити приводи до різних виходів, вам потрібно буде змінити відображення виходів приводу (див. <a href="./../config/actuators.html">Конфігурацію приводу</a>).</p><h2 id="налаштування-програмного-забезпечення" tabindex="-1">Налаштування програмного забезпечення <a class="header-anchor" href="#налаштування-програмного-забезпечення" aria-label="Permalink to &quot;Налаштування програмного забезпечення&quot;">​</a></h2><h3 id="вибір-планера" tabindex="-1">Вибір планера <a class="header-anchor" href="#вибір-планера" aria-label="Permalink to &quot;Вибір планера&quot;">​</a></h3><ol><li>Відкрийте QGC, виберіть значок <strong>Q</strong>, а потім виберіть <strong>Налаштування транспортного засобу</strong>.</li><li>Виберіть вкладку <a href="./../config/airframe.html">Airframe</a></li><li>Виберіть <a href="./../airframes/airframe_reference.html#vtol_standard_vtol_generic_standard_vtol">Generic Standard VTOL</a> з групи <em>Standard VTOL</em> а потім клікніть <strong>Apply and Restart</strong>.</li></ol><h3 id="завантажте-фаил-параметрів" tabindex="-1">Завантажте файл параметрів <a class="header-anchor" href="#завантажте-фаил-параметрів" aria-label="Permalink to &quot;Завантажте файл параметрів&quot;">​</a></h3><p>Далі ми завантажуємо <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/airframes/vtol/foxtech_loong_2160/loong.params" target="_blank" rel="noreferrer">файл параметрів</a>, який містить параметри, що визначають геометрію каркасу, відображення виводів та значення настройки - тож вам не потрібно! Якщо ви вже дотримувалися інструкцій щодо підключення моторів, вам, ймовірно, не знадобиться багато подальшої конфігурації, окрім калібрування сенсорів та фіксація обтискань.</p><p>Щоб завантажити файл:</p><ol><li>Завантажте <a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/airframes/vtol/foxtech_loong_2160/loong.params" target="_blank" rel="noreferrer">файл параметрів</a>.</li><li>Виберіть вкладку <a href="./../advanced_config/parameters.html#finding-updating-parameters">Параметри</a>, а потім натисніть на <strong>Tools</strong> в правому верхньому кутку.</li><li>Виберіть <strong>Завантажити з файлу</strong>, а потім виберіть файл <code>loong.params</code>, який ви щойно завантажили.</li><li>Перезапустіть транспортний засіб.</li></ol><h3 id="вибір-датчика" tabindex="-1">Вибір датчика <a class="header-anchor" href="#вибір-датчика" aria-label="Permalink to &quot;Вибір датчика&quot;">​</a></h3><ul><li>Якщо <a href="./../sensor/sfxx_lidar.html">Lidar Lightware lw20-c (включено в Skynode eval. kit)</a> використовується, потрібно встановити <a href="./../advanced_config/parameter_reference.html#SENS_EN_SF1XX">SENS_EN_SF1XX</a> на 6 (SF/LW/20c).</li><li>Впевніться, що обраний правильний датчик швидкості. Якщо ви використовуєте рекомендований <a href="https://www.dualrc.com/parts/airspeed-sensor-sdp33" target="_blank" rel="noreferrer">датчик швидкості SDP33</a>, зміни не будуть потрібні, оскільки <a href="./../advanced_config/parameter_reference.html#SENS_EN_SDP3X">SENS_EN_SDP3X</a> увімкнено (встановлено на <code>1</code>) в файлі параметрів.</li></ul><h3 id="калібрування-датчика" tabindex="-1">Калібрування датчика <a class="header-anchor" href="#калібрування-датчика" aria-label="Permalink to &quot;Калібрування датчика&quot;">​</a></h3><p>Спочатку переконайтеся, що встановлено <a href="./../config/flight_controller_orientation.html">правильну орієнтацію контролера політної системи</a>. Це має бути значенням за замовчуванням (<code>ROTATION_NONE</code>).</p><p>Потім калібруйте основні сенсори:</p><ul><li><a href="./../config/compass.html">Компас</a></li><li><a href="./../config/gyroscope.html">Гіроскоп</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/airspeed.html">Швидкість</a></li></ul><h3 id="rc-установка" tabindex="-1">RC-Установка <a class="header-anchor" href="#rc-установка" aria-label="Permalink to &quot;RC-Установка&quot;">​</a></h3><p><a href="./../config/radio.html">Відкалібруйте ваш RC Controller</a> та налаштуйте <a href="./../config/flight_mode.html">перемикач режиму польоту</a>.</p><p>Ми рекомендуємо вам призначити RC перемикачі для набору режимів, визначених у <a href="./../config/flight_mode.html#what-flight-modes-and-switches-should-i-set">Flight Mode Configuration &gt; What Flight Modes and Switches Should I Set?</a>. Зокрема, ви повинні призначити <em>VTOL Transition Switch</em>, <em>Kill Switch</em> та перемикач для вибору <a href="./../flight_modes_fw/stabilized.html">Режиму стабілізації</a> та <a href="./../flight_modes_fw/position.html">Режиму позиціювання</a>.</p><h3 id="налаштування-приводу-та-калібрування-esc" tabindex="-1">Налаштування приводу та калібрування ESC <a class="header-anchor" href="#налаштування-приводу-та-калібрування-esc" aria-label="Permalink to &quot;Налаштування приводу та калібрування ESC&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Переконайтеся, що реквізит видалено! Двигуни легко запустити на вкладці приводів випадково.</p></div><p>Двигуни, керуючі поверхні та інші виконавчі пристрої налаштовані в <a href="./../config/actuators.html">Керуванні актуаторами та тестуванні</a> QGroundControl.</p><p>Файл параметрів, завантажений раніше <a href="#load-parameters-file">параметр-файл</a>, означає, що цей екран вже повинен бути налаштований правильно: вам лише потрібно відрегулювати обтинання для вашого конкретного транспортного засобу. Якщо двигуни / сервоприводи були підключені до інших виходів, ніж рекомендовані, вам потрібно буде змінити відображення виходу на функції в розділі виведення приводу.</p><p>Для калібрування потужності ESC увімкніть транспортний засіб без крил і перейдіть на вкладку <strong>Actuators</strong> в QGC. Увімкніть тестування двигуна та зсуньте слайдер для двигуна, який ви бажаєте налаштувати, до максимуму. Вставте крила в фюзеляж і зачекайте, поки закінчиться послідовність звуків (близько 5 секунд). Потім передвиньте боковий слайдер на мінімум.</p><h4 id="керування-поверхнями" tabindex="-1">Керування поверхнями <a class="header-anchor" href="#керування-поверхнями" aria-label="Permalink to &quot;Керування поверхнями&quot;">​</a></h4><p>Перевірте, чи потрібно перекласти приводи, використовуючи пульт дистанційного керування:</p><ul><li>Перемкніться в <a href="./../flight_modes_fw/stabilized.html">Ручний режим</a></li><li>Перемістіть стік вправо. Правий елерон повинен іти вгору, лівий елерон повинен опускатися.</li><li>Нахили стік назад (політ вверх). Обидві поверхні V-хвоста повинні підніматися.</li><li>Перемістіть стік вправо. Обидві поверхні повинні рухатися вправо</li></ul><p>Тепер відкоригуйте значення газу так, щоб всі поверхні були в нейтральному положенні.</p><p><img src="'+b+'" alt="Подруливаюче серво"></p><h4 id="напрямок-та-орієнтація-двигуна" tabindex="-1">Напрямок та орієнтація двигуна <a class="header-anchor" href="#напрямок-та-орієнтація-двигуна" aria-label="Permalink to &quot;Напрямок та орієнтація двигуна&quot;">​</a></h4><p>Зафіксуйте літальний апарат зі знімними пропелерами у режимі <a href="./../flight_modes_fw/stabilized.html">Стабілізованого польоту</a>. Перевірте, щоб всі чотирьохмотори обертались з схожою низькою швидкістю холостого ходу та перевірте, що напрямок правильний. Перевірте наступні реакції:</p><ul><li>Перемістіть стік вправо. Ліві два двигуни повинні розганятися</li><li>Перемістіть стік вниз. Передні два двигуни повинні розганятися</li><li>Упріть стік вправо. Передній правий та задній лівий двигун повинні розганятися</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>збільшіть газ трохи, якщо ви не бачите реакції, оскільки <a href="./../config_mc/pid_tuning_guide_multicopter.html#airmode-mixer-saturation">Airmode</a> не ввімкнено для осі розвороту.</p></div><h2 id="першии-політ" tabindex="-1">Перший політ <a class="header-anchor" href="#першии-політ" aria-label="Permalink to &quot;Перший політ&quot;">​</a></h2><ul><li>Встановити гвинти (використовувати блакитний Loctite для гвинтів).</li><li>Перевірте центр мас (GG). Щоб перевірити ЦМ ліфтінг - підніміть транспортний засіб двома пальцями вгору на засувках, що з&#39;єднують крило. Транспортний засіб повинен бути в горизонтальному положенні. Якщо транспортний засіб нахилився вперед або назад, вам потрібно перемістити батарею в протилежному напрямку. Якщо ви не здатні врівноважити транспортний засіб цим методом, вам доведеться перенести деякі компоненти або додати вагу для вирівнювання транспортного засобу.</li><li>Перевірте орієнтацію приводів та нейтральну передачу</li><li>Перевірте реакції поверхонь управління в режимі <a href="./../flight_modes_fw/stabilized.html">Стабілізованого польоту</a>. Перемикайте транспортний засіб у режим польоту вперед. <ul><li>Переваліть транспортний засіб на праве крило. Правий елерон повинен опуститися. Лівий елерон повинен піти вгору.</li><li>Підійміть транспортний засіб вверх (носом вгору). Обидва елерони повинні опуститися.</li><li>Рискніть транспортним засобом праворуч (носом вправо). Обидва елерони повинні йти вліво.</li></ul></li><li>Якщо використовується <a href="./../config/safety.html#kill-switch">kill-switch</a>, переконайтеся, що він працює належним чином і не буде активований випадково в повітрі!</li><li>Включіть <a href="./../flight_modes_fw/stabilized.html">режим стабілізації</a> та перевірте, чи відповідають двигуни на команди, наприклад, перевалюємося вліво, збільшувати оберти на правому двигуні</li><li>Злетайте у режимі <a href="./../flight_modes_fw/stabilized.html">стабілізованого положення</a> і виконайте деякі базові маневри</li><li>Якщо все пройшло без проблем, злітайте у <a href="./../flight_modes_fw/position.html">Position mode</a> та зробіть перехід приблизно на висоті 50м. Якщо щось пішло не так, якнайшвидше перейдіть в режим мультикоптера (використовуючи перемикач переходу).</li></ul>',100),C=[v];function y(T,A,L,M,N,F){return S(),w("div",null,C)}const R=x(q,[["render",y]]);export{I as __pageData,R as default};
