import{_ as e}from"./chunks/caipiroshka.N_v-UGfQ.js";import{_ as r}from"./chunks/airframe_px4_vtol_caipiroshka_duo_tailsitter.CGSjLuNv.js";import{_ as i,c as o,o as l,ab as t,m as a}from"./chunks/framework.CUflZczI.js";const w=JSON.parse('{"title":"TBS Caipiroshka","description":"","frontmatter":{},"headers":[],"relativePath":"uk/frames_vtol/vtol_tailsitter_caipiroshka_pixracer.md","filePath":"uk/frames_vtol/vtol_tailsitter_caipiroshka_pixracer.md"}'),h={name:"uk/frames_vtol/vtol_tailsitter_caipiroshka_pixracer.md"},s=t('<h1 id="tbs-caipiroshka" tabindex="-1">TBS Caipiroshka <a class="header-anchor" href="#tbs-caipiroshka" aria-label="Permalink to &quot;TBS Caipiroshka&quot;">​</a></h1><p>Caipiroshka VTOL - це трохи модифікований <em>TBS Caipirinha</em>.</p><div class="info custom-block"><p class="custom-block-title"><em>TBS Caipirinha</em> було замінено та більше не доступно. Ці інструкції <em>повинні</em> працювати з оновленим транспортним засобом: <a href="https://team-blacksheep.com/products/prod:tbs_caipi2_pnp" target="_blank" rel="noreferrer">TBS Caipirinha 2</a>. Кілька інших компонентів також було оновлено в списку деталей.</p></div>',3),p=a("p",null,[a("div",{class:"embed-responsive embed-responsive-16by9"},[a("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/acG0aTuf3f8",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),d=t('<h2 id="список-деталеи" tabindex="-1">Список деталей <a class="header-anchor" href="#список-деталеи" aria-label="Permalink to &quot;Список деталей&quot;">​</a></h2><ul><li>TBS Caipirinha Wing (більше не доступний - спробуйте <a href="https://team-blacksheep.com/products/prod:tbs_caipi2_pnp" target="_blank" rel="noreferrer">TBS Caipirinha 2</a>)</li><li>Ліве та праве кріплення двигуна з друкованої 3D-моделі (<a href="https://github.com/PX4/PX4-user_guide/raw/v1.15/assets/airframes/vtol/caipiroshka/motor_mounts.zip" target="_blank">файли дизайну</a>)</li><li>Гвинт CW 8045 (<a href="https://www.banggood.com/GEMFAN-Carbon-Nylon-8045-CWCCW-Propeller-For-Quadcopters-1-Pair-p-950874.html" target="_blank" rel="noreferrer">магазин Eflight</a>)</li><li>Гвинт CCW 8045 (<a href="https://www.banggood.com/GEMFAN-Carbon-Nylon-8045-CWCCW-Propeller-For-Quadcopters-1-Pair-p-950874.html" target="_blank" rel="noreferrer">магазин Eflight</a>)</li><li>2x 1800 kV 120-180W двигуни <ul><li><a href="https://www.galaxus.ch/en/s5/product/epower-22081400-fuer-2-3-lipo-imax-rc-motors-8355913" target="_blank" rel="noreferrer">ePower 2208</a></li><li><a href="https://www.amazon.com/Armattan-2208-1800kV-Multirotor-Motor/dp/B00UWLW0C8" target="_blank" rel="noreferrer">Багатороторний двигун Armattan 2208 1800kV</a></li></ul></li><li>2x 20-30S ESC <ul><li><a href="https://www.getfpv.com/lumenier-30a-blheli-s-esc-opto-2-4s.html" target="_blank" rel="noreferrer">GetFPV</a></li></ul></li><li>BEC (3А, 5-5.3В) (потрібно лише у випадку, якщо використовуєте ESC, які не можуть діяти як джерело живлення 5В для вихідної рейки)</li><li>3S 2200 mA LiPo акумулятор <ul><li>Team Orion 3S 11.1V 50 C (<a href="https://www.hobbyshop.ch/modellbau-elektronik/akku/team-orion-lipo-2200-3s-11-1v-50c-xt60-ori60163.html" target="_blank" rel="noreferrer">Hobbyshop store</a>)</li></ul></li><li><a href="./../flight_controller/pixracer.html">Плата автопілота Pixracer + модуль живлення</a></li><li><a href="https://hobbyking.com/en_us/hkpilot-32-digital-air-speed-sensor-and-pitot-tube-set.html" target="_blank" rel="noreferrer">Цифровий датчик швидкості</a></li></ul><h2 id="збірка" tabindex="-1">Збірка <a class="header-anchor" href="#збірка" aria-label="Permalink to &quot;Збірка&quot;">​</a></h2><p>Нижче наведено, як може виглядати повністю складена Caipiroshka.</p><p><img src="'+e+'" alt="Caipiroshka"></p><p>У наступному наведено деякі загальні поради щодо того, як зібрати дрон.</p><h3 id="автопілот" tabindex="-1">Автопілот <a class="header-anchor" href="#автопілот" aria-label="Permalink to &quot;Автопілот&quot;">​</a></h3><p>Встановіть автопілот у середині близько до центра тяжіння корпусу повітряного судна.</p><h3 id="кронштеини-для-мотора" tabindex="-1">Кронштейни для мотора <a class="header-anchor" href="#кронштеини-для-мотора" aria-label="Permalink to &quot;Кронштейни для мотора&quot;">​</a></h3><p>Роздрукуйте кріплення мотору (2 рази), до якого посилання на файл STL було вказано у списку частин. Прикріпіть одне кріплення двигуна на кожному боці крила так, щоб вісь двигуна приблизно проходила через центр елеронів (див. малюнок). У верхньому зображенні горизонтальна відстань між двома кріпленнями двигуна становить 56 см. Як тільки ви відзначите правильне положення на крилі, ви можете вкрити область, яка буде знаходитися в контакті з кріпленням, стандартною прозорою стрічкою на обох сторонах верхнього і нижнього крила. Потім нанесіть шар гарячого клею на цю область та приклейте кріплення двигуна на крило. Причина наявності стрічки між поверхнею крила та розплавленим клеєм полягає в тому, що ви можете дуже легко видалити кріплення двигуна, розриваючи стрічку з крила без будь-яких пошкоджень. Це корисно, коли намагаєтесь замінити пошкоджений кронштейн мотору.</p><h3 id="контролери-двигуна" tabindex="-1">Контролери двигуна <a class="header-anchor" href="#контролери-двигуна" aria-label="Permalink to &quot;Контролери двигуна&quot;">​</a></h3><p>Регулятори обертання можуть бути прямо закріплені на пласкій поверхні кріплення двигуна за допомогою клею або кабельного стяжника. Вам потрібно буде прокласти кабелі живлення до батарейного відсіку. Ви можете використовувати старий паяльник, щоб розтопити канали в піну. Підключіть живильні кабелі обох контролерів двигуна в батарейному відділенні та припаяйте роз&#39;єм в кінець. Це дозволить вам підключити обидва контролери двигуна до модуля живлення. Якщо у вас немає регуляторів швидкості, які можуть постачати 5 В для вихідної рейки автопілота, вам доведеться використовувати зовнішнє джерело живлення (BEC).</p><h3 id="gps" tabindex="-1">GPS <a class="header-anchor" href="#gps" aria-label="Permalink to &quot;GPS&quot;">​</a></h3><p>GPS може бути встановлений у середині, ззаду повітряної конструкції. Це допомагає переносити вагу літака назад, оскільки два двигуни, камера та, можливо, більша батарея можуть зробити його досить важким у носу. Також велику відстань до 12В кабелів живлення корисно для зменшення магнітних перешкод зовнішнього магнітометра.</p><h3 id="датчик-швидкості" tabindex="-1">Датчик швидкості <a class="header-anchor" href="#датчик-швидкості" aria-label="Permalink to &quot;Датчик швидкості&quot;">​</a></h3><p>Прикріпіть трубу Піто близько до зовнішнього краю одного з бічних крил. Переконайтеся, що пітот-труба не постраждала від впливу повітря пропелера. Все має бути в порядку, якщо горизонтальна відстань від трубки до вісі моторів більша, ніж радіус пропелера. Використовуйте, наприклад, старий паяльник, щоб створити виступ для пітотрубки, трубки та фактичного сенсора (див. малюнок). Створіть канал для прокладання кабелю через крило до інших компонентів.</p><h3 id="підключення-сенсора-до-шини-i2c" tabindex="-1">Підключення сенсора до шини I2C <a class="header-anchor" href="#підключення-сенсора-до-шини-i2c" aria-label="Permalink to &quot;Підключення сенсора до шини I2C&quot;">​</a></h3><p>Як датчик швидкості повітря, так і зовнішній магнітометр (розташований у корпусі Gps), необхідно підключити до шини I2C автопілота. Отже, вам доведеться використовувати розгалужувач I2C, подібний до вказаного в переліку деталей. Підключіть плату розподільника до шини I2C автопілота. Потім підключіть і зовнішній магнітометр, і датчик швидкості повітря до плати-розділювача за допомогою стандартного кабелю I2C. На верхньому зображенні плата роздільника знаходиться зліва від блоку GPS.</p><h3 id="елевони" tabindex="-1">Елевони <a class="header-anchor" href="#елевони" aria-label="Permalink to &quot;Елевони&quot;">​</a></h3><p>Елерони можуть бути прикріплені до задньої сторони крила за допомогою прозорої стрічки. Ви можете дотримуватися інструкцій, наданих командою Blacksheep у будівельному посібнику для корпусу повітряного судна TBS Caiprinha.</p><h3 id="загальні-правила-збірки" tabindex="-1">Загальні правила збірки <a class="header-anchor" href="#загальні-правила-збірки" aria-label="Permalink to &quot;Загальні правила збірки&quot;">​</a></h3><p>Перед установкою всіх компонентів на крило використовуйте клейку стрічку, щоб утримувати їх у одному положенні та перевірте, чи центр тяжіння крила знаходиться в рекомендованому діапазоні, зазначеному в керівництві зі збірки для TBS Caipirinha. Залежно від додаткових компонентів, які ви хочете мати на борту (наприклад, GoPro впереду або більший акумулятор), вам знадобиться змінити місце розташування компонентів.</p><h2 id="конфігурація-планера" tabindex="-1">Конфігурація планера <a class="header-anchor" href="#конфігурація-планера" aria-label="Permalink to &quot;Конфігурація планера&quot;">​</a></h2><p>Перейдіть до розділу конфігурації в <a href="./../config/airframe.html">QGroundControl</a> та виберіть вкладку конструкції повітряного судна. Прокрутіть список вниз, щоб знайти значок <em>VTOL Duo Tailsitter</em>. Виберіть <em>Caipiroshka Duo Tailsitter</em> зі списку випадаючих списків.</p><p><img src="'+r+'" alt="caipiroshka"></p><h2 id="підключення-серводвигунів" tabindex="-1">Підключення серводвигунів <a class="header-anchor" href="#підключення-серводвигунів" aria-label="Permalink to &quot;Підключення серводвигунів&quot;">​</a></h2><p>Описи у таблиці нижче стосуються користувача, який стикається з передньою частиною транспортного засобу, коли він лежить рівно на столі на животі.</p><table><thead><tr><th>Output</th><th>Rate</th><th>Actuator</th></tr></thead><tbody><tr><td>MAIN1</td><td>400 Hz</td><td>Правий (правий борт) контролер двигуна</td></tr><tr><td>MAIN2</td><td>400 Hz</td><td>Контролер лівого (лівий борт) двигуна</td></tr><tr><td>MAIN3</td><td>400 Hz</td><td>Пусто</td></tr><tr><td>MAIN4</td><td>400 Hz</td><td>Пусто</td></tr><tr><td>MAIN5</td><td>50 Hz</td><td>Правий (правий борт) серводвигун ейлерона</td></tr><tr><td>MAIN6</td><td>50 Hz</td><td>Лівий (лівий борт) сервопривід елерона</td></tr></tbody></table>',28),n=[s,p,d];function c(m,_,u,b,f,k){return l(),o("div",null,n)}const T=i(h,[["render",c]]);export{w as __pageData,T as default};
