import{_ as t,a}from"./chunks/locosys_hawk_a1_leds.Br0RVX18.js";import{_ as r}from"./chunks/locosys_gps_cable.i1V998rT.js";import{_ as e,c as d,o as l,ab as o}from"./chunks/framework.CUflZczI.js";const f=JSON.parse('{"title":"LOCOSYS Hawk A1 GPS/GNSS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/gps_locosys_hawk_a1.md","filePath":"uk/gps_compass/gps_locosys_hawk_a1.md"}'),s={name:"uk/gps_compass/gps_locosys_hawk_a1.md"},h=o('<h1 id="locosys-hawk-a1-gps-gnss" tabindex="-1">LOCOSYS Hawk A1 GPS/GNSS <a class="header-anchor" href="#locosys-hawk-a1-gps-gnss" aria-label="Permalink to &quot;LOCOSYS Hawk A1 GPS/GNSS&quot;">​</a></h1><p><a href="https://www.locosystech.com/en/product/hawk-a1-LU23031-V2.html" target="_blank" rel="noreferrer">LOCOSYS HAWK A1 GPS/GNSS приймач</a> - це двочастотний багатосупутниковий GNSS/GPS приймач, сумісний з PX4.</p><p>Основні функції включають:</p><ul><li>Одночасний прийом сигналів смуг L1 і L5</li><li>Підтримка GPS, ГЛОНАСС, BEIDOU, GALILEO, QZSS</li><li>Підтримка SBAS (WAAS, EGNOS, MSAS, GAGAN)</li><li>Підтримка GNSS з 135 каналами</li><li>Швидкий TTFF на низькому рівні сигналу</li><li>Безкоштовне прогнозування гібридних ефемерид для швидшого холодного старту</li><li>За замовчуванням 5 Гц, частота оновлення до 10 Гц (SBAS лише з частотою оновлення 5 Гц)</li><li>Вбудований суперконденсатор для резервування системних даних для швидкого отримання супутникових даних</li><li>Три світлодіодного індикатора для живлення, PPS та передачі даних</li></ul><p><img src="'+t+'" alt="Hawk A1"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/en/product/hawk-a1-LU23031-V2.html" target="_blank" rel="noreferrer">LOCOSYS</a> (Тайвань)</li></ul><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Ви можете використовувати Hawk A1 як основну або додаткову GPS-систему. Параметри PX4 повинні бути встановлені, як показано нижче, для кожного випадку.</p><h3 id="головнии-gnss" tabindex="-1">Головний GNSS <a class="header-anchor" href="#головнии-gnss" aria-label="Permalink to &quot;Головний GNSS&quot;">​</a></h3><p>Використовуйте Hawk A1 як основний GPS-пристрій:</p><table><thead><tr><th>Параметр</th><th>Значення</th><th>Опис</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_1_CONFIG">GPS_1_CONFIG</a></td><td>102 (Telem 2 або інший доступний послідовний порт)</td><td>Налаштування основного порту GPS</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_1_PROTOCOL">GPS_1_PROTOCOL</a></td><td>1 (u-blox)</td><td>Налаштування протоколу GPS</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#SER_TEL2_BAUD">SER_TEL2_BAUD</a></td><td>230400</td><td>Налаштування швидкості послідовного порту (тут GPS підключено до <code>TELEM2</code>, наприклад)</td></tr></tbody></table><h3 id="другоряднии-gnss" tabindex="-1">Другорядний GNSS <a class="header-anchor" href="#другоряднии-gnss" aria-label="Permalink to &quot;Другорядний GNSS&quot;">​</a></h3><p>Використовувати Hawk A1 як допоміжний GPS-пристрій (на додаток до основного GPS):</p><table><thead><tr><th>Параметр</th><th>Значення</th><th>Опис</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_2_CONFIG">GPS_2_CONFIG</a></td><td>102 (Telem 2 або інший доступний послідовний порт)</td><td>Налаштування основного порту GPS</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_2_PROTOCOL">GPS_2_PROTOCOL</a></td><td>1 (u-blox)</td><td>Налаштування протоколу GPS</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#SER_TEL2_BAUD">SER_TEL2_BAUD</a></td><td>230400</td><td>Налаштування швидкості послідовного порту (тут GPS підключено до TELEM2, наприклад)</td></tr></tbody></table><h2 id="підключення-та-з-єднання" tabindex="-1">Підключення та з&#39;єднання <a class="header-anchor" href="#підключення-та-з-єднання" aria-label="Permalink to &quot;Підключення та з&#39;єднання&quot;">​</a></h2><p>Locosys GPS поставляється з 6-контактним роз&#39;ємом JST-GH стандарту Pixhawk, який можна вставити безпосередньо в UART-порт GPS1 (або в UART-порти GPS2 від Pixhawk FMUv5).</p><p><img src="'+r+'" alt="GPS cable"></p><h3 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h3><p>LOCOSYS схема виводів GPS наведена нижче. Це може бути використано для модифікації роз&#39;єму для інших плат автопілота.</p><table><thead><tr><th>пін</th><th>Locosys GPS</th><th>пін</th><th>Pixhawk GPS 2</th></tr></thead><tbody><tr><td>1</td><td>VCC_5V</td><td>1</td><td>VCC</td></tr><tr><td>2</td><td>GPS_RX</td><td>2</td><td>GPS_TX</td></tr><tr><td>3</td><td>GPS_TX</td><td>3</td><td>GPS_RX</td></tr><tr><td>4</td><td>NC</td><td>4</td><td>SDA</td></tr><tr><td>5</td><td>NC</td><td>5</td><td>SCL</td></tr><tr><td>6</td><td>GND</td><td>6</td><td>GND</td></tr></tbody></table><h2 id="індикатори-статусу-leds" tabindex="-1">Індикатори статусу LEDs <a class="header-anchor" href="#індикатори-статусу-leds" aria-label="Permalink to &quot;Індикатори статусу LEDs&quot;">​</a></h2><table><thead><tr><th>Колір</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>Зелений</td><td>Індикатор TX</td><td>Передача даних GNSS</td></tr><tr><td>Червоний</td><td>Індикатор живлення</td><td>Потужність</td></tr><tr><td>Синій</td><td>PPS</td><td>Активна служба точного позиціонування</td></tr></tbody></table><p><img src="'+a+'" alt="Hawk A1 LEDs"></p><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><ul><li><strong>Тип приймача:</strong> 135-канальний двигун LOCOSYS MC-1612-V2b, GPS/QZSS L1 C/A, L5C, ГЛОНАСС L1OF, BeiDou B1I, B2a Galileo:E1, E5a SBAS L1 C/A: WAAS, EGNOS, MSAS, GAGAN</li><li><strong>Частота оновлення навігації:</strong> Макс: 5 Гц за замовчуванням Макс: 10 Гц</li><li><strong>Точність позиціювання:</strong> 3D Fix</li><li><strong>Час до першого виправлення:</strong><ul><li><strong>Холодний запуск:</strong> 28с</li><li><strong>Допоміжний запуск:</strong> EASY</li></ul></li><li><strong>Чутливість:</strong><ul><li><strong>Відстеження &amp; навігація:</strong> -165 дБм</li></ul></li><li><strong>Підтримка GNSS:</strong> EASY DGPS</li><li><strong>Осцилятор:</strong> 26Mhz TCXO</li><li><strong>RTC кристал:</strong> 32.768KHz</li><li>** Доступні антени:** Багаточастотна антена L1+L5</li><li>** Цілісність сигналу:** L1+L5 GPS GLONASS GALILEO BEIDOU QZSS SBAS</li><li><strong>Протоколи &amp; Інтерфейси:</strong><ul><li><strong>UART/I2C:</strong> JST_GH Головний інтерфейс, внутрішній перемикач.</li></ul></li></ul><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/Templates/att/LU23031-V2%20datasheet_v0.2.pdf?lng=en" target="_blank" rel="noreferrer">Посібник користувача GPS від LOCOSYS</a></li></ul>',28),i=[h];function S(n,_,c,G,P,p){return l(),d("div",null,i)}const b=e(s,[["render",S]]);export{f as __pageData,b as default};
