import{_ as t}from"./chunks/freefly_gps_module.BHDff67v.js";import{_ as l,c as e,o as r,ab as a}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Freefly Системи RTK GPS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/rtk_gps_freefly.md","filePath":"uk/gps_compass/rtk_gps_freefly.md"}'),i={name:"uk/gps_compass/rtk_gps_freefly.md"},o=a('<h1 id="freefly-системи-rtk-gps" tabindex="-1">Freefly Системи RTK GPS <a class="header-anchor" href="#freefly-системи-rtk-gps" aria-label="Permalink to &quot;Freefly Системи RTK GPS&quot;">​</a></h1><p><a href="https://store.freeflysystems.com/products/rtk-gps-ground-station" target="_blank" rel="noreferrer">Модуль RTK GPS від Freefly Systems</a> - це багатосмуговий <a href="./../gps_compass/rtk_gps.html">модуль RTK GPS</a> від Freefly Systems, який забезпечує дуже надійну навігацію. Модулі можуть діяти як роувери (коли встановлені на повітряну судну), так і базові станції (коли підключені до комп&#39;ютера).</p><p>Основні функції включають:</p><ul><li>Отримувач з багаторозрядним (L1/L2) (u-blox ZED-F9P)</li><li>Одночасний прийом всіх 4 GNSS (GPS, Galileo, GLONASS, BeiDou)</li><li>Вбудований магнітометр (IST8310), баро (BMP388), RGB LED, безпечний вимикач та світлодіод безпеки</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей модуль можна використовувати з PX4 v1.9 або вище (підтримка для u-blox ZED-F9P була додана в PX4 v1.9).</p></div><p><img src="'+t+'" alt="FreeFly GPS Module"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://store.freeflysystems.com/products/rtk-gps-ground-station" target="_blank" rel="noreferrer">Магазин Freefly</a></li></ul><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2><p>Набір RTK GPS включає в себе:</p><ul><li>2x GPS модулі з антенами</li><li>Кабель USB C на USB A довжиною 3 метри</li><li>Магнітний швидкозамок для базової станції модуля (1/4-20 різьблення для монтажу на штатив)</li><li>Гвинти для кріплення на Freefly AltaX</li></ul><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Налаштування та використання RTK на PX4 за допомогою <em>QGroundControl</em> відбувається за принципом &quot;підключи і працюй&quot; (див. <a href="./../gps_compass/rtk_gps.html">RTK GPS</a> для отримання додаткової інформації).</p><p>Для літака вам слід встановити параметр <a href="./../advanced_config/parameter_reference.html#SER_GPS1_BAUD">SER_GPS1_BAUD</a> на 115200 8N1, щоб забезпечити, що PX4 налаштує правильну швидкість передачі.</p><h2 id="підключення-та-з-єднання" tabindex="-1">Підключення та з&#39;єднання <a class="header-anchor" href="#підключення-та-з-єднання" aria-label="Permalink to &quot;Підключення та з&#39;єднання&quot;">​</a></h2><p>RTK GPS Freefly поставляється з роз&#39;ємом JST-GH на 8 контактів, який можна підключити до автопілота PixHawk. Для використання як базова станція, модуль має роз&#39;єм USB-C</p><h3 id="розпіновка" tabindex="-1">Розпіновка <a class="header-anchor" href="#розпіновка" aria-label="Permalink to &quot;Розпіновка&quot;">​</a></h3><p>Схема виводів Freefly GPS наведена нижче. Для деяких автопілотів, таких як <a href="./../flight_controller/pixhawk-2.html">Hex Cube</a> та <a href="./../flight_controller/pixracer.html">PixRacer</a>, все, що потрібно, це кабель JST-GH 1-1 8-контактний.</p><table><thead><tr><th>Pin</th><th>Freefly GPS</th></tr></thead><tbody><tr><td>1</td><td>VCC_5V</td></tr><tr><td>2</td><td>GPS_RX</td></tr><tr><td>3</td><td>GPS_TX</td></tr><tr><td>4</td><td>I2C_SCL</td></tr><tr><td>5</td><td>I2C_SDA</td></tr><tr><td>6</td><td>BUTTON</td></tr><tr><td>7</td><td>BUTTON_LED</td></tr><tr><td>8</td><td>GND</td></tr></tbody></table><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><ul><li>Приймач GPS u-blox ZED-F9P <ul><li>Резервне живлення Ultracap для швидкого (гарячого запуску)</li><li>Штормова пластина над приймачем для покращення стійкості до ЕМП</li></ul></li><li>Магнітометр IST8310</li><li>Блок безпеки та безпечний світлодіод LED</li><li>Світлодіоди RGB для показу статусу <ul><li>Драйвер I2C NCP5623CMUTBG</li></ul></li><li>BMP388 Baro на шині I2C</li><li>Зовнішня активна антена (Maxtena M7HCT) <ul><li>SMA конектор</li></ul></li><li>STM32 МКП для майбутньої комунікації на основі CAN <ul><li>Оновлення ПЗ через USB-роз&#39;єм</li></ul></li><li>Підключення: <ul><li>USB-C</li><li>2-way USB перемикач до МКП та F9P</li><li>SMA для активної антени (макс. 20 мА)</li><li>4-контактний JST-GH CAN шина (сумісність зі стандартами dronecode)</li><li>8-контактний JST-GH UART/I2C -** Живлення:</li><li>Вхід з одного з (діод OR&#39;d):</li><li>USB (5V)</li><li>CAN (4.7 до 25.2В)</li><li>(4.7 до 25.2V)</li><li>Споживання електроенергії &lt;1W</li></ul></li></ul><h2 id="докладніше" tabindex="-1">Докладніше <a class="header-anchor" href="#докладніше" aria-label="Permalink to &quot;Докладніше&quot;">​</a></h2><p>Більше інформації можна знайти на <a href="https://freefly.gitbook.io/freefly-public/products/rtk-gps" target="_blank" rel="noreferrer">Вікі Freefly</a></p>',23),s=[o];function d(h,p,f,n,c,_){return r(),e("div",null,s)}const m=l(i,[["render",d]]);export{P as __pageData,m as default};
