import{_ as l,a as e}from"./chunks/locosys_hawk_a1_leds.Br0RVX18.js";import{_ as r,E as i,c as o,J as d,m as t,a as s,ab as h,o as c}from"./chunks/framework.CUflZczI.js";const O=JSON.parse('{"title":"LOCOSYS Hawk R1 RTK GPS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/rtk_gps_locosys_r1.md","filePath":"uk/gps_compass/rtk_gps_locosys_r1.md"}'),n={name:"uk/gps_compass/rtk_gps_locosys_r1.md"},p=t("h1",{id:"locosys-hawk-r1-rtk-gps",tabindex:"-1"},[s("LOCOSYS Hawk R1 RTK GPS "),t("a",{class:"header-anchor",href:"#locosys-hawk-r1-rtk-gps","aria-label":'Permalink to "LOCOSYS Hawk R1 RTK GPS"'},"​")],-1),S=h('<p><a href="https://www.locosystech.com/en/product/hawk-r1.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R1</a> - це двочастотний <a href="./../gps_compass/rtk_gps.html">RTK GPS модуль</a> приймач, розроблений для сумісності з Pixhawk.</p><p>Модуль може діяти як RTK GPS ровер при встановленні на літак.</p><p>Приймач здатний одночасно відстежувати всі глобальні цивільні навігаційні системи, включаючи GPS, ГЛОНАСС, GALILEO, BEIDOU і QZSS. Він приймає сигнали L1 і L5 одночасно, забезпечуючи сантиметрову точність позиціонування RTK.</p><p>Вбудована легка спіральна антена підвищує стабільність позиціонування RTK. Швидкий час до першої перевірки, сумісність з RTK, чудова чутливість, низьке енергоспоживання роблять його кращим вибором для платформних БПЛА на базі Pixhawk.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей модуль не має компасу. Спробуйте еквівалентний модуль GPS з компасом: <a href="./../gps_compass/rtk_gps_locosys_r2.html">LOCOSYS Hawk R2</a>.</p></div><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li>Одночасний прийом сигналів діапазонів L1 і L5</li><li>Підтримка GPS, ГЛОНАСС, BEIDOU, GALILEO, QZSS</li><li>Підтримка SBAS (WAAS, EGNOS, MSAS, GAGAN)</li><li>Підтримка GNSS з 135 каналами</li><li>Швидкий TTFF на низькому рівні сигналу</li><li>Безкоштовне гібридне передбачення ефемерид для досягнення швидкого холодного запуску</li><li>За замовчуванням 5 Гц, частота оновлення до 10 Гц (SBAS підтримує тільки 5 Гц).</li><li>Вбудований суперконденсатор для резервування системних даних для швидкого отримання супутникових даних</li></ul><p><img src="'+l+'" alt="LOCOSYS Hawk R1"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/en/product/hawk-r1.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R1</a></li></ul><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2><p>Набір RTK GPS включає в себе:</p><ul><li>1x Модуль GPS</li><li>1x Спіральна антена</li><li>1x 6-контактний JST-GH</li></ul><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Налаштування та використання RTK на PX4 за допомогою <em>QGroundControl</em> відбувається за принципом «підключи і працюй» (див. <a href="./../gps_compass/rtk_gps.html">RTK GPS</a> для отримання додаткової інформації). Підключіть Hawk R1 до порту <code>GPS2</code> на сумісних платах Pixhawk (бажано, але ви можете використовувати будь-який інший невикористаний порт UART).</p><p>Для літака слід встановити параметр <a href="./../advanced_config/parameter_reference.html#SER_GPS1_BAUD">SER_GPS2_BAUD</a> на 230400 8N1, щоб переконатися, що PX4 налаштував правильну швидкість передачі даних.</p><h2 id="підключення-та-з-єднання" tabindex="-1">Підключення та з&#39;єднання <a class="header-anchor" href="#підключення-та-з-єднання" aria-label="Permalink to &quot;Підключення та з&#39;єднання&quot;">​</a></h2><p>Hawk R1 RTK GPS поставляється з 6-контактним роз&#39;ємом JST-GH, який можна підключити до автопілота Pixhawk.</p><h3 id="pinout" tabindex="-1">Pinout <a class="header-anchor" href="#pinout" aria-label="Permalink to &quot;Pinout&quot;">​</a></h3><p>Нижче наведена розводка LOCOSYS GPS.</p><table><thead><tr><th>Pin</th><th>Hawk R1 GPS</th></tr></thead><tbody><tr><td>1</td><td>VCC_5V</td></tr><tr><td>2</td><td>GPS_RX</td></tr><tr><td>3</td><td>GPS_TX</td></tr><tr><td>4</td><td>Null</td></tr><tr><td>5</td><td>Null</td></tr><tr><td>6</td><td>Null</td></tr><tr><td>7</td><td>Null</td></tr><tr><td>8</td><td>Null</td></tr><tr><td>9</td><td>GND</td></tr></tbody></table><h2 id="leds-статуси" tabindex="-1">LEDs статуси <a class="header-anchor" href="#leds-статуси" aria-label="Permalink to &quot;LEDs статуси&quot;">​</a></h2><table><thead><tr><th>Колір</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>Green</td><td>TX Indicator</td><td>Передача даних GNSS</td></tr><tr><td>Red</td><td>Power Indicator</td><td>Живлення</td></tr><tr><td>Blue</td><td>PPS</td><td>Precise Positioning Service активна</td></tr></tbody></table><p><img src="'+e+'" alt="Hawk A1 LEDs"></p><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><ul><li>Частоти <ul><li>GPS/QZSS: L1 C/A, L5C</li><li>GLONASS: L1OF</li><li>BEIDOU: B1I, B2a</li><li>GALILEO: E1, E5a</li></ul></li><li>Підтримка 135 каналів</li><li>Частота оновлення до 10 Гц (за замовчуванням 5 Гц)</li><li>Час отримання <ul><li>Гарячий старт (Open Sky) за 2 секунди</li><li>Холодний старт (Open Sky) за 28 секунд без AGPS</li></ul></li><li>PPS з тривалістю імпульсу 100 мс, 1,8 В постійного струму</li><li>Зовнішня, активна антена Helix <ul><li>SMA конектор</li></ul></li><li>Підтримка протоколу UBlox <ul><li>U5Hz:UBX-NAV-PVT,UBX-NAV-DOP</li><li>1Hz: UBX-NAV-TIMEGPS</li></ul></li><li>Підключення: <ul><li>6-контактний JST-GH UART/I2C (сумісний з Pixhawk)</li></ul></li><li>Живлення: <ul><li>Напруга живлення постійного струму 3,3 В ~ 5,0 В на вході</li><li>Енергоспоживання &lt;1 Вт</li></ul></li></ul><h2 id="більше-інформаціі" tabindex="-1">Більше інформації <a class="header-anchor" href="#більше-інформаціі" aria-label="Permalink to &quot;Більше інформації&quot;">​</a></h2><p>Більше інформації можна знайти на <a href="https://www.locosystech.com/en/product/hawk-r1.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R1</a></p>',28);function _(u,P,k,m,G,b){const a=i("Badge");return c(),o("div",null,[p,d(a,{type:"tip",text:"PX4 v1.13"}),S])}const R=r(n,[["render",_]]);export{O as __pageData,R as default};
