import{_ as l,a as r}from"./chunks/locosys_hawk_a1_leds.Br0RVX18.js";import{_ as e}from"./chunks/locosys_hawk_r2_jst6_cable.DxzdIM-l.js";import{_ as i,E as o,c as d,J as s,m as t,a as h,ab as p,o as S}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"Геодезичний компас LOCOSYS Hawk R2 RTK-GPS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/rtk_gps_locosys_r2.md","filePath":"uk/gps_compass/rtk_gps_locosys_r2.md"}'),c={name:"uk/gps_compass/rtk_gps_locosys_r2.md"},n=t("h1",{id:"геодезичнии-компас-locosys-hawk-r2-rtk-gps",tabindex:"-1"},[h("Геодезичний компас LOCOSYS Hawk R2 RTK-GPS "),t("a",{class:"header-anchor",href:"#геодезичнии-компас-locosys-hawk-r2-rtk-gps","aria-label":'Permalink to "Геодезичний компас LOCOSYS Hawk R2 RTK-GPS"'},"​")],-1),_=p('<p><a href="https://www.locosystech.com/en/product/hawk-r2.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R2</a> є двочастотним модулем <a href="./../gps_compass/rtk_gps.html">RTK GPS-приймачем</a>, розробленим для сумісності з Pixhawk. Єдине відмінність між цим модулем та <a href="./rtk_gps_locosys_r1.html">LOCOSYS Hawk R1</a> полягає у наявності магнітометра на Hawk R2.</p><p>Модуль може діяти як RTK GPS ровер, коли встановлено на літак.</p><p>Отримувач здатний одночасно відстежувати всі глобальні цивільні навігаційні системи, включаючи GPS, GLONASS, GALILEO, BEIDOU і QZSS. Воно одночасно отримує сигнали як L1, так і L5, забезпечуючи точність RTK-позиціонування на рівні сантиметрів.</p><p>Вбудована легка гвинтова антена підвищує стабільність позиціювання RTK. Додатково, він обладнаний компасом. Швидкий час першої установки, збіжність RTK, вища чутливість, низьке споживання енергії роблять його кращим вибором для безпілотних літальних апаратів на базі Pixhawk.</p><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li>Висока точність позиціонування на рівні сантиметра RTK + інтегрований магнітометр з 3 осями</li><li>Одночасний прийом сигналів смуг L1 і L5</li><li>Підтримка GPS, ГЛОНАСС, BEIDOU, GALILEO, QZSS</li><li>Здатний до SBAS (WAAS, EGNOS, MSAS, GAGAN)</li><li>Підтримка GNSS з 135 каналами</li><li>Швидкий TTFF на низькому рівні сигналу</li><li>Безкоштовне гібридне передбачення ефемерид для досягнення швидкого холодного запуску</li><li>Стандартна частота оновлення 5 Гц, до 10 Гц (підтримка SBAS лише 5 Гц)</li><li>Вбудований суперконденсатор для резервування системних даних для швидкого отримання супутників</li><li>Вбудована функція компасу 3-х вимірів</li><li>Три світлодіодного індикатора для живлення, PPS та передачі даних</li></ul><p><img src="'+l+'" alt="LOCOSYS Hawk R2"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/en/product/hawk-r2.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R2</a></li></ul><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2><p>Набір RTK GPS включає в себе:</p><ul><li>1x Модуль GPS</li><li>1x Геліксова антена</li><li>1x 6-пінний кабель готовий до JST</li></ul><h2 id="проводка-та-з-єднання" tabindex="-1">Проводка та з&#39;єднання <a class="header-anchor" href="#проводка-та-з-єднання" aria-label="Permalink to &quot;Проводка та з&#39;єднання&quot;">​</a></h2><p>Hawk R2 RTK GPS поставляється з роз&#39;ємом JST на 6 контактів, який можна підключити до порту GPS2 на автопілотах стандарту Pixhawk.</p><p><img src="'+e+'" alt="LOCOSYS Hawk R2 cable for connecting to flight controller"></p><p>Це також може бути використано на інших портах UART, але вам доведеться підключити й налаштувати використаний порт. Нижче наведено роз&#39;єм для надання вам можливості створити власний кабель.</p><h3 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h3><p>LOCOSYS схема виводів GPS наведена нижче.</p><table><thead><tr><th>Пін</th><th>Hawk R2 GPS</th></tr></thead><tbody><tr><td>1</td><td>VCC_5V</td></tr><tr><td>2</td><td>GPS_RX</td></tr><tr><td>3</td><td>GPS_TX</td></tr><tr><td>4</td><td>GNSS_PPS</td></tr><tr><td>5</td><td>Null</td></tr><tr><td>6</td><td>Null</td></tr><tr><td>7</td><td>I2C_CLK</td></tr><tr><td>8</td><td>I2C_DAT</td></tr><tr><td>9</td><td>GND</td></tr></tbody></table><h2 id="налаштування-px4" tabindex="-1">Налаштування PX4 <a class="header-anchor" href="#налаштування-px4" aria-label="Permalink to &quot;Налаштування PX4&quot;">​</a></h2><p>Після підключення вашого Hawk R2 до порту <code>GPS2</code> на сумісних платах Pixhawk, налаштування та використання RTK на PX4 через <em>QGroundControl</em> в основному є &quot;встав і грай&quot; (plug and play). Для отримання додаткової інформації дивіться: <a href="./../gps_compass/rtk_gps.html#positioning-setup-configuration">RTK GPS</a>.</p><p>Ви також повинні налаштувати послідовний порт, який ви використовуєте на правильну швидкість передачі даних. Якщо ви використовуєте GPS2, встановіть параметр <a href="./../advanced_config/parameter_reference.html#SER_GPS2_BAUD">SER_GPS2_BAUD</a> на 230400 8N1.</p><p>Компас вимагає лише звичайного <a href="./../config/compass.html">Калібрування Компасу</a>.</p><h2 id="індикатори-статусу-leds" tabindex="-1">Індикатори статусу LEDs <a class="header-anchor" href="#індикатори-статусу-leds" aria-label="Permalink to &quot;Індикатори статусу LEDs&quot;">​</a></h2><table><thead><tr><th>Колір</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>Зелений</td><td>Індикатор TX</td><td>Передача даних GNSS</td></tr><tr><td>Червоний</td><td>Індикатор живлення</td><td>Потужність</td></tr><tr><td>Синій</td><td>PPS</td><td>Служба точного позиціонування активна</td></tr></tbody></table><p><img src="'+r+'" alt="Hawk A1 LEDs"></p><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><ul><li>Частоти <ul><li>GPS/QZSS: L1 C/A, L5C</li><li>GLONASS: L1OF</li><li>BEIDOU: B1I, B2a</li><li>GALILEO: E1, E5a</li></ul></li><li>Підтримка 135 каналів</li><li>Частота оновлення до 10 Гц (за замовчуванням 5 Гц)</li><li>Час отримання <ul><li>Гарячий старт (Open Sky) за 2 секунди</li><li>Холодний старт (Open Sky) за 28 секунд без AGPS</li></ul></li><li>PPS з тривалістю імпульсу 100 мс, 1,8 В постійного струму</li><li>Зовнішня, активна антена Helix <ul><li>SMA конектор</li></ul></li><li>Підтримка протоколу UBlox <ul><li>U5Hz:UBX-NAV-PVT,UBX-NAV-DOP</li><li>1Hz: UBX-NAV-TIMEGPS</li></ul></li><li>Підключення: <ul><li>6-контактний JST-GH UART/I2C (сумісний з Pixhawk)</li></ul></li><li>Живлення: <ul><li>Напруга живлення постійного струму 3,3 В ~ 5,0 В на вході</li><li>Енергоспоживання &lt;1 Вт</li></ul></li></ul><h2 id="докладніше" tabindex="-1">Докладніше <a class="header-anchor" href="#докладніше" aria-label="Permalink to &quot;Докладніше&quot;">​</a></h2><p>Більше інформації можна знайти на <a href="https://www.locosystech.com/en/product/hawk-r2.html" target="_blank" rel="noreferrer">LOCOSYS Hawk R2</a></p>',30);function u(P,k,m,f,G,O){const a=o("Badge");return S(),d("div",null,[n,s(a,{type:"tip",text:"PX4 v1.13"}),_])}const g=i(c,[["render",u]]);export{T as __pageData,g as default};
