import{_ as t,a as e}from"./chunks/rtk_trimble_two_gnss_pinouts.CqOznO7r.js";import{_ as a,c as r,o,ab as l}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Trimble MB-Two","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/rtk_gps_trimble_mb_two.md","filePath":"uk/gps_compass/rtk_gps_trimble_mb_two.md"}'),i={name:"uk/gps_compass/rtk_gps_trimble_mb_two.md"},c=l('<h1 id="trimble-mb-two" tabindex="-1">Trimble MB-Two <a class="header-anchor" href="#trimble-mb-two" aria-label="Permalink to &quot;Trimble MB-Two&quot;">​</a></h1><p>GPS-приймач <a href="https://www.trimble.com/Precision-GNSS/MB-Two-Board.aspx" target="_blank" rel="noreferrer">Trimble MB-Two RTK</a> - це високоякісний двочастотний <a href="./../gps_compass/rtk_gps.html">RTK GPS-модуль</a>, який можна налаштувати як базовий або трекерний.</p><p>Крім того, надаючи точну інформацію про положення, MB-Two може оцінити кут напрямку (він має підтримку двох антен). Це корисно для ситуацій, де компас не може надати надійну інформацію про напрямок, наприклад, при польоті близько до металевих конструкцій.</p><p><img src="'+t+'" alt="MB-Two Hero image"></p><h2 id="необхідні-параметри-прошивки" tabindex="-1">Необхідні параметри прошивки <a class="header-anchor" href="#необхідні-параметри-прошивки" aria-label="Permalink to &quot;Необхідні параметри прошивки&quot;">​</a></h2><p>При купівлі пристрою необхідно вибрати наступні параметри вбудованого програмного забезпечення:</p><ul><li>[X] [2] [N] [G] [W] [Y] [J] для оновлень позиції 20 Гц та підтримки RTK, горизонтальна точність позиції 1 см та вертикальна 2 см</li><li>[L] LBAND</li><li>[D] DUO - Напрямок з двома антенами</li><li>[B] BEIDOU + [O] GALILEO, за потреби</li></ul><h2 id="антени-та-кабель" tabindex="-1">Антени та кабель <a class="header-anchor" href="#антени-та-кабель" aria-label="Permalink to &quot;Антени та кабель&quot;">​</a></h2><p>Для Trimble MB-Two потрібні дві двохчастотні (L1/L2) антени. Хорошим прикладом є <a href="http://www.maxtena.com/products/helicore/m1227hct-a2-sma/" target="_blank" rel="noreferrer">Maxtenna M1227HCT-A2-SMA</a> (який можна купити, наприклад, від <a href="https://uk.farnell.com/maxtena/m1227hct-a2-sma/antenna-1-217-1-25-1-565-1-61ghz/dp/2484959" target="_blank" rel="noreferrer">Farnell</a>).</p><p>Тип роз&#39;єму антени на пристрої - MMCX. Підходящі кабелі для вищезазначених антен (коннектор SMA) можна знайти тут:</p><ul><li><a href="https://www.digikey.com/products/en?mpart=415-0073-012&amp;v=24" target="_blank" rel="noreferrer">30 см версія</a></li><li><a href="https://www.digikey.com/products/en?mpart=415-0073-018&amp;v=24" target="_blank" rel="noreferrer">45 см версія</a></li></ul><h2 id="підключення-та-з-єднання" tabindex="-1">Підключення та з&#39;єднання <a class="header-anchor" href="#підключення-та-з-єднання" aria-label="Permalink to &quot;Підключення та з&#39;єднання&quot;">​</a></h2><p>Trimble MB-Two підключений до UART на польотному контролері (порт GPS) для передачі даних.</p><p>Для живлення модуля вам знадобиться окреме джерело живлення 3,3 В (максимальне споживання 360 мА).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Модуль не може запитуватися з Pixhawk.</p></div><p>Контакти на 28-контактному роз&#39;ємі пронумеровані, як показано нижче:</p><p><img src="'+e+'" alt="MB-Two Pinout"></p><table><thead><tr><th>Пін</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>6</td><td>Vcc 3.3V</td><td>Джерело живлення</td></tr><tr><td>14</td><td>GND</td><td>Connect to power the supply and GND of the Autopilot</td></tr><tr><td>15</td><td>TXD1</td><td>Connect to RX of the Autopilot</td></tr><tr><td>16</td><td>RXD1</td><td>Connect to TX of the Autopilot</td></tr></tbody></table><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Спочатку встановіть протокол GPS на Trimble (<a href="./../advanced_config/parameter_reference.html#GPS_1_PROTOCOL">GPS_x_PROTOCOL=3</a>).</p><p>Для оцінювання заголовку потрібно, щоб дві антени були на одному рівні і були відстані щонайменше 30 см одна від одної. Напрям, у якому вони обертаються, не має значення, оскільки його можна налаштувати за допомогою параметра <a href="./../advanced_config/parameter_reference.html#GPS_YAW_OFFSET">GPS_YAW_OFFSET</a>.</p><div class="info custom-block"><p class="custom-block-title">Кут <code>GPS_YAW_OFFSET</code> утворюється <em>базовою лінією</em> (лінія між двома GPS антенами) відносно осі x транспортного засобу (передня/задня вісь, як показано <a href="./../config/flight_controller_orientation.html#calculating-orientation">тут</a>).</p></div><p><a href="./../peripherals/serial_configuration.html">Налаштуйте послідовний порт</a>, на якому працюватиме Trimple, використовуючи <a href="./../advanced_config/parameter_reference.html#GPS_1_CONFIG">GPS_1_CONFIG</a>, і встановіть швидкість передачі даних на 115200 за допомогою <a href="./../advanced_config/parameter_reference.html#SER_GPS1_BAUD">SER_GPS1_BAUD</a>.</p><p>Для активації ф’южена покриву для оцінки нахилу, встановіть параметр <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_CTRL">EKF2_GPS_CTRL</a> для увімкнення <em>Орієнтація з подвійною антеною</em>.</p><div class="info custom-block"><p class="custom-block-title">Див. також: <a href="./../gps_compass/#configuring-gps-as-yaw-heading-source">GPS &gt; Configuration &gt; GPS як Yaw/Heading Source</a></p></div>',25),n=[c];function p(_,s,m,d,h,f){return o(),r("div",null,n)}const T=a(i,[["render",p]]);export{g as __pageData,T as default};
