import{_ as t,a,b as r}from"./chunks/automatic_mode.DPW5jJal.js";import{_ as o}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as i,c as l,o as s,ab as e,m as n}from"./chunks/framework.CUflZczI.js";const y=JSON.parse('{"title":"Посібник користувача автопілота PX4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/index.md","filePath":"uk/index.md"}'),h={name:"uk/index.md"},c=e('<div style="float:right;padding:10px;margin-right:20px;"><a href="https://px4.io/"><img src="'+t+'" title="Логотип PX4" width="180px"></a></div><h1 id="посібник-користувача-автопілота-px4" tabindex="-1">Посібник користувача автопілота PX4 <a class="header-anchor" href="#посібник-користувача-автопілота-px4" aria-label="Permalink to &quot;Посібник користувача автопілота PX4&quot;">​</a></h1><p><a href="https://github.com/PX4/PX4-Autopilot/releases" target="_blank" rel="noreferrer"><img src="https://img.shields.io/badge/release-main-blue.svg" alt="Релізів"></a> <a href="https://discuss.px4.io//" target="_blank" rel="noreferrer"><img src="https://img.shields.io/badge/discuss-px4-ff69b4.svg" alt="Обговорення"></a> <a href="https://discord.gg/dronecode" target="_blank" rel="noreferrer"><img src="https://discordapp.com/api/guilds/1022170275984457759/widget.png?style=shield" alt="Discord"></a></p><p>PX4 це <em>Professional Autopilot</em>. Розроблений розробниками світового класу з дрон індустрії та наукових закладів і активно підтримується спільнотою у світі. Він дозволяє працювати з різними типами безпілотних транспортних засобів від гоночних, вантажних дронів до сухопутних автомобілів та надводних човнів.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Цей посібник містить всю інформацію, що вам потрібно знати щоб зібрати, налаштувати та безпечно запускати пристрій на основі PX4. Хочете зробити внесок? Перегляньте розділ <a href="./development/development.html">Розробка</a>.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Цей посібник стосується версії PX4 для <em>розробки</em> (<code>основна</code> гілка). Використовуйте селектор <strong>Версія</strong> для поточної <em>стабільної</em> версії.</p><p>Задокументовані зміни з моменту випуску стабільної версії відображені в <a href="./releases/main.html">примітці до випуску</a>, що розвивається. :::</p><h2 id="як-почати" tabindex="-1">Як почати? <a class="header-anchor" href="#як-почати" aria-label="Permalink to &quot;Як почати?&quot;">​</a></h2><p><a href="./getting_started/">Початок роботи</a> цей гайд мають прочитати всі користувачі! Він включає в себе огляд PX4, функції, які надає стек польотів (режими польоту та функції безпеки) та підтримувані апаратні платформи (контролери польотів, транспортні засоби, планери, системи телеметрії, системи контролю RC).</p><p>Залежно від того, чого ви хочете досягти, наступні поради допоможуть вам мандрувати по цьому посібнику:</p><p><strong>У мене вже є безпілотник, і я просто хочу літати:</strong></p><p>Якщо ви маєте готові до польоту (RTF), який підтримує PX4:</p><ul><li><a href="./config/">Базова конфігурація</a> пояснює, як оновити вашу прошивку до останньої версії, калібрувати основні датчики (компас, спорт/МВФ, швидкість польоту тощо, налаштування свого дистанційного контролю та безпеки функції.</li><li><a href="./flying/">Розділ Політ</a> навчає польоту, в тому числі де і як безпечно літати, і як вирішувати проблеми, пов&#39;язані з приведенням у стан готовності та польотом. Він також надає детальну інформацію про режими польоту.</li></ul><p><strong>Я хочу побудувати дрон з PX4 з нуля:</strong></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>&quot;Безпілотні засоби які підтримуються&quot; перераховані в <a href="./airframes/airframe_reference.html">Airframes</a>. Це транспортні засоби, які тестували і налаштовували конфігурації, які ви можете завантажити за допомогою <em>QGroundControl</em>.</p></div></div><p>Якщо ви хочете побудувати автомобіль з нуля:</p><ul><li>Оберіть шасі — <a href="./airframes/">Airframe Builds</a> надає перелік підтримуваних шасі та детальні інструкції щодо збірки транспортних засобів.</li><li>Виберіть контролер польоту - див. <a href="./getting_started/flight_controller_selection.html">Початок &gt; Польотні контролери</a> and <a href="./flight_controller/">Апаратне забезпечення автопілота</a>.</li><li><a href="./assembly/">Збірка</a> пояснює, як підключити важливі периферійні пристрої до вашого автопілота.</li><li><a href="./config/">Базова конфігурація</a> показує, як оновити прошивку і налаштувати її з параметрами, які відповідають вашому шасі. Цей розділ також пояснює, як калібрувати основні датчики (компас, gyro/IMU, швидкість польоту тощо), а також налаштувати ваш дистанційний контроль та функції безпеки.</li></ul><p>Коли ви будете готові керувати літаючим засобом, відвідайте розділ <a href="./flying/README.html">Політ</a>.</p><p><strong>Я хочу додати вантаж або камеру:</strong></p><p>Розділ корисних навантажень описує як додати камеру або як налаштувати PX4 для доставки вантажів.</p><ul><li><a href="./payloads/">Корисне навантаження</a> описує як інтегрувати корисні навантаження</li></ul><p><strong>Я змінюю підтримувану версію засобу ( дрона чи іншу) :</strong></p><p>Модифікації польотного контролера і основні датчики покриті вище. Для того, щоб використовувати нові сенсори, або якщо ви зробили зміни, що суттєво впливають на характеристики польоту:</p><ul><li><a href="./peripherals/">Периферійне обладнання</a> надає додаткову інформацію про використання зовнішніх датчиків.</li><li><a href="./config/">Базова конфігурація</a> пояснює, як відкалібрувати основні датчики.</li><li><a href="./advanced_config/">Розширену конфігурацію</a> слід використовувати для повторного/точного налаштування планера.</li></ul><p><strong>Я хочу запустити PX4 на новому апаратному забезпеченні та розширити платформу:</strong></p><ul><li><a href="./development/development.html">Розробка</a> пояснює як підтримувати нові повітряні рамки та типи транспортних засобів, змінювати алгоритми польоту, додавайте нові режими, інтегруйте нове обладнання, спілкуйтеся з PX4 ззовні контролера польоту, і зробіть внесок до PX4.</li></ul><h2 id="отримання-допомоги" tabindex="-1">Отримання допомоги <a class="header-anchor" href="#отримання-допомоги" aria-label="Permalink to &quot;Отримання допомоги&quot;">​</a></h2><p>На сторінці <a href="./contribute/support.html">Підтримка</a> пояснюється, як отримати допомогу від основної команди розробників і всієї спільноти.</p><p>Серед інших речей, які вона покриває:</p><ul><li><a href="./contribute/support.html#forums-and-chat">Форуми, в яких ви можете отримати допомогу</a></li><li><a href="./contribute/support.html#diagnosing-problems">Проблеми діагностики</a></li><li><a href="./contribute/support.html#issue-bug-reporting">Як повідомляти про помилки</a></li><li><a href="./contribute/support.html#weekly-dev-call">Дзвінок за тиждень</a></li></ul><h2 id="повідомлення-про-помилки-та-задачі" tabindex="-1">Повідомлення про помилки та задачі <a class="header-anchor" href="#повідомлення-про-помилки-та-задачі" aria-label="Permalink to &quot;Повідомлення про помилки та задачі&quot;">​</a></h2><p>Якщо у вас виникли проблеми з використанням PX4, спершу опублікуйте їх на <a href="./contribute/support.html#forums-and-chat">форумах підтримки</a> (оскільки вони можуть бути спричинені специфічною апаратною конфігурацією вашого засобу).</p><p>За вказівкою команди розробників проблеми з кодом можуть бути підняті та вирішені на <a href="https://github.com/PX4/PX4-Autopilot/issues" target="_blank" rel="noreferrer">Github тут</a>. Якщо це можливо, надайте <a href="./getting_started/flight_reporting.html">журнали польотів</a> та іншу інформацію, яка вимагається в шаблоні проблеми.</p><h2 id="долучитись-до-проєкту" tabindex="-1">Долучитись до проєкту <a class="header-anchor" href="#долучитись-до-проєкту" aria-label="Permalink to &quot;Долучитись до проєкту&quot;">​</a></h2><p>Інформацію про те, як зробити внесок у код і документацію, можна знайти в розділі <a href="./contribute/">Contributing</a>:</p><ul><li><a href="./contribute/">Код</a></li><li><a href="./contribute/docs.html">Документація</a></li><li><a href="./contribute/translation.html">Переклади</a></li></ul><h2 id="переклади" tabindex="-1">Переклади <a class="header-anchor" href="#переклади" aria-label="Permalink to &quot;Переклади&quot;">​</a></h2><p>Існує кілька варіантів <a href="./contribute/translation.html">перекладів</a> цього посібника. Ви можете отримати доступ до вибору конкретного доступного переклада з меню Мови (вгорі праворуч):</p><p><img src="'+a+'" alt="Вибір мови"></p><h2 id="🇺🇦особлива-подяча-всім-контрибюторам-цього-перекладу🇺🇦" tabindex="-1">🇺🇦Особлива подяча всім контрибюторам цього перекладу🇺🇦 <a class="header-anchor" href="#🇺🇦особлива-подяча-всім-контрибюторам-цього-перекладу🇺🇦" aria-label="Permalink to &quot;🇺🇦Особлива подяча всім контрибюторам цього перекладу🇺🇦&quot;">​</a></h2><p>Подяка цим неймовірним людям які допомогли перекласти цей посібник (<a href="https://docs.px4.io/main/uk/" target="_blank" rel="noreferrer">на українську мову</a>) і зробили основну частину перекладу:</p><table><thead><tr><th style="text-align:center;"><a href="https://github.com/tomkaXX" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/8326985?v=3" width="100px;"><br><sub>Tamara Koliada - Project Lead (56%)</sub></a><br><a href="https://github.com/tomkaXX" target="_blank" rel="noreferrer">💻</a> <a href="https://github.com/tomkaXX" target="_blank" rel="noreferrer">📖</a></th><th style="text-align:center;"><a href="https://github.com/freedomserg" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/14028399?v=3" width="100px;"><br><sub>Serhii Syrotskyi(6%)</sub></a><br><a href="https://github.com/freedomserg" target="_blank" rel="noreferrer">💻</a></th><th style="text-align:center;"><a href="https://github.com/Vikkach" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/12811749?v=3" width="100px;"><br><sub>Viktoriia Kachanovska (8%) </sub></a><br><a href="https://github.com/Vikkach" target="_blank" rel="noreferrer">💻</a></th><th style="text-align:center;"><a href="http://github.com/45uee" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/37076939?v=3" width="100px;"><br><sub>Oleksandr Yalynskyi (10%)</sub></a><br><a href="https://github.com/45uee" target="_blank" rel="noreferrer">💻</a></th><th style="text-align:center;"><a href="https://github.com/khrystyna27uz" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/16465609?v=3" width="100px;"><br><sub>Sika Khrystyna (7%) </sub></a><br> <a href="https://github.com/khrystyna27uz" target="_blank" rel="noreferrer">💻</a></th><th style="text-align:center;"><a href="https://github.com/Eduard-05" target="_blank" rel="noreferrer"><img src="https://avatars.githubusercontent.com/u/155777656?v=3" width="100px;"><br><sub>Eduard Pavlenko(7%)</sub></a><br><a href="https://github.com/Eduard-05" target="_blank" rel="noreferrer">💻</a></th></tr></thead></table><p>Також подяка що долучились ⭐ Gazzaladra, ⭐ Aleksi (ukrcherry), ⭐ dimapuntus, ⭐ Andrii Shelestov (streamx3), ⭐ AnastasiiaVS, ⭐ Borys Biletskyy, ⭐ Андрей Свереденко, ⭐ Janna (JannaBi) ⭐ Orm та ⭐ Radiocab.</p><p>Дякуємо за ваш внесок у нашу спільну перемогу! І популяризацію розробки дронів серед українців Слава Україні!🇺🇦</p><h2 id="ліцензія" tabindex="-1">Ліцензія <a class="header-anchor" href="#ліцензія" aria-label="Permalink to &quot;Ліцензія&quot;">​</a></h2><p>PX4 код є вільним для використання та зміни термінів дозволеного <a href="https://opensource.org/licenses/BSD-3-Clause" target="_blank" rel="noreferrer">Ліцензія на речення BSD 3 символів</a>. Ця документація ліцензована за <a href="https://creativecommons.org/licenses/by/4.0/" target="_blank" rel="noreferrer">CC BY 4.0</a>. Для отримання додаткової інформації: <a href="./contribute/licenses.html">ліцензії</a>.</p><h2 id="календар-подіі" tabindex="-1">Календар &amp; події <a class="header-anchor" href="#календар-подіі" aria-label="Permalink to &quot;Календар  &amp; події&quot;">​</a></h2><p><em>Календар Dronecode</em> показує важливі події спільноти для користувачів платформи і розробників. Виберіть посилання нижче, щоб відобразити календар у вашому часовому поясі (а також додати його до вашого власного календаря):</p><ul><li><a href="https://calendar.google.com/calendar/embed?src=linuxfoundation.org_g21tvam24m7pm7jhev01bvlqh8%40group.calendar.google.com&amp;ctz=Europe%2FZurich" target="_blank" rel="noreferrer">Швейцарія - Цюрих</a></li><li><a href="https://calendar.google.com/calendar/embed?src=linuxfoundation.org_g21tvam24m7pm7jhev01bvlqh8%40group.calendar.google.com&amp;ctz=America%2FTijuana" target="_blank" rel="noreferrer">Тихоокеанський час - Тіхуана</a></li><li><a href="https://calendar.google.com/calendar/embed?src=linuxfoundation.org_g21tvam24m7pm7jhev01bvlqh8%40group.calendar.google.com&amp;ctz=Australia%2FSydney" target="_blank" rel="noreferrer">Австралія - Мельбурн/Сідней/Хобарт</a></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Типовий часовий пояс в календарі - Центральноєвропейський час (CET).</p></div>',41),p=n("iframe",{src:"https://calendar.google.com/calendar/embed?title=Dronecode%20Calendar&mode=WEEK&height=600&wkst=1&bgcolor=%23FFFFFF&src=linuxfoundation.org_g21tvam24m7pm7jhev01bvlqh8%40group.calendar.google.com&color=%23691426&ctz=Europe%2FZurich",style:{"border-width":"0"},width:"800",height:"600",frameborder:"0",scrolling:"no"},null,-1),m=e('<h3 id="іконки" tabindex="-1">Іконки <a class="header-anchor" href="#іконки" aria-label="Permalink to &quot;Іконки&quot;">​</a></h3><p>Наступні значки, що використовуються в цій бібліотеці, мають ліцензію окремо (як показано нижче):</p><p><img src="'+o+'" title="Необхідне виправлення позиції (напр. GPS)" width="30px"> <em>placeholder</em> зроблена <a href="https://www.flaticon.com/authors/smashicons" title="Smashicons">Smashicons</a> з <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a> і ліцензована <a href="https://creativecommons.org/licenses/by/3.0/" title="Creative Commons BY 3.0" target="_blank">CC 3.0 BY</a>.</p><p><img src="'+r+'" title="Автоматичний режим" width="30px"> <em>camera-automatic-mode</em> іконка зроблена <a href="https://www.freepik.com" title="Freepik">Freepik</a> з <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a> і ліцензована <a href="http://creativecommons.org/licenses/by/3.0/" title="Creative Commons BY 3.0" target="_blank">CC 3.0 BY</a>.</p><h2 id="управління" tabindex="-1">Управління <a class="header-anchor" href="#управління" aria-label="Permalink to &quot;Управління&quot;">​</a></h2><p>Стек польотів PX4 розміщено під управлінням проєкту <a href="https://www.dronecode.org/" target="_blank" rel="noreferrer">Dronecode</a>.</p><p><a href="https://www.dronecode.org/" style="padding:20px;"><img src="https://mavlink.io/assets/site/logo_dronecode.png" alt="Логотип Dronecode " width="110px"></a><a href="https://www.linuxfoundation.org/projects" style="padding:20px;"><img src="https://mavlink.io/assets/site/logo_linux_foundation.png" alt="Логотип Linux Foundation" width="80px"></a></p><div style="padding:10px;"> </div>',8),d=[c,p,m];function u(g,f,b,_,k,v){return s(),l("div",null,d)}const q=i(h,[["render",u]]);export{y as __pageData,q as default};
